song["sda-hymnal/1"] = {
	title: "Praise to the Lord the Almighty the King of creation",
	composer: "Choral Book for England, 1863",
	poet: "Joachim Neander (1650 - 1680)",
	tune: "Lobe den Herren",
	scripture: "Psalm 103:2-5",
	1: "Praise to the Lord, the Almighty, the King of creation!\nO my soul, praise Him, for He is thy health and salvation!\nAll ye who hear, now to His temple draw near;\nJoin ye in glad adoration!",
	2: "Praise to the Lord, Who o’er all things so wondrously reigneth,\nShieldeth thee under His wings, yea, so gently sustaineth!\nHast thou not seen how thy desires e’er have been\nGranted in what He ordaineth?",
	3: "Praise to the Lord, who doth prosper thy work and defend thee;\nSurely His goodness and mercy here daily attend thee.\nPonder anew what the Almighty can do,\nIf with His love He befriend thee."
}
song["sda-hymnal/2"] = {
	title: "All Creatures of Our God and King",
	composer: "Geistliche Kirchengesänge, Köln, 1623",
	poet: "Francis of Assisi, 1225 (1182 - 1226)",
	arranger: "Ralph Vaughan Williams, 1906 (1872 - 1958)",
	tune: "Lasst uns erfreuen",
	info: "Higher key, No. 228",
	copyright: "“All Creatures of Our God and King” Words copyright © by J. Curwen & Sons, Ltd. Used by permission of G. Schirmer, Inc. Music from The English Hymnal; used by permission of Oxford University Press.",
	1: "All creatures of our God and King\nLift up your voice and with us sing,\nAlleluia! Alleluia!\nO burning sun with golden beam\nAnd silver moon with softer gleam!\nRefrain\nO praise Him! O praise Him!\nAlleluia! Alleluia! Alleluia!",
	2: "O rushing wind and breezes soft,\nO clouds that ride the winds aloft,\nO praise Him! Alleluia!\nO rising morn, in praise rejoice,\nO lights of evening, find a voice!",
	3: "O flowing waters, pure and clear,\nMake music for your Lord to hear,\nO praise Him! Alleluia!\nO fire so masterful and bright,\nProviding us with warmth and light.",
	4: "Let all things their Creator bless,\nAnd worship Him in humbleness,\nO praise Him! Alleluia!\nOh, praise the Father, praise the Son,\nAnd praise the Spirit, three in One!"
}
song["sda-hymnal/3"] = {
	title: "God Himself Is With Us",
	composer: "Joachim Neander, 1680 (1650 - 1680)",
	poet: "Gerhard Tersteegen, 1729",
	tune: "Wunderbarer König",
	copyright: "“God Himself Is With Us” Words from The Hymnal 1940, copyright The Church Pension Fund. Used by permission.",
	1: "God Himself is with us;\nLet us all adore Him,\nAnd with awe appear before Him.\nGod is here within us;\nSoul, in silence fear Him,\nHumbly, fervently draw near Him.\nNow His own who have known God,\nIn worship lowly,\nYield their spirits wholly.",
	2: "Come, abide within me;\nLet my soul, like Mary,\nBe Thine earthly sanctuary.\nCome, indwelling Spirit,\nWith transfigured splendor;\nLove and honor will I render.\nWhere I go here below,\nLet me bow before Thee,\nKnow Thee and adore Thee.",
	3: "Gladly we surrender\nEarth’s deceitful treasures,\nPride of life and sinful pleasures:\nGladly, Lord, we offer\nThine to be forever,\nSoul and life and each endeavor.\nThou alone shall be known\nLord of all our being,\nLife’s true way decreeing."
}
song["sda-hymnal/4"] = {
	title: "Praise, My Soul, the King of Heaven",
	composer: "John Goss (1800 - 1880)",
	poet: "Henry Francis Lyte (1793 - 1847)",
	tune: "Lauda anima",
	scripture: "Psalm 103:2-5",
	info: "Alternate tune, REGENT SQUARE, No. 119",
	1: "Praise, my soul, the King of heaven;\nTo his feet thy tribute bring;\nRansomed, healed, restored, forgiven,\nWho like thee His praise should sing?\nPraise Him, praise Him, alleluia!\nPraise the everlasting King.",
	2: "Praise him for his grace and favor\nTo our fathers in distress;\nPraise him still the same forever,\nSlow to chide and swift to bless:\nPraise Him, praise Him, alleluia!\nGlorious in His faithfulness.",
	3: "Tenderly He shields and spares us;\nWell our feeble frame he knows;\nIn his hands He gently bears us,\nRescues us from all our foes.\nPraise Him, praise Him, alleluia!\nWidely yet his mercy flows.",
	4: "Angels, help us to adore him;\nYe behold him face to face;\nSun and moon, bow down before him,\nDwellers all in time and space.\nPraise Him, praise Him, alleluia!\nPraise with us the God of grace."
}
song["sda-hymnal/5"] = {
	title: "All My Hope on God Is Founded",
	composer: "Herbert Howells, 1936 (1892 - 1983)",
	poet: "Joachim Neander (1650 - 1680)",
	tune: "Michael",
	copyright: "“All My Hope on God Is Founded” Music reproduced by permission of Novello & Company Limited.",
	1: "All my hope on God is founded;\nHe doth still my trust renew.\nMe through change and chance He guideth,\nOnly good and only true.\nGod unknown, He alone\nCalls my heart to be His own.",
	2: "Pride of man and earthly glory,\nSword and crown betray his trust;\nWhat with care and toil he buildeth,\nTower and temple fall to dust.\nBut God’s power, hour by hour,\nIs my temple and my tower.",
	3: "God’s great goodness aye endureth,\nDeep His wisdom, passing thought;\nSplendor, light and life attend Him,\nBeauty springeth out of naught.\nLove doth stand at His hand;\nJoy doth wait on His command.",
	4: "Still from man to God eternal,\nSacrifice of praise be done.\nHigh above all praises praising,\nFor the gift of Christ His son.\nChrist doth call one and all:\nYe who follow shall not fall."
}
song["sda-hymnal/6"] = {
	title: "O Worship the Lord",
	composer: "Edwin Barnes, 1886 (1864 - 1930)",
	poet: "John S. B. Monsell (1811 - 1875)",
	tune: "Southampton",
	1: "O worship the Lord in the beauty of holiness,\nBow down before Him, His glory proclaim;\nWith gold of obedience, and incense of lowliness,\nKneel and adore Him: the Lord is His name.",
	2: "Low at His feet lay thy burden of carefulness,\nHigh on His heart He will bear it for thee,\nComfort thy sorrows, and answer thy prayerfulness,\nGuiding thy steps as may best for thee be.",
	3: "Fear not to enter His courts in the slenderness\nOf the poor wealth thou wouldst reckon as thine;\nTruth in its beauty, and love in its tenderness,\nThese are the offerings to lay on His shrine.",
	4: "These, though we bring them in trembling and fearfulness,\nHe will accept for the name that is dear;\nMornings of joy give for evenings of tearfulness,\nTrust for our trembling, and hope for our fear."
}
song["sda-hymnal/7"] = {
	title: "The Lord in Zion Reigneth",
	composer: "Hart P. Danks (1834 - 1903)",
	poet: "Fanny J. Crosby (1820 - 1915)",
	1: "The Lord in Zion reigneth, let all the world rejoice,\nAnd come before His throne of grace with tuneful heart and voice;\nThe Lord in Zion reigneth, and there His praise shall ring,\nTo Him shall princes bend the knee and kings their glory bring.",
	2: "The Lord in Zion reigneth, and who so great as He?\nThe depths of earth are in His hands; He rules the mighty sea.\nO crown His Name with honor, and let His standard wave,\nTill distant isles beyond the deep shall own His power to save.",
	3: "The Lord in Zion reigneth, these hours to Him belong;\nO enter now His temple gates, and fill His courts with song;\nBeneath His royal banner let every creature fall,\nExalt the King of heaven and earth, and crown Him Lord of all."
}
song["sda-hymnal/8"] = {
	title: "We Gather Together",
	poet: "Anonymous, 1625",
	arranger: "Edward Kremser (1838 - 1914)",
	tune: "Kremser",
	1: "We gather together to ask the Lord’s blessing;\nHe chastens and hastens His will to make known.\nThe wicked oppressing now cease from distressing.\nSing praises to His Name; He forgets not His own.",
	2: "Beside us to guide us, our God with us joining,\nOrdaining, maintaining His kingdom divine;\nSo from the beginning the fight we were winning;\nThou, Lord, were at our side, all glory be Thine!",
	3: "We all do extol Thee, Thou leader triumphant,\nAnd pray that Thou still our defender wilt be.\nLet Thy congregation escape tribulation;\nThy name be ever praised! O Lord, make us free!"
}
song["sda-hymnal/9"] = {
	title: "Let All the World in Every Corner Sing",
	composer: "Basil Harwood (1859 - 1949)",
	poet: "George Herbert (1593 - 1632)",
	tune: "Luckington",
	copyright: "“Let All the World in Every Corner Sing”  Used by permission of the Executors of the late Dr. Basil Harwood",
	1: "Let all the world in every corner sing, My God and King!\nThe heavens are not too high, His praise may thither fly;\nThe earth is not too low, His praises there may grow.\nLet all the world in every corner sing, My God and King!",
	2: "Let all the world in every corner sing, My God and King!\nThe church with psalms must shout, No door can keep them out;\nBut, above all, the heart must bear the longest part.\nLet all the world in every corner sing, My God and King!"
}
song["sda-hymnal/10"] = {
	title: "Come, Christians, Join to Sing",
	poet: "Christian Henry Bateman (1813 - 1889)",
	arranger: "David Evans (1874 - 1948)",
	tune: "Madrid",
	copyright: "“Come, Christians, Join to Sing”  Music from the Revised Church Hymnary 1927 by permission of Oxford University Press.",
	1: "Come, Christians, join to sing,\nAlleluia! Amen!\nLoud praise to Christ our King;\nAlleluia! Amen!\nLet all, with heart and voice,\nBefore His throne rejoice;\nPraise is His gracious choice:\nAlleluia! Amen!",
	2: "Come, lift your hearts on high;\nAlleluia! Amen!\nLet praises fill the sky;\nAlleluia! Amen!\nHe is our Guide and Friend;\nTo us He’ll condescend;\nHis love shall never end:\nAlleluia! Amen!",
	3: "Praise yet our Christ again;\nAlleluia! Amen!\nLife shall not end the strain;\nAlleluia! Amen!\nOn heaven’s blissful shore\nHis goodness we’ll adore,\nSinging forevermore,\nAlleluia! Amen!"
}
song["sda-hymnal/11"] = {
	title: "The God of Abraham Praise",
	poet: "Thomas Olivers, c. 1770 (1725 - 1799)",
	tune: "Leoni",
	scripture: "Exodus 3:14, 15",
	1: "The God of Abraham praise,\nWho reigns enthroned above;\nAncient of everlasting days,\nAnd God of love;\nJehovah! Great I AM!\nBy earth and heaven confessed;\nI bow and bless the sacred name,\nForever blest.",
	2: "The God of Abraham praise,\nAt whose supreme command\nFrom earth I rise, and seek the joys\nAt His right hand;\nI all on earth forsake,\nIts wisdom, fame and power;\nAnd Him my only portion make,\nMy shield and tower.",
	3: "The whole triumphant host\nGive thanks to God on high;\n“Hail, Father, Son, and Holy Ghost!”\nThey ever cry;\nHail, Abraham’s God and mine!\nI join the heavenly lays;\nAll might and majesty are Thine,\nAnd endless praise."
}
song["sda-hymnal/12"] = {
	title: "Joyful, Joyful, We Adore Thee",
	composer: "Ludwig van Beethoven, 1824 (1770 - 1827)",
	poet: "Henry van Dyke, 1907 (1852 - 1933)",
	tune: "Hymn to Joy",
	copyright: "“Joyful, Joyful, We Adore Thee” Words reprinted with the permission of Charles Scribner’s Sons.",
	1: "Joyful, joyful, we adore Thee,\nGod of glory, Lord of love;\nHearts unfold like flow’rs before Thee,\nHail Thee as the sun above.\nMelt the clouds of sin and sadness,\nDrive the dark of doubt away;\nGiver of immortal gladness,\nFill us with the light of day!",
	2: "All Thy works with joy surround Thee,\nEarth and heav’n reflect Thy rays,\nStars and angels sing around Thee,\nCenter of unbroken praise;\nField and forest, vale and mountain,\nBloss’ming meadow, flashing sea,\nChanting bird and flowing fountain\nCall us to rejoice in Thee.",
	3: "Thou art giving and forgiving,\nEver blessing, ever blest,\nWellspring of the joy of living,\nOceandepth of happy rest!\nThou the father, Christ our Brother –\nAll who live in love are Thine:\nTeach us how to love each other,\nLift us to the joy divine."
}
song["sda-hymnal/13"] = {
	title: "New Songs of Celebration Render",
	tune: "Rendez a Dieu",
	scripture: "Psalm 98",
	copyright: "“New Songs of Celebration Render” Words copyright © 1974 by Agape, Carol Stream, IL 60188. All rights reserved. Used by permission. Adaption copyright © 1979 by Hinshaw Music, Inc. Used by permission 2/15/84.",
	1: "New songs of celebration render\nTo Him who has great wonders done.\nLove sits enthroned in ageless splendor:\nCome, and adore the mighty One.\nHe has made known His great salvation\nWhich all His friends with joy confess:\nHe has revealed to every nation\nHis everlasting righteousness.",
	2: "Joyfully, heartily resounding,\nLet every instrument and voice\nPeal out the praise of grace abounding\nCalling the whole world to rejoice.\nTrumpets and organs, set in motion\nSuch sounds as make the heavens ring;\nAll things that live in earth and ocean,\nMake music for your mighty King.",
	3: "Rivers and seas and torrents roaring,\nHonor the Lord with wild acclaim;\nMountains and stones look up adoring,\nAnd find a voice to praise His name.\nRighteous, commanding, ever glorious,\nPraises be His that never cease:\nJust is our God, whose truth victorious,\nEstablishes the world in peace."
}
song["sda-hymnal/14"] = {
	title: "Let Us Praise the Name of the Lord",
	composer: "Alfred Stier, 1949 (1880 - 1967)",
	poet: "Ursula Schlenker, 1949 (1930 - )",
	copyright: "“Let Us Praise the Name of the Lord”  Copyright Verlag Merseburger.",
	1: "Let us praise the name of the Lord!\nGive Him glory, Amen.",
	2: "Go ye into all the world.\nAlleluia, Amen.",
	3: "Amen, amen,\nAmen, amen."
}
song["sda-hymnal/15"] = {
	title: "My Maker and My King",
	poet: "Anne Steele (1716 - 1778)",
	tune: "El Kader",
	1: "My Maker and my King,\nTo Thee my all I owe;\nThy sovereign bounty is the spring\nWhence all my blessings flow;\nThy sovereign bounty is the spring\nWhence all my blessings flow.",
	2: "The creature of Thy hand,\nOn Thee alone I live;\nMy God, Thy benefits demand\nMore praise than I can give.\nMy God, Thy benefits demand\nMore praise than I can give.",
	3: "Lord, what can I impart\nWhen all is Thine before?\nThy love demands a thankful heart;\nThe gift, alas! how poor.\nThy love demands a thankful heart;\nThe gift, alas! how poor.",
	4: "O! let Thy grace inspire\nMy soul with strength divine;\nLet every word each desire\nAnd all my days be Thine.\nLet every word each desire\nAnd all my days be Thine."
}
song["sda-hymnal/16"] = {
	title: "All People That on Earth Do Well",
	composer: "Louis Bourgeois, 1551 (c. 1510 - 1561)",
	poet: "William Kethe (? - c. 1593)",
	tune: "Old Hundredth",
	scripture: "Psalm 100",
	1: "All people that on earth do dwell,\nsing to the Lord with cheerful voice.\nHim serve with mirth, his praise forth tell;\ncome ye before him and rejoice.",
	2: "Know that the Lord is God indeed;\nwithout our aid he did us make;\nwe are his folk, he doth us feed,\nand for his sheep he doth us take.",
	3: "O enter then his gates with praise;\napproach with joy his courts unto;\npraise, laud, and bless his name always,\nfor it is seemly so to do.",
	4: "For why! the Lord our God is good;\nhis mercy is forever sure;\nhis truth at all times firmly stood,\nand shall from age to age endure."
}
song["sda-hymnal/17"] = {
	title: "Lord of All Being, Throned Afar",
	tune: "Park Street",
	1: "Lord of all being, throned afar,\nThy glory flames from sun and star;\nCenter and soul of every sphere\nYet to each loving heart how near!\nYet to each loving heart how near!",
	2: "Sun of our life, Thy quickening ray\nShed on our path the glow of day;\nStar of our hope, Thy softened light\nCheers the long watches of the night,\nCheers the long watches of the night.",
	3: "Our midnight is Thy smile withdrawn;\nOur noontide is Thy gracious dawn;\nOur rainbow arch, Thy mercy’s sign;\nAll, save the clouds of sin, are Thine,\nAll, save the clouds of sin, are Thine.",
	4: "Lord of all life, below, above\nWhose light is truth, whose warmth is love,\nBefore Thy ever-blazing throne\nWe ask no luster of our own,\nWe ask no luster of our own.",
	5: "Grant us Thy truth to make us free\nAnd kindling hearts that burn for Thee;\nTill all Thy living altars claim\nOne holy light, one heavenly flame!\nOne holy light, one heavenly flame."
}
song["sda-hymnal/18"] = {
	title: "O Morning Star, How Fair and Bright",
	tune: "Wie schön leuchtet",
	copyright: "“O Morning Star, How Fair and Bright” Words copyright © 1978 Lutheran Book of Worship. Used by permission of Augsburg Publishing House. Setting from Orgelchoralbuch Wurtemberg © Gesangbuchverlag der Evangelischen Landeskirche Wuttemberg, Stuttgart. Used b",
	1: "O Morning Star, how fair and bright!\nYou shine with God’s own truth and light,\nAglow with grace and mercy!\nOf Jacob’s race, King David’s son,\nOur Lord and Master, You have won\nOur hearts to serve You only!\nLowly, holy!\nGreat and Glorious, all victorious,\nRich in blessing! Rule and might o’er all possessing!",
	2: "Lord, when you look on us in love,\nAt once there falls from God above\nA ray of purest pleasure.\nYour Word and Spirit, flesh and blood,\nRefresh our souls with heav-‘nly food.\nYou are our dearest treasure!\nLet Your mercy warm and cheer us!\nO draw near us! For You teach us\nGod’s own love through You has reached us.",
	3: "Almighty Father, in Your Son\nYou loved us, when not yet begun\nWas this old earth’s foundation!\nYour Son has ransomed us in love\nTo live in Him here and above:\nThis is Your great salvation.\nAlleluia! Christ the living,\nTo us giving life forever,\nKeeps us Yours and fails us never!",
	4: "O let the harps break forth in sound!\nOur joy be all with music crowned,\nOur voices gaily blending!\nFor Christ goes with us all the way –\nToday, tomorrow, every day!\nHis love is never ending!\nSing out! Ring out!\nJubilation! Exultation!\nTell the story!\nGreat is He, the King of glory!"
}
song["sda-hymnal/19"] = {
	title: "O Sing a New Song to the Lord",
	tune: "St. Magnus",
	scripture: "Psalm 98",
	1: "O sing a new song to the Lord\nFor marvels He has done;\nHis right hand and His holy arm\nThe victory have won.",
	2: "With harp, and voice of psalms\nUnto Jehovah sing;\nLet trumpets and the echoing horn\nAcclaim the Lord our King!",
	3: "Let seas with all their creatures roar,\nThe world and dwellers there,\nAnd let the rivers clap their hands,\nThe hills their joy declare.",
	4: "Before the Lord: because He comes,\nTo judge the earth come He;\nHe’ll judge the world\nwith righteousness,\nHis folk with equity."
}
song["sda-hymnal/20"] = {
	title: "O Praise Ye the Lord",
	tune: "Laudate Dominum",
	scripture: "Psalm 150",
	1: "O praise ye the Lord!\nPraise Him in the height;\nRejoice in His word,\nYe angels of light;\nYe heavens, adore Him\nBy whom ye were made,\nAnd worship before Him,\nIn brightness arrayed.",
	2: "O praise ye the Lord!\nPraise Him upon earth,\nIn tuneful accord:\nYe sons of new birth;\nPraise Him who hath brought\nyou His grace from above,\nPraise Him who hath taught\nyou To sing of His love.",
	3: "O praise ye the Lord,\nAll things that give sound;\nEach jubilant chord,\nRe-echo around;\nLoud organs His glory\nForth tell in deep tone,\nAnd sweet harp, the story\nOf what He hath done.",
	4: "O praise ye the Lord!\nThanksgiving and song\nTo Him be outpoured\nAll ages along:\nFor love in creation,\nFor heaven restored.\nFor grace of salvation,\nO praise ye the Lord!"
}
song["sda-hymnal/21"] = {
	title: "Immortal, Invisible, God Only Wise",
	tune: "St. Denio",
	scripture: "1 Tim. 1:17",
	1: "Immortal, invisible, God only wise,\nIn light inaccessible hid from our eyes,\nMost blessed, most glorious, the Ancient of Days,\nAlmighty, victorious, Thy great Name we praise.",
	2: "Unresting, unhasting, and silent as light,\nNor wanting, nor wasting, Thou rulest in might;\nThy justice, like mountains, high soaring above\nThy clouds, which are fountains of goodness and love.",
	3: "To all, life Thou givest, to both great and small;\nIn all life Thou livest, the true life of all;\nWe blossom and flourish as leaves on the tree,\nAnd wither and perish – but naught changeth Thee.",
	4: "Great Father of glory, pure Father of light,\nThine angels adore Thee, all veiling their sight;\nAll praise we would render; O help us to see\n‘Tis only the splendor of light hideth Thee!"
}
song["sda-hymnal/22"] = {
	title: "God Is Our Song",
	tune: "Old 124th",
	scripture: "Psalm 118:14",
	copyright: "“God Is Our Song” Words copyright © 1976 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "God is our Song, and every singer blest\nWho praising Him finds energy and rest.\nAll who praise God with unaffected joy\nGive back to us the widom we destroy,\nGive back to us the widom we destroy.",
	2: "God is our Song, for Jesus comes to save;\nWhile praising Him we offer all we have.\nNew songs we sing, in ventures new unite,\nWhen Jesus leads us upward into light,\nWhen Jesus leads us upward into light.",
	3: "This is our Song no conflict ever drowns;\nWho praises God our human wrath disowns.\nLove knows what rich complexities of sound\nGod builds upon a simple common ground,\nGod builds upon a simple common ground.",
	4: "God is our Silence when no songs are sung,\nWhen ecstasy or sorrow stills the tongue.\nGlorious the faith which silently obeys\nUntil we find again the voice of praise,\nUntil we find again the voice of praise."
}
song["sda-hymnal/23"] = {
	title: "Now the Joyful Bells A-Ringing",
	tune: "Nos Galan",
	copyright: "“Now the Joyful Bells A-Ringing” Words from Oxford Book of Carols by permission of Oxford University Press. Arrangement copyright © by Wayne Hooper.",
	1: "Now the joyful bells a-ring,\nAll ye mountains of the Lord!\nLift our hearts like birds a-winging,\nAll ye mountains, praise the Lord!\nNow our festal season bringing\nKinsmen all to bide and board,\nSets our cheery voices singing;\nAll ye mountains, praise the Lord!",
	2: "Dear our home as dear none other;\nWhere the mountains praise the Lord!\nGladly here our care we smother;\nWhere the mountains praise the Lord!\nHere we know that Christ our brother\nBinds us all as by a cord:\nHe was born of Mary mother\nWhere the mountains praise the Lord!",
	3: "Cold the year, new whiteness wearing,\nAll ye mountains, praise the Lord!\nPeace, good will to us a-bearing,\nAll ye mountains, praise the Lord!\nNow we all God’s goodness sharing\nBreak the bread and sheath the Sword:\nBright our hearths the signal flaring,\nAll ye mountains, praise the Lord!"
}
song["sda-hymnal/24"] = {
	title: "Every Star Shall Sing a Carol",
	tune: "Every Star",
	copyright: "“Every Star Shall Sing a Carol” Copyright © 1961, by Galliard, Ltd. Used by permission of Galaxy Music Corporation, New York, sole U.S. agent.",
	1: "Every star shall sing a carol;\nEvery creatures, high or low,\nCome and praise the King of heaven\nBy whatever name you know.\nGod above, Man below,\nHoly is the name I know.",
	2: "When the King of all creation\nHad a cradle on the earth,\nHoly was the human body,\nHoly was the human birth.\nGod above, Man below,\nHoly is the name I know.",
	3: "Every star and every planet,\nEvery creature, high or low,\nCome and praise the King of heaven\nBy whatever name you know.\nGod above, Man below,\nHoly is the name I know."
}
song["sda-hymnal/25"] = {
	title: "Praise the Lord His Glories Show",
	tune: "Llanfair",
	scripture: "Psalm 150",
	1: "Praise the Lord, His glories show, Alleluia!\nSaints within His courts below, Alleluia!\nAngels ’round His throne above, Alleluia!\nAll that see and share His love, Alleluia!",
	2: "Earth to heaven and heaven to earth, Alleluia!\nTell His wonders, sing His worth, Alleluia!\nAge to age and shore to shore, Alleluia!\nPraise Him, praise Him evermore! Alleluia!",
	3: "Praise the Lord, His mercies trace, Alleluia!\nPraise His providence and grace, Alleluia!\nAll that He for man hath done, Alleluia!\nAll He sends us through His Son. Alleluia!"
}
song["sda-hymnal/26"] = {
	title: "Praise the Lord! You Heavens Adore Him",
	tune: "An die Freude",
	scripture: "Psalm 148",
	1: "Praise the Lord! you heavens, adore Him;\nPraise Him, angels in the height;\nSun and moon, rejoice before Him,\nPraise Him, all you stars of light.\nPraise the Lord, for He has spoken;\nWorld His mighty voice obeyed;\nLaws which never shall be broken\nFor their guidance He has made.",
	2: "Praise the Lord! for He is glorious;\nNever shall HIs promise fail.\nGod has made His saints victorious;\nSin and death shall not prevail.\nPraise the God of our salvation!\nHosts on high, His power proclaim;\nHeaven and earth and all creation,\nLaud and magnify His name.",
	3: "Worship, honor, glory, blessing,\nLord, we offer as our gift.\nYoung and old, Your praise expressing,\nOur glad songs to You we lift.\nAll the saints in heaven adore You,\nWe would join their glad acclaim;\nAs Your angels serve before You,\nSo on earth we praise Your name."
}
song["sda-hymnal/27"] = {
	title: "Rejoice Ye Pure in Heart!",
	tune: "Marion",
	scripture: "Psalm 20:5",
	1: "Rejoice ye pure in heart!\nRejoice, give thanks, and sing;\nYour festal banner wave on high,\nThe cross of Christ your King.\nRefrain\nRejoice, rejoice, rejoice,\nGive thanks and sing.",
	2: "With voice as full and strong\nAs ocean’s surging praise,\nSend forth the sturdy hymns of old,\nThe psalms of ancient days.",
	3: "With all the angel choirs,\nWith all the saints of earth,\nPour out the strains of joy and bliss,\nTrue rapture, noblest mirth.",
	4: "Yes, on through life’s long path,\nStill chanting as ye go;\nFrom youth to age, by night and day,\nIn gladness and in woe.",
	5: "Praise Him who reigns on high,\nThe Lord whom we adore,\nThe Father, Son and Holy Ghost,\nOne God forever more."
}
song["sda-hymnal/28"] = {
	title: "Praise We the Lord",
	tune: "Fragrance",
	copyright: "“Praise We the Lord” Words and arrangement from the Oxford Book of Carols by permission of Oxford University Press.",
	1: "Praise we the Lord, who made all beauty\nFor all our senses to enjoy;\nOwe we our humble thanks and duty\nThat simple pleasures never cloy;\nPraise we the Lord, who made all beauty\nFor all our senses to enjoy.",
	2: "Praise Him who loves to see young lovers,\nFresh hearts that swell with youthful pride;\nThank Him who sends the sun above us,\nAs bridegroom fit to meet his bride;\nPraise Him who loves to see young lovers,\nFresh hearts that swell with youthful pride.",
	3: "Praise Him who by a simple flower\nLifts up our hearts to things above;\nThank Him who gives to each one power\nTo find a friend to know and love;\nPraise Him who by a simple flower\nLifts up our hearts to things above."
}
song["sda-hymnal/29"] = {
	title: "Sing Praise to God",
	tune: "Mit Freuden zart",
	1: "Sing praise to God who reigns above,\nthe God of all creation,\nthe God of power, the God of love,\nthe God of our salvation.\nWith healing balm my soul He fills,\nand every faithless murmur stills;\nTo God all praise and glory!",
	2: "What God’s almighty power hath made\nHis gracious mercy keepeth;\nBy morning glow or evening shade,\nHis watchful eye ne’er sleepeth,\nWithin the kingdom of his might,\nLo! all is just, and all is right:\nTo God all praise and glory!",
	3: "The Lord is never far away,\nthroughout all grief distressing,\nan ever present help and stay,\nour peace and joy and blessing.\nAs with a mother’s tender hand,\nHe leads His own, His chosen band:\nTo God all praise and glory!",
	4: "Then all my gladsome way along,\nI sing aloud thy praises,\nthat men may hear the grateful song\nmy voice unwearied raises:\nBe joyful in the Lord, my heart!\nboth soul and body bear your part!\nTo God all praise and glory."
}
song["sda-hymnal/30"] = {
	title: "Holy God, We Praise Your Name",
	tune: "Grosser Gott",
	1: "Holy God, we praise Your name;\nLord of all, we bow before You!\nAll on earth Your scepter claim,\nAll in heaven above adore You;\nInfinite Your vast domain.\nEverlasting is Your reign.",
	2: "Hark! the loud celestial hymn\nAngel choirs above are raising,\nCherubim and seraphim,\nIn unceasing chorus praising;\nFill the heavens with sweet accord:\nHoly, holy, holy, Lord.",
	3: "Holy Father, Holy Son,\nHoly Spirit, three we name You;\nWhile in essence only one,\nUndivided God we claim You;\nAnd adoring bend the knee,\nWhile we own the mystery."
}
song["sda-hymnal/31"] = {
	title: "Tell Out, My Soul",
	tune: "Morestead",
	scripture: "Luke 1:46-55",
	copyright: "“Tell Out, my Soul” Words copyright © 1962 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © by Sydney Watson.",
	1: "Tell out my soul, the greatness of the Lord:\nUnnumbered blessings, give my spirit voice;\nTender to me the promise of His Word;\nIn God my Saviour shall my heart rejoice.",
	2: "Tell out, my soul, the greatness of His name:\nMake known His might, the deeds His arm has done;\nHis mercy sure, from age to age the same;\nHis holy name, the Lord, the Mighty One.",
	3: "Tell out, my soul, the greatness of His might:\nPow’rs and dominions lay their glory by;\nProud hearts and stubborn wills are put to flight,\nThe hungry fed, the humble lifted high.",
	4: "Tell out, my soul, the glories of His word:\nFirm is His promise, and His mercy sure.\nTell out, my soul, the greatness of the Lord\nTo children’s children and forevermore."
}
song["sda-hymnal/32"] = {
	title: "When in Our Music God is Glorified",
	tune: "Engelberg",
	copyright: "“When in Our Music God Is Glorified” Words copyright © 1972 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "When in our music God is glorified,\nAnd adoration leaves no room for pride,\nIt is as though the whole creation cried:\nAlleluia!",
	2: "How oft, in making music, we have found\nA new dimension in the world of sound,\nAs worship moved us to a more profound\nAlleluia!",
	3: "And did not Jesus sing a psalm that night\nWhen utmost evil strove against the light?\nThen let us sing for whom He won the fight:\nAlleluia!",
	4: "Let every instrument be used for praise;\nLet all rejoice who have a voice to raise;\nAnd may God give us faith to sing always:\nAlleluia!"
}
song["sda-hymnal/33"] = {
	title: "Sing a New Song to the Lord",
	scripture: "Psalm 98",
	copyright: "“Sing A New Song to the Lord” Words copyright © 1973 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © by G. I. A. Publications, Inc. Used by permission.",
	1: "Sing a new song to the Lord,\nHe to whom wonders belong.\nRejoice in His triumph and tell of His power.\nO sing a new song to the Lord.",
	2: "Now to the ends of the earth\nSee His salvation is shown.\nAnd still He remembers His mercy and truth,\nUnchanging in love to His own.",
	3: "Sing a new song and rejoice.\nPublish His praises abroad.\nLet voices in chorus with trumpet and horn,\nResound for the joy of the Lord.",
	4: "Join with the hills and the sea,\nThunders of praise to prolong.\nIn judgement and justice\nHe comes to the earth\nO sing a new song to the Lord."
}
song["sda-hymnal/34"] = {
	title: "Wake the Song",
	tune: "Anniversary Song (Sherwin)",
	1: "Wake the song of joy and gladness;\nHither bring your noblest lays;\nBanish every thought of sadness,\nPouring forth your highest praise.\nSing to Him whose care has brought us\nOnce again with friends to meet,\nAnd whose loving voice has taught us\nOf the way to Jesus’ feet.\nRefrain\nWake the song, wake the song,\nThe song of joy and gladness,\nWake the song, wake the song,\nThe song of Jubilee.",
	2: "Joyfully with songs and banners,\nWe will greet the festal day;\nShout aloud our glad hosannas,\nAnd our grateful homage pay.\nWe will chant our Savior’s glory\nwhile our thoughts we raise above,\nTelling still the old, old, story,\nPrecious theme- redeeming love!",
	3: "Thanks to Thee, O holy Father,\nFor the mercies of the year;\nMay each heart, as here we gather,\nSwell with gratitude sincere,\nThanks to Thee, O loving Savior,\nFor redemption through Thy blood.\nBreathe upon us, Holy Spirit,\nSweetly draw us near to God."
}
song["sda-hymnal/35"] = {
	title: "With Songs and Honors",
	tune: "Bedford",
	scripture: "Psalm 147",
	1: "With songs and honors sounding loud,\nAddress the Lord on high;\nOver the heavens He spreads His cloud,\nAnd waters veil the sky.",
	2: "He sends His showers of blessing down\nTo cheer the plains below;\nHe makes the grass the mountains crown,\nAnd corning valleys grow.",
	3: "His steady counsels change the face\nOf the declining year;\nHe bids the sun cut short his race,\nAnd wintry days appear.",
	4: "He sends His word, and melts the snow;\nThe fields no longer mourn;\nHe calls the warmer gales to blow,\nAnd bids the spring return.",
	5: "The changing wind, the flying cloud,\nObey His mighty word:\nWith songs and honors sounding loud\nPraise ye the sovereign Lord!"
}
song["sda-hymnal/36"] = {
	title: "O Thou in Whose Presence",
	tune: "Beloved",
	scripture: "Song of Solomon 1:7",
	1: "O Thou in whose presence my soul takes delight,\nOn whom in affliction I call,\nMy comfort by day and my song in the night,\nMy hope, my salvation, my all!",
	2: "His voice, as the sound of the dulcimer sweet,\nIs heard through the shadows of death;\nThe cedars of Lebanon bow at His feet,\nThe air is perfumed with His breath.",
	3: "His lips, as a fountain of righteousness flow,\nTo water the gardens of grace;\nFrom which their salvation the Gentiles shall know,\nAnd bask in the smiles of His face.",
	4: "He looks, and ten thousands of angels rejoice,\nAnd myriads wait for His word;\nHe speaks, and eternity, filled with His voice,\nRe-echoes the praise of the Lord."
}
song["sda-hymnal/37"] = {
	title: "O Sing, My Soul, Your Maker's Praise",
	tune: "Finland",
	scripture: "Psalm 34",
	1: "O sing my soul, your Maker’s praise\nIn grateful hymns ascending;\nWhose steadfast love has crowned your days\nWith heav’nly gifts un ending.\nI sought the Lord, He heard my cry;\nHis holy angels hover nigh\nThe tents of those who love Him.",
	2: "The Lord is good to those who seek\nHis face in time of sorrow,\nProviding comfort to the weak\nAnd grace for each tomorrow.\nThough grief may tarry for a night,\nThe morn shall break in joy and light\nWith blessings from His presence.",
	3: "The Lord will turn His face in peace\nWhen troubled souls draw near Him;\nHis loving kindness shall not cease\nTo those who trust and fear Him.\nOur God will not forsake His own;\nEternal is His heav’nly throne;\nHis kingdom stands forever."
}
song["sda-hymnal/38"] = {
	title: "Arise, My Soul, Arise!",
	tune: "Nyt ylös, sieluni",
	copyright: "“Arise, My Soul, Arise!” Words and arrangement copyright © 1978 by Service Book and Hymnal. Used by permission of Augsburg Publishing House.",
	1: "Arise, my soul, arise!\nStretch forth to things eternal\nAnd hasten to the feet of your Redeemer God.\nThough hid from mortal eyes,\nHe dwells in light supernal;\nYet worship Him in humbleness and call Him Lord.\nHis banquet of love\nAwaits you above;\nYet here He grants a foretaste of the feast\nto come!\nRejoice, my soul, rejoice,\nTo heav’n lift up your voice:\nAlleluia, alleluia, alleluia!",
	2: "Now hear the harps of heav’n!\nOh, hear the song victorious,\nThe never-ending anthem sounding\nthrough the sky!\nTo mortals is not giv’n\nTo join in strains so glorious;\nYet here on earth we too can sing\nour praises high!\nHe bought with His blood\nThe ransomed of God;\nTo Him be everlasting pow’r and victory.\nAnd let the great amen\nResound through heav’n again.\nAlleluia, alleluia, alleluia!"
}
song["sda-hymnal/39"] = {
	title: "Lord, in the Morning",
	tune: "Mear",
	scripture: "Psalm 5",
	1: "Lord, in the morning Thou shalt hear\nMy voice ascending high;\nTo Thee will I direct my prayer,\nTo Thee lift up mine eye-",
	2: "Up to the hills where Christ is gone\nTo plead for all His saints,\nPresenting at His Father’s throne\nOur songs and our complaints.",
	3: "O may Thy Spirit guide my feet\nIn ways of righteousness;\nMake every path of duty straight\nAnd plain before my face.\n4 The men that love and fear Thy name\nShall see their hopes fulfilled;\nThe mighty God will compass them\nWith favor as a shield."
}
song["sda-hymnal/40"] = {
	title: "The Dawn of God's Dear Sabbath",
	tune: "St. George's, Bolton",
	1: "The dawn of God’s dear Sabbath\nBreaks o’er the earth again,\nAs some sweet summer morning\nAfter a night of pain;\nIt comes as cooling showers\nTo some exhausted land,\nAs shade of clustered palm trees\n‘Mid weary wastes of sand.",
	2: "Lord, we would bring for offering,\nThough marred with earthly soil,\nA week of earnest labor,\nOf steady, faithful toil,\nFair fruits of self denial,\nOf strong, deep love to Thee,\nFostered by Thine own Spirit,\nIn true humility.",
	3: "And we would bring our burden\nOf sinful thought and deed,\nIn Thy pure presence kneeling,\nFrom bondage to be freed,\nOur heart’s most bitter sorrow\nFor all Thy work undone-\nSo many talents wasted!\nSo few bright laurels won!",
	4: "And with that sorrow mingling,\nA steadfast faith, and sure,\nAnd love so deep and fervent,\nFor Thee to make it pure,\nIn Thy dear presence finding\nThe pardon that we need,\nAnd then the peace so lasting-\nCelestial peace indeed."
}
song["sda-hymnal/41"] = {
	title: "O Splendor of God's Glory Bright",
	tune: "Gonfalon Royal",
	scripture: "Hebrews 1:3",
	copyright: "“O Splendor of God's Glory Bright” Music by permission of Oxford University Press.",
	1: "O splendor of God’s glory bright,\nFrom light eternal bringing light;\nO Light of life, light’s living spring,\nTrue day, all days illumining.",
	2: "O Thou true Sun, on us Thy glance\nLet fall in royal radiance;\nThe Spirit’s sanctifying beam\nUpon our earthly senses stream.",
	3: "O joyful be the passing day\nWith thoughts as clear as morning’s ray,\nWith faith like noontide shining bright,\nOur souls unshadowed by the night.",
	4: "Dawn’s glory gilds the earth and skies;\nLet Him, our perfect morn, arise;\nThe Father’s help His children claim,\nAnd sing the Father’s glorious name. Amen."
}
song["sda-hymnal/42"] = {
	title: "Now That the Daylight Fills the Sky",
	tune: "Laurel",
	copyright: "“Now That the Daylight Fills the Sky” Music copyright © by Lutheran Church Press and Augsburg Publishing House. Used by permission.",
	1: "Now that the daylight fills the sky,\nWe lift our hearts to God on high,\nThat He, in all we do or say,\nWould keep us free from harm today;",
	2: "Would guard our hearts and tongues from strife;\nFrom anger’s din would shield our life;\nFrom evil sights would turn our eyes,\nAnd close our ears to vanities;",
	3: "So we, when this new day is gone\nAnd night in turn is drawing on,\nWith conscience by the world unstained\nShall praise His name for vict’ry gained.",
	4: "“All praise to You, creator Lord!\nAll praise to You, eternal Word!\nAll praise to You, O Spirit wise!”\nWe sing as daylight fills the skies."
}
song["sda-hymnal/43"] = {
	title: "When Morning Gilds the Skies",
	tune: "Laudes Domini",
	scripture: "Psalm 150:6",
	1: "When morning gilds the skies my heart awaking cries,\nMay Jesus Christ be praised!\nAlike at work and prayer, to Jesus I repair:\nMay Jesus Christ be praised!",
	2: "Whene’er the sweet church bell peals over hill and dell,\nMay Jesus Christ praised!\nO hark to what it sings, as joyously it rings,\nMay Jesus Christ be praised!",
	3: "The night becomes as day when from the heart we say:\nMay Jesus Christ be praised!\nThe powers of darkness fear when this sweet chant they hear:\nMay Jesus Christ be praised!",
	4: "Ye nations of mankind, in this your concord find,\nMay Jesus Christ praised!\nLet all the earth around ring joyous with the sound,\nMay Jesus Christ praised!",
	5: "In heaven’s eternal bliss the loveliest strain is this,\nMay Jesus Christ praised!\nLet earth, and sea and sky from depth to height reply,\nMay Jesus Christ praised!",
	6: "Be this, while life is mine, my canticle divine:\nMay Jesus Christ be praised!\nBe this th’eternal song through all the ages long,\nMay Jesus Christ be praised!"
}
song["sda-hymnal/44"] = {
	title: "Morning Has Broken",
	tune: "Bunessan",
	copyright: "“Morning Has Broken” Words used by permission of David Higham Associates Ltd. Arrangement copyright © 1984 by Melvin West.",
	1: "Morning has broken like the first morning,\nBlackbird has spoken like the first bird.\nPraise for the singing!Praise for the morning!\nPraise for the springing fresh from the Word!",
	2: "Sweet the rain’s new fall sunlit from heaven,\nLike the first dewfall on the first grass.\nPraise for the sweetness of the wet garden,\nSprung in completeness where His feet pass.",
	3: "Mine is the sunlight! Mine is the morning\nBorn of the one light Eden saw play!\nPraise with elation, Praise every morning,\nGod’s recreation of the new day!"
}
song["sda-hymnal/45"] = {
	title: "Open Now Thy Gates of Beauty",
	tune: "Unser Herrscher",
	info: "Alternate Harmony, No. 418",
	1: "Open now thy gates of beauty,\nZion, let me enter there,\nWhere my soul in joyful duty\nWaits for God Who answers prayer.\nOh, how blessèd is this place,\nFilled with solace, light, and grace!",
	2: "Gracious God, I come before Thee,\nCome Thou also unto me;\nWhere we find Thee and adore Thee,\nThere a heav’n on earth must be.\nTo my heart, oh, enter Thou,\nLet it be Thy temple now!",
	3: "Here Thy praise is gladly chanted,\nHere Thy seed is duly sown;\nLet my soul, where it is planted,\nBring forth precious sheaves alone,\nSo that all I hear may be\nFruitful unto life in me.",
	4: "Thou my faith increase and quicken,\nLet me keep Thy gift divine,\nHowsoe’er temptations thicken;\nMay Thy Word still o’er me shine\nAs my guiding star through life,\nAs my comfort in my strife.",
	5: "Speak, O God, and I will hear Thee,\nLet Thy will be done indeed;\nMay I undisturbed draw near Thee\nWhile Thou dost Thy people feed.\nHere of life the fountain flows;\nHere is balm for all our woes."
}
song["sda-hymnal/46"] = {
	title: "Abide With Me, 'Tis Eventide",
	tune: "Welcome Guest",
	1: "Abide with me, ’tis eventide!\nThe day is past and gone;\nThe shadows of the evening fall;\nThe night is coming on!\nWithin my heart a welcome guest,\nWithin my home abide;\nRefrain\nO Savior, stay this night with me;\nBehold, ’tis eventide!\nO Savior, stay this night with me;\nBehold, ’tis eventide.",
	2: "Abide with me, ’tis eventide!\nThy walk today with me\nHas made my heart within me burn,\nAs I communed with Thee.\nThy earnest words have filled my soul\nAnd kept me near Thy side;",
	3: "Abide with me, ’tis eventide!\nAnd lone will be the night,\nIf I cannot commune with Thee,\nNor find in Thee my light.\nThe darkness of the world, I fear,\nWould in my home abide;"
}
song["sda-hymnal/47"] = {
	title: "God, Who Made the Earth and Heaven",
	tune: "Ar hyd y nos",
	1: "God, who made the earth and heaven, Darkness and light:\nYou the day for work have given, For rest the night.\nMay Your angel guards defend us,\nSlumber sweet Your mercy send us,\nHoly dreams and hopes attend us\nAll through the night.",
	2: "And when morn again shall call us to run life’s way,\nMay we still, what-e’er befall us, Your will obey.\nFrom the pow’r of evil hide us,\nIn the narrow pathway guide us,\nNever be Your smile denied us\nAll through the day.",
	3: "Guard us waking, guard us sleeping, And, when we die,\nMay we in Your mighty keeping All peaceful lie.\nWhen the trumpet call shall wake us, Then,\nO Lord, do not forsake us,\nBut to reign in glory take us with You on high.",
	4: "Holy Father, throned in heaven, All holy Son,\nHoly Spirit, freely given, Blest Three in One:\nGrant us grace, we now implore You,\nTill we lay our crowns before You\nAnd in worthier strains adore You while ages run."
}
song["sda-hymnal/48"] = {
	title: "Softly Now the Light of Day",
	tune: "Seymour",
	1: "Softly now the light of day\nFades upon out sight away:\nFree from care, from labor free,\nLord, we would commune with Thee.",
	2: "Thou, whose allpervading eye\nNought escapes, without, within,\nPardon each infirmity,\nOpen fault, and secret sin.",
	3: "Soon from us the light of day\nShall forever pass away;\nThen, from sin and sorrow free,\nTake us, Lord, to dwell with Thee."
}
song["sda-hymnal/49"] = {
	title: "Savior, Breathe an Evening Blessing",
	tune: "Evening Prayer",
	1: "Savior, breathe an evening blessing,\nEre repose our spirits seal;\nSin and want we come confessing;\nThou canst save, and Thou canst heal.",
	2: "Though the night be dark and dreary,\nDarkness cannot hide from Thee;\nThou art He who, never weary,\nWatchest where Thy people be.",
	3: "Though destruction walk around us.\nThough the arrow past us fly,\nAngel guards from Thee Surround us,\nWe are safe if Thou art nigh.",
	4: "Should swift death this night o’re-take us,\nAnd our couch become our tomb,\nMay the morn of glory wake us,\nClad in light and deathless bloom."
}
song["sda-hymnal/50"] = {
	title: "Abide With Me",
	tune: "Eventide",
	scripture: "Luke 24:29",
	1: "Abide with me; fast falls the eventide;\nThe darkness deepens; Lord with me abide!\nWhen other helpers fail and comforts flee,\nHelp of the helpless, O abide with me.",
	2: "Swift to its close ebbs out life’s little day;\nEarth’s joys grow dim; its glories pass away;\nChange and decay in all around I see;\nO Thou who changest not, abide with me.",
	3: "I need Thy presence every passing hour.\nWhat but Thy grace can foil the tempter’s power?\nWho, like Thyself, my guide and stay can be?\nThrough cloud and sunshine, Lord, abide with me.",
	4: "I fear no foe, with Thee at hand to bless;\nIlls have no weight, and tears no bitterness.\nWhere is death’s sting? Where, grave, thy victory?\nI triumph still, if Thou abide with me!"
}
song["sda-hymnal/51"] = {
	title: "Day Is Dying in the West",
	tune: "Chautauqua",
	1: "Day is dying in the west;\nHeaven is touching earth with rest;\nWait and worship while the night\nSets the evening lamps alight\nThrough all the sky.\nRefrain\nHoly, holy, holy, Lord God of Hosts!\nHeaven and earth are full of Thee!\nHeaven and earth are praising Thee,\nO Lord most high!",
	2: "Lord of life, beneath the dome\nOf the universe, Thy home,\nGather us who seek Thy face\nTo the fold of Thy embrace,\nFor Thou art nigh.",
	3: "While the deepening shadows fall,\nHeart of love enfolding all,\nThrough the glory and the grace\nOf the stars that veil Thy face,\nOur hearts ascend.",
	4: "When forever from our sight\nPass the stars, the day, the night,\nLord of angels, on our eyes\nLet eternal morning rise\nAnd shadows end."
}
song["sda-hymnal/52"] = {
	title: "Now the Day Is Over",
	tune: "Merrial",
	1: "Now the day is over,\nnight is drawing nigh,\nShadows of the evening\nsteal across the sky.",
	2: "Father, give the weary,\ncalm and sweet repose;\nWith Thy tenderest blessing\nMay our eyelids close.",
	3: "Through the long night watches,\nmay thine angels spread\nTheir white wings above me,\nwatching round my bed."
}
song["sda-hymnal/53"] = {
	title: "All Praise to Thee",
	tune: "Tallis' Cannon",
	1: "All praise to thee, my God, this night,\nfor all the blessings of the light!\nKeep me, O keep me, King of kings,\nbeneath thine own almighty wings.",
	2: "Forgive me, Lord, for thy dear Son,\nthe ill that I this day have done,\nthat with the world, myself, and thee,\nI, ere I sleep, at peace may be.",
	3: "O may my soul on thee repose,\nand with sweet sleep mine eyelids close,\nsleep that may me more vigorous make\nto serve my God when I awake.",
	4: "Praise God, from whom all blessings flow;\npraise him, all creatures here below;\npraise him above, ye heavenly host;\npraise Father, Son, and Holy Ghost."
}
song["sda-hymnal/54"] = {
	title: "O Gladsome Light",
	tune: "Phos hilaron",
	copyright: "“O Gladsome Light” Music copyright © 1984 by James Bingham.",
	1: "O gladsome light,\nOf God the Father’s face,\nThe eternal splendour wearing;\nCelestial, holy, blest,\nOur Saviour, Jesus Christ,\nJoyful in Thine appearing.",
	2: "Now e’er day fadeth quite,\nWe see the evening light,\nOur wonted hymn outpouring;\nFather of might unknown,\nThee His incarnate Son,\nAnd Holy Spirit adoring.",
	3: "To Thee of right belongs\nAll praise of holy songs,\nO Son of God, life giver;\nThee, therefore, O most high,\nThe world doth glorify\nAnd shall exalt forever."
}
song["sda-hymnal/55"] = {
	title: "Jesus, Tender Shepherd, Hear Me",
	tune: "Shipston",
	info: "Lower key, No. 544",
	copyright: "“Jesus, Tender Shepherd, Hear Me” Music from the English Hymnal by permission of Oxford University Press.",
	1: "Jesus, tender Shepherd, hear me,\nBless Thy little lamb tonight;\nThrough the darkness be Thou near me;\nWatch my sleep till morning light.",
	2: "All this day Thy hand has led me,\nAnd I thank Thee for Thy care;\nThou hast clothed me, warmed and fed me;\nListen to my evening prayer."
}
song["sda-hymnal/56"] = {
	title: "The Day Thou Gavest",
	tune: "St. Clement",
	1: "The day Thou gavest, Lord, is ended;\nthe darkness falls at Thy behest;\nto Thee our morning hymns ascended;\nThy praise shall hallow now our rest.",
	2: "We thank Thee that Thy church, unsleeping\nwhile earth rolls onward into light,\nthrough all the world her watch is keeping,\nand rests not now by day or night.",
	3: "As o’er each continent and island\nthe dawn leads on another day,\nthe voice of prayer is never silent,\nnor die the strains of praise away.",
	4: "So be it, Lord; Thy throne shall never,\nlike earth’s proud empires, pass away.\nThy kingdom stands, and grows forever,\ntill all Thy creatures own Thy sway."
}
song["sda-hymnal/57"] = {
	title: "Now All the Woods Are Sleeping",
	poet: "Paul Gerhardt (1607 - 1676)",
	arranger: "Johann Sebastian Bach (1685 - 1750)",
	tune: "Innsbruck",
	scripture: "Psalm 3:5",
	1: "Now all the woods are sleeping,\nAnd night and stillness creeping\nO’er city, man, and beast;\nBut thou, my heart, awake thee,\nTo pray’r awhile be take thee,\nAnd praise thy Maker ere thou rest.",
	2: "My Jesus, stay Thou by me,\nAnd let no foe come nigh me,\nSafe sheltered by Thy wing;\nBut would the foe alarm me,\nO let him never harm me,\nBut still Thine angels round me sing!",
	3: "My loved ones, rest securely,\nFrom every peril surely\nOur God will guard your heads;\nAnd happy slumbers send you,\nAnd bid His hosts attend you,\nAnd golden armed watch o’er your beds."
}
song["sda-hymnal/58"] = {
	title: "Hark, the Vesper Hymn Is Stealing",
	tune: "Russian Air",
	info: "Jubilate means \"Be joyful;\" pronounced, \"You-bee-lah-tay.\"",
	copyright: "“Hark, the Vesper Hymn Is Stealing” Arrangement copyright © 1984 by Melvin West.",
	1: "Hark! the vesper hymn is stealing\nO’er the waters soft and clear;\nJubilate, Jubilate, Jubilate,\nAmen.",
	2: "Nearer yet, and nearer pealing,\nSoft it breaks upon the ear.\nJubilate, Jubilate, Jubilate,\nAmen."
}
song["sda-hymnal/59"] = {
	title: "Great Our Joy as Now We Gather",
	tune: "Michael",
	copyright: "“Great Our Joy as Now We Gather” Words and copyright © 1973 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music reproduced by permission of Novello & Company Limited.",
	1: "Great our joy as now we gather\nWhere the Master makes us one:\nWhere we worship God the Father\nThro’ the Spirit of His Son.\nAll who search for His church\nFind it where His will is done.",
	2: "Precious is the tie that binds us\nTo our God when faith grows cold;\nPrecious all that now reminds us\nHe is still our safe stronghold.\nFaithful love serves to prove\nHere the Shepherd has His fold.",
	3: "May we learn from Christ’s example\nHow to use this house of prayer:\nHe who loved and cleansed His temple\nWants us all to worship there.\nGod the Son shuts out none:\nIn His kingdom all may share.",
	4: "Lord, inspire us with Your vision\nOf a world which must be won!\nGlorious is the church’s mission,\nLong endeavoured, scarce begun!\nFaithful now – this is how\nGod’s eternal will is done."
}
song["sda-hymnal/60"] = {
	title: "Blessed Jesus at Thy Word",
	tune: "Liebster Jesu",
	1: "Blessed Jesus, at Thy word,\nWe have gathered all to hear Thee;\nBy Thy word our hearts were stirred,\nNow to seek and love and fear Thee.\nBy Thy teachings sweet and holy,\nLet us learn to love Thee solely.",
	2: "All our knowledge, sense, and sight,\nLie in deepest darkness shrouded;\nTill Thy Spirit breaks our night,\nWith His beams of truth unclouded.\nHe alone to God canst win us,\nHe who works all good within us.",
	3: "Glorious Lord, Thy self impart!\nLight of light, from God proceeding.\nOpen Thou our ears and heart\nHelp us, by Thy Spirit’s pleading.\nHear the cry Thy people raises!\nHear, and bless our prayer and praises!"
}
song["sda-hymnal/61"] = {
	title: "God Is Here!",
	tune: "Abbot's Leigh",
	copyright: "“God Is Here!” Words copyright © 1979 and music copyright © 1942 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission,",
	1: "God is here as we his people\nmeet to offer praise and prayer,\nmay we find in fuller measure\nwhat it is in Christ we share.\nHere, as in the world around us,\nall our varied skills and arts\nwait the coming of his Spirit\ninto open minds and hearts.",
	2: "Here are symbols to remind us\nof our lifelong need of grace;\nhere are table, font and pulpit;\nhere the Word has central place.\nHere in honesty of preaching,\nhere in silence, as in speech,\nhere in newness and renewal,\nGod the Spirit comes to each.",
	3: "Here our children find a welcome\nin the Shepherd’s flock and fold.\nHere, as bread and wine are taken,\nChrist sustains us, as of old.\nHere the servants of the Servant\nseek in worship to explore\nwhat it means in daily living\nto believe and to adore.",
	4: "Lord of all, Church and Kingdom,\nin an age of change and doubt,\nkeep us faithful to the Gospel,\nhelp us work your purpose out.\nHere, in this day’s dedication,\nall we have to give, receive:\nwe, who cannot live without you,\nwe adore you! We believe!"
}
song["sda-hymnal/62"] = {
	title: "How Lovely Is Thy Dwelling Place",
	tune: "McKee",
	scripture: "Psalm 84",
	1: "How lovely is Thy dwelling place,\nO Lord of hosts to me!\nThe tabenacles of Thy grace\nHow pleasant, Lord, they be!",
	2: "My thirsty soul longs ardently,\nYea, faints Thy courts to see;\nMy very heart and flesh cry out,\nO living God, for Thee.",
	3: "Behold the sparrow findeth out\nA house where-in to rest;\nThe swallow also, for herself\nProvided hath a nest.",
	4: "Ev’n Thine own altars, where she safe\nHer young ones forth may bring,\nO Thou, almighty Lord of hosts,\nWho art my God and King.",
	5: "Blest are they in Thy house that dwell,\nThey ever give Thee praise.\nBlest is the man whose strength Thou art,\nIn whose heart are Thy ways."
}
song["sda-hymnal/63"] = {
	title: "O Come, Let Us Sing to the Lord",
	tune: "Irish",
	scripture: "Psalm 95:1-6",
	1: "O come, let us sing to the Lord,\nCome let us every one\nA joyful noise make to the Rock\nOf our salvation.",
	2: "Let us before His presence come\nWith glad and thankful voice;\nLet us sing psalms of praise to Him,\nAnd make a joyful noise.",
	3: "For God, a great God and great King,\nAbove all gods, He is;\nThe depths of earth are in His hand,\nThe strength of hills is His.",
	4: "To Him the ocean vast belongs,\nFor He the sea did make;\nThe dry land also from His hands,\nIts form at first did take.",
	5: "O come, bow down and worship Him,\nAnd kneeling, humbly pray,\nCome to our Maker and our God,\nAnd hear His voice today."
}
song["sda-hymnal/64"] = {
	title: "Lord, Dismiss Us With Thy Blessing",
	tune: "Sicilian Mariners",
	1: "Lord, dismiss us with thy blessing;\nfill our hearts with joy and peace;\nlet us each, thy love possessing,\ntriumph in redeeming grace.\nO refresh us, O refresh us,\ntraveling through this wilderness.",
	2: "Thanks we give and adoration\nfor thy gospel’s joyful sound.\nMay the fruits of thy salvation\nin our hearts and lives abound;\never faithful, ever faithful\nto the truth may we be found."
}
song["sda-hymnal/65"] = {
	title: "God Be With You",
	scripture: "2 Cor. 13:11",
	1: "God be with you till we meet again;\nBy His counsels guide, uphold you,\nWith His sheep securely fold you;\nGod be with you till we meet again.\nRefrain\nTill we meet, till we meet,\nTill we meet at Jesus’ feet;\nTill we meet, till we meet,\nGod be with you till we meet again.",
	2: "God be with you till we meet again;\n‘Neath His wings securely hide you;\nDaily manna still provide you;\nGod be with you till we meet again.",
	3: "God be with you till we meet again;\nWhen life’s perils thick confound you;\nPut His arms unfailing round you;\nGod be with you till we meet again.",
	4: "God be with you till we meet again;\nKeep love’s banner floating over you,\nStrike death’s threatening wave before you;\nGod be with you till we meet again."
}
song["sda-hymnal/66"] = {
	title: "God Be With You",
	tune: "Randolph",
	copyright: "“God Be With You” Music from the English Hymnal by permission of Oxford University Press.",
	1: "God be with you till we meet again;\nBy His counsels guide, uphold you,\nWith His sheep securely fold you:\nGod be with you till we meet again.",
	2: "God be with you till we meet again;\nNeath His wings securely hide you,\nDaily manna still provide you:\nGod be with you till we meet again.",
	3: "God be with you till we meet again;\nWhen life’s perils thick confound you,\nPut His arms unfailing round you:\nGod be with you till we meet again.",
	4: "God be with you till we meet again;\nKeep love’s banner floating o’er you,\nSmite death’s threatening wave before you:\nGod be with you till we meet again."
}
song["sda-hymnal/67"] = {
	title: "O Lord, Now Let Your Servant",
	tune: "Nyland",
	scripture: "Luke 2:29-32",
	copyright: "“O Lord, Now Let Your Servant” Words copyright © by Board of Publication, Lutheran Church in America. Music from Revised Church Hymnary 1927 by permission of Oxford University Press.",
	1: "O Lord, now let Your servant\nDepart in heav’nly peace,\nFor I have seen the glory\nOf Your redeeming grace:\nA light to lead the Gentiles\nUnto Your holy hill,\nThe glory of Your people,\nYour chosen Israel.",
	2: "Then grant that I may follow Your gleam,\nO glorious Light,\nTill earthly shadows scatter,\nAnd faith is changed to sight;\nTill raptured saints shall gather\nUpon that shining shore,\nWhere Christ, the blessed Day star,\nShall light them evermore."
}
song["sda-hymnal/68"] = {
	title: "On Our Way Rejoicing",
	tune: "Hermas",
	1: "On our way rejoicing\nGladly let us go;\nConquer’d hath our Leader,\nVanquish’d is the foe.\nChrist without, our safety;\nChrist within, our joy;\nWho, if we be faithful,\nCan our hope destroy?\nRefrain\nOn our way rejoicing\nAs we forward move,\nHearken to our praises,\nO blest God of love!",
	2: "Unto God the Father\nJoyful songs we sing,\nUnto God the Savior\nThankful hearts we bring,\nUnto God the Spirit\nBow we and adore,\nOn our way rejoicing\nNow and evermore."
}
song["sda-hymnal/69"] = {
	title: "Lord, Make Us More Holy",
	copyright: "“Lord, Make Us More Holy” Arrangement copyright © 1984 by Alma Blackmon.",
	1: "Lord, make us more holy;\nLord, make us more holy;\nLord, make us more holy\nUntil we meet again.",
	2: "Lord, make us more faithful;\nLord, make us more faithful;\nLord, make us more faithful\nUntil we meet again.",
	3: "Lord, make us more humble;\nLord, make us more humble;\nLord, make us more humble\nUntil we meet again.",
	4: "Lord, make us more loving;\nLord, make us more loving;\nLord, make us more loving\nUntil we meet again.\nLike Jesus, the Saviour,\nUntil we meet again."
}
song["sda-hymnal/70"] = {
	title: "Praise Ye the Father",
	tune: "Flemming",
	scripture: "2 Corinthians 13:14",
	1: "Praise ye the Father for His loving kindness,\nTenderly cares He for His erring children;\nPraise Him, ye angels, praise Him in the heavens;\nPraise ye Jehovah!",
	2: "Praise ye the Savior, great is the compassion,\nGraciously cares He for His chosen people;\nYoung men and maidens, ye old men and children,\nPraise ye the Savior!",
	3: "Praise ye the Spirit, comforter of Israel,\nSent of the Father and the Son to bless us;\nPraise ye the Father, Son, and Holy Spirit,\nPraise the Eternal Three!"
}
song["sda-hymnal/71"] = {
	title: "Come Thou Almighty King",
	tune: "Italian Hymn",
	copyright: "“Come Thou Almighty King” Arrangement copyright © 1964 by Abingdon Press. Used by permission.",
	1: "Come, Thou almighty King,\nHelp us Thy name to sing,\nHelp us to praise!\nFather all glorious,\nO’er all victorious,\nCome, and reign over us,\nAncient of Days!",
	2: "Come, Thou incarnate Word,\nGird on Thy mighty sword,\nOur prayer attend;\nCome, and Thy people bless,\nAnd give Thy Word success;\nSpirit of holiness,\nOn us descend!",
	3: "Come, holy Comforter,\nThy sacred witness bear,\nIn this glad hour:\nThou who almighty art,\nNow rule in every heart,\nAnd ne’er from us depart,\nSpirit of power!",
	4: "To Thee, great One in Three,\nEternal praises be,\nHence, evermore:\nThy sovereign majesty\nMay we in glory see,\nAnd to eternity\nLove and adore!"
}
song["sda-hymnal/72"] = {
	title: "Creator of the Stars of Night",
	tune: "Conditor alme",
	1: "Creator of the stars of night,\nThy people’s everlasting light,\nO Christ, Thou Saviour of us all,\nWe pray Thee, hear us when we call.",
	2: "At the great name of Jesus, now\nAll knees must bend, all hearts must bow;\nAnd things celestial Thee shall own,\nAnd things terrestrial, Lord alone.",
	3: "To God the Father, God the Son,\nAnd God the Spirit, Three in One,\nLaud, honour, might, and glory be\nFrom age to age eternally.\nAmen."
}
song["sda-hymnal/73"] = {
	title: "Holy Holy Holy",
	tune: "Nicaea",
	scripture: "Revelation 4:8-11",
	1: "Holy, holy, holy! Lord God Almighty!\nEarly in the morning our song shall rise to Thee;\nHoly, holy, holy, merciful and mighty!\nGod in three Persons, blessed Trinity!",
	2: "Holy, holy, holy! Angels adore Thee,\nCasting down their golden crowns around the glassy sea;\nThousands and ten thousands worship low before Thee,\nWhich wert, and art, and evermore shalt be.",
	3: "Holy, holy, holy! though the darkness hide Thee,\nThough the eye of sinful man Thy glory may not see;\nOnly Thou art holy; there is none beside Thee,\nPerfect in power, in love, and purity.",
	4: "Holy, holy, holy! Lord God Almighty!\nAll Thy works shall praise Thy name, in earth, and sky, and sea;\nHoly, holy, holy; merciful and mighty!\nGod in three Persons, blessed Trinity!"
}
song["sda-hymnal/74"] = {
	title: "Like a River Glorious",
	tune: "Wye Valley",
	scripture: "Isaiah 48:18",
	1: "Like a river glorious, is God’s perfect peace,\nOver all victorious, in its bright increase;\nPerfect, yet it floweth, fuller every day,\nPerfect, yet it groweth, deeper all the way.\nRefrain\nStayed upon Jehovah, hearts are fully blessed\nFinding, as He promised, perfect peace and rest.",
	2: "Hidden in the hollow of His blessed hand,\nNever foe can follow, never traitor stand;\nNot a surge of worry, not a shade of care,\nNot a blast of hurry touch the spirit there.",
	3: "Every joy or trial falleth from above,\nTraced upon our dial by the Sun of Love;\nWe may trust Him fully all for us to do.\nThey who trust Him wholly find Him wholly true."
}
song["sda-hymnal/75"] = {
	title: "The Wonder of It All",
	tune: "Wonder of It All",
	copyright: "“The Wonder of It All” Copyright © by Chancel Music, Inc. Assigned to The Rodeheaver Co. (A Div. of Word, Inc.). All rights reserved. International copyright secured. Used by permission.",
	1: "There’s the wonder of sunset at evening,\nThe wonder as sunrise I see;\nBut the wonder of wonders that thrills my soul\nIs the wonder that God loves me.\nRefrain\nO, the wonder of it all!\nThe wonder of it all!\nJust to think that God loves me.\nO, the wonder of it all!\nThe wonder of it all!\nJust to think that God loves me.",
	2: "There’s the wonder of springtime and harvest,\nThe sky, the stars, the sun;\nBut the wonder of wonders that thrills my soul\nIs the wonder that’s only begun."
}
song["sda-hymnal/76"] = {
	title: "O Love That Wilt Not Let Me Go",
	tune: "St. Margaret",
	1: "O Love that wilt not let me go,\nI rest my weary soul in thee;\nI give thee back the life I owe,\nthat in thine ocean depths\nits flow may richer, fuller be.",
	2: "O Light that followest all my way,\nI yield my flickering torch to thee;\nmy heart restores its borrowed ray,\nthat in they sunshine’s blaze\nits day may brighter, fairer be.",
	3: "O Joy that seekest me through pain,\nI cannot close my heart to thee;\nI trace the rainbow thru the rain,\nand feel the promise is not vain,\nthat morn shall tearless be.",
	4: "O Cross that liftest up my head,\nI dare not ask to fly from thee;\nI lay in dust life’s glory dead,\nand from the ground there blossoms\nred life that shall endless be."
}
song["sda-hymnal/77"] = {
	title: "O Love of God Most Full",
	tune: "Vandeman",
	copyright: "“O Love of God Most Full” Copyright © 1963 by Pro Art Publications. Required by permission of Belwin-Mills Publishing Corp. All rights reserved.",
	1: "O love of God most full,\nO love of God most free,\nCome warm my heart, come fill my soul,\nCome lead me unto Thee.",
	2: "Warm as the glowing sun\nSo shines Thy love on me,\nIt wraps me ‘round with kindly care,\nIt draws me unto Thee.",
	3: "The wildest sea is calm,\nThe tempest brings no fear,\nThe darkest night is full of light,\nBecause Thy love is near.",
	4: "O love of God most full,\nO love of God most free,\nIt warms my heart, it fills my soul,\nWith might it strengthens me."
}
song["sda-hymnal/78"] = {
	title: "For God So Loved Us",
	tune: "Gott is die Liebe",
	1: "For God so loved us,\nHe sent the Savior:\nFor God so loved us,\nand loves me too.\nRefrain\nLove so unending!\nI’ll sing His praises,\nGod loves His children,\nloves even me.",
	2: "He sent the Savior,\nthe blest Redeemer;\nHe sent the Savior\nto set me free.",
	3: "He bade me welcome,\nO word of mercy;\nHe bade me welcome,\nO voice divine.",
	4: "Glory and honor,\nO Love eternal,\nTo Him be given\nwhile life shall last."
}
song["sda-hymnal/79"] = {
	title: "O Love of God, How Strong and True!",
	tune: "Ombersley",
	1: "O love of God, how strong and true!\nEternal, and yet ever new;\nUncomprehended and unbought,\nBeyond all knowledge and all thought.",
	2: "O love of God, how deep and great,\nFar deeper than man’s deepest hate;\nSelf-fed, self-kindled like the light,\nChangeless, eternal, infinite.",
	3: "We read thee best in Him who cam\nTo bear for us the cross of shame;\nSent by the Father from on high,\nOur life to live, our death to die.",
	4: "We read thy power to bless and save,\nE’en in the darkness of the grave;\nStill more in resurrection light\nWe read the fullness of thy might.",
	5: "O love of God, our shield and stay\nThrough all the perils of our way!\nEternal love, in thee we rest,\nForever safe, forever blest."
}
song["sda-hymnal/80"] = {
	title: "O World of God",
	tune: "Jerusalem",
	copyright: "“O World of God” Words copyright © by R. B. Y. Scott. Music copyright © by J. Curwen & Sons. Used by permission of G. Schirmer, Inc., U. S. A. agents.",
	1: "O world of God, so vast and strange,\nProfound and wonderful and fair,\nBeyond the utmost reach of thought,\nBut not beyond a Father’s care!\nWe are not strangers on this earth\nWhirling amid the suns of space;\nWe are God’s children, this our home,\nWith those of every clime and race.",
	2: "O world of man where life is lived,\nSo strangely mingling joy and pain,\nSo full of evil and of good,\nSo needful that the god shall reign!\nIt is this world that God has loved,\nAnd goodness was its Maker’s plan,\nThe promise of God’s triumph is\nHis coming in a Son of Man.",
	3: "O world of time’s far-stretching years!\nThere was a day when time stood still,\nA central moment when there rose\nA cross upon a cruel hill;\nIn pain and death love’s power was seen,\nThe mystery of time revealed,\nThe wisdom of the ways of God,\nThe grace through which man’s hurt is healed."
}
song["sda-hymnal/81"] = {
	title: "Though I Speak With Tongues",
	tune: "Janelle",
	scripture: "1 Corinthians 13",
	copyright: "“Though I Speak With Tongues” Words copyright © 1982 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © 1984 by James Bingham.",
	1: "Though I speak with tongues of men and angels,\nThough I have the prophet’s gift,\nThough I hold the keys to hidden knowledge,\nThough my faith can mountains shift;\nWithout love I am no better,\nWithout love it’s all for naught;\nLord, You spent Your life in loving others:\nWhat this means I would be taught.",
	2: "Love is patient, knows no envy,\nNever gloats when others sin;\nLove is never glad to see injustice,\nAlways wants the truth to win.\nThere’s no end to love’s endurance,\nThere’s no test it cannot face;\nLord, You spent Your life in loving others:\nI shall fail without Your grace.",
	3: "Though there’ll be an end to hidden knowledge,\nVisions, raptures, prophecy:\nFaith and hope and love shall last forever,\nLove the greatest of the three.\nWithout love I am no better,\nWithout love it’s all for naught;\nLord, You gave Your life in saving others:\nWhat this means I would be taught."
}
song["sda-hymnal/82"] = {
	title: "Before Jehovah's Awful Throne",
	tune: "Duke Street",
	scripture: "Psalm 100",
	info: "Alternate harmony, No. 227",
	1: "Before Jehovah’s awful throne,\nYe nations, bow with sacred joy;\nKnow that the Lord is God alone;\nHe can create, and He destroy.",
	2: "His sovereign power, without our aid,\nMade us of clay, and formed us men;\nand when like wandering sheep we strayed,\nHe brought us to His fold again.",
	3: "We’ll crowd His gates with thankful songs,\nHigh as the heavens our voices raise;\nAnd earth, with her ten thousand tongues,\nShall fill His courts with sounding praise.",
	4: "Wide as the world is His command,\nVast as Eternity His love;\nFirm as a rock His truth shall stand,\nWhen rolling years shall cease to move."
}
song["sda-hymnal/83"] = {
	title: "O Worship the King",
	tune: "Lyons",
	scripture: "Psalm 104",
	1: "O worship the King, all glorious above,\nO gratefully sing His wonderful love;\nOur Shield and Defender, the Ancient of Days,\nPavilioned in splendor, and girded with praise.",
	2: "O tell of His might, O sing of His grace,\nWhose robe is the light, whose canopy space,\nHis chariots of wrath the deep thunderclouds form,\nAnd dark is His path on the wings of the storm.",
	3: "Thy bountiful care, what tongue can recite?\nIt breathes in the air, it shines in the light;\nIt streams from the hills, it descends to the plain,\nAnd sweetly distills in the dew and the rain.",
	4: "Frail children of dust, and feeble as frail,\nIn Thee do we trust, nor find Thee to fail;\nThy mercies how tender, how firm to the end!\nOur Maker, Defender, Redeemer, and Friend."
}
song["sda-hymnal/84"] = {
	title: "God the Omnipotent",
	tune: "Russian Hymn",
	1: "God the Omnipotent! King, who ordainest\nGreat winds Thy clarions, the lightnings Thy sword;\nShow forth Thy pity on high where Thou reignest,\nGive to us peace, O most merciful Lord.",
	2: "God the all merciful! earth hath forsaken\nThy precepts holy, and slighted Thy word;\nBid not Thy wrath in its terrors awaken;\nGive to us peace, O most merciful Lord.",
	3: "God the all righteousness One! man hath defied Thee;\nYet to eternity standeth Thy word;\nFalsehood and wrong shall not tarry beside Thee;\nProsper the right, O most merciful Lord.",
	4: "So shall we render Thee thankful devotion,\nFor Thy deliverance from peril and sword,\nSinging in chorus from ocean to ocean,\n“Thine is the power and the glory, O Lord.”"
}
song["sda-hymnal/85"] = {
	title: "Eternal Father Strong to Save",
	tune: "Melita",
	1: "Eternal Father, strong to save,\nWhose arm doth bind the restless wave,\nWho bidd’st the mighty ocean deep\nIts own appointed limits keep;\nO hear us when we cry to Thee\nFor those in peril on the sea.",
	2: "O Saviour, whose almighty word\nThe winds and waves submissive heard,\nWho walkedst on the foaming deep,\nAnd calm amid its rage didst sleep;\nO hear us when we cry to Thee\nFor those in peril on the sea.",
	3: "O Sacred Spirit, who didst brood\nUpon the chaos dark and rude,\nWho badd’st its angry tumult cease,\nAnd gavest light and life and peace;\nO hear us when we cry to Thee\nFor those in peril on the sea.",
	4: "O Trinity of love and power,\nOur brethren shield in danger’s hour;\nFrom rock and tempest, fire and foe,\nProtect them wheresoe’er they go;\nAnd ever let there rise to Thee\nGlad hymns of praise from land and sea."
}
song["sda-hymnal/86"] = {
	title: "How Great Thou Art",
	info: "Author's original words are \"works\" and \"mighty.\"",
	copyright: "“How Great Thou Art”  Copyright © 1953, and 1955 by Stuart K. Hine. Renewed 1981 by Manna Music, Inc., 2111 Kenmere Ave., Burbank, CA 91504. International copyright secured. All rights reserved. Used by permission.",
	1: "O Lord my God\nWhen I in awesome wonder\nConsider all the worlds Thy Hands have made\nI see the stars\nI hear the rolling thunder\nThy power throughout the universe displayed\nRefrain\nThen sings my soul\nMy Saviour God to Thee\nHow great Thou art\nHow great Thou art\nThen sings my soul\nMy Saviour God to Thee\nHow great Thou art\nHow great Thou art",
	2: "When through the woods\nAnd forest glades I wander\nAnd hear the birds sing sweetly in the trees\nWhen I look down\nFrom lofty mountain grandeur\nAnd see the brook and feel the gentle breeze",
	3: "And when I think\nThat God His Son not sparing\nSent Him to die I scarce can take it in\nThat on the Cross\nMy burden gladly bearing\nHe bled and died to take away my sin.",
	4: "When Christ shall come\nWith shouts of acclamation\nAnd take me home\nWhat joy shall fill my heart\nThen I shall bow\nIn humble adoration\nAnd then proclaim\n“My God, how great Thou art!”"
}
song["sda-hymnal/87"] = {
	title: "God Who Spoke in the Beginning",
	tune: "New Malden",
	info: "Alternate tune, REGENT SQUARE, No. 119",
	copyright: "“God Who Spoke in the Beginning” Words copyright © 1968 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © 1975 by Stainer & Bell Ltd. Used by permission of Galaxy Music Corporation, New York, sole U",
	1: "God who spoke in the beginning,\nForming rock and shaping spar,\nSet all life and growth in motion,\nEarthly world and distant star;\nHe who calls the earth to order\nIs the ground of what we are.",
	2: "God who spoke thro’ men and nations,\nThro’ events long past and gone,\nShowing still today His purpose,\nSpeaks supremely through His Son;\nHe who calls the earth to order\nGives His word and it is done.",
	3: "God whose speech becomes incarnate –\nChrist is servant, Christ is Lord –\nCalls us to a life of service,\nHeart and will to action stirred;\nHe who uses man’s obedience\nHas the first and final word."
}
song["sda-hymnal/88"] = {
	title: "I Sing the Mighty Power of God",
	tune: "Varina",
	scripture: "Genesis 1",
	1: "I sing the almighty power of God,\nthat made the mountains rise,\nthat spread the flowing seas abroad,\nand built the lofty skies.\nI sing the wisdom that ordained\nthe sun to rule the day;\nthe moon shines full at God’s command,\nand all the stars obey.",
	2: "I sing the goodness of the Lord,\nwho filled the earth with food,\nwho formed the creatures thru the Word,\nand then pronounced them good.\nLord, how thy wonders are displayed,\nwhere’er I turn my eye,\nif I survey the ground I tread,\nor gaze upon the sky!",
	3: "There’s not a plant or flower below,\nbut makes thy glories known,\nand clouds arise, and tempests blow,\nby order from thy thrown;\nwhile all that borrows life from thee\nis ever in thy care;\nand everywhere that we can be,\nthou, God, art present there."
}
song["sda-hymnal/89"] = {
	title: "Let All on Earth Their Voices Raise",
	tune: "Old 113th",
	scripture: "Psalm 96",
	copyright: "“Let All on Earth Their Voices Raise” Arrangement copyright © 1964 by Abingdon Press. Used by permission.",
	1: "Let all on earth their voices raise,\nTo sing the great Jehovah’s praise,\nAnd bless His holy name:\nHis glory let the people know,\nHis wonders to the nations show,\nHis saving grace proclaim.",
	2: "He framed the globe; He built the sky;\nHe made the shining worlds on high,\nAnd reigns in glory there:\nHis beams are majesty and light;\nHis beauties, how divinely bright!\nHis dwelling place, how fair!",
	3: "Come, the great day, the glorious hour,\nWhen earth shall feel His saving power,\nAll nations fear His name;\nThen shall the race of men confess\nThe beauty of His holiness,\nHis saving grace proclaim."
}
song["sda-hymnal/90"] = {
	title: "Eternal God, Whose Power Upholds",
	tune: "Forest Green",
	copyright: "“Eternal God, Whose Power Upholds” Music from The English Hymnal by permission of Oxford University Press",
	1: "Eternal God, whose power upholds Both flower and flaming star,\nTo whom there is no here nor there, No time, no near nor far,\nNo alien race, no foreign shore, No child unsought, unknown:\nO send us forth, Your prophets true, To make all lands Your own!",
	2: "O God of truth, whom science seeks And reverent souls adore,\nIllumine every earnest mind Of every clime and shore:\nDispel the gloom of error’s night, Of ignorance and fear,\nUntil true wisdom from above Shall make life’s pathway clear!",
	3: "O God of beauty, oft revealed In dreams of human art,\nIn speech that flows to melody, In holiness of heart:\nTeach us to ban al ugliness, And all disharmony,\nTill all shall know the loveliness Of lives made fair and free!",
	4: "O God of righteousness and grace, Seen in the Christ, Your Son,\nWhose life and death reveal Your face, By whom Your will was done;\nHelp us to spread Your gracious reign Till greed and hate shall cease,\nAnd kindness dwell in human hearts, And all the earth find peace!"
}
song["sda-hymnal/91"] = {
	title: "Ye Watchers and Ye Holy Ones",
	tune: "Lasst uns erfreuen",
	info: "Lower key, No. 2",
	copyright: "“Ye Watchers and Ye Holy Ones” Words and music from The English Hymnal by permission of Oxford University Press.",
	1: "Ye watchers and ye holy ones,\nBright seraphs, cherubim and thrones,\nRaise the glad strain, Alleluia!\nCry out, dominions, princedoms, powers,\nVirtues, archangels, angels’ choirs:\nRefrain\nAlleluia! Alleluia!\nAlleluia! Alleluia!\nAlleluia!",
	2: "O higher than the cherubim,\nMore glorious than the seraphim,\nLead their praises, Alleluia!\nThou bearer of th’eternal Word,\nMost gracious, magnify the Lord.",
	3: "O friends, in gladness let us sing,\nSupernal anthems echoing,\nAlleluia! Alleluia!\nTo God the Father, God the Son,\nAnd God the Spirit, Three in One."
}
song["sda-hymnal/92"] = {
	title: "This Is My Father's World",
	tune: "Terra beata",
	1: "This is my Father’s world,\nand to my listening ears\nall nature sings, and round me rings\nthe music of the spheres.\nThis is my Father’s world:\nI rest me in the thought\nof rocks and trees, of skies and seas;\nhis hand the wonders wrought.",
	2: "This is my Father’s world,\nthe birds their carols raise,\nthe morning light, the lily white,\ndeclare their maker’s praise.\nThis is my Father’s world:\nhe shines in all that’s fair;\nin the rustling grass I hear him pass;\nhe speaks to me everywhere.",
	3: "This is my Father’s world.\nO let me ne’er forget\nthat though the wrong seems oft so strong,\nGod is the ruler yet.\nThis is my Father’s world:\nwhy should my heart be sad?\nThe Lord is King; let the heavens ring!\nGod reigns; let the earth be glad!"
}
song["sda-hymnal/93"] = {
	title: "All Things Bright and Beautiful",
	tune: "Royal Oak",
	info: "Stanza 1 to be sung as refrain after stanzas 2 to 5.",
	copyright: "“All Things Bright and Beautiful” By permission of J. Curwen & Sons, Ltd. Used by permission of G. Schirmer, Inc., U.S.A. agents.",
	1: "Each little flow’r that opens\nEach little bird that sings\nHe made their glowing colours\nHe made their tiny wings.",
	2: "The purple-headed mountain\nThe river running by\nThe sunset and the morning\nThat brighten up the sky.",
	3: "The cold wind in the winter\nThe pleasant summer sun\nThe ripe fruits in the garden\nHe made them, every one.",
	4: "He gave us eyes to see them\nAnd lips that we might tell\nHow great is God Almighty\nWho has made all things well.",
	refrain: "Refrain:\nAll things bright and beautiful\nAll creatures great and small\nAll things wise and wonderful\nThe Lord God made them all."
}
song["sda-hymnal/94"] = {
	title: "Nature With Open Volume Stands",
	tune: "Angelus",
	1: "Nature with open volume stands,\nTo spread its Maker’s praise abroad;\nAnd every labor of His hands\nShows something worthy of our God.",
	2: "But in the grace that rescued us\nHis brightest form of glory shines;\n‘Tis fairest drawn upon the cross\nIn precious blood and crimson lines.",
	3: "Here His whole name appears complete.\nNor wit can guess, nor reason prove,\nWhich of the letters best is writ,\nThe pow’r, the wisdom, or the love.",
	4: "We would forever speak His name\nIn sounds to mortal ears unknown,\nWith angels join to praise the Lamb,\nAnd worship at His Father’s throne."
}
song["sda-hymnal/95"] = {
	title: "Spring Has Now Unwrapped the Flowers",
	tune: "Tempus adest floridum",
	copyright: "“Spring Has Now Unwrapped the Flowers” Words from the Oxford Book of Carols by permission of Oxford University Press. Music used by permission of Ernest MacMillan.",
	1: "Spring has now unwrapped the flowers,\nDay is fast reviving,\nLife in all her growing powers\nTowards the light is striving:\nGone the iron touch of cold,\nWinter time and frost time,\nSeedlings, working through the mould,\nNow make up for lost time.",
	2: "Herb and plant that winter long,\nSlumbered at their leisurek,\nNow be stirring, green and strong,\nFind in growth their pleasure:\nAll the world with beauty fills,\nGold the green enhacing;\nFlowers make glee among the hills,\nSet the meadows dancing.",
	3: "Through each wonder of fair days\nGod Himself expresses;\nBeauty follows all His ways,\nAs the world He blesses:\nSo, as He renews the earth,\nArtist without rival,\nIn His grace of glad new birth\nWe must seek revival."
}
song["sda-hymnal/96"] = {
	title: "The Spacious Firmament",
	tune: "Creation",
	scripture: "Psalm 19",
	1: "The spacious firmament on high,\nWith all the blue, ethereal sky,\nAnd spangled heavens, a shining frame,\nTheir great Original proclaim.\nTh’unwearied sun from day to day\nDoes his Creator’s power display,\nAnd publishes to every land\nThe work of an almighty hand",
	2: "Soon as the evening shades prevail,\nThe moon takes up the wondrous tale;\nAnd nightly to the listening earth\nRepeats the story of her birth;\nWhile all the stars that round her burn,\nAnd all the planets in their turn,\nConfirm the tidings as they roll,\nAnd spread the truth from pole to pole",
	3: "What though in solemn silence all\nMove round the dark terrestrial ball?\nWhat though no real voice nor sound\nAmid their radiant orbs be found?\nIn reason’s ear they all rejoice\nAnd utter forth a glorious voice,\nForever singing as they shine,\n“The hand that made us is divine.”"
}
song["sda-hymnal/97"] = {
	title: "Lord of the Boundless Curves of Space",
	tune: "San Rocco",
	info: "Alternate tune, AZMON, No. 250",
	copyright: "“Lord of the Boundless Curves of Space” Words by permission of Oxford University Press. Music copyright by Derek Williams. Used by permission.",
	1: "Lord of the boundless curves of space\nAnd time’s deep mystery,\nTo Your creative might we trace\nAll nature’s energy.",
	2: "Your mind conceived the galaxy,\nEach atom’s secret planned,\nAnd every age of history\nYour purpose, Lord, has spanned.",
	3: "Yours is the image stamped on man,\nThough marred by man’s own sin;\nAnd Yours the liberating plan\nAgain his soul to win.",
	4: "Give us to know Your truth; but more,\nThe strength to do Your will;\nUntil the love our souls adore\nShall all our being fill."
}
song["sda-hymnal/98"] = {
	title: "Can You Count the Stars",
	1: "Can you count the stars that brightly\nTwinkle in the midnight sky?\nCan you count the clouds, so lightly\nO’er the meadows floating by?\nGod, the Lord, doth mark their number\nWith His eyes that never slumber;\nHe hath made every one,\nHe hath made them every one.",
	2: "Can you count the wings now flashing\nIn the sunshine’s golden light?\nCan you count the fishes splashing\nIn the cooling waters bright?\nGod, the Lord, a name hath given,\nTo all creatures under heaven;\nHe hath named them every one,\nHe hath named them every one.",
	3: "Do you know how many children\nRise each morning blithe and gay?\nCan you count their jolly voices,\nSinging sweetly day by day?\nGod hears all the happy voices,\nIn their merry songs rejoices;\nAnd He loves them, every one,\nAnd He loves them, every one."
}
song["sda-hymnal/99"] = {
	title: "God Will Take Care of You",
	scripture: "1 Peter 5:7",
	1: "Be not dismayed whate’er betide,\nGod will take care of you;\nbeneath his wings of love abide,\nGod will take care of you.\nRefrain\nGod will take care of you,\nthrough every day, o’er all the way;\nhe will take care of you,\nGod will take care of you.",
	2: "Through days of toil when heart doth fail,\nGod will take care of you;\nwhen dangers fierce your path assail,\nGod will take care of you.",
	3: "All you may need he will provide,\nGod will take care of you;\nnothing you ask will be denied,\nGod will take care of you.",
	4: "No matter what may be the test,\nGod will take care of you;\nlean, weary one, upon his breast,\nGod will take care of you."
}
song["sda-hymnal/100"] = {
	title: "Great Is Thy Faithfulness",
	tune: "Faithfulness",
	scripture: "Lamentations 3:22, 23",
	copyright: "“Great Is Thy Faithfulness” Copyright © 1923. Renewal 1951 extended by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Great is Thy faithfulness, O God my Father;\nThere is no shadow of turning with Thee;\nThou changest not, Thy compassions, they fail not;\nAs Thou hast been, Thou forever will be.\nRefrain\nGreat is Thy faithfulness!\nGreat is Thy faithfulness!\nMorning by morning new mercies I see.\nAll I have needed Thy hand hath provided;\nGreat is Thy faithfulness, Lord, unto me!",
	2: "Summer and winter and springtime and harvest,\nSun, moon and stars in their courses above\nJoin with all nature in manifold witness\nTo Thy great faithfulness, mercy and love.",
	3: "Pardon for sin and a peace that endureth\nThine own dear presence to cheer and to guide;\nStrength for today and bright hope for tomorrow,\nBlessings all mine, with ten thousand beside!"
}
song["sda-hymnal/101"] = {
	title: "Children of the Heavenly Father",
	tune: "Tryggare kan ingen vara",
	info: "Unison setting, No. 153",
	copyright: "“Children of the Heavenly Father” Words copyright Board of Publication, Lutheran Church in America. Reprinted by permission.",
	1: "Children of the heavenly Father\nSafely in His bossom gather;\nNestling bird nor star in heaven\nSuch a refuge e’er was given.",
	2: "God His own doth tend and nourish,\nIn His holy love they flourish;\nFrom all evil things He spares them,\nIn His mighty arms He bears them.",
	3: "Neither life nor death shall ever\nFrom the Lord His children sever;\nUnto them His grace He showeth,\nAnd their sorrows all He knoweth.",
	4: "Praise the Lord in joyful numbers,\nYour Protector never slumbers;\nAt the will of your Defender\nEvery foe-man must surrender.",
	5: "Though He giveth or He taketh,\nGod His children ne’er forsaketh;\nHis the loving purpose solely\nTo preserve them pure and holy."
}
song["sda-hymnal/102"] = {
	title: "Unto the Hills",
	tune: "You Yangs",
	scripture: "Psalm 121",
	copyright: "“Unto the Hills” Music copyright © 1984 by James Bingham",
	1: "Unto the hills around shall I lift up my longing eyes?\nO whence for me shall my salvation come, from whence arise?\nFrom God the Lord doth come my certain aid,\nFrom God the Lord, who heav’n and earth hath made.",
	2: "He will not suffer that thy foot be moved; safe shalt Thou be.\nNo careless slumber shall His eyelids close, who keepeth thee.\nBehold our God, the Lord, He slumbereth ne’er,\nWho keepeth Israel in His holy care.",
	3: "Jehovah is Himself thy keeper true, thy changeless shade.\nJehovah they defense on thy right hand Himself hath made.\nAnd thee no sun by day shall ever smite,\nNo moon shall harm thee in the silent night.",
	4: "From every evil shall He keep thy soul, from every sin;\nJehovah shall preserve thy going our,n thy coming in.\nAbove thee watching, He, whom we adore.,\nShall keep thee henceforth, yea, forevermore."
}
song["sda-hymnal/103"] = {
	title: "O God, Our Help",
	tune: "St. Anne",
	scripture: "Psalm 90",
	1: "O God, our help in ages past,\nour hope for years to come,\nour shelter from the stormy blast,\nand our eternal home!",
	2: "Under the shadow of thy throne,\nstill may we dwell secure;\nsufficient is thine arm alone,\nand our defense is sure.",
	3: "Before the hills in order stood,\nor earth received her frame,\nfrom everlasting, thou art God,\nto endless years the same.",
	4: "A thousand ages, in thy sight,\nare like an evening gone;\nshort as the watch that ends the night,\nbefore the rising sun.",
	5: "O God, our help in ages past,\nour hope for years to come;\nbe thou our guide while life shall last,\nand our eternal home!"
}
song["sda-hymnal/104"] = {
	title: "My Shepherd Will Supply My Need",
	tune: "Resignation",
	scripture: "Psalm 23",
	copyright: "“My Shepherd Will Supply My Need” Music copyright © 1949 by H. W. Gray, a division of Belwin - Mills Publishing Corp. Used by permission. All rights reserved.",
	1: "My Shepherd will supply my need,\nJehovah is His name.\nIn pastures fresh He makes me feed\nBeside the living stream.\nHe brings my wand’ring spirit back\nWhen I forsake His ways,\nAnd leads, me for His mercy’s sake,\nIn paths of truth and grace.",
	2: "When I walk through the shades of death,\nThy presence is my stay;\nA word of Thy supporting breath\nDrives all my fears away.\nThy hand in sight of all my foes\nDoth still my table spread;\nMy cup with blessings overflows,\nThine oil anoints my head.",
	3: "The sure provisions of my God\nAttend me all my days;\nO may Thy house be mine abode\nAnd all my work be praise.\nThere would I find a settled rest\nWhile others go and come;\nNo more a stranger or a guest,\nBut like a child at home."
}
song["sda-hymnal/105"] = {
	title: "Sing to the Great Jehovah's Praise",
	tune: "Lobt Gott ihr Christen",
	1: "Sing to the great Jehovah’s praise!\nAll praise to Him belongs;\nWho kindly lengthens out our days,\nIn spires our choicest songs,\nInspires our choicest songs.",
	2: "His providence hath brought us through\nAnother various year;\nWe all, with vows and anthems new,\nBefore our God appear,\nBefore our God appear.",
	3: "O God, Thy mercies past we own,\nAnd Thy continued care;\nTo Thee presenting through Thy Son\nWhat e’er we have and are,\nWhat e’er we have and are."
}
song["sda-hymnal/106"] = {
	title: "Give to Our god Immortal Praise",
	tune: "Dunedin",
	scripture: "Psalm 136",
	info: "Alternate tune, Duke Street, Nos. 82, 227",
	1: "Give to our God immortal praise;\nMercy and truth are all His ways:\nWonders of grace to God belong,\nRepeat His mercies in your song.",
	2: "Give to the Lord of lords renown,\nThe King of kings with glory crown:\nHis mercies ever shall endure\nWhen lords and kings are known no more.",
	3: "He sent His Son with power to save\nFrom guilt and darkness and the grave:\nWonders of grace to God belong,\nRepeat His mercies in your song.",
	4: "Thro’ this vain world He guides our feet,\nAnd leads us to His heav’nly seat;\nHis mercies ever shall endure,\nWhen this vain world shall be no more."
}
song["sda-hymnal/107"] = {
	title: "God Moves in a Mysterious Way",
	tune: "Dundee",
	scripture: "John 13:7",
	1: "God moves in a mysterious way\nHis wonders to perform;\nHe plants His footsteps in the sea,\nAnd rides upon the storm.",
	2: "Ye fearful saints, fresh courage take;\nThe clouds ye so much dread\nAre big with mercy, and shall break\nIn blessings on your head.",
	3: "Judge not the Lord by feeble sense,\nBut trust Him for His grace;\nBehind a frowning providence\nHe hides a smiling face.",
	4: "His purposes will ripen fast,\nUnfolding every hour;\nThe bud may have a bitter taste,\nBut sweet will be the flower.",
	5: "Blind unbelief is sure to err,\nAnd scan His work in vain;\nGod is His own interpreter,\nAnd He will make it plain."
}
song["sda-hymnal/108"] = {
	title: "Amazing Grace",
	tune: "New Britain",
	scripture: "1 Chronicles 17:16, 17",
	copyright: "“Amazing Grace” Arr. copyright © by G. I. A. Publications, Inc. Used by permission.",
	1: "Amazing grace! How sweet the sound\nthat saved a wretch like me!\nI once was lost, but now am found;\nwas blind, but now I see.",
	2: "‘Twas grace that taught my heart to fear,\nand grace my fears relieved;\nhow precious did that grace appear\nthe hour I first believed.",
	3: "The Lord has promised good to me,\nhis word my hope secures;\nhe will my shield and portion be,\nas long as life endures.",
	4: "Through many dangers, toils, and snares,\nI have already come;\n’tis grace hath brought me safe thus far,\nand grace will lead me home.",
	5: "When we’ve been there ten thousand years,\nbright shining as the sun,\nwe’ve no less days to sing God’s praise\nthan when we first begun."
}
song["sda-hymnal/109"] = {
	title: "Marvelous Grace",
	copyright: "“Marvelous Grace” Copyright 1910. Renewal 1938 extended by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Marvelous grace of our loving Lord.\nGrace that exceeds our sin and our guilt!\nYonder on Calvary’s mount outpoured\nThere where the blood of the Lamb was spilt.\nRefrain\nGrace, grace, God’s grace,\nGrace that will pardon and cleanse within\nGrace, grace, God’s grace,\nGrace that is greater than all our sin!",
	2: "Sin and despair, like the sea waves cold,\nThreaten the soul with infinite loss;\nGrace that is greater, yes, grace untold\nPoints to the Refuge, the mighty Cross.",
	3: "Marvelous, infinite, matchless grace,\nFreely bestowed on all who believe!\nYou that are longing to see His face,\nWill you this moment His grace receive?"
}
song["sda-hymnal/110"] = {
	title: "God's Free Mercy Streameth",
	tune: "Ruth",
	scripture: "Ecclesiastes 11:7",
	1: "God’s free mercy streameth over all the world,\nAnd His banner gleameth, By His church unfuried;\nBroad and deep and glorious, As the heaven above,\nShines in might victorious His eternal love.",
	2: "Summer suns are glowing over land and sea;\nHappy light is flowing, Bountiful and free;\nEverything rejoices in the mellow rays;\nEarth’s ten thousand voices swell the psalm of praise.",
	3: "Lord, upon our blindness thy pure radiance pour;\nFor Thy loving kindness we would love Thee more;\nAnd when clouds are drifting dark across the sky,\nThen, the veil uplifting, father, be Thou nigh.",
	4: "We will never doubt Thee, tho’ veil Thy light;\nLife is dark without Thee, death with Thee is bright.\nLight of light, shine o’er us on our pilgrim way,\nGo Thou still before us to the endless day."
}
song["sda-hymnal/111"] = {
	title: "It Took a Miracle",
	tune: "Montrose",
	copyright: "“It Took a Miracle” Copyright © 1948 by John W. Peterson Music Co. All rights reserved. International copy right secured. Used by permission of Good Life Publications, Inc., Scottsdale, AZ 85258.",
	1: "My Father is omnipotent\nAnd that you can’t deny;\nA God of might and miracles;\n‘Tis written in the sky.\nRefrain\nIt took a miracle to put the stars in place;\nIt took a miracle to hang the world in space.\nBut when He saved my soul,\nCleansed and made me whole,\nIt took a miracle of love and grace!",
	2: "Though here His glory has been shown,\nWe still can’t fully see\nThe wonders of His might, His throne;\n‘Till eternity.",
	3: "The Bible tells us of His power\nAnd wisdom all way through;\nAnd every little bird and flower\nAre testimonies, too."
}
song["sda-hymnal/112"] = {
	title: "Let Us With a Gladsome Mind",
	tune: "Monkland",
	scripture: "Psalm 136",
	1: "Let us with a gladsome mind\nPraise the Lord, for He is kind:\nFor His mercies shall endure,\nEver faithful, ever sure.",
	2: "He, with all commanding might,\nFilled the new made world with light:\nFor His mercies shall endure,\nEver faithful, ever sure.",
	3: "All things living He does feed;\nHis full hand supplies their need:\nFor His mercies shall endure,\nEver faithful, ever sure.",
	4: "Let us then with gladsome mind\nPraise the Lord, for He is kind:\nFor His mercies shall endure,\nEver faithful, ever sure."
}
song["sda-hymnal/113"] = {
	title: "As Pants the Hart",
	tune: "Martyrdom",
	scripture: "Psalm 42:1 - 5",
	1: "As pants the hart for cooling streams\nWhen heated in the chase,\nSo longs my soul, O God, for Thee,\nAnd Thy refreshing grace.",
	2: "For Thee, my God, the living God,\nMy thirsty soul doth pine:\nO when shall I behold Thy face,\nThou Majesty divine?",
	3: "Why restless, why cast down, my soul?\nHope still, and thou shalt sing\nThe praise of Him who is thy God,\nThy health’s eternal spring.",
	4: "To Father, Son, and Holy Ghost,\nThe God whom we adore,\nBe glory, as it was, is now,\nAnd shall be evermore."
}
song["sda-hymnal/114"] = {
	title: "There's a Wideness",
	tune: "Wellesley",
	1: "There’s a wideness, in God’s mercy,\nlike the wideness of the sea;\nThere is mercy, with the Savior,\nWhich is more than liberty.",
	2: "There is welcome, for the sinner,\nand more graces for the good;\nThere is mercy, with the Savior,\nThere is healing in His blood.",
	3: "For the love of God is broader\nThan the measure of man’s mind.\nAnd the heart of the Eternal,\nis most wonderfully kind.",
	4: "If our love were but more simple,\nWe should take Him at His word;\nAnd our lives would be all sunshine,\nIn the sweetness of our Lord."
}
song["sda-hymnal/115"] = {
	title: "O Come, O Come, Immanuel",
	tune: "Veni, Emmanuel",
	1: "O come, O come, Emmanuel,\nAnd ransom captive Israel,\nThat mourns in lonely exile here\nUntil the Son of God appear.\nRefrain\nRejoice! Rejoice!\nEmmanuel shall come to thee,\nO Israel.",
	2: "O come, Thou Wisdom from on high,\nAnd order all things far and night;\nTo us the path of knowledge show,\nAnd cause us in her ways to go.",
	3: "O come, Desire of nations, bind\nIn one the hearts of all mankind;\nBid Thou our sad divisions cease,\nAnd be Thyself our King of Peace."
}
song["sda-hymnal/116"] = {
	title: "Of the Father's Love Begotten",
	tune: "Divinum mysterium",
	1: "Of the Father’s love begotten,\nEre the worlds began to be,\nHe is Alpha and Omega,\nHe the source, the ending He,\nOf the things that are, that have been,\nAnd that future years shall see,\nEvermore and evermore! Amen.",
	2: "O ye heights of heaven adore Him;\nAngel hosts, His praises sing;\nPowers, dominions, bow before Him,\nAnd extol our God and King;\nLet no tongue on earth be silent,\nEvery voice in concert ring,\nEvermore and evermore! Amen.",
	3: "Christ, to Thee with God the Father\nAnd, O Holy Ghost, to Thee,\nHymn and chant and high thanksgiving\nAnd unwearied praises be.\nHonor, glory, and dominion,\nAnd eternal victory,\nEvermore and evermore! Amen."
}
song["sda-hymnal/117"] = {
	title: "The Advent of Our God",
	tune: "Festal Song",
	info: "Higher key, No. 615",
	1: "The advent of our God\nWith eager prayers we greet,\nAnd singing haste upon His road\nHis coming reign to meet.",
	2: "The everlasting Son\nWas born to make us free;\nAnd He a servant’s form put on\nTo gain our liberty.",
	3: "As Judge, on clouds of light,\nHe soon will come again,\nHis scattered people to unite\nWith them in heaven to reign.",
	4: "Praise to the incarnate Son\nWho comes to set us free,\nWith Father, Spirit, ever one,\nTo all eternity."
}
song["sda-hymnal/118"] = {
	title: "The First Noel",
	tune: "The First Noel",
	1: "The first noel the angel did say\nWas to certain poor shepherds in fields where they lay;\nIn fields where they lay keeping their sheep,\nOn a cold winter’s night that was so deep.\nRefrain\nNoel, Noel, Noel, Noel,\nBorn is the King of Israel",
	2: "They looked up and say a star\nShining in the east, beyond them far,\nAnd to the earth it gave great light,\nAnd so it continued both day and night.",
	3: "And by the light of that same star,\nThree wise men came from country far,\nAnd to the earth it gave great light,\nAnd to follow the star wherever it went.",
	4: "This star drew nigh to the northwest,\nO’er Bethlehem it took its rest,\nAnd there it did both stop and stay,\nRight over the place where Jesus lay.",
	5: "Then entered in those wise men three,\nFull reverently upon the knee,\nAnd offered there, in His presence,\nTheir gold, and myrrh, and frankincense."
}
song["sda-hymnal/119"] = {
	title: "Angels From the Realms of Glory",
	tune: "Regent Square",
	scripture: "Luke 2",
	1: "Angels from the realms of glory,\nWing your flight o`er all the earth;\nYe, who sang creation`s story,\nNow proclaim Messiah`s birth;\nCome and worship, Come and worship,\nWorship Christ, the newborn King.",
	2: "Shepherds, in the field abiding,\nWatching o`er your flocks by night,\nGod with man is now residing;\nYonder shines the Infant Light;\nCome and worship, Come and worship,\nWorship Christ, the newborn King.",
	3: "Sages, leave your contemplations,\nBrighter visions beam afar;\nSeek the great Desire of nations;\nYe have seen His natal star;\nCome and worship, Come and worship,\nWorship Christ, the newborn King.",
	4: "Saints, before the altar bending,\nWatching long in hope and fear,\nSuddenly the Lord, descending,\nIn His temple shall appear;\nCome and worship, Come and worship,\nWorship Christ, the newborn King."
}
song["sda-hymnal/120"] = {
	title: "There's a Song in the Air",
	tune: "Christmas Song",
	1: "There’s a song in the air!\nThere’s a star in the sky!\nThere’s a mother’s deep prayer\nAnd a baby’s low cry!\nAnd the star rains its fire\nwhile the beautiful sing,\nFor the manger of Bethlehem\ncradles a King!",
	2: "There’s a tumult of joy\nO’er the wonderful birth,\nFor the virgin’s sweet boy\nIs the Lord of the earth.\nAye! the star rains its fire\nwhile the beautiful sing,\nFor the manger of Bethlehem\ncradles a King!",
	3: "In the light of that star\nLie the ages impearled;\nAnd that song from afar\nHas swept over the world.\nEvery hearth is aflame\nand the beautiful sing\nIn the homes of the nations\nthat Jesus is King!",
	4: "We rejoice in the light,\nAnd we echo the song\nThat comes down through the night\nFrom the heavenly throng.\nAye! we shout to the lovely evangel they bring,\nAnd we greet in His cradle\nour Savior and King!"
}
song["sda-hymnal/121"] = {
	title: "Go, Tell It On the Mountain",
	copyright: "“Go, Tell It on the Mountain” Copyright © 1945 by Galaxy Music Corporation, New York. Used by permission.",
	1: "While shepherds kept their watching\nO’er silent flocks by night,\nBehold throughout the heavens\nThere shone a holy light.",
	2: "The shepherds feared and trembled\nWhen lo! Above the earth\nRang out the angel chorus\nThat hailed our Savior’s birth.",
	3: "Down in a lowly manger\nThe humble Christ was born,\nAnd brought us God’s salvation\nThat blessed Christmas morn.",
	refrain: "Refrain:\nGo, tell it on the mountain,\nOver the hills and everywhere:\nGo, tell it on the mountain\nThat Jesus Christ is born!"
}
song["sda-hymnal/122"] = {
	title: "Hark! the Herald Angels Sing",
	tune: "Mendelssohn",
	scripture: "Luke 2:14",
	1: "Hark! the herald angels sing,\n“Glory to the new born King,\npeace on earth, and mercy mild,\nGod and sinners reconciled!”\nJoyful, all ye nations rise,\njoin the triumph of the skies;\nwith th’ angelic host proclaim,\n“Christ is born in Bethlehem!”\nHark! the herald angels sing,\n“Glory to the new born King!”",
	2: "Christ, by highest heaven adored;\nChrist, the everlasting Lord;\nlate in time behold him come,\noffspring of a virgin’s womb.\nVeiled in flesh the Godhead see;\nhail th’ incarnate Deity,\npleased with us in flesh to dwell,\nJesus, our Emmanuel.\nHark! the herald angels sing,\n“Glory to the new born King!”",
	3: "Hail the heaven-born Prince of Peace!\nHail the Sun of Righteousness!\nLight and life to all he brings,\nrisen with healing in his wings.\nMild he lays his glory by,\nborn that we no more may die,\nborn to raise us from the earth,\nborn to give us second birth.\nHark! the herald angels sing,\n“Glory to the new born King!”"
}
song["sda-hymnal/123"] = {
	title: "As With Gladness Men of Old",
	tune: "Dix",
	scripture: "Matthew 2:1 - 11",
	info: "Harmony setting, No. 565",
	copyright: "“As With Gladness Men of Old” Arrangement copyright © 1984 by Melvin West.",
	1: "As with gladness men of old\nDid the guiding star behold,\nAs with joy they hailed its light,\nLeading onward, beaming bright,\nSo, most gracious Lord, may we\nEvermore be led to Thee.",
	2: "As with joyful steps they sped\nTo that lowly manger bed,\nThere to bend the knee before\nHim whom heav’n and earth adore,\nSo may we with willing feet\nEver seek Thy mercy seat.",
	3: "As they offered gifts most rare\nAt that manger rude and bare,\nSo may we with holy joy,\nPure, and free from sin’s alloy,\nAll our costliest treasures bring,\nChrist, to Thee our heav’nly King.",
	4: "Holy Jesus, every day\nKeep us in the narrow way;\nAnd, when earthly things are past,\nBring our ransomed souls at last\nWhere they need no star to guide,\nWhere no clouds Thy glory hide."
}
song["sda-hymnal/124"] = {
	title: "Away in a Manger",
	tune: "Cradle Song",
	copyright: "“Away in a Manger” Arr. from the Australian Hymnal by permission of the Australian Hymn Book Co.",
	1: "Away in a manger, no crib for a bed,\nThe little Lord Jesus laid down his sweet head.\nThe stars in the sky looked down where he lay,\nThe little Lord Jesus asleep in the hay.",
	2: "The cattle are lowing, the baby awakes,\nBut little Lord Jesus no crying he makes.\nI love Thee, Lord Jesus, look down from the sky\nAnd stay by my cradle til morning is nigh.",
	3: "Be near me, Lord Jesus, I ask Thee to stay\nClose by me forever, and love me, I pray.\nBless all the dear children in thy tender care,\nAnd take us to heaven, to live with Thee there."
}
song["sda-hymnal/125"] = {
	title: "Joy to the World",
	poet: "Isaac Watts, 1719 (1674 - 1748)",
	tune: "Antioch",
	scripture: "Psalms 98:4-9",
	1: "Joy to the world, the Lord is come!\nLet earth receive her King;\nLet every heart prepare Him room,\nAnd heaven and nature sing, And heaven and nature sing,\nAnd heaven, and heaven and nature sing.",
	2: "Joy to the earth, the Savior reigns!\nLet men their songs employ;\nWhile fields and floods, rocks, hills, and plains,\nRepeat the sounding joy, Repeat the sounding joy,\nRepeat, repeat the sounding joy.",
	3: "No more let sin and sorrow grow,\nNor thorns infest the ground;\nHe comes to make His blessings flow\nFar as the curse is found, Far as the curse is found,\nFar as, far as the curse is found.",
	4: "He rules the world with truth and grace,\nAnd makes the nations prove\nThe glories of His righteousness,\nAnd wonders of His love, And wonders of His love,\nAnd wonders, and wonders of His love."
}
song["sda-hymnal/126"] = {
	title: "In the Bleak Midwinter",
	tune: "Uinta",
	info: "Alternate tune, Cranham, No. 224",
	copyright: "“In the Bleak Midwinter” Music copyright © 1984 by Wayne Hooper.",
	1: "In the bleak midwinter\nFrosty wind made moan;\nEarth stood hard as iron,\nWater like a stone;\nSnow had fallen, snow on snow,\nSnow on snow,\nIn the bleak midwinter,\nLong ago.",
	2: "Angels and archangels\nMay have gathered there,\nCherubim and seraphim\nThronged the air:\nBut His mother only\nIn her maiden bliss,\nWorshipped the beloved\nWith a kiss.",
	3: "What can I give Him,\nPoor as I am?\nIf I were a shepherd\nI would bring a lamb;\nIf I were a wise man\nI would do my part;\nYet what can I give Him?\nGive my heart."
}
song["sda-hymnal/127"] = {
	title: "Infant Holy, Infant Lowly",
	tune: "W zolbie lezy",
	scripture: "Luke 2:7 - 11",
	copyright: "“Infant Holy, Infant Lowly” Arrangement copyright © by Australian Hymn Book Co.",
	1: "Infant holy, infant lowly,\nFor His bed a cattle stall;\nOxen lowing, little knowing\nChrist the babe is Lord of all;\nSwift are winging angels singing,\nNoels ringing, tidings bringing,\nChrist the babe is Lord of all,\nChrist the babe is Lord of all.",
	2: "Flocks were sleeping, shepherds keeping\nVigil till the morning new;\nSaw the glory, heard the story,\nTidings of the gospel true;\nThus rejoicing, free from sorrow,\nPraises voicing greet the morrow,\nChrist the babe was born for you,\nChrist the babe was born for you."
}
song["sda-hymnal/128"] = {
	title: "Break Forth, O Beauteous Heavenly Light",
	tune: "Ermuntre dich",
	copyright: "“Break Forth, O Beauteous Heavenly Light” Second stanza copyright 1973 by Covenant Press. Used by permission.",
	1: "Break forth, O beauteous heavenly light,\nAnd usher in the morning;\nYe shepherds, shrink not with affright,\nBut hear the angel’s warning.\nThis Child, now weak in infancy,\nOur confidence and joy shall be,\nThe power of Satan breaking,\nOur peace eternal making.",
	2: "Break forth, O beauteous heavenly light,\nTo herald our salvation;\nHe stoops to earth – the God of might,\nOur hope and expectation.\nHe comes in human flesh to dwell,\nOur God with us, Immanuel,\nThe night of darkness ending,\nOur fallen race befriending."
}
song["sda-hymnal/129"] = {
	title: "As It Fell Upon a Night",
	tune: "Puer nobis nascitur",
	copyright: "“As It Fell Upon a Night” Words copyright © 1942 by Galaxy Music Co., New York. Used by permission.  Music from the Oxford Book of Carols by permission of Oxford University Press.",
	1: "As it fell upon a night\nIn the winter weather,\nAngels bright in starry height\nBegan to sing together.",
	2: "Shepherds sleeping on the plain\nWoke to see the glory,\nAll amazed they stood and gazed\nAnd heard the angels’ story.",
	3: "Unto you a child is born\nIn a manger lowly,\nHumble, He, yet born to be\nThe King of Love most holy.",
	4: "Happy angels from afar,\nCease your singing never!\nIn excelsis gloria!\nForever and forever."
}
song["sda-hymnal/130"] = {
	title: "It Came Upon the Midnight Clear",
	tune: "Carol",
	scripture: "Luke 2:13, 14",
	1: "It came upon the midnight clear,\nThat glorious song of old,\nFrom angels bending near the earth,\nTo touch their harps of gold:\n“Peace on the earth, goodwill to men\nFrom heavens all gracious King!”\nThe world in solemn stillness lay\nTo hear the angels sing.",
	2: "Still through the cloven skies they come,\nWith peaceful wings unfurled;\nAnd still their heavenly music floats\nO’er all the weary world:\nAbove its sad and lowly plains\nThey bend on hovering wing,\nAnd ever o’er its Babel sounds\nThe blessed angels sing.",
	3: "O ye beneath life’s crushing load,\nWhose forms are bending low,\nWho toil along the climbing way\nWith painful steps and slow;\nLook now, for glad and golden hours\nCome swiftly on the wing;\nOh rest beside the weary road\nAnd hear the angels sing."
}
song["sda-hymnal/131"] = {
	title: "Lo, How a Rose E'er Blooming",
	tune: "Es ist ein Ros",
	1: "Lo, how a rose e’re blooming\nFrom tenderstem hath sprung,\nOf Jesse’s lineage coming\nAs men of old have sung.\nIt came, a floweret bright,\nAmid the cold of winter\nWhen half spent was the night.",
	2: "Isaiah ’twas foretold it,\nThe Rose I have in mind,\nWith Mary we beheld it,\nThe virgin mother kind.\nTo show God’s love aright\nShe bore to them a Savior,\nWhen half spent was the night."
}
song["sda-hymnal/132"] = {
	title: "O Come, All Ye Faithful",
	tune: "Adeste fideles",
	1: "O come, all ye faithful, joyful and triumphant,\nO come ye, O come ye to Bethlehem!\nCome and behold Him, born the King of angels!\nO come, let us adore Him, O come, let us adore Him,\nO come, let us adore Him, Christ, the Lord!",
	2: "Sing, choirs of angels sing in exultation,\nO sing all ye citizens of heaven above!\nGlory to God, all glory in the highest!\nO come, let us adore Him, O come, let us adore Him,\nO come, let us adore Him, Christ, the Lord!",
	3: "Yea, Lord, we greet Thee, born this happy morning,\nJesus, to Thee be all glory given;\nWord of the Father, now in flesh appearing!\nO come, let us adore Him, O come, let us adore Him,\nO come, let us adore Him, Christ, the Lord!"
}
song["sda-hymnal/133"] = {
	title: "Now Is Born the Divine Christ Child",
	tune: "Il est né",
	copyright: "“Now Is Born the Divine Christ Child” Arrangement copyright © 1984 by Wayne Hooper.",
	1: "He was born in a stable bare,\nOn bed of straw how He sleeps so soundly,\nHe was born in a stable bare,\nLet us worship and to Him bow.",
	2: "Ages long since are past and gone,\nWhen the wise men foretold His coming,\nAges long since are past and gone,\nWhen the wise men foretold His birth.",
	refrain: "Refrain:\nNow is born the divine Christ child,\nPlay the musette, play the tuneful oboe,\nNow is born the divine Christ child,\nSing we all and rejoice this day."
}
song["sda-hymnal/134"] = {
	title: "O Jesus Sweet",
	tune: "O Jesulein süss",
	copyright: "“O Jesus Sweet” Copyright © 1933 by Carl Fischer. Inc., NewYork. Copyright renewed 1960. Reprinted by permission.",
	1: "O Jesus sweet, O Jesus mild,\nThy Father’s will hast Thou fulfilled;\nFor Thou hast left Thy heavenly throne\nOur lowly state to make Thine own.\nO Jesus sweet, O Jesus mild.",
	2: "O Jesus sweet, O Jesus mild,\nWith joy hast Thou the whole world filled;\nThou comest down from heaven’s hall\nTo comfort us whom tears enthrall.\nO Jesus sweet, O Jesus mild.",
	3: "O Jesus sweet, O Jesus mild,\nThou art love’s image undefiled.\nInflame our hearts with love’s pure fire,\nThat we may share Thy heart’s desire.\nO Jesus sweet, O Jesus mild.",
	4: "O Jesus sweet, O Jesus mild,\nHelp us to do as Thou hast willed.\nWhat’er we have belongs to Thee:\nO may we ever faithful be.\nO Jesus sweet, O Jesus mild."
}
song["sda-hymnal/135"] = {
	title: "O Little Town of Bethlehem",
	tune: "St. Louis",
	scripture: "Luke 2:13, 14",
	info: "Alternate tune Forest Green, No. 90",
	1: "O little town of Bethlehem, how still we see thee lie!\nAbove thy deep and dreamless sleep the silent stars go by.\nYet in thy dark streets shineth the everlasting Light;\nThe hopes and fears of all the years are met in thee tonight.",
	2: "For Christ is born of Mary, and gathered all above,\nWhile mortals sleep, the angels keep their watch of wondering love.\nO morning stars together, proclaim the holy birth,\nAnd praises sing to God the King, and peace to men on earth!",
	3: "How silently, how silently, the wondrous Gift is giv’n;\nSo God imparts to human hearts the blessings of His heav’n.\nNo ear may hear His coming, but in this world of sin,\nWhere meek souls will receive Him still, the dear Christ enters in.",
	4: "O holy Child of Bethlehem, descend to us, we pray;\nCast out our sin, and enter in, be born in us today.\nWe hear the Christmas angels the great glad tidings tell;\nO come to us, abide with us, our Lord Emmanuel!"
}
song["sda-hymnal/136"] = {
	title: "Good Christians, Now Rejoice",
	tune: "In dulci jubilo",
	1: "Good Christians, now rejoice,\nWith heart, and soul, and voice;\nGive ye heed to what we say:\nJesus Christ is born today;\nOx and ass before Him bow,\nAnd He is in the manger now.\nCrist is born today!\nCrist is born today!",
	2: "Good Christians, now rejoice,\nWith heart, and soul, and voice;\nNow ye hear of endless bliss:\nJesus Christ was born for this!\nHe hath ope’d the heav’nly door,\nAnd we are blessed evermore.\nChrist was born for this!\nChrist was born for this!",
	3: "Good Christians, now rejoice,\nWith heart, and soul, and voice;\nNow ye need not fear the grave:\nJesus Christ was born to save!\nCalls you one and calls you all\nTo gain His everlasting hall.\nChrist was born to save!\nChrist was born to save!"
}
song["sda-hymnal/137"] = {
	title: "We Three Kings",
	tune: "Kings of Orient",
	1: "We three kings of Orient are;\nBearing gifts we traverse afar\nField and fountain, moor and mountain,\nFollowing yonder star.\nRefrain\nO star of wonder, star of night,\nStar with royal beauty bright,\nWestward leading, still proceeding,\nGuide us to Thy perfect light.",
	2: "Born a King on Bethlehem’s plain,\nGold I bring to crown Him again,\nKing forever, ceasing never\nOver us all to reign.",
	3: "Frankincense to offer have I;\nIncense owns a Deity nigh;\nPrayer and praising all men raising,\nWorship Him, God on high.",
	4: "Myrrh is mine; its bitter perfume\nBreathes a life of gathering gloom:\nSorrowing, sighing, bleeding, dying,\nSealed in the stonecold tomb.",
	5: "Glorious now behold Him arise,\nKing and God and sacrifice;\nAlleluia, Alleluia!\nSounds through the earth and skies."
}
song["sda-hymnal/138"] = {
	title: "Rise Up, Shepherd, and Follow",
	copyright: "“Rise Up, Shepherd, and Follow” Arrangement copyright © 1984 by Alma Blackmon.",
	1: "There’s a star in the east on Christmas morn.\nRise up, shepherd, and follow.\nIt will lead to the place and where the Saviour’s born,\nRise up, shepherd, and follow.\nRefrain:\nLeave your sheep and leave your lambs,\nRise up, shepherd, and follow.\nLeave your ewes and leave your rams,\nRise up, shepherd, and follow.\nFollow, follow,\nRise up shepherd, and follow.\nFollow the star of Bethlehem,\nRise up, shepherd, and follow.",
	2: "If you take good heed to the angel’s words,\nRise up, shepherd, and follow.\nYou’ll forget your flocks,\nyou’ll forget your herds,\nRise up, shepherd, and follow."
}
song["sda-hymnal/139"] = {
	title: "While Shepherds Watched Their Flocks",
	tune: "Winchester Old",
	scripture: "Luke 2:8 - 14",
	1: "While shepherds watched their flocks by night,\nAll seated on the ground,\nThe angel of the Lord came down,\nAnd glory shone around.",
	2: "“Fear nor!” said he- for mighty dread\nHad seized their troubled mind-\n“Glad tidings of great joy I bring,\nTo you and all mankind.",
	3: "“To you, in David’s town this day,\nIs born of David’s line,\nThe Savior who is Christ the Lord;\nAnd this shall be the sign:",
	4: "“The heavenly Babe you there shall find\nTo human view displayed,\nAll meanly wrapped in swathing bands,\nAnd in a manger laid.”",
	5: "Thus spake the seraph; and forth with\nAppeared a shining throng\nOf angels praising God on high,\nWho thus addressed their song:",
	6: "“All glory be to God on high,\nAnd to the earth be peace;\nGood will henceforth from heaven to men,\nBegin and never cease!”"
}
song["sda-hymnal/140"] = {
	title: "Thou Didst Leave Thy Throne",
	tune: "Margaret",
	1: "Thou didst leave Thy throne\nAnd Thy kingly crown\nWhen Thou camest to earth for me;\nBut in Bethlehem’s home\nWas there found no room\nFor Thy holy nativity.\nRefrain\nO come to my heart, Lord Jesus,\nThere is room in my heart for Thee.",
	2: "Heaven’s arches rang\nWhen the angels sang\nProclaiming Thy royal degree;\nBut of lowly birth\nDidst Thou come to earth,\nAnd in greatest humility.",
	3: "The foxes found rest,\nAnd the birds their nest\nIn the shade of the forest tree;\nBut Thy couch was the sod,\nO Thou Son of God,\nIn the deserts of Galilee.",
	4: "Thou camest, O Lord,\nWith the living word\nThat should set Thy people free;\nBut with mocking scorn,\nAnd with crown of thorn,\nThey bore Thee to Calvary.",
	5: "When the heavens shall ring,\nAnd the angels sing,\nAt Thy coming to victory,\nLet Thy voice call me home,\nSaying, “Yet there is room,\nThere is room at My side for thee.”\nRefrain\nMy heart shall rejoice, Lord Jesus,\nWhen Thou comest and callest for me."
}
song["sda-hymnal/141"] = {
	title: "What Child Is This",
	tune: "Greensleeves",
	1: "What child is this, who, laid to rest,\nOn Mary`s lap is sleeping?\nWhom angels greet with anthems sweet,\nWhile shepherds watch are keeping?\nRefrain\nThis, this is Christ the King,\nWhom shepherds guard and angels sing:\nHaste, haste to bring Him laud,\nThe babe, the son of Mary.",
	2: "Why lies He in such mean estate\nWhere ox and ass are feeding?\nGood Christian, fear: for sinners here\nThe silent Word is pleading.",
	3: "So bring Him incense, gold, and myrrh,\nCome, peasant, king, to own Him,\nThe King of kings salvation brings,\nLet loving hearts enthrone Him."
}
song["sda-hymnal/142"] = {
	title: "Angels We Have Heard on High",
	tune: "Gloria",
	scripture: "Luke 2:13 - 17",
	copyright: "“Angels We Have Heard on High” From The New Church Hymnal copyright © 1937 by Flemming H. Revell Company; renewal © 1965 H. Augustine Smith, Jr. Published by Flemming H. Revell Company.",
	1: "Angels we have heard on high\nSweetly singing o’er the plains,\nAnd the mountains in reply\nEchoing their joyous strains.\nRefrain\nGloria, in excelsis Deo!\nGloria, in excelsis Deo!",
	2: "Shepherds, why this jubilee?\nWhy your joyous strains prolong?\nWhat the gladsome tidings be\nWhich inspire your heavenly song?",
	3: "Come to Bethlehem and see\nChrist Whose birth the angels sing;\nCome, adore on bended knee,\nChrist the Lord, the newborn King.",
	4: "See Him in a manger laid,\nWhom the choirs of angels praise;\nMary, Joseph, lend your aid,\nWhile our hearts in love we raise."
}
song["sda-hymnal/143"] = {
	title: "Silent Night, Holy Night",
	tune: "Stille Nacht",
	1: "Silent night! holy night!\nAll is calm, all is bright\nRound yon virgin mother and Child,\nHoly Infant so tender and mild,\nSleep in heavenly peace,\nsleep in heavenly peace.",
	2: "Silent night! holy night!\nShepherds quake at the sight,\nglories stream from heaven afar,\nheavenly hosts sing Alleluia;\nChrist, the Savior is born,\nChrist, the Savior is born.",
	3: "Silent night! holy night!\nSon of God, love’s pure light,\nRadiant beams from Thy holy face,\nwith the dawn of redeeming grace,\nJesus, Lord, at Thy birth,\nJesus, Lord, at Thy birth."
}
song["sda-hymnal/144"] = {
	title: "O Sing a Song of Bethlehem",
	tune: "Kingsfold",
	info: "Alternate tune, Forest Green, No. 90",
	copyright: "“O Sing a Song of Bethlehem” Music from The English Hymnal by permission of Oxford University Press.",
	1: "O sing a song of Bethlehem,\nOf shepherds watching there,\nAnd of the news that came to them\nFrom angels in the air:\nThe light that shone on Bethlehem\nFills all the world today;\nOf Jesus’birth and peace on earth\nThe angels sing alway.",
	2: "O sing a song of Nazareth,\nOf sunny days of joy,\nO sing of fragrant flowers breath,\nAnd of the sinless Boy:\nFor now the flowers of Nazareth\nIn every heart may grow;\nNow spreads the fame of His dear name\nOn all the winds that blow.",
	3: "O sing a song of Calvary,\nIts glory and dismay;\nOf Him who hung upon the tree,\nAnd took our sins away:\nFor He who died on Calvary\nIs risen from the grave;\nAnd Christ, our Lord, by heaven adored,\nIs mighty now to save."
}
song["sda-hymnal/145"] = {
	title: "Songs of Thankfulness and Praise",
	tune: "Riley",
	info: "Alternate tune, St. George's Windsor, No. 557",
	copyright: "“Songs of Thankfulness and Praise” Music copyright © J. Curwen & Sons. Used by permission of G. Schirmer, Inc., U.S.A. agents.",
	1: "Songs of thankfulness and praise,\nJesus, Lord, to You we raise,\nManifested by the star\nTo the sages from afar;\nBranch of royal David’s stem\nIn Your birth at Bethlehem;\nAnthems be to You addressed,\nGod in man made manifest.",
	2: "Manifest at Jordan’s stream,\nProphet, Priest, and King supreme;\nAnd at Cana, wedding guest,\nIn Your Godhead manifest;\nManifest in power divine,\nChanging water into wine;\nAnthems be to You addressed,\nGod in man made manifest.",
	3: "Manifest in making whole\nPalsied limbs and fainting soul;\nManifest in valiant fight,\nQuelling all the devil’s might;\nManifest in gracious will,\nEver bringing good from ill;\nAnthems be to You addressed,\nGod in man made manifest.",
	4: "Grant us grace to see You, Lord,\nMirrored in Your holy Word;\nMay we imitate Your way,\nAnd be pure, as pure as we may,\nThat we like You may be\nAt Your great epiphany;\nLet us praise You, ever blest,\nGod in man made manifest."
}
song["sda-hymnal/146"] = {
	title: "I Think When I Read That Sweet Story",
	tune: "East Horndon",
	scripture: "Matt. 19:14, 15",
	copyright: "“I Think When I Read That Sweet Story” Music from the English Hymnal by permission of Oxford University Press.",
	1: "I think when I read that sweet story of old,\nWhen Jesus was here among men,\nHow He called little children as lambs to His fold,\nI should like to have been with Him then.",
	2: "I wish that His hands had been placed on my head,\nThat His arm had been thrown around me,\nAnd that I might have seen His kind look when He said,\n“Let the little ones come unto Me.”",
	3: "I long for the joy of that glorious time,\nThe sweetest and brightest and best,\nWhen the dear little children of every clime\nShall crowd to His arms and be blest."
}
song["sda-hymnal/147"] = {
	title: "Christ Upon the Mountain Peak",
	tune: "Feniton",
	scripture: "Mark 9:2 - 7",
	copyright: "“Christ Upon the Mountain Peak” Words copyright © 1977 by the Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music by permission of Hymns Ancient and Modern Ltd.",
	1: "Christ upon the mountain peak\nStands alone in glory blazing;\nLet us, if we dare to speak,\nWith the saints and angels praise Him:\nRefrain:\nAlleluia, Alleluia, Alleluia.",
	2: "Trembling at His feet we saw\nMoses and Elijah speaking;\nAll the prophets and the law\nShout thro’ them their joyful greeting:\nRefrain:\nAlleluia, Alleluia, Alleluia.",
	3: "Swift the cloud of glory came,\nGod proclaiming in the thunder\nJesus as His Son by name;\nNations cry aloud in wonder!\nRefrain:\nAlleluia, Alleluia, Alleluia.",
	4: "This is God’s beloved Son:\nLaw and prophets fade before Him;\nFirst and last, the only One,\nLet creation now adore Him.\nRefrain:\nAlleluia, Alleluia, Alleluia."
}
song["sda-hymnal/148"] = {
	title: "O Love, How Deep, How Broad",
	tune: "Infinite Love",
	info: "Alternate tune, Dues Tuorum Militum, No. 404",
	copyright: "“O Love, How Deep, How Broad” Music copyright © 1984 by James Bingham.",
	1: "O love, how deep, how broad, how high,\nBeyond all thought and fantasy,\nThat God, the Son of God, should take\nOur mortal form for mortal’s sake!",
	2: "For us baptized, for us He bore\nHis holy fast and hungered sore;\nFor us temptation sharp He knew;\nFor us the tempter overthrew.",
	3: "For us by wickedness betrayed,\nFor us, in crown of thorns arrayed,\nHe bore the shameful cross and death;\nFor us He gave His dying breath.",
	4: "For us He rose from death again,\nFor us He went on high to reign;\nFor us He sent His Spirit here\nTo guide, to strengthen, and to cheer.",
	5: "All glory to our Lord and God\nFor love so deep, so high, so broad;\nThe Trinity whom we adore\nForevermore and forevermore."
}
song["sda-hymnal/149"] = {
	title: "Once in Royal David's City",
	tune: "Irby",
	1: "Once in royal David’s city\nStood a lowly cattle shed,\nWhere a mother laid her Baby\nIn a manger for His bed;\nMary was that mother mild,\nJesus Christ her little Child.",
	2: "He came down to earth from heaven,\nWho is God and Lord of all,\nAnd His shelter was a stable,\nAnd His cradle was a stall;\nWith the poor, and mean, and lowly,\nLived on earth our Savior holy.",
	3: "And through all His wondrous childhood\nHe would honor and obey,\nLove and watch the lowly mother\nIn whose gentle arms He lay.\nChristian children all must be\nMild, obedient, good as He.",
	4: "And our eyes at last shall see Him,\nThrough His own redeeming love;\nFor that Child so dear and gentle\nIs our Lord in heaven above;\nAnd He leads His children on\nTo the place where He is gone."
}
song["sda-hymnal/150"] = {
	title: "Who Is He in Yonder Stall",
	tune: "Resonet in laudibus",
	1: "Who is He in yonder stall,\nAt whose feet the shepherds fall?\nRefrain\n‘Tis the Lord, O wondrous story,\n‘Tis the Lord, the King of glory!\nAt His feet we humbly fall,\nCrown Him, crown Him Lord of all.\nAt His feet we humbly fall- the Lord of all.\nCrown Him, crown Him, Jesus, Jesus,\nCrown Him Lord of all.",
	2: "Who is He in deep distress,\nFasting in the wilderness?",
	3: "Who is He, the gathering throng\nGreet with loud triumphant song?",
	4: "Lo, at midnight, who is He\nPrays in dark Gethsemane?",
	5: "Who is He on yonder tree\nDies in shame and agony?",
	6: "Who is He that from the grave\nComes to heal and help and save?",
	7: "Who is He that from His throne\nRules through all the world alone?"
}
song["sda-hymnal/151"] = {
	title: "Jesus Walked This Lonesome Valley",
	copyright: "“Jesus Walked This Lonesome Valley” Arrangement copyright © 1984 by Allen W. Foster.",
	1: "Jesus walked this lonesome valley;\nHe had to walk it by Himself.\nO nobody else could walk it for Him.\nHe had to walk it by Himself.",
	2: "I must go and stand my trial.\nI have to stand it by myself.\nO nobody else could stand it for me.\nI have to stand it by myself.",
	3: "Jesus walked this lonesome valley;\nHe had to walk it by Himself.\nO nobody else could walk it for Him.\nHe had to walk it by Himself."
}
song["sda-hymnal/152"] = {
	title: "Tell Me the Story of Jesus",
	poet: "Fanny J. Crosby (1820 - 1915)",
	1: "Tell me the story of Jesus,\nWrite on my heart every word.\nTell me the story most precious,\nSweetest that ever was heard.\nTell how the angels in chorus,\nSang as they welcomed His birth.\n“Glory to God in the highest!\nPeace and good tidings to earth.”\nRefrain\nTell me the story of Jesus,\nWrite on my heart every word.\nTell me the story most precious,\nSweetest that ever was heard.",
	2: "Fasting alone in the desert,\nTell of the days that are past.\nHow for our sins He was tempted,\nYet was triumphant at last.\nTell of the years of His labor,\nTell of the sorrow He bore.\nHe was despised and afflicted,\nHomeless, rejected and poor.",
	3: "Tell of the cross where they nailed Him,\nWrithing in anguish and pain.\nTell of the grave where they laid Him,\nTell how He liveth again.\nLove in that story so tender,\nClearer than ever I see.\nStay, let me weep while you whisper,\nLove paid the ransom for me."
}
song["sda-hymnal/153"] = {
	title: "Prince of Peace, Control My Will",
	tune: "Tryggare kan ingen vara",
	info: "Harmony setting, No. 101",
	copyright: "“Prince of Peace, Control My Will” Arrangement copyright © 1984 by Melvin West.",
	1: "Prince of Peace, control my will,\nBid this struggling heart be still;\nBid my fears and doubtings cease,\nHush my spirit into peace.",
	2: "Thou hast bought me with Thy blood,\nOpened wide the gate to God;\nPeace, I ask, but peace must be,\nLord, in being one with Thee.",
	3: "May Thy will, not mine, be done,\nMay Thy will and mine be one;\nChase these doubtings from my heart,\nNow Thy perfect peace impart."
}
song["sda-hymnal/154"] = {
	title: "When I Survey the Wondrous Cross",
	scripture: "Gal. 6:14",
	1: "When I survey the wondrous cross\non which the Prince of Glory died;\nmy richest gain I count but loss,\nand pour contempt on all my pride.",
	2: "Forbid it, Lord, that I should boast,\nsave in the death of Christ, my God;\nall the vain things that charm me most,\nI sacrifice them to his blood.",
	3: "See, from his head, his hands, his feet,\nsorrow and love flow mingled down.\nDid e’er such love and sorrow meet,\nor thorns compose so rich a crown.",
	4: "Were the whole realm of nature mine,\nthat were an offering far too small;\nlove so amazing, so divine,\ndemands my soul, my life, my all."
}
song["sda-hymnal/155"] = {
	title: "When I Survey the Wondrous Cross",
	scripture: "Gal. 6:14",
	info: "Alternate tune, McCabe, No. 401",
	1: "When I survey the wondrous cross\nOn which the Prince of glory died,\nMy richest gain I count but loss,\nAnd pour contempt on all my pride.",
	2: "See, from His head, His feet,\nSorrow and love flow mingled down;\nDid e’er such love and sorrow meet?\nOr thorns compose so rich a crown?",
	3: "Since I, who was undone and lost,\nHave pardon through His name and word;\nForbid it, then, that I should boast,\nSave in the cross of Christ my Lord.",
	4: "Were the whole realm of nature mine,\nThat were a tribute far too small;\nLove so amazing, so divine,\nDemands my life, my soul, my all."
}
song["sda-hymnal/156"] = {
	title: "O Sacred Head Now Wounded",
	composer: "Hans Leo Hassler, 1601 (1564 - 1612)",
	poet: "Bernard of Clairvaux (1091 - 1153)",
	arranger: "Johann Sebastian Bach, 1729 (1685 - 1750)",
	tune: "Passion Chorale",
	1: "O sacred Head, now wounded,\nwith grief and shame weighed down,\nnow scornfully surounded\nwith thorns, thine only crown:\nhow pale thou art with anguish,\nwith sore abuse and scorn!\nHow does that visage languish\nwhich once was bright as morn!",
	2: "What thou, my Lord, has suffered\nwas all for sinners’ gain;\nmine, mine was the transgression,\nbut thine the deadly pain.\nLo, here I fall, my Savior!\n‘Tis I deserve thy place;\nlook on me with thy favor,\nvouchsafe to me thy grace.",
	3: "What language shall I borrow\nto thank thee, dearest friend,\nfor this thy dying sorrow,\nthy pity without end?\nO make me thine forever;\nand should I fainting be,\nLord, let me never, never\noutlive my love for thee."
}
song["sda-hymnal/157"] = {
	title: "Go to Dark Gethsemane",
	1: "Go to dark Gethsemane,\nYe who feel the tempter’s power\nYour Redeemer’s conflict see.\nWatch with him one bitter hour;\nTurn not from his griefs away;\nLearn from Jesus Christ to pray.",
	2: "See Him at the judgment hall,\nBeaten, bound, reviled, arraigned;\nSee Him meekly bearing all;\nLove to man His soul sustained!\nShun not suffering, shame, or loss;\nLearn from him to bear the cross.",
	3: "Calvary’s mournful mountain climb;\nThere, adoring at his feet,\nMark that miracle of time,\nGod’s own sacrifice complete.\n“It is finished!” hear him cry;\nLearn from Jesus Christ to die."
}
song["sda-hymnal/158"] = {
	title: "Were You There",
	1: "Were you there when they crucified my Lord?\nWere you there when they crucified my Lord?\nO! Sometimes it causes me to tremble, tremble, tremble.\nWere you there when they crucified my Lord?",
	2: "Were you there when they nailed Him to the tree?\nWere you there when they nailed Him to the tree?\nO! Sometimes it causes me to tremble, tremble, tremble.\nWere you there when they nailed Him to the tree?",
	3: "Were you there when they pierced Him in the side?\nWere you there when they pierced Him in the side?\nO! Sometimes it causes me to tremble, tremble, tremble.\nWere you there when they pierced Him in the side?",
	4: "Were you there when the sun refused to shine?\nWere you there when the sun refused to shine?\nO! Sometimes it causes me to tremble, tremble, tremble.\nWere you there when the sun refused to shine?",
	5: "Were you there when they laid Him in the tomb?\nWere you there when they laid Him in the tomb?\nO! Sometimes it causes me to tremble, tremble, tremble.\nWere you there when they laid Him in the tomb?"
}
song["sda-hymnal/159"] = {
	title: "The Old Rugged Cross",
	copyright: "“The Old Rugged Cross” Copyright 1913 by George Bennard © renewed 1941, The Rodeheaver Co. (A Div. of Word, Inc.). All rights reserved. International copyright secured. Used by permission.",
	1: "On a hill far away stood an old rugged cross,\nthe emblem of suffering and shame;\nand I love that old cross where the dearest and best\nfor a world of lost sinners was slain.\nRefrain\nSo I’ll cherish the old rugged cross,\ntill my trophies at last I lay down;\nI will cling to the old rugged cross,\nand exchange it some day for a crown.",
	2: "O that old rugged cross, so despised by the world,\nhas a wondrous attraction for me;\nfor the dear Lamb of God left his glory above\nto bear it to dark Calvary.",
	3: "To that old rugged cross I will ever be true,\nits shame and reproach gladly bear;\nthen he’ll call me some day to my home far away,\nwhere his glory forever I’ll share."
}
song["sda-hymnal/160"] = {
	title: "Ride On in Majesty",
	copyright: "“Ride On in Majesty” Music from the BBC Hymn Book by permission of Oxford University Press.",
	1: "Ride on! ride on in majesty!\nHark, all the tribes hosanna cry;\nO Savior meek, pursue Thy road\nWith palms and scattered garments strowed.",
	2: "Ride on! ride on in majesty!\nIn lowly pomp ride on to die;\nO Christ, Thy triumphs now begin\nO’er captive death and conquered sin.",
	3: "Ride on! ride on in majesty!\nThe winged squadrons of the sky\nLook down with sad and wondering eyes\nTo see the approaching sacrifice.",
	4: "Ride on! ride on in majesty!\nIn lowly pomp ride on to die;\nBow Thy meek head to mortal pain,\nThen take, O God, Thy power and reign."
}
song["sda-hymnal/161"] = {
	title: "Throned Upon the Awful Tree",
	scripture: "Matt. 27:45, 46",
	info: "*Hymn may also be played and sung in G major.",
	1: "Throned upon the awful tree,\nLamb of God, Your grief we see.\nDarkness veils Your anguished face;\nNone its lines of woe can trace.\nNone can tell what pangs unknown\nHold You silent and alone.",
	2: "Silent through those three dread hours,\nWrestling with the evil powers,\nLeft alone with human sin,\nGloom around You and within,\nTill th’appointed time is nigh,\nTill the Lamb of God may die.",
	3: "Hark, that cry that peals aloud\nUpward through the whelming cloud!\nYou, the Father’s only Son,\nYou, His own anointed one,\nYou are asking- can it be?\n“Why have You forsaken Me?”",
	4: "Lord, should fear and anguish roll\nDarkly o’er our sinful soul,\nYou, who once were thus bereft\nThat Your own might ne’er be left,\nTeach us by that bitter cry\nIn the gloom to know You nigh."
}
song["sda-hymnal/162"] = {
	title: "Wondrous Love",
	copyright: "“Wondrous Love” Copyright © 1975 by G. . A. Publications, Inc., Chicago, IL. All rights reserved.",
	1: "What wondrous love is this,\nO my soul, O my soul?\nWhat wondrous love is this, O my soul?\nWhat wondrous love is this\nThat caused the Lord of bliss\nTo bear the dreadful curse for my soul, for my soul;\nTo bear the dreadful curse for my soul, for my soul;",
	2: "To God and to the Lamb\nI will sing, I will sing;\nTo God and to the Lamb, I will sing;\nTo God and to the Lamb\nWho is the great I am,\nWhile millions join the theme, I will sing, I will sing;\nWhile millions join the theme, I will sing, I will sing;",
	3: "And when from death I’m free,\nI’ll sing on, I’ll sing on;\nAnd when from death I’m free, I’ll sing on;\nAnd when from death I’m free,\nI’ll sing and joyful be,\nAnd through eternity I’ll sing on, I’ll sing on!\nAnd through eternity, I’ll sing on."
}
song["sda-hymnal/163"] = {
	title: "At the Cross",
	info: "Alternate tune without refrain, MARTYRDOM, No. 113",
	1: "Alas! and did my Savior bleed,\nand did my Sovereign die?\nWould he devote that sacred head\nfor sinners such as I?\nRefrain\nAt the cross, at the cross,\nwhere I first saw the light,\nand the burden of my heart rolled away;\nit was there by faith I received my sight,\nand now I am happy all the day!",
	2: "Was it for crimes that I have done,\nhe groaned upon the tree?\nAmazing pity! Grace unknown!\nAnd love beyond degree!",
	3: "But drops of grief can ne’er repay\nthe debt of love I owe:\nHere, Lord, I give myself away;\n’tis all that I can do!"
}
song["sda-hymnal/164"] = {
	title: "There Is a Green Hill Far Away",
	1: "There is a green hill far away,\nWithout a city wall,\nWhere the dear Lord was crucified,\nWho died to save us all.",
	2: "We may not know, we cannot tell,\nWhat pains He had to bear,\nBut we believe it was for us\nHe hung and suffered there.",
	3: "He died that we might be forgiven,\nHe died to make us good,\nThat we might go at last to heaven,\nSaved by His precious blood.",
	4: "There was no other good enough\nTo pay the price of sin;\nHe only could unlock the gate\nOf heaven, and let us in.",
	5: "O dearly, dearly has He loved!\nAnd we must love Him too,\nAnd trust in His redeeming blood,\nAnd try His works to do."
}
song["sda-hymnal/165"] = {
	title: "Look, You Saints! the Sight Is Glorious",
	scripture: "Rev. 11:15",
	info: "Alternate tune, CWM Rhondda, Nos. 201, 538",
	1: "Look, you saints, the sight is glorious,\nSee the Man of sorrows now;\nFrom the fight returned victorious,\nEvery knee to Him shall bow.\nCrown Him! Crown Him! Crown Him!\nCrown Him! Crown Him! Crown Him!\nCrowns become the victor’s brow.\nCrowns become the victor’s brow.",
	2: "Crown the Savior! angels crown Him!\nRich the trophies Jesus brings;\nOn the seat of power enthrone Him\nWhile the vault of heaven rings.\nCrown Him! Crown Him! Crown Him!\nCrown Him! Crown Him! Crown Him!\nCrown the Savior, King of Kings.\nCrown the Savior, King of Kings.",
	3: "Sinners in derision crowned Him,\nMocking thus the Savior’s claim;\nSaints and angels crowd around Him,\nOwn His title, praise His name.\nCrown Him! Crown Him! Crown Him!\nCrown Him! Crown Him! Crown Him!\nSpread abroad the victor’s fame!\nSpread abroad the victor’s fame!",
	4: "Hark! those bursts of acclamation!\nHark! those loud triumphant chords!\nJesus takes the highest station;\nOh, what joy the sight affords!\nCrown Him! Crown Him! Crown Him!\nCrown Him! Crown Him! Crown Him!\nKing of kings and Lord of lords!\nKing of kings and Lord of lords!"
}
song["sda-hymnal/166"] = {
	title: "Christ the Lord Is Risen Today",
	1: "Christ the Lord is risen today, Alleluia!\nSons of man and angels say, Alleluia!\nRaise your joys and triumphs high, Alleluia!\nSing, ye heavens, and earth reply, Alleluia!",
	2: "Lives again our glorious King, Alleluia!\nWhere, O death, is now thy sting? Alleluia!\nOnce he died our souls to save, Alleluia!\nWhere’s thy victory, boasting grave? Alleluia!",
	3: "Love’s redeeming work is done, Alleluia!\nFought the fight, the battle won, Alleluia!\nDeath in vain forbids him rise, Alleluia!\nChrist has opened paradise, Alleluia!",
	4: "Soar we now where Christ has led, Alleluia!\nFollowing our exalted Head, Alleluia!\nMade like him, like him we rise, Alleluia!\nOurs the cross, the grave, the skies, Alleluia!"
}
song["sda-hymnal/167"] = {
	title: "Alleluia! Sing to Jesus!",
	tune: "Hyfrydol",
	info: "Alternate harmony, No. 204",
	copyright: "“Alleluia! Sing to Jesus!” Arrangement copyright © 1984 by Melvin West.",
	1: "Alleluia! sing to Jesus! His the scepter, His the throne.\nAlleluia! His the triumph, His the victory alone.\nHark! the songs of peaceful Zion thunder like a mighty flood.\nJesus out of every nation has redeemed us by His blood.",
	2: "Alleluia! not as orphans are we left in sorrow now;\nAlleluia! He is near us, faith believes, nor questions how;\nThough the cloud from sight received Him when the forty days were o’er\nShall our hearts forget His promise, “I am with you evermore”?",
	3: "Alleluia! bread of angels, Thou on earth our food, our stay;\nAlleluia! here the sinful flee to Thee from day to day:\nIntercessor, Friend of sinners, Earth’s Redeemer, plead for me,\nWhere the songs of all the sinless sweep across the crystal sea."
}
song["sda-hymnal/168"] = {
	title: "And Have the Bright Immensities",
	copyright: "“And Have the Bright Immensities” Words reprinted by permission of Morehouse - Barlow Co., Inc. Music from The English Hymnal by permission of Oxford University Press.",
	1: "And have the bright immensities\nReceived our risen Lord,\nWhere light years frame the Pleiades\nAnd point Orion’s sword?\nDo flaming suns His footsteps trace\nThro’corridors sublime,\nThe Lord of inter stellar space\nAnd conqueror of time?",
	2: "The heav’n that hides Him from our sight\nKnows neither near nor far;\nA little candle sheds its light\nAs surely as a star.\nAnd where His loving people meet\nTo share the gift divine,\nThere stands He with unhurrying feet;\nThere heav’nly splendors shine."
}
song["sda-hymnal/169"] = {
	title: "Come, You Faithful",
	1: "Come, you faithful, raise the strain\nOf triumphant gladness;\nGod has brought His people\nNow rejoice, Jerusalem,\nAnd with true affection\nWelcome in unwearied strains\nJesus’resurrection.",
	2: "‘Tis the spring of souls today;\nChrist has burst His prison\nFrom the frost and gloom of death\nlight and life have risen\nAll the winter of our sins,\nLong and dark, is flying\nFrom His ligth, to whom we give\nThanks and praise undying.",
	3: "“Alleluia!” now we cry\nto our King immortal,\nWho, triumphant, burst the\nbars of the tomb’s dark portal\n“Alleluia!” with the Son,\nGod the Father praising;\n“Alleluia!” yet again to\nthe Spirit raising."
}
song["sda-hymnal/170"] = {
	title: "Come, You Faithful",
	1: "Come, you faithful, raise the strain\nOf triumphant gladness;\nGod has brought His Israel\nInto joy from sadness;\nLoosed from Pharoah’s bitter yoke\nJacob’s sons and daughters;\nLed them with unmoistened foot\nThrough the Red Sea waters.",
	2: "‘Tis the spring of souls today;\nChrist has burst His prison,\nAnd from three days’sleep in death\nAs a sun has risen;\nAll the winter of our sins,\nLong and dark, is flying\nFrom His light, to whom is giv’n\nLaud and praise undying.",
	3: "Now the queen of seasons, bright\nWith the day of splendor,\nWith the royal feast of feasts,\nComes its joy to render;\nComes to gladden faithful hearts\nWhich with true affection\nWelcome in unwearied strain\nJesus’resurrection.",
	4: "For today among the twelve\nChrist appeared, be stowing\nHis deep peace, which evermore\nPasses human knowing.\nNeither could the gates of death,\nNor the tomb’s dark portal,\nNor the watchers, nor the seal,\nHold Him as a mortal.",
	5: "“Alleluia!” now we cry\nTo our King immortal,\nWho, triumphant, burst the bars\nOf the tomb’s dark portal;\n“Alleluia” with the Son,\nGod the Father praising;\n“Alleluia!” yet again\nTo the Spirit raising."
}
song["sda-hymnal/171"] = {
	title: "Thine Is the Glory",
	copyright: "“Thine Is the Glory” Words from the Cantate Domino. Copyright by World Student Christina Federation. Used by permission.",
	1: "Thine is the glory,\nRisen, conquering Son;\nEndless is the victory thou o’er death hast won.\nAngels in bright raiment rolled the stone away,\nKept the folded grave-clothes where Thy body lay.\nRefrain :\nThine is the glory,\nRisen, conquering Son;\nEndless is the victory Thou o’er death hast won.",
	2: "Lo! Jesus meets us.\nRisen from the tomb,\nLovingly He greets us,\nScatters fear and gloom;\nLet His Church with gladness hymns of triumph sing,\nFor her Lord now liveth;\nDeath has lost its sting.",
	3: "No more we doubt Thee,\nGlorious Prince of life!\nLife is nought without Thee;\nAid us in our strife;\nMake us more than conquerors through Thy deathless love\nBring us safe through Jordan to Thy home above."
}
song["sda-hymnal/172"] = {
	title: "The Strife Is O'er",
	info: "*These Alleluias to be sung before stanza 1 and after stanza 4. Alternate tune, Gelbobt Sei Gott, No. 173",
	1: "Alleluia, alleluia, alleluia!",
	2: "The strife is o’er, the battle done;\nNow is the victor’s triumph won!\nNow be the song of praise begun. Alleluia!",
	3: "The pow’rs of death have done their worst,\nBut Christ their legions has dispersed.\nLet shouts of holy joy outburst. Alleluia!",
	4: "The three sad days have quickly sped,\nHe rises glorious from the dead.\nAll glory to our risen head! Alleluia!",
	5: "Lord, by the stripes which wounded You,\nFrom death’s sting free Your servants too,\nThat we may live and sing to You. Alleluia!"
}
song["sda-hymnal/173"] = {
	title: "Good Christian Friends, Rejoice!",
	copyright: "“Good Christian Friends, Rejoice!” Words by permission of Hymns Ancient and Modern.",
	1: "Good Christian friends, rejoice and sing!\nNow is the triumph of our King!\nTo all the world glad news we bring:\nAlleluia! Alleluia! Alleluia!",
	2: "The Lord of life is risen for aye;\nBring flowers of song to strew His way;\nLet all mankind rejoice and say:\nAlleluia! Alleluia! Alleluia!",
	3: "Praise we in songs of victory\nThat love, that life which cannot die,\nAnd sing with hearts uplifted high:\nAlleluia! Alleluia! Alleluia!",
	4: "Thy name we bless, O risen Lord,\nAnd sing today with one accord\nThe life laid down, the life restored:\nAlleluia! Alleluia! Alleluia!"
}
song["sda-hymnal/174"] = {
	title: "Star of Our Hope",
	scripture: "1 Thess. 4:16, 17",
	info: "Higher key, No. 226.",
	1: "Star of our hope! He’ll soon appear,\nThe last loud trumpet speaks Him near;\nHail Him, all saints, from pole to pole-\nHow welcome to the faithful soul!",
	2: "From heaven angelic voices sound:\nBehold the Lord of glory crowned,\nArrayed in majesty divine,\nAnd in His highest glories shine.",
	3: "The grave yields up its precious trust,\nWhich long has slumbered in the dust,\nResplendent forms ascending, fair,\nNow meet the Savior in the air.",
	4: "Descending with His azure throne,\nHe claims the kingdom for His own;\nThe saints rejoice, they shout, they sing,\nAnd hail Him their triumphant King."
}
song["sda-hymnal/175"] = {
	title: "Now the Green Blade Rises",
	copyright: "“Now the Green Blade Rises” Words and arrangement from the Oxford Book of Carols by permission of Oxford University Press.",
	1: "Now the green blade rises from the buried grain,\nWhat that in dark earth many days has lain;\nLove lives again, that with the dead has been;\nLove is come again like wheat arising green.",
	2: "In the grave they laid Him, love by hatred slain,\nThinking that He would never wake again,\nLaid in the earth like grain that sleeps unseen;\nLove is come again like wheat arising green.",
	3: "Forth He came in triumph, like the risen grain,\nHe that for three days in the grave had lain;\nRaised from the dead, my living Lord is seen;\nLove is come again like wheat arising green.",
	4: "When our hearts are wintry, grieving, or in pain,\nYour touch can call us back to life again,\nField of our hearts that dead and bare have been;\nLove is come again like wheat arising green."
}
song["sda-hymnal/176"] = {
	title: "Hail the Day That Sees Him Rise",
	1: "Hail the day that sees Him rise, Alleluia!\nGlorious to His native skies; Alleluia!\nChrist, awhile to mortals giv’n, Alleluia!\nEnters now the highest heav’n! Alleluia!",
	2: "There the glorious triumph waits; Alleluia!\nLift your heads, eternal gates! Alleluia!\nWide unfold the radiant scene; Alleluia!\nTake the King of glory in! Alleluia!",
	3: "See! He lifts His hands above; Alleluia!\nSee! He shows the prints of love: Alleluia!\nHark! His gracious lips bestow, Alleluia!\nBlessings on His Church below. Alleluia!",
	4: "Lord beyond our mortal sight, Alleluia!\nRaise our hearts to reach Thy height, Alleluia!\nThere Thy face unclouded see, Alleluia!\nFind our heav’n of heav’ns in Thee. Alleluia!"
}
song["sda-hymnal/177"] = {
	title: "Jesus, Your Blood and Righteousness",
	tune: "Gardiner",
	info: "Higher Key, No. 335",
	1: "Jesus, your blood and righteousness\nMy beauty are, my glorious dress;\nMid flaming worlds, in these arrayed,\nWith joy shall I lift up my head.",
	2: "Bold shall I stand in that great day,\nCleansed and redeemed, no debt to pay;\nFor by your cross absolved I am\nFrom sin and guilt, from fear and shame.",
	3: "Lord, I believe your precious blood,\nWhich at the mercy seat of God\nPleads for the captives’ liberty,\nWas also shed in love for me.",
	4: "When from the dust of death I rise\nTo claim my mansion in the skies,\nThis then shall be my only plea:\nChrist Jesus lived and died for me."
}
song["sda-hymnal/178"] = {
	title: "The Unveiled Christ",
	scripture: "Mark 15:37, 38",
	copyright: "“The Unveiled Christ” Copyright 1916. Renewed 1943 by Nazarene Publishing House. Used by. permission",
	1: "Once our blessed Christ of beauty\nWas veiled off from human views;\nBut through suffering, death, and sorrow\nHe has rent the veil in two.\nRefrain:\nO behold the Man of Sorrows!\nO behold Him in plain view!\nLo! He is the mighty Conqueror\nSince her rent the veil in two;\nLo! He is the mighty Conqueror\nSince her rent the veil in two.",
	2: "Yes, He is with God, the Father,\nInterceding there for you;\nFor He is the Well beloved\nSince He rent the veil in two.",
	3: "Holy angels bow before Him,\nMen of earth give praises due;\nFor He is the mighty Conqueror\nSince He rent the veil in two."
}
song["sda-hymnal/179"] = {
	title: "The Wonders of Redeeming Love",
	tune: "La Sierra",
	scripture: "Heb. 4:14 -16",
	copyright: "“The Wonders of Redeeming Love” Music copyright © 1985 by Review and Herald Publishing Association.",
	1: "The wonders of redeeming love\nOur highest thoughts exceed;\nThe Son of God comes from above\nFor sinful man to bleed.",
	2: "He gives Himself, His life, His all,\nA sinless sacrifice.\nFor man He drains the cup of gall,\nFor man the victim dies.",
	3: "And now before His Father’s face\nHis precious blood He pleads;\nFor those who seek the throne of grace\nHis love still intercedes.",
	4: "He knows the frailties of our frame,\nFor He has borne our grief;\nOur great High Priest once felt the same,\nAnd He can send relief.",
	5: "His love will not be satisfied,\nTill He in glory sees\nThe faithful ones for whom He died\nFrom sin forever free.\nPosted by HYMNS at 05:34 0 comments\nLabels: The Wonders of Redeeming Love"
}
song["sda-hymnal/180"] = {
	title: "O Listen to Our Wondrous Story",
	tune: "What Did He Do?",
	1: "O listen to our wondrous story,\nCounted once among the lost;\nYet One came down from heaven’s glory,\nSaving us at awful cost!\nRefrain\nWho saved us from eternal loss?\nWho but God’s Son upon the cross?\nWhat did He do? He died for you!\nWhere is He now? Believe it thou,\nIn heaven interceding! In heaven interceding!",
	2: "No angel could His place have taken,\nHighest of the high tho’ he;\nThe loved One on the cross forsaken\nWas One of the Godhead three!",
	3: "Will you surrender to this Savior?\nTo His scepter humbly bow?\nYou, too, shall come to know His favor,\nHe will save you, save you now."
}
song["sda-hymnal/181"] = {
	title: "Does Jesus Care",
	scripture: "1 Peter 5:7",
	1: "Does Jesus care when my heart is pained\nToo deeply for mirth and song;\nAs the burdens press, and the cares distress,\nAnd the way grows weary and long?\nRefrain\nO yes, He cares- I know He cares!\nHis heart is touched with my grief;\nWhen the days are weary,\nThe long nights dreary,\nI know my Savior cares. (He cares.)",
	2: "Does Jesus care when my way is dark\nWith a nameless dread and fear?\nAs the daylight fades into deep night shades,\nDoes He care enough to be near?",
	3: "Does Jesus care when I’ve said goodbye\nTo the dearest on earth to me,\nAnd my sad heart aches till it nearly breaks ­\nIs it aught to Him? Does He see?"
}
song["sda-hymnal/182"] = {
	title: "Christ Is Alive",
	tune: "Truro",
	copyright: "“Christ Is Alive” Words copyright © 1975 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Christ is alive!\nLet Christians sing.\nHis cross stands empty to the sky.\nLet streets and homes with praises ring.\nHis love in death shall never die.",
	2: "Christ is alive!\nNo longer bound\nTo distant years in Palestine,\nHe comes to claim the here and now\nAnd conquer every place and time.",
	3: "In every insult,\nrift, and war\nWhere color, scorn or wealth divide,\nHe suffers still, yet loves the more,\nAnd lives, though ever crucified.",
	4: "Christ is alive!\nAscended Lord\nHe rules the world His Father made,\nTill, in the end, His love adored\nShall be to all on earth displayed."
}
song["sda-hymnal/183"] = {
	title: "I Will Sing of Jesus' Love",
	composer: "F. E. Belden, 1886 (1858 - 1945)",
	poet: "F. E. Belden, 1886 (1858 - 1945)",
	1: "I Will sing of Jesus love,\nSing of Him,who first loved me;\nfor He left bright worlds above,\nAnd died on Calvary.\nRefrain\nI will sing of Jesus love\nEndless praise my heart shall give;\nHe has died that I might live\nI will sing His love to me.",
	2: "O the depths of love divine!\nEarth or heaven can never know\nHow that sin as dark as mine\ncan be made as white as snow.",
	3: "Nothing good for Him I’ve done;\nHow could He such love bestow?\nLord, I own my heart is won,\nhelp me now my love to show."
}
song["sda-hymnal/184"] = {
	title: "Jesus Paid It All",
	1: "I hear the Savior say,\n“Thy strength indeed is small;\nChild of weakness, watch and pray,\nFind in Me thine all in all.”\nRefrain\nJesus paid it all,\nAll to Him I owe;\nSin had left a crimson stain,\nHe washed it white as snow.",
	2: "Lord, now indeed I find\nThy power and Thine alone,\nCan change the leper’s spots\nAnd melt the heart of stone.",
	3: "Since nothing good have I\nWhereby Thy grace to claim,\nI’ll wash my garments white\nIn the blood of Calv’ry’s Lamb.",
	4: "And when before the throne\nI stand in Him complete,\nI’ll lay my trophies down\nAll down at Jesus’ feet."
}
song["sda-hymnal/185"] = {
	title: "Jesus Is All the World to Me",
	1: "Jesus is all the world to me,\nmy life, my joy, my all;\nhe is my strength from day to day,\nwithout him I would fall.\nWhen I am sad, to him I go,\nno other one can cheer me so;\nwhen I am sad, he makes me glad,\nhe’s my friend.",
	2: "Jesus is all the world to me,\nmy friend in trials sore;\nI go to him for blessings, and\nhe gives them o’er and o’er.\nHe sends the sunshine and the rain,\nhe sends the harvest’s golden grain;\nsunshine and rain, harvest of grain,\nhe’s my friend.",
	3: "Jesus is all the world to me,\nand true to him I’ll be;\nO how could I this friend deny,\nwhen he’s so true to me?\nFollowing him I know I’m right,\nhe watches o’er me day and night;\nfollowing him by day and night,\nhe’s my friend.",
	4: "Jesus is all the world to me,\nI want no better friend;\nI trust him now, I’ll trust him when\nlife’s fleeting days shall end.\nBeautiful life with such a friend,\nbeautiful life that has no end;\neternal life, eternal joy,\nhe’s my friend."
}
song["sda-hymnal/186"] = {
	title: "I've Found a Friend",
	tune: "Friend",
	1: "I’ve found a Friend; oh, such a Friend!\nHe loved me ere I knew Him;\nHe drew me with the cords of love,\nAnd thus He bound me to Him.\nAnd ’round my heart still closely twine\nThose ties which nought can sever,\nFor I am His, and He is mine,\nForever and forever.",
	2: "I’ve found a Friend; oh, such a Friend!\nHe bled, He died to save me;\nAnd not alone the gift of life,\nBut His own self He gave me.\nNought that I have my own I call,\nI hold it for the Giver;\nMy heart, my strength, my life my all,\nAre His, and His forever.",
	3: "I’ve found a Friend; oh, such a Friend!\nAll power to Him is given;\nTo guard me on my upward course,\nAnd bring me safe to heaven.\nThe eternal glories gleam afar,\nTo nerve my faint endeavor;\nSo now to watch, to work, to war,\nAnd then to rest forever.",
	4: "I’ve found a Friend; oh, such a Friend!\nSo kind, and true, and tender,\nSo wise a counselor and guide,\nSo mighty a defender.\nFrom Him, who loveth me so well,\nWhat power my soul can sever?\nShall life or death, or earth, or hell?\nNo; I am His forever."
}
song["sda-hymnal/187"] = {
	title: "Jesus, What a Friend for Sinners",
	tune: "Holy Manna",
	copyright: "“Jesus, What a Friend for Sinners” Words copyright 1910. Renewal 1938 extended by Hope Publishing Co., Carol Stream, IL 60188, All rights reserved. Used by permission. Arrangment copyright © 1984 by Wayne Hooper.",
	1: "Jesus! what a Friend for sinners!\nJesus! Lover of my soul;\nFriends may fail me, foes assail me,\nHe, my Savior, makes me whole.\nRefrain\nHallelujah! what a Savior!\nHallelujah! what a Friend!\nSaving, helping, keeping, loving,\nHe is with me to the end.",
	2: "Jesus! what a Strength in weakness!\nLet me hide myself in Him.\nTempted, tried, and sometimes failing,\nHe, my Strength, my victory wins.",
	3: "Jesus! what a Help in sorrow!\nWhile the billows over me roll,\nEven when my heart is breaking,\nHe, my Comfort, helps my soul.",
	4: "Jesus! I do now receive Him,\n[or Jesus! I do now adore Him,]\nMore than all in Him I find.\nHe hath granted me forgiveness,\nI am His, and He is mine."
}
song["sda-hymnal/188"] = {
	title: "My Song Is Love Unknown",
	tune: "Love Unknown",
	info: "Alternate tune, Rhosymedre, No. 650",
	copyright: "“My Song Is Love Unknown” Music by permission of executors of Mrs. N. K. Kirby.",
	1: "My song is love unknown,\nMy Savior’s love to me,\nLove to the loveless shown,\nThat they might lovely be.\nO who am I that for my sake\nMy Lord should take frail flesh and die?",
	2: "He came from His blest throne,\nSalvation to bestow,\nBut men made strange, and none\nThe longed for Christ would know.\nBut O my Friend, my Friend indeed\nWho at my need His life did spend.",
	3: "Sometimes they strew His way,\nAnd His sweet praises sing,\nResounding all the day,\nHosannas to their King.\nThen “Crucify” is all their breath,\nAnd for His death they thirst and cry.",
	4: "Why, what hath my Lord done?\nWhat makes this rage and spite?\nHe made the lame to run,\nHe gave the blind their sight.\nSweet injuries! yet they at these\nThemselves displease, and ‘gainst Him rise.",
	5: "They rise, and needs will have\nMy dear Lord made away;\nA murderer they save,\nThe Prince of life they slay.\nYet cheerful He to suff’ring goes,\nThat He His foes from thence might free.",
	6: "Here might I stay and sing,\nNo story so divine:\nNever was love, dear King,\nNever was grief like Thine!\nThis is my Friend, in whose sweet praise\nI all my days could gladly spend."
}
song["sda-hymnal/189"] = {
	title: "All That Thrills My Soul",
	copyright: "“All That Thrills My Soul” Copyright 1931. Renewal 1959 by Nazarene Publishing House. Used by permission.",
	1: "Who can cheer the heart like Jesus,\nBy His presence all divine?\nTrue and tender, pure and precious,\nOh, how blest to call Him mine!\nRefrain:\nAll that thrills my soul is Jesus;\nHe is more than life to me; (to me;)\nAnd the fairest of ten thousand,\nIn my blessed Lord I see.",
	2: "Love of Christ so freely given,\nGrace of God beyond degree,\nMercy higher than the heaven,\nDeeper than the deepest sea.",
	3: "Every need His hand supplying,\nEvery good in Him I see;\nOn His strength divine relying,\nHe is All in All to me.",
	4: "By the crystal flowing river\nWith the ransomed I will sing,\nAnd forever and forever\nPraise and glorify the King."
}
song["sda-hymnal/190"] = {
	title: "Jesus Loves Me",
	tune: "Bradbury",
	scripture: "Proverbs 8:17",
	1: "Jesus loves me! this I know,\nFor the Bible tells me so;\nLittle ones to Him belong\nThey are weak but He is strong\nRefrain\nYes, Jesus loves me!\nYes, Jesus loves me!\nYes, Jesus loves me!\nThe Bible tells me so.",
	2: "Jesus loves me! He wo died\nHeaven’s gate to open wide:\nHe will wash away my sin,\nLet His little child come in.",
	3: "Jesus, take this heart of mine,\nMake it pure and wholly thine;\nOn the coross You died for me,\nI will love and live for Thee."
}
song["sda-hymnal/191"] = {
	title: "Love Divine",
	1: "Love divine, all loves excelling,\njoy of heaven, to earth come down;\nfix in us thy humble dwelling;\nall thy faithful mercies crown!\nJesus thou art all compassion,\npure, unbounded love thou art;\nvisit us with thy salvation;\nenter every trembling heart.",
	2: "Breathe, O breathe thy loving Spirit\ninto every troubled breast!\nLet us all in thee inherit;\nlet us find the promised rest.\nTake away our bent to sinning;\nAlpha and Omega be;\nend of faith, as its beginning,\nset our hearts at liberty.",
	3: "Come, Almighty to deliver,\nlet us all thy grace receive;\nsuddenly return and never,\nnevermore thy temples leave.\nThee we would be always blessing,\nserve thee as thy hosts above,\npray and praise thee without ceasing,\nglory in thy perfect love.",
	4: "Finish, then, thy new creation;\npure and spotless let us be.\nLet us see thy great salvation\nperfectly restored in thee;\nchanged from glory into glory,\ntill in heaven we take our place,\ntill we cast our crowns before thee,\nlost in wonder, love, and praise."
}
song["sda-hymnal/192"] = {
	title: "O Shepherd Divine",
	tune: "My Shepherd",
	scripture: "John 10:16",
	copyright: "“O Shepherd Divine” Copyright 1947 by Herbert Work. Renewal. Used by permission.",
	1: "O Shepherd divine, I know Thou art mine;\nThy search in the night was for me.\nThis bleak world is cold, but warm is Thy fold;\nMy Shepherd, I follow Thee.\nThy beautiful lamp shineth bright o’er my way,\nThy glorious light unto Thy perfect day.\nThro’ pastures serene,\nThro’ valleys of green,\nMy Shepherd, I follow Thee.",
	2: "O Shepherd divine, I know Thou art mine;\nThy great heart was broken for me.\nThy grace and Thy law I picture in awe;\nThey kissed upon Calvary.\nAh! Life that was given to ransom my soul,\nAh! Heart that was broken to make sinners whole,\nThis world is but loss\nIn view of Thy cross,\nMy Shepherd, I follow Thee.",
	3: "O Shepherd divine, I know Thou art mine;\nI hear Thee say, “Follow thou Me.”\nThy message today illumines my way;\nThe Spirit of Prophecy.\nI thrill at Thy marvellous love to Thy sheep,\nThe way Thou dost lead to the still waters deep,\nOne staff and one rod,\nOne fold and one God;\nMy Shepherd, I follow Thee."
}
song["sda-hymnal/193"] = {
	title: "Savior, Teach Me",
	copyright: "“Savior, Teach Me” Copyright © 1969 by Fine Arts Music Press.",
	1: "Saviour, teach me day by day,\nLove’s sweet lesson to obey,\nSweeter lesson cannot be,\nLoving Him who first loved me.",
	2: "With a child’s glad heart of love,\nAt Thy bidding may I move,\nPrompt to serve and follow Thee,\nLoving Him who first loved me.",
	3: "Teach me I am not my own,\nI am Thine, and Thine alone;\nThine to keep, to rule, to save\nFrom all sin that would enslave.",
	4: "Love in loving finds employ,\nIn obedience all her joy;\nEver new that joy will be,\nLoving Him who first loved me.",
	5: "Teach me thus Thy steps to trace,\nStrong to follow in Thy grace.\nLearning how to love from Thee,\nLoving Him who first loved me."
}
song["sda-hymnal/194"] = {
	title: "Sing We of the Modern City",
	tune: "Geneva",
	copyright: "“Sing We of the Modern City” Words copyright © 1968 by Hope Publishing Co., Carol Stream, IL 60188 .All rights reserved. Used by permission. Music from The Hymnal 1940, copyright by The Church Pension Fund. Used by permission.",
	1: "Sing we of the modern city,\nScene alike of joy and stress;\nSing we of its nameless people\nIn their urban wilderness.\nInto endless rows of houses\nLife is set a millionfold,\nLife expressed in human beings\nDaily born and growing old.",
	2: "In the city full of people,\nWorld of speed and hectic days,\nIn the ever-changing setting\nOf the latest trend and craze,\nChrist is present, and among us;\nIn the crowd we see Him stand.\nIn the bustle of the city\nJesus Christ is every man.",
	3: "God is not remote in heaven,\nBut on earth to share our shame,\nChanging graph and mass and number\nInto persons with a name.\nChrist has shown, beyond statistics,\nHuman life with glory crowned,\nBy His timeless presence proving\nPeople matter, people count!"
}
song["sda-hymnal/195"] = {
	title: "Showers of Blessing",
	scripture: "Ezekiel 34:26",
	1: "“There shall be showers of blessing;”\nThis is the promise of love;\nThere shall be seasons refreshing,\nSent from the Savior above.\nRefrain\nShowers of blessing,\nShowers of blessing we need;\nMercy drops round us are falling,\nBut for the showers we plead.",
	2: "“There shall be showers of blessing;”\nPrecious reviving again;\nOver the hills and the valleys,\nSound of abundance of rain.",
	3: "“There shall be showers of blessing;”\nSend them upon us, O Lord;\nGrant to us now a refreshing;\nCome, and now honor Thy word.",
	4: "“There shall be showers of blessing;”\nO that today they might fall,\nNow as to God were confessing,\nNow as on Jesus we call!"
}
song["sda-hymnal/196"] = {
	title: "Tell Me the Old, Old Story",
	tune: "Evangel",
	1: "Tell me the old, old story of unseen things above,\nOf Jesus and His glory, of Jesus and His love.\nTell me the story simply, as to a little child,\nFor I am weak and weary, and helpless and defiled.\nRefrain\nTell me the old, old story, tell me the old, old story,\nTell me the old, old story, of Jesus and His love.",
	2: "Tell me the story softly, with earnest tones and grave;\nRemember I’m the sinner whom Jesus came to save.\nTell me the story always, if you would really be,\nIn any time of trouble, a comforter to me.",
	3: "Tell me the same old story when you have cause to fear\nThat this world’s empty glory is costing me too dear.\nYes, and when that world’s glory is dawning on my soul,\nTell me the old, old story: “Christ Jesus makes thee whole.”"
}
song["sda-hymnal/197"] = {
	title: "The King of Love My Shepherd Is",
	tune: "Dominus regit me",
	scripture: "Psalm 23",
	info: "Alternate tune, St. Columba, No. 639",
	1: "The King of love my Shepherd is,\nWhose goodness faileth never;\nI nothing lack if I am His,\nAnd He is mine forever.",
	2: "Where streams of living water flow\nMy ransomed soul He leadeth,\nAnd, where the verdant pastures grow,\nWith food celestial feedeth.",
	3: "Perverse and foolish, oft I strayed,\nBut yet in love He sought me,\nAnd on His shoulder gently laid,\nAnd home, rejoicing, brought me.",
	4: "And so through all the length of days\nThy goodness faileth never;\nGood Shepherd! I would sing Thy praise\nWithin Thy house forever."
}
song["sda-hymnal/198"] = {
	title: "And Can It Be",
	tune: "Sagina",
	1: "And can it be that I should gain\nan interest in the Savior’s blood!\nDied he for me? who caused his pain!\nFor me? who him to death pursued?\nAmazing love! How can it be\nthat thou, my God, shouldst die for me?\nRefrain\nAmazing love! How can it be\nthat thou, my God, shouldst die for me?",
	2: "He left his Father’s throne above\nso free, so infinite his grace!;\nemptied himself of all but love,\nand bled for Adam’s helpless race.\n‘Tis mercy all, immense and free,\nfor O my God, it found out me!",
	3: "Long my imprisoned sprit lay,\nfast bound in sin and nature’s night;\nthine eye diffused a quickening ray;\nI woke, the dungeon flamed with light;\nmy chains fell off, my heart was free,\nI rose, went forth, and followed thee.",
	4: "No condemnation now I dread;\nJesus, and all in him, is mine;\nalive in him, my living Head,\nand clothed in righteousnes divine,\nbold I approach th’ eternal throne,\nand claim the crown, through Christ my own."
}
song["sda-hymnal/199"] = {
	title: "The Head That Once Was Crowned",
	tune: "St. Magnus",
	info: "Alternate harmony, No. 19",
	copyright: "“The Head That Once Was Crowned” Arrangement copyright © 1984 by Melvin West.",
	1: "The head that once was crowned with thorns\nIs crowned with glory now;\nA royal diadem adorns\nThe mighty victor’s brow.",
	2: "The highest place that heaven affords\nIs His, is His by right;\nThe King of kings, and Lord of lords,\nAnd heaven’s eternal light.",
	3: "The joy of all who dwell above,\nThe joy of all below,\nTo whom He manifests His love,\nAnd grants His name to know.",
	4: "To them the cross, with all its shame,\nWith all its grace is given;\nTheir name an everlasting name,\nTheir joy the joy of heaven.",
	5: "To them the cross, with all its shame,\nWith all its grace is given;\nTheir name an everlasting name,\nTheir joy the joy of heaven."
}
song["sda-hymnal/200"] = {
	title: "The Lord Is Coming",
	scripture: "1 Cor. 16:22",
	info: "Alternate harmony, No. 522",
	copyright: "“The Lord Is Coming” Arrangement copyright © 1984 by Melvin West.",
	1: "The Lord is coming, let this be\nThe herald note of jubilee;\nAnd when we meet and when we part\nThe salutation from the heart.\nRefrain:\nThe Lord is coming, let this be\nThe herald note of jubilee,\nThe herald note of jubilee.",
	2: "The Lord is coming, sound it forth\nFrom east to west, from south to north;\nSpeed on! Speed on the tidings glad,\nThat none who love Him may be sad.\nRefrain:\nThe Lord is coming, let this be\nThe herald note of jubilee,\nThe herald note of jubilee.",
	3: "The Lord is coming, swift and sure\nAnd all His judgements shall endure,\nAnd none can hope to escape His wrath,\nWho walk not in the narrow path.\nRefrain:\nThe Lord is coming, let this be\nThe herald note of jubilee,\nThe herald note of jubilee.",
	4: "This earth, with her ten thousand wrongs\nWill soon be tuned to nobler songs;\nOur praise shall then, in realms of light,\nWith all His universe unite.\nRefrain:\nThe Lord is coming, let this be\nThe herald note of jubilee,\nThe herald note of jubilee."
}
song["sda-hymnal/201"] = {
	title: "Christ Is Coming",
	info: "Alternate tune, BRYN CALFARIA, No. 165 Lower key, No. 538",
	1: "Christ is coming! let creation\nBid her groans and travail cease;\nLet the glorious proclamation\nHope restore and faith increase;\nChrist is coming! Christ is coming!\nCome, Thou blessed Prince of Peace!\n(Prince of Peace!)\nCome, Thou blessed Prince of Peace!",
	2: "Earth can now but tell the story\nOf Thy bitter cross and pain;\nShe shall yet behold Thy glory\nWhen Thou comest back to reign;\nChrist is coming! Christ is coming!\nLet each heart repeat the strain.\n(repeat the strain)\nLet each heart repeat the strain.",
	3: "With that “blessed hope” before us,\nLet no harp remain unstrung;\nLet the mighty advent chorus\nOnward roll from tongue to tongue:\nChrist is coming! Christ is coming!\nCome, Lord Jesus, quickly come!\n(quickly come!)\nCome, Lord Jesus, quickly come!"
}
song["sda-hymnal/202"] = {
	title: "Hail Him the King of Glory",
	1: "Tell it to every kindred and nation,\nTell it far and near;\nEarth’s darkest night will fade with the dawning,\nJesus will soon appear.\nRefrain\nHail Him the King of glory,\nOnce the Lamb for sinners slain;\nTell, tell the wondrous story,\n“Jesus comes to reign.”",
	2: "Nations again in strife and commotion,\nWarnings by the way;\nSigns in the heavens, unerring omens,\nHerald the glorious day.",
	3: "Children of God look up with rejoicing;\nShout and sing His praise;\nBlessèd are they who, waiting and watching,\nLook for the dawning rays."
}
song["sda-hymnal/203"] = {
	title: "This Is the Threefold Truth",
	copyright: "“This Is the Threefold Truth” Words copyright © 1980 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © 1984 by Allen W. Foster",
	1: "This is the threefold truth\nOn which our faith depends;\nAnd with this joyful cry\nWorship begins and ends;\nRefrain:\nChrist has died!\nChrist is risen!\nChrist will come again!",
	2: "By this we are upheld\nWhen doubt and grief assails\nOur Christian fortitude,\nAnd only grace avails.\nRefrain:\nChrist has died!\nChrist is risen!\nChrist will come again!",
	3: "This is the threefold truth\nWhich, if we hold it fast,\nChanges the world and us\nAnd brings us home at last.\nRefrain:\nChrist has died!\nChrist is risen!\nChrist will come again!"
}
song["sda-hymnal/204"] = {
	title: "Come, Thou Long Expected Jesus",
	info: "Alternate tune, STUTTGART, No. 659 Alternate harmony, No. 167",
	copyright: "“Come, Thou Long Expected Jesus” Music from the English Hymnal by permission of Oxford University Press",
	1: "Come, thou long expected Jesus!\nborn to set thy people free;\nfrom our fears and sins release us,\nlet us find our rest in thee.\nIsrael’s strength and consolation,\nhope of all the earth thou art;\ndear desire of every nation,\njoy of every longing heart.",
	2: "Born thy people to deliver,\nborn a child and yet a King,\nborn to reign in us forever,\nnow thy gracious kingdom bring.\nBy thine own eternal spirit\nrule in all our hearts alone;\nby thine all sufficient merit,\nraise us to thy glorious throne."
}
song["sda-hymnal/205"] = {
	title: "Gleams of the Golden Morning",
	1: "The golden morning is fast approaching;\nJesus soon will come\nTo take his faithful and happy children\nto their promised home\nRefrain\nO, we see the gleams of the golden morning\npiercing thro’ this night of gloom!\nO, see the gleams of the golden morning\nthat will burst the tomb.",
	2: "The gospel summons will soon be carried\nto the nations round;\nThe Bridegroom then will cease to tarry\nand the trumpet sound.",
	3: "Attended by all the shining angels,\nDown the flaming sky\nthe Judge will come, and will take his people\nwhere they will not die.",
	4: "The lov’d of earth who have long been parted,\nMeet in that glad day;\nThe tears of those who are broken hearted\nshall be wiped away."
}
song["sda-hymnal/206"] = {
	title: "Face to Face",
	1: "Face to face with Christ my Savior,\nFace to face, what will it be,\nWhen with rapture I behold Him,\nJesus Christ, who died for me?\nRefrain\nFace to face shall I behold Him,\nFar beyond the starry sky;\nFace to face in all His glory\nI shall see Him by and by!",
	2: "Only faintly now I see Him,\nWith the darkening veil between,\nBut a blessed day is coming,\nWhen His glory shall be seen.",
	3: "What rejoicing in His presence,\nWhen are banished grief and pain;\nWhen the crooked ways are straightened,\nAnd the dark things shall be plain!",
	4: "Face to face! oh, blissful moment!\nFace to face to see and know;\nFace to face with my Redeemer,\nJesus Christ, who loves me so."
}
song["sda-hymnal/207"] = {
	title: "It May Be at Morn",
	scripture: "Mark 13:35",
	1: "It may be at morn, when the day is awaking,\nWhen sunlight through darkness and shadow is breaking,\nThat Jesus will come in the fullness of glory\nTo receive from the world His own.\nRefrain\nO Lord Jesus, how long, how long\nEre we shout the glad song?\nChrist returneth, Hallelujah!\nHallelujah! Amen, Hallelujah! Amen.",
	2: "It may be at midday, it may be at twilight,\nIt may be, perchance, that the blackness of midnight\nWill burst into light in the blaze of His glory,\nWhen Jesus receives His own.",
	3: "O joy! O delight! should we go without dying,\nNo sickness, no sadness, no dread, and no crying,\nCaught up through the clouds with our Lord into glory,\nWhen Jesus receives His own."
}
song["sda-hymnal/208"] = {
	title: "There'll Be No Dark Valley",
	1: "There’ll be no dark valleys when Jesus comes,\nThere’ll be no dark valleys when Jesus comes,\nThere’ll be no dark valleys when Jesus comes,\nTo gather His loved ones home.\nRefrain\nTo gather His loved ones home,\nTo gather His loved ones home.\nThere’ll be no dark valleys when Jesus comes,\nTo gather His loved ones home.",
	2: "There’ll be no more sorrow when Jesus comes,\nThere’ll be no more sorrow when Jesus comes,\nThere’ll be a happy tomorrow when Jesus comes,\nTo gather His loved ones home.",
	3: "There’ll be songs of greeting when Jesus comes,\nThere’ll be songs of greeting when Jesus comes,\nThere’ll be songs of greeting when Jesus comes,\nTo gather His loved ones home."
}
song["sda-hymnal/209"] = {
	title: "That Glorious Day Is Coming",
	copyright: "“That Glorious Day Is Coming” Music copyright © 1984 by Melvin West.",
	1: "That glorious day is coming,\nThe hour is hastening on;\nIts radiant light is nearing,\nFar brighter than the sun;\nIn yonder clouds of heaven,\nThe Saviour will appear,\nAnd gather all His chosen,\nTo meet Him in the air.\nThe saints, then all victorious\nWill go to meet their Lord;\nAn earth both bright and glorious,\nWill then be their reward;\nAnd God Himself there reigning,\nWill wipe all tears away;\nNor clouds nor night remaining,\nBut one eternal day.",
	2: "O Christian, keep from sleeping,\nAnd let your love abound;\nBe watchful, prayerful, faithful,\nThe trumpet soon will sound!\nO sinner, hear the warning!\nTo Jesus quickly fly!\nThen you, in that blest morning,\nMay meet Him in the sky!"
}
song["sda-hymnal/210"] = {
	title: "Wake, Awake for the Night Is Flying",
	scripture: "Matt. 25:6,7",
	1: "Wake, awake, for night is flying,\nThe watchmen on the heights are crying,\nAwake, Jerusalem, arise!\nMidnight’s solemn hour is tolling,\nHis chariot wheels are nearer rolling,\nHe comes; prepare, ye virgins wise.\nRise up with willing feet\nGo forth, the Bridegroom meet; Alleluia!\nBear through the night your well-trimmed light,\nSpeed forth to join the marriage rite.",
	2: "Zion hears the watchmen singing,\nHer heart with deep delight is springing,\nShe wakes, she rises from her gloom;\nForth her Bridegroom comes, all-glorious,\nIn grace arrayed, by truth victorious;\nHer Star is risen, her Light is come!\nAll hail, incarnate Lord,\nOur crown, and our reward! Alleluia!\nWe haste along, in pomp and song,\nAnd gladsome join the marriage throng.",
	3: "Lamb of God, the heavens adore Thee,\nAnd men and angels sing before Thee,\nWith harp and cymbal’s clearest tone.\nBy the pearly gates in wonder\nWe stand, and swell the voice of thunder,\nThat echoes round Thy dazzling throne.\nNo vision ever brought,\nNo ear hath ever caught,\nSuch bliss and joy;\nWe raise the song, we swell the throng,\nTo praise Thee ages all along."
}
song["sda-hymnal/211"] = {
	title: "Lo! He Comes",
	scripture: "Revelation 1:7",
	1: "Lo! He comes, with clouds descending,\nOnce for favored sinners slain;\nCountless angels, Him attending,\nSwell the triumph of His train:\nHallelujah! Hallelujah!\nJesus comes, and comes to reign.",
	2: "Every eye shall now behold Him\nRobed in dreadful majesty!\nThose who set at nought and sold Him,\nPierced, and nailed Him to the tree,\nDeeply wailing, Deeply wailing,\nShall the true Mesiah see!",
	3: "When the solemn trump has sounded,\nHeaven and earth shall flee away;\nAll who hate Him, must, confounded,\nHear the summons of that day-\n“Come to judgment! Come to judgment!\nCome to judgment! Come away!”",
	4: "Yea, amen! let all adore Thee,\nHigh on Thy eternal throne!\nSavior, take the power and glory,\nMake Thy righteous sentence known;\nO come quickly, O come quickly,\nClaim the kingdom for Thine own!"
}
song["sda-hymnal/212"] = {
	title: "'Tis Almost Time for the Lord to Come",
	1: "’Tis almost time for the Lord to come,\nI hear the people say;\nThe stars of heaven are growing dim,\nIt must be the breaking of the day.\nRefrain\nO it must be the breaking of the day!\nO it must be the breaking of the day!\nThe night is almost gone,\nThe day is coming on;\nO it must be the breaking of the day!",
	2: "The signs foretold in the sun and moon,\nIn earth and sea and sky,\nAloud proclaim to all mankind,\nThe coming of the Master draweth nigh.",
	3: "It must be time for the waiting church\nTo cast her pride away,\nWith girded loins and burning lamps,\nTo look for the breaking of the day.",
	4: "Go quickly out in the streets and lanes\nAnd in the broad highway,\nAnd call the maimed, the halt, and blind,\nTo be ready for the breaking of the day."
}
song["sda-hymnal/213"] = {
	title: "Jesus Is Coming Again",
	1: "Lift up the trumpet, and loud let it ring:\nJesus is coming again!\nCheer up, ye pilgrims, be joyful and sing:\nJesus is coming again!\nRefrain\nComing again, coming again,\nJesus is coming again!",
	2: "Echo it, hilltops; proclaim it, ye plains:\nJesus is coming again!\nComing in glory, the Lamb that was slain;\nJesus is coming again!",
	3: "Heavings of earth, tell the vast, wondering throng:\nJesus is coming again!\nTempests and whirlwinds, the anthem prolong;\nJesus is coming again!",
	4: "Nations are angry–by this we do know\nJesus is coming again!\nKnowledge increases; men run to and fro;\nJesus is coming again!"
}
song["sda-hymnal/214"] = {
	title: "We Have This Hope",
	tune: "General Conference",
	copyright: "“We Have This Hope” Copyright © 1962, 1995 by Wayne Hooper",
	1: "We have this hope that burns within our heart,\nHope in the coming of the Lord.\nWe have this faith that Christ alone imparts,\nFaith in the promise of His Word.\nWe believe the time is here,\nWhen the nations far and near\nShall awake, and shout and sing\nHallelujah! Christ is King!\nWe have this hope that burns within our heart,\nHope in the coming of the Lord.",
	2: "We are united in Jesus Christ our Lord.\nWe are united in His love.\nLove for the waiting people of the world,\nPeople who need our Savior’s love.\nSoon the heav’ns will open wide,\nChrist will come to claim His bride,\nAll the universe will sing\nHallelujah! Christ is King!\nWe have this hope this faith, and God’s great love,\nWe are united in Christ."
}
song["sda-hymnal/215"] = {
	title: "The King Shall Come",
	info: "Harmony setting, No. 576",
	copyright: "“The King Shall Come” Arrangement copyright © 1984 by Melvin West.",
	1: "The King shall come when morning dawns\nAnd light triumphant breaks,\nWhen beauty gilds the eastern hills\nAnd life to joy awakes.",
	2: "Not as of old a little child,\nTo bear and fight and die,\nBut crowned with glory like the sun\nThat lights the morning sky.",
	3: "O, brighter than that rising morn\nWhen Christ, victorious, rose\nAnd left the lonesome place death,\nDespite the rage of foes.",
	4: "O, brighter than that glorious morn\nShall dawn upon our race\nThey day when Christ in splendour comes,\nAnd we shall see His face.",
	5: "The King shall come when morning dawns\nAnd light and beauty brings.\nHail, Christ the Lord! Your people pray:\nCome quickly, King of kings."
}
song["sda-hymnal/216"] = {
	title: "When the Roll Is Called Up Yonder",
	1: "When the trumpet of the Lord shall sound,\nand time shall be no more,\nAnd the morning breaks, eternal, bright and fair;\nWhen the saved of earth shall gather\nover on the other shore,\nAnd the roll is called up yonder, I’ll be there.\nRefrain\nWhen the roll is called up yonder,\nWhen the roll is called up yonder,\nWhen the roll is called up yonder,\nWhen the roll is called up yonder, I’ll be there.",
	2: "On that bright and cloudless morning,\nwhen the dead in Christ shall rise,\nAnd the glory of His resurection share;\nWhen His chosen ones shall gather\nto their home beyond the skies,\nAnd the roll is called up yonder, I’ll be there.",
	3: "Let us labor for the Master\nfrom the dawn till setting sun,\nLet us talk of all His wondrous love and care,\nThen, when all of life is over,\nand our work on earth is done,\nAnd the roll is called up yonder I’ll be there."
}
song["sda-hymnal/217"] = {
	title: "The Church Has Waited Long",
	1: "The church has waited long\nHer absent Lord to see;\nAnd still in loneliness she waits,\nA friendless stranger she.",
	2: "How long, O Lord our God,\nHoly and true and good,\nWilt Thou not judge Thy suffering church,\nHer sighs and tears and blood?",
	3: "We long to hear Thy voice,\nTo see Thee face to face,\nTo share Thy crown and glory then,\nAs now we share Thy grace.",
	4: "Come, Lord, and wipe away\nThe curse, the sin, the stain,\nAnd make this blighted world of ours\nThine own fair world again."
}
song["sda-hymnal/218"] = {
	title: "When He Cometh",
	scripture: "Malachi 3:17",
	1: "When He cometh, when He cometh\nTo make up His jewels,\nAll His jewels, precious jewels,\nHis loved and His own.\nRefrain\nLike the stars of the morning,\nHis brightness adorning,\nThey shall shine in their beauty,\nBright gems for His crown.",
	2: "He will gather, He will gather\nThe gems for His kingdom;\nAll the pure ones, all the bright ones,\nHis loved and His own.",
	3: "Little children, little children,\nWho love their Redeemer,\nAre the jewels, precious jewels,\nHis loved and His own."
}
song["sda-hymnal/219"] = {
	title: "When Jesus Comes in Glory",
	info: "Alternate tune, ST. THEODULPH, No. 230",
	copyright: "“When Jesus Comes in Glory” Music copyright © 1933 by Methodist Conference.",
	1: "When Jesus comes in glory,\nAs Lord and King of kings,\nO what a wondrous story\nThe blessed Bible brings:\nHis face will shine like the sunlight,\nHis head be white as snow,\nHis eyes like flaming firelight,\nHis feet like brass aglow.",
	2: "His voice like rushing waters\nWill reach with mighty sound\nInto the deepest quarters\nOf all creation round;\nAnd at this wondrous greeting\nThe dead in Christ shall rise,\nTheir Lord and Saviour meeting\nIn glory in the skies.",
	3: "And we who believing\nAnd His appearing love,\nShall know we are receiving\nHis glory from above;\nHis resurrection power\nWill raise us to the place\nWhere we that wondrous hour\nShall see Him face to face.",
	4: "O hasten Thine appearing\nThou bright and Morning Star!\nLord, may we soon be hearing\nThe trumpet sound afar;\nThey people all are yearning\nTo be Thy raptured bride,\nTo be Thy own returning\nBe caught up to Thy side."
}
song["sda-hymnal/220"] = {
	title: "When He Comes",
	scripture: "1 Thess. 4:14-17",
	copyright: "“When He Comes” Words copyright © 1968 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © 1984 by Wayne Hooper.",
	1: "When He comes,\nWhen He comes,\nWe shall see the Lord in glory when He comes!\nAs I read the gospel story,\nWe shall see the Lord in glory,\nWe shall see the Lord in glory when He comes!\nRefrain:\nWith the alleluias ringing to the sky,\nWith the alleluias ringing to the sky!\nAs I read the gospel in glory,\nWe shall see the Lord in glory,\nWith the alleluias ringing in the sky!",
	2: "When He comes,\nWhen He comes,\nWe shall hear the trumpet sounded when He comes!\nWe shall hear the trumpet sounded,\nSee the Lord by saints surrounded,\nWe shall hear the trumpet sounded when He comes!",
	3: "When He comes,\nWhen He comes,\nWe shall all rise up to meet Him when He comes!\nWe shall all rise up to meet Him,\nWhen He calls His own to greet Him\nWe shall all rise up to meet Him when He comes!"
}
song["sda-hymnal/221"] = {
	title: "Rejoice, the Lord Is King",
	tune: "Darwall's 148th",
	scripture: "Philippians 4:4",
	1: "Rejoice, the Lord is King! Your Lord and King adore!\nRejoice, give thanks, and sing and triumph evermore\nLift up your heart, lift up your voice!\nRejoice, again I say, rejoice!",
	2: "Jesus, the Savior, reigns, The God of truth and love;\nWhen He had purged our stains, He took His seat above\nLift up your heart, lift up your voice!\nRejoice, again I say, rejoice!",
	3: "His kingdom cannot fail, He rules o’er earth and heaven\nThe keys of death and grave Are to our Jesus given\nLift up your heart, lift up your voice!\nRejoice, again I say, rejoice!",
	4: "Rejoice in glorious hope! Our Lord the judge shall come,\nAnd take His servants up To their eternal home\nLift up your heart, lift up your voice!\nRejoice, again I say, rejoice!"
}
song["sda-hymnal/222"] = {
	title: "Hark! Ten Thousand Harps and Voices",
	1: "Hark! ten thousand harps and voices\nSound the note of praise above;\nJesuse reigns, and heaven rejoices,\nJesus reigns, the God of love:\nSee, He sits on yonder throne;\nJesus rules the world alone.\nAlleluia! Alleluia!\nAlleluia! Amen.",
	2: "King of glory, reign forever,\nThine an everlasting crown;\nNothing from Thy love shall sever\nThose whom Thou hast made Thine own;\nHappy objects of Thy grace,\nDestined to behold Thy face.\nAlleluia! Alleluia!\nAlleluia! Amen.",
	3: "Savior, hasten Thine appearing;\nBring, O bring the glorious day,\nWhen, the awful summons hearing,\nHeaven and earth shall pass away:;\nThen, with golden harps we’ll sing,\n“Glory, glory to our King!”\nAlleluia! Alleluia!\nAlleluia! Amen."
}
song["sda-hymnal/223"] = {
	title: "Crown Him With Many Crowns",
	scripture: "Revelation 19:12",
	1: "Crown him with many crowns,\nthe Lamb upon his throne,\nHark! how the heavenly anthem drowns\nall music but its own.\nAwake, my soul, and sing\nof him who died for thee,\nand hail him as thy matchless King\nthrough all eternity.",
	2: "Crown him the Lord of love!\nbehold his hands and side,\nthose wounds, yet visible above,\nin beauty glorified;\nNo angel in the sky\nCan fully bear that sight,\nBut downward bend his wondering eye\nAt mysteries so great.",
	3: "Crown him the Lord of peace!\nwhose power a scepter sways\nfrom pole to pole, that wars may cease,\nand all be prayer and praise.\nHis reign shall know no end,\nand round his pierced feet\nfair flowers of paradise extend\ntheir fragrance ever sweet.",
	4: "Crown him the Lord of years;\nThe potentate of time,\nCreator of the rolling spheres,\nIneffably sublime!\nAll hail, Redeemer, hail!\nFor thou hast died for me;\nthy praise shall never, never fail\nthroughout eternity."
}
song["sda-hymnal/224"] = {
	title: "Seek Ye First the Kingdom",
	tune: "Cranham",
	info: "Alternate tune, CUDDESON, No. 360",
	copyright: "“Seek Ye First the Kingdom” Words copyright © United Reformed Church.",
	1: "Seek ye first the kingdom,\n‘Tis your Father’s will.\nSo the voice of Jesus bids us follow still.\nSaviour, we would hear Thee,\nFollow, find, and see;\nAnd in life’s adventure Thy disciples be.",
	2: "As for hidden treasure,\nOr for matchless pearl,\nWhen at last discovered,\nsome will sell their all;\nSo, when breaks the vision of that\nkingdom fair,\nOurs shall be its riches And its beauty rare.",
	3: "As the silent leaven works its secret way,\nOr as grows the seed grain\nthrough the night and day;\nLord, so be the increase, peaceable but sure,\nOf Thy word within us,\nAnd Thy kingdom’s power.",
	4: "As the tender seedling grows up\ntall and strong,\nAnd the birds of heaven to its\nbranches throng; So shall all God’s children,\nfrom the east and west,\nGather to His kingdom, In its shadow rest.",
	5: "Humblest shall be greatest,\npoor in spirit reign;\nHome shall come the childlike,\nborn through Thee again;\nEager hearts arrive there on the\npilgrim’s road,\nHail! The kingdom glorious of the living God!"
}
song["sda-hymnal/225"] = {
	title: "God Is Working His Purpose Out",
	scripture: "Hab. 2:14",
	copyright: "“God Is Working His Purpose Out” Music from Enlarged Songs of Praise by permission of Oxford University Press.",
	1: "God is working His purpose out\nAs year suceeds to year;\nGod is working His purpose out,\nAnd the time is drawing near;\nNearer and nearer draws the time,\nThe time that shall surely be\nWhen the earth shall be filled with the glory of God\nAs the waters cover the sea.",
	2: "From utmost east to utmost west,\nWhere’er man’s foot hath trod,\nBy the mouth of many messengers\nGoes forth the voice of God;\nGive ear to me, ye confinents,\nYe isles, give ear to me,\nThat the earth shall be filled with the glory of God\nAs the waters cover the sea.",
	3: "March we forth in the strength of God\nWith the banner of Christ unfurled,\nThat the light of the glorious gospel of truth\nMay shine throughout the world:\nFight we the fight with sorrow and sin\nTo set their captives free,\nThat the earth shall be filled with the glory of God\nAs the waters cover the sea.",
	4: "All we can do is nothing worth\nUnless God blesses the deed;\nVainly we hope for the harvest tide\nTill God gives life to teh seed;\nYet nearer and nearer draws the time,\nThe time that shall surely be,\nWhen the earth shall be filled with the glory of God\nAs the waters cover the sea."
}
song["sda-hymnal/226"] = {
	title: "Lift Up Your Heads",
	info: "Alternate tune, TRURO, No. 182 Lower key, No. 174",
	1: "Lift up your heads, ye mighty gates!\nBehold the King of glory waits;\nThe King of kings is drawing near,\nThe Savior of the world is here.",
	2: "The Lord is just, a helper tried;\nMercy is ever at His side;\nHis kingly crowns is holiness,\nHis scepter, pity in distress.",
	3: "O blest the land, the city blest,\nWhere Christ the Ruler is confessed!\nO happy hearts and happy homes\nTo whom this King in triumph comes!",
	4: "Fling wide the portals of your heart;\nMake it a temple, set apart\nFrom earthly use for heaven’s employ,\nAdorned with prayer, and love, and joy.",
	5: "Redeemer, come; I open wide\nMy heart to Thee; here, Lord, abide.\nLet me Thy inner presence feel,\nThy grace and love in me reveal."
}
song["sda-hymnal/227"] = {
	title: "Jesus Shall Reign",
	scripture: "Psalm 72:8-19",
	info: "Alternate harmony, No. 82",
	copyright: "“Jesus Shall Reign” Arrangement copyright © 1984 by Melvin West.",
	1: "Jesus shall reign where’er the sun\nDoes his successive journeys run;\nHis kingdom stretch from shore to shore,\nTill moons shall wax and wane no more.",
	2: "People and realms of ev’ry tongue\nDwell on His love with sweetest song,\nAnd infant voices shall proclaim\nTheir early blessings on His name.",
	3: "Blessings abound wherever He reigns;\nThe prisoner leaps to lose his chains;\nThe weary find eternal rest,\nAnd all who suffer want are blessed.",
	4: "Let every creature rise and bring\nHonors peculiar to our King;\nAngels descend with songs again,\nAnd earth repeat the loud amen!"
}
song["sda-hymnal/228"] = {
	title: "A Hymn of Glory Let Us Sing",
	info: "Lower key, No. 2",
	copyright: "“A Hymn of Glory Let Us Sing” Words copyright 1978 Lutheran Book of Worship. Used by permission of Augsburg Publishing House. Music from The English Hymnal by permission of Oxford University Press.",
	1: "A hymn of glory let us sing;\nNew hymns thro’-out the world shall ring\nAlleluia! Alleluia!\nChrist, by a road before untrod,\nAscends unto the throne of God.\nAlleluia! Alleluia!\nAlleluia, alleluia, alleluia!",
	2: "O Lord, our homeward pathway bend,\nThat our unwearied hearts ascend.\nAlleluia! Alleluia!\nWhere, seated on Your Father’s throne,\nYou reign as King of kings alone.\nAlleluia! Alleluia!\nAlleluia, alleluia, alleluia!",
	3: "Give us Your joy on earth, O Lord,\nIn heav’n to be our great reward.\nAlleluia! Alleluia!\nWhen throned with You forever, we\nShall praise Your name eternally.\nAlleluia! Alleluia!\nAlleluia, alleluia, alleluia!",
	4: "O risen Christ, ascended Lord,\nAll praise to you let earth accord:\nAlleluia! Alleluia!\nYou are, while endless ages run,\nWith Father and with Spirit one.\nAlleluia! Alleluia!\nAlleluia, alleluia, alleluia!"
}
song["sda-hymnal/229"] = {
	title: "All Hail the Power of Jesus' Name",
	1: "All hail the power of Jesus’ name! Let angels prostrate fall;\nBring forth the royal diadem, and crown Him Lord of all.\nBring forth the royal diadem, and crown Him Lord of all.",
	2: "Ye chosen seed of Israel’s race, ye ransomed from the fall,\nHail Him who saves you by His grace, and crown Him Lord of all.\nHail Him who saves you by His grace, and crown Him Lord of all.",
	3: "Let every kindred, every tribe on this terrestrial ball\nTo Him all majesty ascribe, and crown Him Lord of all.\nTo Him all majesty ascribe, and crown Him Lord of all.",
	4: "O that with yonder angel throng we at His feet may fall!\nWe’ll join the everlasting song, and crown Him Lord of all.\nWe’ll join the everlasting song, and crown Him Lord of all."
}
song["sda-hymnal/230"] = {
	title: "All Glory, Laud, and Honor",
	scripture: "Matt. 21:8,9",
	1: "All glory, laud, and honor,\nto thee, Redeemer, King,\nto whom the lips of children\nmade sweet hosannas ring.\nThou art the King of Israel,\nthou David’s royal Son,\nwho in the Lord’s name comest,\nthe King and Blessed One.",
	2: "The company of angels\nare praising thee on high,\nand we with all creation\nin chorus make reply.\nThe people of the Hebrews\nwith psalms before thee went;\nour prayer and praise and anthems\nbefore thee we present.",
	3: "To thee, before thy passion,\nthey sang their hymns of praise;\nto thee, now high exalted,\nour melody we raise.\nThou didst accept their praises;\naccept the prayers we bring,\nwho in all good delightest,\nthou good and gracious King."
}
song["sda-hymnal/231"] = {
	title: "Blest Be the King",
	scripture: "Mark 11:9,10",
	info: "Alternate tune, ST. THEODULPH, No. 230",
	copyright: "“Blest Be the King” Words copyright © by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music reproduced by permission of Novello & Company Limited.",
	1: "Blest be the King whose coming\nIs in the name of God!\nFor Him let doors be opened,\nNo hearts against Him barred!\nNot robed in royal splendor,\nIn power and pomp comes He;\nBut clad as are the poorest,\nSuch His humility.",
	2: "Blest be the King whose coming\nIs in the name of God!\nBy those who truly listen\nHis voice is truly heard;\nPity the proud and haughty,\nWho have not learned to heed\nThe Christ who is the promise,\nWho has atonement made.",
	3: "Blest be the King whose coming\nIs in the name of God!\nHe only to the humble\nReveals the face of God.\nAll power is His, all glory!\nAll things are in His hand,\nAll ages and all peoples,\n‘Till time itself shall end.",
	4: "Blest be the King whose coming\nIs in the name of God!\nHe offers to the burdened\nThe rest and grace they need.\nGentle is He and humble!\nAnd light his Yoke shall be,\nFor He would have us bear it,\nSo He can made us free!"
}
song["sda-hymnal/232"] = {
	title: "At the Name of Jesus",
	scripture: "Phil. 2:8-11",
	info: "Harmony setting, No. 581",
	copyright: "“At the Name of Jesus” Music from Enlarged Songs of Praise by permission of Oxford University Press.",
	1: "At the name of Jesus every knee shall bow,\nEvery tongue confess Him King of glory now;\n“Tis the Father’s pleasure we should call Him Lord,\nWho from the beginning was the mighty Word.",
	2: "At His voice creation sprang at once to sight,\nAll the angel faces, All the hosts of light,\nThrones and dominations stars upon their way,\nAll the heavenly orders in their great array.",
	3: "Humbled for a season, To receive a name\nFrom the lips of sinners, Unto whom He came;\nHe is God the Savior, He is Christ the Lord,\nEver to be worshiped, Trusted and adored.",
	4: "In your hearts enthrone Him: There let Him subdue\nAll that is not holy, All that is not true;\nCrown Him as your captain in temptation’s hour,\nLet His will enfold you in its light and power.",
	5: "Surely, this Lord Jesus shall return again,\nWith His Father’s glory, With His angel train;\nFor all wreaths of empire meet upon His brow,\nAnd our hearts confess Him King of glory now."
}
song["sda-hymnal/233"] = {
	title: "Christ, Whose Glory Fills the Skies",
	1: "Christ, whose glory fills the skies,\nChrist, the true, the only light,\nSun of Righteousness, arise,\ntriumph o’er the shades of night;\nDayspring from on high, be near;\nDaystar, in my heart appear.",
	2: "Dark and cheerless is the morn\nunaccompanied by thee;\njoyless is the day’s return,\ntill thy mercy’s beams I see;\ntill they inward light impart,\ncheer my eyes and warm my heart.",
	3: "Visit then this soul of mine;\npierce the gloom of sin and grief;\nfill me, Radiancy divine,\nscatter all my unbelief;\nmore and more thyself display,\nshining to the perfect day."
}
song["sda-hymnal/234"] = {
	title: "Christ Is the World's Light",
	copyright: "“Christ Is the World's Light” Words copyright © 1969 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Christ is the world’s light,\nChrist and none other;\nborn in our darkness,\nHe became our brother-\nif we have seen Him,\nwe have seen the Father:\nGlory to God on high!",
	2: "Christ is the world’s peace,\nChrist and none other;\nno one can serve Him,\nand despise another.\nWho else unites us,\none in God the Father?\nGlory to God on high!",
	3: "Christ is the world’s life,\nChrist and none other;\nsold once for silver,\nmurdered here, our brother-\nHe, who redeems us,\nreigns with God the Father:\nGlory to God on high!",
	4: "Give God the glory,\nGod and none other;\ngive God the glory,\nSpirit, Son and Father;\ngive God the glory,\nGod-with-us our brother:\nGlory to God on high!"
}
song["sda-hymnal/235"] = {
	title: "Christ Is Made the Sure Foundation",
	scripture: "Eph. 2:20; 1 Pet. 2:6",
	info: "Alternate tune, WESTMINSTER ABBEY, No. 607",
	copyright: "“Christ Is Made the Sure Foundation” Copyright © 1959, 1964 by Schmitt, Hall & McCreary, a division of Belwin-Mills Publishing Corp. Used with permission. All rights reserved.",
	1: "Christ is made the sure foundation,\nChrist the head and cornerstone,\nchosen of the Lord, and precious,\nbinding all the Church in one;\nholy Zion’s help for ever,\nand her confidence alone.",
	2: "All that dedicated city,\ndearly loved of God on high,\nin exultant jubilation\npours perpetual melody;\nGod the One in Three adoring\nin glad hymns eternally.",
	3: "To this temple, where we call thee,\ncome, O Lord of Hosts, today;\nwith thy wonted loving-kindness\nhear thy servants as they pray,\nand thy fullest benediction\nshed within its walls alway.",
	4: "Here vouchsafe to all thy servants\nwhat they ask of thee of gain;\nwhat they gain from thee, for ever\nwith the blessèd to retain,\nand hereafter in thy glory\nevermore with thee to reign.",
	5: "Laud and honor to the Father,\nlaud and honor to the Son,\nlaud and honor to the Spirit,\never Three, and ever One,\ncon-substantial, co-eternal,\nwhile unending ages run."
}
song["sda-hymnal/236"] = {
	title: "I Love Thee",
	1: "I love Thee, I love Thee, I love Thee,my Lord;\nI love Thee, my Savior, I love Thee, my God.\nI love Thee, I love Thee, and that Thou dost know;\nBut how much I love Thee my actions will show.",
	2: "I’m happy, I’m happy, O, wondrous account!\nMy joys are immortal, I stand on the mount!\nI gaze on my treasure and long to be there,\nWith Jesus and angels, and kindred so dear.",
	3: "O Jesus, my Savior, with Thee I am blest,\nMy life and salvation, my joy and my rest:\nThy name be my theme, and Thy love be my song;\nThy grace shall inspire both my heart and my tongue.",
	4: "O, who’s like my Savior? He’s Salem’s bright King;\nHe smiles, and He loves me, and helps me to sing.\nI’ll praise Him, I’ll praise Him, with notes loud and clear\nWhile rivers of pleasure my spirit do cheer."
}
song["sda-hymnal/237"] = {
	title: "In the Cross of Christ I Glory",
	1: "In the cross of Christ I glory,\ntowering o’er the wrecks of time;\nall the light of sacred story\ngathers round its head sublime.",
	2: "When the woes of life o’ertake me,\nhopes deceive, and fears annoy,\nnever shall the cross forsake me.\nLo! it glows with peace and joy.",
	3: "When the sun of bliss is beaming\nlight and love upon my way,\nfrom the cross the radiance streaming\nadds more luster to the day.",
	4: "Bane and blessing, pain and pleasure,\nby the cross are sanctified;\npeace is there that knows no measure,\njoys that through all time abide."
}
song["sda-hymnal/238"] = {
	title: "How Sweet the Name!",
	scripture: "Song of Sol. 1:3",
	1: "How sweet the name of Jesus sounds\nIn a believer’s ear!\nIt soothes his sorrows, heals his wounds,\nAnd drives away his fear.",
	2: "It makes the wounded spirit whole,\nAnd calms the troubled breast;\n“Tis manna to the hungry soul,\nAnd to the weary, rest.",
	3: "Dear name, the rock on which I build,\nMy shield and hiding place,\nMy neverfailing treasury, filled\nWith boundless stores of grace.",
	4: "Jesus! my Shepherd, Guardian, Friend,\nMy Prophet, Priest, and King!\nMy Lord, my life, my way, my end!\nAccept the praise I bring.",
	5: "Weak is the effort of my heart,\nAnd cold my warmest thought;\nBut when I see Thee as Thou art,\nI’ll praise Thee as I ought."
}
song["sda-hymnal/239"] = {
	title: "Jesus, Priceless Treasure",
	1: "Jesus, priceless Treasure, Source of purest pleasure, Truest Friend to me:\nAh! how long I’ve panted, And my heart has fainted, thirsting, Lord, for Thee.\nThine I am, O spotless Lamb, I will suffer naught to hide Thee,\nNought I ask beside Thee.",
	2: "In Thine arms I rest me; foes who would molest me Cannot reach me here.\nThough the earth be shaking, Every heart be quaking, Jesus calms our fear.\nSin and hell in conflict fell, With their bitter storms assail me,\nJesus will not fail me.",
	3: "Hence, all thought of sadness! For the Lord of gladness, Jesus, enters in.\nThose who love the Father, Though the storms may gather, still have peace within;\nYea, whatever we here must bear, Still in Thee lies purest pleasure,\nJesus, priceless Treasure!"
}
song["sda-hymnal/240"] = {
	title: "Fairest Lord Jesus",
	tune: "Crusader's Hymn",
	1: "Fairest Lord Jesus, ruler of all nature,\nO thou of God and man the Son!\nThee will I cherish, Thee will I honor,\nthou, my soul’s glory, joy, and crown.",
	2: "Fair are the meadows, fairer still the woodlands,\nrobed in the blooming garb of spring:\nJesus is fairer, Jesus is purer\nwho makes the woeful heart to sing.",
	3: "Fair is the sunshine, fairer still the moonlight,\nand all the twinkling starry host:\nJesus shines brighter, Jesus shines purer\nthan all the angels heaven can boast.",
	4: "Beautiful Savior! Lord of all the nations,\nSon of God and Son of Man!\nGlory and honor, praise, adoration,\nnow and forevermore be thine!"
}
song["sda-hymnal/241"] = {
	title: "Jesus, the Very Thought of Thee",
	tune: "St. Agnes",
	1: "Jesus, the very thought of thee\nwith sweetness fills the breast;\nbut sweeter far thy face to see,\nand in thy presence rest.",
	2: "No voice can sing, no heart can frame,\nNor can the memory find\nA sweeter sound than Jesus’ name,\nThe Savior of mankind.",
	3: "O hope of every contrite heart!\nO joy of all the meek,\nto those who fall, how kind thou art!\nHow good to those who seek!",
	4: "But what to those who find? Ah, this\nnor tongue nor pen can show;\nthe love of Jesus, what it is,\nnone but his loved ones know.",
	5: "Jesus, our only joy be thou,\nas thou our prize wilt be;\nJesus, be thou our glory now,\nand through eternity."
}
song["sda-hymnal/242"] = {
	title: "Jesus, Thou Joy of Loving Hearts",
	tune: "Abends",
	1: "Jesus, Thou joy of loving hearts!\nThou fount of life! Thou light of men!\nFrom the best bliss that earth imparts,\nWe turn unfilled to Thee again.",
	2: "Thy truth unchanged has ever stood;\nThou savest those that on Thee call;\nTo them that seek Thee Thou art good,\nTo them that find Thee, all in all.",
	3: "We taste Thee, O Thou living Bread,\nAnd long to feast upon Thee still;\nWe drink of Thee, the Fountainhead,\nAnd thirst our souls from Thee to fill!",
	4: "Our restless spirits yearn for Thee,\nWhere-e’r our changeful lot is cast;\nGlad, when Thy gracious smile we see,\nBlest, when our faith can hold Thee fast.",
	5: "O Jesus, ever with us stay;\nMake all our moments calm and bright;\nChase the dark night of sin away,\nShed o’r the world Thy holy light!"
}
song["sda-hymnal/243"] = {
	title: "King of Glory, King of Peace",
	tune: "Gwalchmai",
	info: "Alternate tune, LLANFAIR, No. 176",
	1: "King of glory, King of peace, I will love Thee;\nAnd that love may never cease, I will move Thee.\nThou hast granted my request, Thou hast heard me;\nThou didst note my working breast, Thou hast spared me.",
	2: "Wherefore with my utmost art I will sing Thee,\nAnd the cream of all my heart I will bring Thee.\nThough my sins against me cried, Thou didst clear me;\nAnd alone, when they replied, Thou didst hear me.",
	3: "Seven whole days, not one in seven, I will praise Thee;\nIn my heart, though not in heav’n I can raise Thee.\nSmall it is, in this poor sort to enroll Thee:\nEv’n eternity’s too short to extol Thee."
}
song["sda-hymnal/244"] = {
	title: "My Song Shall Be of Jesus",
	poet: "Fanny J. Crosby, 1875 (1820 - 1915)",
	tune: "Nyland",
	copyright: "“My Song Shall Be of Jesus” Words adapted by permission of E. Margaret Clarkson. Music from the Revised Church Hymnary 1927 by permission of Oxford University Press.",
	1: "My song shall be of Jesus;\nHis mercy crowns my days,\nHe fills my cup with blessings,\nAnd tunes my heart to praise.\nMy song shall be of Jesus,\nThe precious Lamb of God,\nWho gave Himself my ransom,\nAnd bought me with His blood.",
	2: "My song shall be of Jesus\nWhen, sitting at His feet,\nI call to mind His goodness\nIn meditation sweet:\nMy song shall be of Jesus,\nWhatever ill betide;\nI’ll sing the grace that saves me,\nAnd keeps me at His side.",
	3: "My song shall be of Jesus\nWhile pressing on my way\nTo reach the blissful kingdom\nOf pure and perfect day:\nAnd when my soul shall enter\nThat realm of splendor fair,\nA song of praise to Jesus\nI’ll sing forever there."
}
song["sda-hymnal/245"] = {
	title: "More About Jesus",
	1: "More about Jesus, I would know,\nMore of His grace to others show;\nMore of His saving fullness see,\nMore of His love who died for me.\nRefrain\nMore, more about Jesus,\nMore, more about Jesus;\nMore of His saving fullness see,\nMore of His love who died for me.",
	2: "More about Jesus, let me learn,\nMore of His holy will discern;\nSpirit of God, my teacher be,\nShowing the things of Christ to me.",
	3: "More about Jesus, in His word,\nHolding communion with my Lord;\nHearing His voice in every line,\nMaking each faithful saying mine.",
	4: "More about Jesus, on His throne,\nRiches in glory all His own;\nMore of His kingdom’s sure increase,\nMore of His coming, Prince of Peace."
}
song["sda-hymnal/246"] = {
	title: "Worthy, Worthy Is the Lamb",
	scripture: "Revelation 5:12",
	1: "Worthy, worthy is the Lamb,\nWorthy, worthy is the Lamb,\nWorthy, worthy is the Lamb,\nThat was slain.\nRefrain\nGlory, hallelujah!\nPraise Him, hallelujah!\nGlory, hallelujah!\nTo the Lamb!",
	2: "Savior, let Thy kingdom come!\nNow the power of sin consume;\nBring Thy blest millennium,\nHoly Lamb.",
	3: "Thus may we\neach moment feel,\nLove Him, serve Him,\npraise Him still,\nTill we all on Zion’s hill\nSee the Lamb."
}
song["sda-hymnal/247"] = {
	title: "Come, My Way",
	tune: "Come, My Way",
	info: "Alternate tune, TRYGGARE KAN INGEN VARA, No. 101",
	copyright: "“Come, My Way” Copyright by Headmasters’ Conference, 29 Gordon Square, London, WC1H ODS.",
	1: "Come, my Way, my Truth, my Life:\nsuch a way as gives us breath,\nsuch a truth as ends all strife,\nsuch a life as killeth death.",
	2: "Come, my Light, my Feast, my Strength:\nsuch a light as shows a feast,\nsuch a feast as mends in length,\nsuch a strength as makes his guest.",
	3: "Come, my Joy, my Love, my Heart:\nsuch a joy as none can move,\nsuch a love as none can part,\nsuch a heart as joys in love."
}
song["sda-hymnal/248"] = {
	title: "O, How I Love Jesus",
	1: "There is a Name I love to hear,\nI love to sing its worth;\nIt sounds like music in my ears,\nThe sweetest Name on earth.\nRefrain\nO how I love Jesus,\nO how I love Jesus,\nO how I love Jesus,\nBecause He first loved me.",
	2: "It tells me of a Savior’s love,\nWho died to set me free;\nIt tells me of a Savior’s blood,\nThe sinners’ perfect plea.",
	3: "It tells of one whose loving heart\nCan feel my deepest woe;\nWho in each sorrow bears\nA part that none can bear below."
}
song["sda-hymnal/249"] = {
	title: "Praise Him! Praise Him!",
	poet: "Fanny J. Crosby, 1869 (1820 - 1915)",
	1: "Praise Him! praise Him! Jesus, our blessed Redeemer!\nSing, O Earth, His wonderful love proclaim!\nHail Him! hail Him! highest archangels in glory;\nStrength and honor give to His holy Name!\nLike a shepherd, Jesus will guard His children,\nIn His arms He carries them all day long:\nRefrain\nPraise Him! Praise Him! Tell of His excellent greatness.\nPraise Him! Praise Him! Ever in joyful song!",
	2: "Praise Him! Praise Him! Jesus, our blessed Redeemer!\nFor our sins He suffered, and bled, and died.\nHe our Rock, our hope of eternal salvation,\nHail Him! hail Him! Jesus the Crucified.\nSound His praises! Jesus who bore our sorrows,\nLove unbounded, wonderful, deep and strong.",
	3: "Praise Him! Praise Him! Jesus, our blessed Redeemer!\nHeavenly portals loud with hosannas ring!\nJesus, Savior, reigneth forever and ever.\nCrown Him! Crown Him! Prophet, and Priest, and King!\nChrist is coming! over the world victorious,\nPower and glory unto the Lord belong."
}
song["sda-hymnal/250"] = {
	title: "O for a Thousand Tongues to Sing",
	tune: "Azmon",
	1: "O for a thousand tongues to sing\nmy great Redeemer’s praise,\nthe glories of my God and King,\nthe triumphs of his grace!",
	2: "My gracious Master and my God,\nassist me to proclaim,\nto spread through all the earth abroad\nthe honors of thy name.",
	3: "Jesus! the name that charms our fears,\nthat bids our sorrows cease;\n’tis music in the sinner’s ears,\n’tis life, and health, and peace.",
	4: "He breaks the power of canceled sin,\nhe sets the prisoner free;\nhis blood can make the foulest clean;\nhis blood availed for me.",
	5: "He speaks, and listening to his voice,\nnew life the dead receive;\nthe mournful, broken hearts rejoice,\nthe humble poor believe.",
	6: "Hear him, ye deaf; his praise, ye dumb,\nyour loosened tongues employ;\nye blind, behold your savior come,\nand leap, ye lame, for joy."
}
song["sda-hymnal/251"] = {
	title: "He Lives",
	copyright: "“He Lives” Copyright 1933 by Homer A. Rodeheaver © renewed 1961, The Rodeheaver Co. (A Div. of Word, Inc.). All rights reserved. International copyright secured. Used by permission.",
	1: "I serve a risen Savior,\nHe’s in the world today;\nI know that He is living,\nwhatever men may say;\nI see His hand of mercy,\nI hear His voice of cheer\nAnd just the time\nI need Him He’s always near.\nRefrain\nHe lives, He lives,\nChrist Jesus lives today!\nHe walks with me\nand talks with me\nalong life’s narrow way.\nHe lives, He lives,\nsalvation to impart!\nYou ask me how I know He lives?\nHe lives within my heart.",
	2: "In all the world around me\nI see His loving care,\nAnd tho’ my heart grows weary\nI never will despair;\nI know that He is leading\nthru all the stormy blast,\nThe day of His appearing\nwill come at last.",
	3: "Rejoice, rejoice, O Christian,\nlift up your voice and sing,\nEternal hallelujahs\nto Jesus Christ the King!\nThe hope of all who seek Him,\nthe help of all who find,\nNone other is so loving,\nso good and kind."
}
song["sda-hymnal/252"] = {
	title: "Come, Let Us Sing",
	tune: "Russia",
	scripture: "Rev. 5:9-13",
	info: "Alternate tune, WAREHAM, Nos. 174, 226",
	1: "Come, let us sing the song of songs-\nThe angels first began the strain-\nThe homage which to Christ belongs:\n“Worthy the Lamb, for He was slain!”",
	2: "Slain to redeem us by His blood,\nTo cleanse from every sinful stain,\nAnd make us kings and priests to God:\n“Worthy the Lamb, for He was slain!”",
	3: "To Him who suffered on the tree,\nOur souls, at His soul’s price, to gain,\nBlessing, and praise, and glory be:\n“Worthy the Lamb, for He was slain!”",
	4: "To Him, enthroned by filial right\nAll power in heaven and earth proclaim,\nHonor, and majesty, and might:\n“Worthy the Lamb, for He was slain!”",
	5: "Long as we live, and when we die,\nAnd while in heaven with Him we reign,\nThis song our song of songs shall be:\n“Worthy the Lamb, for He was slain!”"
}
song["sda-hymnal/253"] = {
	title: "There's No Other Name Like Jesus",
	scripture: "Acts 4:12",
	1: "There’s no other name like Jesus,\n‘Tis the dearest name we know,\n‘Tis the angel’s joy in heaven,\n‘Tis the Christian’s joy below.\nRefrain\nSweet name, (sweet name) dear name, (dear name)\nThere’s no other name like Jesus;\nSweet name, (sweet name) dear name, (dear name)\nThere’s no other name like Jesus.",
	2: "There’s no other name like Jesus\nWhen the heart with grief is sad,\nThere’s no other name like Jesus\nWhen the heart is free and glad.",
	3: "‘Tis the hope that I shall see Him\nWhen in glory He appears,\n‘Tis the hope to hear His welcome\nThat my fainting spirit cheers.",
	4: "If He wills that I should labor\nIn His vineyard day by day,\nThen ’tis well if only Jesus\nBlesses all I do or say.",
	5: "If He wills that death’s cold finger\nTouch my feeble, mortal clay,\nThen ’tis well if only Jesus\nIs my dying trust and stay."
}
song["sda-hymnal/254"] = {
	title: "The Great Physician Now is Near",
	1: "The great Physician now is near,\nThe sympathizing Jesus;\nHe speaks the drooping heart to cheer,\nOh! hear the voice of Jesus.\nRefrain\nSweetest note in seraph song,\nSweetest name on mortal tongue;\nSweetest carol ever sung,\nJesus, blessed Jesus.",
	2: "Your many sins are all forgiven,\nOh! hear the voice of Jesus;\nGo on your way in peace to heaven,\nAnd wear a crown with Jesus.",
	3: "All glory to the dying Lamb!\nI now believe in Jesus;\nI love the blessed Savior’s name,\nI love the name of Jesus.",
	4: "His name dispels my guilt and fear,\nNo other name but Jesus;\nOh! how my soul delights to hear\nThe charming name of Jesus."
}
song["sda-hymnal/255"] = {
	title: "I Cannot Tell Why",
	tune: "Londonderry Air",
	copyright: "“I Cannot Tell Why” Arrangement copyright © Jubilate Hymns Ltd. Used by permission.",
	1: "I cannot tell why He whom angels worship\nShould set His love upon the souls of men,\nOr why as Shepherd He should seek the wanderers,\nTo bring them back, they know not\nhow nor when.\nBut this I know, that He was born of Mary\nWhen Bethlehem’s manger was His only home,\nAnd that He lived at Nazareth and laboured;\nAnd so the Saviour, Saviour of the world, has come.",
	2: "I cannot tell how silently He suffered\nAs with His peace He graced this place of tears,\nNor how His heart upon the cross was broken,\nThe crown of pain to three and thirty years.\nBut this I know He heals the broken hearted\nAnd stays our sin and calms our lurking fear,\nAnd lifts the burden from the heavy laden;\nFor still the Saviour, Saviour of the world, is here.",
	3: "I cannot tell how He will win the nations,\nHow He will claim His earthly heritage,\nHow satisfy the needs and aspirations\nOf east and west, of sinner and of sage.\nBut this I know, all flesh shall see His glory,\nAnd He shall reap the harvest He has sown,\nAnd some glad day His sun will shine in splendour\nWhen He the Saviour, Saviour, of the world, is known.",
	4: "I cannot tell how all lands shall worship,\nWhen at His biding every storm is stilled,\nOr who can say how great the jubilation\nWhen all our hearts with love for Him are filled.\nBut this I know the skies shall sound His praises,\nTen thousand thousand human voices sing,\nAnd earth to heaven, and heaven to earth will answer,\nAt last the Saviour, Saviour of the world, is King!"
}
song["sda-hymnal/256"] = {
	title: "Ye Servants of God",
	tune: "Hanover",
	1: "Ye servants of God, your Master proclaim,\nand publish abroad his wonderful name;\nthe name all-victorious of Jesus extol,\nhis kingdom is glorious and rules over all.",
	2: "God ruleth on high, almighty to save,\nand still he is nigh, his presence we have;\nthe great congregation his triumph shall sing,\nascribing salvation to Jesus, our King.",
	3: "“Salvation to God, who sits on the throne!”\nLet all cry aloud and honor the Son;\nthe praises of Jesus the angels proclaim,\nfall down on their faces and worship the Lamb.",
	4: "Then let us adore and give him his right,\nall glory and power, all wisdom and might;\nall honor and blessing with angels above,\nand thanks never ceasing and infinite love."
}
song["sda-hymnal/257"] = {
	title: "Come Down, O Love Divine",
	tune: "Down Ampney",
	copyright: "“Come Down, O Love Divine” Music from The English Hymnal by permission of Oxford University Press.",
	1: "Come down, O Love divine, seek thou this soul of mine\nand visit it with thine own ardor glowing;\nO Comforter, draw near, within my heart appear,\nand kindle it, thy holy flame bestowing.",
	2: "O let it freely burn, till earthly passions turn\nto dust and ashes in its heat consuming;\nand let thy glorious light shine ever on my sight,\nand clothe me round, the while my path illuming.",
	3: "Let holy charity Mine outward vesture be,\nAnd lowliness become my inner clothing;\nTrue lowliness of heart which takes the humbler part,\nAnd o’er its own shortcomings weeps with loathing.",
	4: "And so the yearning strong with which the soul will long\nshall far outpass the power of human telling;\nfor none can guess its grace, till Love create a place\nwherein the Holy Spirit makes a dwelling."
}
song["sda-hymnal/258"] = {
	title: "Baptize Us Anew",
	1: "Baptize us anew With power from on high,\nWith love, O refresh us! Dear Savior, draw nigh.\nRefrain\nWe humbly beseech Thee, Lord Jesus, we pray,\nWith love and the Spirit baptize us today.\nWe praise Thee, we bless Thee, dear Lamb that was slain\nWe laud and adore Thee, Amen and Amen.",
	2: "Unworthy we cry, Unholy, unclean,\nO wash us and cleanse us From sin’s guilty stain.",
	3: "O heavenly Dove, Descend from on high!\nWe plead Thy rich blessing; In mercy draw nigh.",
	4: "O list the glad voice! From heaven it came:\nThou art My beloved, Well pleased I am."
}
song["sda-hymnal/259"] = {
	title: "Draw Us in the Spirit's Tether",
	tune: "Union Seminary",
	copyright: "“Draw Us in the Spirit's Tether” Words from Enlarged Songs of Praise by permission of Oxford University Press. Music copyright © 1957 by The H.W. Gray Company, a division of Belwin-Mills Publishing Corp. Used with permission. Adapt. by Jet Turner copyrigh",
	1: "Draw us in the Spirit’s tether,\nFor when humbly in Thy name,\nTwo or three are met together\nThou are in the midst of them;\nAlleluia! Alleluia!\nTouch we now Thy garment’s hem.",
	2: "As the brethren used to gather\nIn the name of Christ to sup,\nThen with thanks to God the Father\nBreak the bread and bless the cup,\nAlleluia! Alleluia!\nSo knit Thou our friendship up.",
	3: "All our meals and all our living\nMake as sacraments of Thee,\nThat by caring, helping, giving\nWe may true disciples be.\nAlleluia! Alleluia!\nWe will serve Thee faithfully."
}
song["sda-hymnal/260"] = {
	title: "Hover O'er Me, Holy Spirit",
	1: "Hover o’er me, Holy Spirit,\nBathe my trembling heart and brow;\nFill me with Thy hallowed presence,\nCome, O come and fill me now.\nRefrain\nFill me now, fill me now,\nJesus, come and fill me now.\nFill me with Thy hallowed presence,\nCome, O come and fill me now.",
	2: "Thou canst fill me, gracious Spirit,\nThough I cannot tell Thee how;\nBut I need Thee, greatly need Thee;\nCome, O come and fill me now.",
	3: "I am weakness, full of weakness;\nAt Thy sacred feet I bow;\nBlest, divine, eternal Spirit,\nFill with love, and fill me now.",
	4: "Cleanse and comfort, bless and save me;\nBathe, O, bathe my heart and brow;\nThou art comforting and saving,\nThou art sweetly filling now."
}
song["sda-hymnal/261"] = {
	title: "The Spirit of the Lord Revealed",
	tune: "Soll's Sein",
	copyright: "“The Spirit of the Lord Revealed” Words from Enlarged Songs of Praise by permission of Oxford University Press.",
	1: "The Spirit of the Lord revealed\nHis will to saints of old;\nTheir heart and mind lips unsealed\nHis glory to unfold.\nAmid the gloom of ancient night\nThey hailed the dawning Word,\nAnd in the coming of the light\nProclaimed the coming Lord.",
	2: "The prophets passed; at length there came\nTo sojourn and abide,\nThe Word incarnate, to whose name\nThe prophets testified;\nAnd He, the twilight overpast,\nHimself, the Light of light,\nAs man with man, revealed at last\nThe Father to our sight.",
	3: "Eternal Spirit, who dost speak\nTo mind and conscience still,\nThat we in this our day, may seek\nTo do our Father’s will,\nTo us the word of life impart,\nOf Christ, the living way;\nGive us the quiet, humble heart\nTo hear and to obey."
}
song["sda-hymnal/262"] = {
	title: "Sweet, Sweet Spirit",
	copyright: "“Sweet, Sweet Spirit” Copyright 1965 by Manna Music, Inc., 2111 Kenmere ave., Burbank, CA 91504. International copyright secured. Used by permission.",
	1: "There’s a sweet, sweet spirit in this place,\nAnd I know that it’s the spirit of the Lord;\nSweet Holy Spirit,\nSweet Heavenly Dove,",
	2: "Stay right here with us,\nFilling us with Your love,\nAnd for these blessings\nWe lift our hearts in praise,\nWithout a doubt we’ll know\nThat we have been revived\nWhen we shall leave this place.",
	3: "There are sweet expressions on each face,\nAnd I know they feel the presence of the Lord.\nSweet Holy Spirit,\nSweet Heavenly Dove,",
	4: "Stay right here with us,\nFilling us with Your love,\nAnd for these blessings\nWe lift our hearts in praise,\nWithout a doubt we’ll know\nThat we have been revived\nWhen we shall leave this place."
}
song["sda-hymnal/263"] = {
	title: "Fire of God, Thou Sacred Flame",
	tune: "Song 13",
	copyright: "“Fire of God, Thou Sacred Flame” Words by permission of Oxford University Press.",
	1: "Fire of God, Thou sacred flame,\nSpirit who in splendor came,\nLet Thy heat my soul refine\nTill it glows with love divine.",
	2: "Breath of God, that swept in power\nIn the pentecostal hour,\nHoly Breath, be Thou in me\nSource of vital energy.",
	3: "Strength of God, Thy might within\nConquers sorrow, pain, and sin:\nFortify from evil’s art\nAll the gateways of my heart.",
	4: "Truth of God, Thy piercing rays\nPenetrate my secret ways.\nMay the light that shames my sin\nGuide me holier paths to win.",
	5: "Love of God, Thy grace profound\nKnoweth neither age nor bound:\nCome, my heart’s own guest to be,\nDwell forevermore in me."
}
song["sda-hymnal/264"] = {
	title: "O for That Flame of Living Fire",
	tune: "Mendon",
	1: "O for that flame of living fire\nWhich shone so bright in saints of old;\nWhich bade their souls to heaven aspire,\nCalm in distress, in danger bold!",
	2: "Where is that spirit, Lord, which dwelt\nIn Abram’s breast, and sealed Him Thine?\nWhich made Paul’s heart with sorrow melt,\nAnd glow with energy divine?",
	3: "That spirit which from age to age\nProclaimed Thy love, and taught Thy ways?\nBrightened Isaiah’s vivid page,\nAnd breathed in David’s hallowed lays?",
	4: "Is not Thy grace as mighty now\nAs when Elijah felt its power?\nWhen glory beamed from Moses’ brow,\nOr Job endured the trying hour?",
	5: "Remember, Lord, the ancient days;\nRenew Thy work, Thy grace restore;\nAnd while to Thee our hearts we raise,\nOn us Thy Holy Spirit pour."
}
song["sda-hymnal/265"] = {
	title: "Breathe on Me, Breath of God",
	tune: "Paraclete",
	1: "Breathe on me, breath of God,\nFill me with life anew,\nThat I may love what Thou dost love,\nAnd do what Thou wouldst do.",
	2: "Breathe on me, breath of God,\nUntil my heart is pure,\nUntil with Thee I will one will,\nTo do and to endure.",
	3: "Breathe on me, breath of God,\nTill I am wholly Thine,\nUntil this earthly part of me\nGlows with Thy fire divine.",
	4: "Breathe on me, breath of God,\nSo shall I constant be,\nAnd live with Thee the perfect life\nOf Thine eternity."
}
song["sda-hymnal/266"] = {
	title: "Spirit of God",
	tune: "Morecambe",
	1: "Spirit of God, descend upon my heart;\nwean it from earth; through all its pulses move;\nstoop to my weakness, mighty as thou art,\nand make me love thee as I ought to love.",
	2: "I ask no dream, no prophet ecstasies,\nno sudden rending of the veil of clay,\nno angel visitant, no opening skies;\nbut take the dimness of my soul away.",
	3: "Has thou not bid me love thee, God and King?\nAll, all thine own, soul, heart and strength and mind.\nI see thy cross; there teach my heart to cling.\nO let me seek thee, and O let me find.",
	4: "Teach me to feel that thou art always nigh;\nteach me the struggles of the soul to bear.\nTo check the rising doubt, the rebel sigh,\nteach me the patience of unanswered prayer.",
	5: "Teach me to love thee as thine angels love,\none holy passion filling all my frame;\nthe kindling of the heaven-descended Dove,\nmy heart an altar, and thy love the flame."
}
song["sda-hymnal/267"] = {
	title: "Spirit Divine",
	tune: "Gräfenberg",
	1: "Spirit divine, attend our prayers,\nAnd make this house Thy home;\nDescend with all Thy gracious powers,\nO come, great Spirit, come!",
	2: "Come as the light; to us reveal\nOur emptiness and woe,\nAnd lead us in those paths of life\nWhere-on the righteous go.",
	3: "Come as the fire, and purge our hearts\nLike sacrificial flame;\nLet our whole soul an of – f’ring be\nTo our Redeemer’s name.",
	4: "Come as the dove, and spread Thy wings,\nThe wings of peaceful love;\nAnd let Thy church on earth become\nBlest as the church above.",
	5: "Spirit divine, attend our prayers,\nMake a lost world Thy home;\nDescend with all Thy gracious powers,\nO come, great Spirit, come!"
}
song["sda-hymnal/268"] = {
	title: "Holy Spirit, Light Divine",
	tune: "Mercy",
	1: "Holy Spirit, light divine,\nShine upon this heart of mine,\nChase the shades of night away,\nTurn my darkness into day.",
	2: "Holy Spirit, power divine,\nCleanse this guilty heart of mine;\nLong has sin, without control,\nHeld dominion o’er my soul.",
	3: "Holy Ghost, with joy divine,\nCheer this saddened heart of mine,\nBid my many woes depart,\nHeal my wounded, bleeding heart.",
	4: "Holy Spirit, all divine,\nDwell within this heart of mine,\nCast down every idol throne,\nReign supreme, and reign alone."
}
song["sda-hymnal/269"] = {
	title: "Come, Holy Spirit",
	tune: "St. Agnes",
	1: "Come, Holy Spirit, heavenly Dove,\nWith all Thy quickening powers;\nKindle a flame of sacred love\nIn these cold hearts of ours.",
	2: "O raise our thoughts from things below,\nFrom vanities and toys!\nThen shall we with fresh courage go\nTo reach eternal joys.",
	3: "Awake our souls to joyful songs;\nLet pure devotions rise;\nTill praise employs our thankful tongues,\nAnd doubt forever dies.",
	4: "Father, we would no longer live\nAt this poor, dying rate;\nTo Thee our thankful love we give,\nFor Thine to us is great.",
	5: "Come, Holy Spirit, heavenly Dove,\nWith all Thy quickening powers;\nCome, shed abroad a Savior’s love,\nAnd that shall kindle ours."
}
song["sda-hymnal/270"] = {
	title: "O Holy Dove of God Descending",
	tune: "Lois",
	copyright: "“O Holy Dove of God Descending” Copyright © by Fred Bock Music Co. All rights reserved. Used by permission.",
	1: "O holy Dove of God descending,\nYou are the love that knows no ending.\nAll of our shattered dreams You’re mending:\nSpirit, now live in me.",
	2: "O holy Wind of God now blowing,\nYou are the seed that God us sowing.\nYou are the life that starts us growing:\nSpirit, now live in me.",
	3: "O holy Rain of God now falling,\nYou make the Word of God enthralling,\nYou are that inner voice now calling:\nSpirit, now live in me.",
	4: "O holy Flame of God now burning,\nYou are the power of Christ returning.\nYou are the answer to our yearning:\nSpirit, now live in me."
}
song["sda-hymnal/271"] = {
	title: "Break Thou the Bread of Life",
	tune: "Bread of Life",
	scripture: "Matt. 14-19",
	1: "Break thou the bread of life, dear Lord, to me,\nas thou didst break the loaves beside the sea;\nbeyond the sacred page I seek thee, Lord;\nmy spirit pants for thee, O Living Word!",
	2: "Bless thou the truth, dear Lord, to me, to me,\nas thou didst bless the bread by Galilee;\nthen shall all bondage cease, all fetters fall;\nand I shall find my peace, my all in all.",
	3: "Thou art the bread of life, O Lord, to me;\nThy holy Word the truth That saveth me;\nGive me to eat and live With Thee above;\nTeach me to love Thy truth, For Thou art love.",
	4: "Spirit and life are they, Words Thou dost speak;\nI hasten to obey, But I am weak;\nThou art my only help, Thou art my life;\nHeeding Thy holy Word I win the strife."
}
song["sda-hymnal/272"] = {
	title: "Give Me the Bible",
	1: "Give me the Bible,\nstar of gladness gleaming,\nTo cheer the wanderer\nlone and tempest tossed,\nNo storm can hide that\npeaceful radiance beaming\nSince Jesus came\nto seek and save the lost.\nRefrain\nGive me the Bible–\nholy message shining,\nThy light shall guide me\nin the narrow way.\nPrecept and promise,\nlaw and love combining,\n‘Til night shall vanish\nin eternal day.",
	2: "Give me the Bible\nwhen my heart is broken,\nWhen sin and grief have\nfilled my soul with fear,\nGive me the precious\nwords by Jesus spoken,\nHold up faith’s lamp\nto show my Savior near.",
	3: "Give me the Bible,\nall my steps enlighten,\nTeach me the danger\nof these realms below,\nThat lamp of safety,\no’er the gloom shall brighten,\nThat light alone\nthe path of peace can show."
}
song["sda-hymnal/273"] = {
	title: "Lord, I Have Made Thy Word My Choice",
	tune: "Wetherby",
	scripture: "Psalm 119:111",
	1: "Lord, I have made Thy word my choice,\nMy lasting heritage;\nThere shall my noblest powers rejoice,\nMy warmest thoughts engage.",
	2: "I’ll read the histories of Thy love,\nAnd keep Thy laws in sight;\nWhile through Thy promises I rove,\nWith ever fresh delight.",
	3: "In this broad land of wealth unknown,\nWhere springs of life arise,\nSeeds of immortal bliss are sown,\nAnd hidden glory lies.",
	4: "The best relief that mourners have,\nIt makes our sorrows blest;\nOur fairest hope beyond the grave,\nAnd our eternal rest."
}
song["sda-hymnal/274"] = {
	title: "O Word of God Incarnate",
	tune: "Atkinson",
	1: "O Word of God incarnate,\nO Wisdom from on high,\nO Truth unchanged, unchanging,\nO Light of our dark sky:\nwe praise you for the radiance\nthat from the hallowed page,\na lantern to our footsteps,\nshines on from age to age.",
	2: "The church from her dear Master\nreceived the gift divine,\nand still that light is lifted\no’er all the earth to shine.\nIt is the sacred vessel\nwhere gems of truth are stored;\nit is the heaven-drawn picture\nof Christ, the living Word.",
	3: "It floateth like a banner\nbefore God’s host unfurled;\nit is a shining beacon\nabove the darkling world.\nIt is the chart and compass\nthat o’er life’s surging tide,\nmid mists and rocks and quicksands,\nStill guides, O Christ, to Thee.",
	4: "O make Thy church, dear Savior,\na lamp of purest gold,\nto bear before the nations\nyour true light as of old.\nO teach your wandering pilgrims\nby this their path to trace,\ntill, clouds and darkness ended,\nthey see Thee face to face."
}
song["sda-hymnal/275"] = {
	title: "O God of Light",
	tune: "Atkinson",
	copyright: "“O God of Light” Words copyright © 1952. Renewal 1980 by The Hymn Society of America, Texas Christian University, Fort Worth, TX 76129. Used by permission. Music copyright 1978 Lutheran Book of Worship. Used by permission of Augsburg Publishing House.",
	1: "O God of light, Your Word, a lamp unfailing,\nShall pierce the darkness of our earthbound way\nAnd show Your grace,\nYour plan for us unveiling,\nAnd guide our footsteps to the perfect day.",
	2: "From days of old, through blind and willful ages,\nThough we rebelled, You gently sought again,\nAnd spoke through saints,\nApostles, prophets, sages,\nWho wrote with eager or reluctant pen.",
	3: "Undimmed by time, those words are still revealing\nTo sinful hearts Your justice and Your grace;\nAnd questing mortals longing for Your healing,\nSee Your compassion in the Savior’s face.",
	4: "To all the world Your summons You are sending,\nThrough all the earth, to ever land and race,\nThat myriad tongues, in one great anthem blending,\nMay praise and celebrate Your gift of grace."
}
song["sda-hymnal/276"] = {
	title: "Thanks to God",
	tune: "Wylde Green",
	copyright: "“Thanks to God” Words copyright © 1954. Renewal 1982 by Agape, Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © 1966 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Thanks to God whose Word was spoken\nIn the deed that made the earth.\nHis the voice that called a nation;\nHis the fires that tried her worth.\nGod has spoken;\nPraise Him For His open Word",
	2: "Thanks to God whose Word incarnate\nGlorified the flesh of man.\nDeeds and words and death and rising\nTell the grace in heaven’s plan.\nGod has spoken;\nPraise Him For His open Word",
	3: "Thanks to God whose Word is answered\nBy the Spirit’s voice within.\nHere we drink of joy unmeasured,\nLife redeemed from death and sin.\nGod has spoken;\nPraise Him For His open Word"
}
song["sda-hymnal/277"] = {
	title: "For Your Holy Book We Thank You",
	tune: "Holy Book",
	copyright: "“For Your Holy Book We Thank You” Words copyright © Mrs. Betty Carter. London, 1950. Music copyright © by Blythe Owen.",
	1: "For Your holy book we thank You,\nAnd for all who served You well,\nWriting, guarding, and translating,\nThat its pages might forth tell\nYour strong love and tender care\nFor Your people everywhere.",
	2: "For Your holy book we thank You,\nAnd for those who work today,\nThat the people of all nations,\nReading it and following may\nKnow Your love and tender care\nFor Your people everywhere.",
	3: "For Your holy book we thank You,\nMay its message be our guide,\nMay we understand the wisdom\nOf the laws it will provide:\nAnd Your love and tender care\nFor Your people everywhere.",
	4: "For Your holy book we thank You,\nMay its message in our hearts\nLead us now to see in Jesus\nAll the grace Your word imparts:\nAll Your love and tender care\nFor Your people everywhere."
}
song["sda-hymnal/278"] = {
	title: "Lord Jesus, Once You Spoke to Men",
	tune: "Bluebonnet",
	copyright: "“Lord Jesus, Once You Spoke to Men” Words from More Hymns and Spiritual Songs © 1971 by Walton Music Corp. Used by permission. Music copyright © 1984 by Clarion Call Music, Inc. All rights reserved.",
	1: "Lord Jesus, once You spoke to men\nUpon the mountain and the plain,\nO help us listen now as then\nAnd wonder at Your words again.",
	2: "We all have secret fears to face,\nOur minds and motives to amend.\nWe seek Your truth,\nWe need your grace,\nOur living Lord and present friend.",
	3: "The gospel speaks; and we receive\nYour light, Your love,\nYour own command.\nO help us live what we believe,\nIn daily word of heart and hand."
}
song["sda-hymnal/279"] = {
	title: "Only Trust Him",
	1: "Come, every soul by sin oppressed,\nThere’s mercy with the Lord,\nAnd He will surely give you rest,\nBy trusting in His word.\nRefrain\nOnly trust Him, only trust Him,\nOnly trust Him now;\nHe will save you, He will save you,\nHe will save you now.",
	2: "For Jesus shed his precious blood\nRich blessings to bestow;\nPlunge now into the crimson flood\nThat washes white as snow.",
	3: "Yes, Jesus is the truth, the way,\nThat leads you into rest;\nBelieve in Him without delay,\nAnd you are fully blest.",
	4: "Come, then, and join this holy band,\nAnd on to glory go,\nTo dwell in that celestial land,\nWhere joys immortal flow."
}
song["sda-hymnal/280"] = {
	title: "Come, Ye Sinners",
	copyright: "“Come, Ye Sinners” Arrangement copyright © 1984 by Melvin West",
	1: "Come, ye sinners poor and needy,\nWeak and wounded, sick and sore;\nJesus ready stands to save you,\nFull of pity, love, and power.\nRefrain\nI will arise and go to Jesus,\nHe will embrace me in His arms;\nIn the arms of my dear Savior,\nO there are ten thousand charms.",
	2: "Come. ye thirsty, come, and welcome,\nGod’s free bounty glorify;\nTrue belief and true repentance,\nEvery grace that brings you nigh.",
	3: "Let not conscience make you linger,\nNor of fitness fondly dream;\nAll the fitness He requireth\nIs to feel your need of Him.",
	4: "Lo! th’incarnate God, ascended,\nPleads the merit of His blood;\nVenture on Him, venture wholly,\nLet no other trust intrude."
}
song["sda-hymnal/281"] = {
	title: "I Gave My Life for Thee",
	tune: "White",
	1: "I gave my life for thee,\nMy precious blood I shed,\nThat thou might’st ransom be,\nAnd quickened from the dead;\nI gave, I gave My life for thee,\nWhat hast thou given for Me?\nI gave, I gave My life for thee,\nWhat hast thou given for Me?",
	2: "My Fathers house of light,\nMy glory circled throne,\nI left for earthly night,\nFor wanderings sad and lone;\nI left, I left it all for thee,\nHast thou left aught for Me?\nI left, I left it all for thee,\nHast thou left aught for Me?",
	3: "I suffered much for thee,\nmore than thy tongue can tell,\nOf bitterest agony,\nTo rescue thee from hell;\nI’ve borne, I’ve borne it all for thee,\nWhat hast thou borne for Me?\nI’ve borne, I’ve borne it all for thee,\nWhat hast thou borne for Me?"
}
song["sda-hymnal/282"] = {
	title: "I Hear Thy Welcome Voice",
	1: "I hear Thy welcome voice,\nThat calls me, Lord, to Thee;\nFor cleansing in Thy precious blood,\nThat flowed on Calvary.\nRefrain\nI am coming, Lord!\nComing now to Thee!\nWash me, cleanse me, in the blood\nThat flowed on Calvary.",
	2: "Though coming weak and vile,\nThou dost my strength assure;\nThou dost my vileness fully cleanse,\nTill spotless all, and pure.",
	3: "‘Tis Jesus calls me on\nTo perfect faith and love,\nTo perfect hope, and peace, and trust,\nFor earth and heaven above.",
	4: "All hail, atoning blood!\nAll hail, redeeming grace!\nAll hail! the gift of Christ, our Lord,\nOur Strength and Righteousness."
}
song["sda-hymnal/283"] = {
	title: "O Jesus, Thou Art Standing",
	scripture: "Revelation 3:20",
	1: "O Jesus, thou art standing,\noutside the fast closed door,\nin lowly patience waiting\nto pass the threshold o’er:\nO shame, professing Christians,\nhis Name and sign who bear,\nO shame, thrice shame upon us,\nto keep him standing there!",
	2: "O Jesus, thou art knocking;\nand lo, that hand is scarred,\nand thorns thy brow encircle,\nand tears thy face have marred:\nO love that passeth knowledge,\nso patiently to wait!\nO sin that hath no equal,\nso fast to bar the gate!",
	3: "O Jesus, thou art pleading\nin accents meek and low,\n“I died for you, my children,\nand will you treat me so?”\nO Lord, with shame and sorrow\nwe open now the door;\ndear Savior, enter, enter,\nand leave us never more."
}
song["sda-hymnal/284"] = {
	title: "For You I Am Praying",
	1: "I have a Savior, He’s pleading in glory,\nA dear, loving Savior, though earth friends be few;\nAnd now He is watching in tenderness o’er me,\nBut oh that my Savior were your Savior, too.\nRefrain\nFor you I am praying, For you I am Praying,\nFor you am I praying, I’m praying for you.",
	2: "I have a Father; to me He has given\nA hope for eternity, blessed and true;\nAnd soon He will call to meet Him in heaven,\nBut oh that He’d let me bring you with me, too!",
	3: "I have a robe; ’tis resplendent in whiteness,\nAwaiting in glory my wondering view;\nOh, when I receive it, all shining in brightness,\nDear friend, could I see you receiving one, too!",
	4: "When Jesus has found you, tell others the story,\nThat my loving Savior is your Savior, too;\nThen pray that your Savior will bring them to glory,\nAnd prayer will be answered-’twas answered for you!"
}
song["sda-hymnal/285"] = {
	title: "Jesus Calls Us",
	tune: "Galilee",
	1: "Jesus calls us o’er the tumult\nof our life’s wild, restless sea;\nday by day his sweet voice soundeth,\nsaying, “Christian, follow me!”",
	2: "Jesus calls us from the worship\nof the vain world’s golden store,\nfrom each idol that would keep us,\nsaying, “Christian, love me more!”",
	3: "In our joys and in our sorrows,\ndays of toil and hours of ease,\nstill he calls, in cares and pleasures,\n“Christian, love me more than these!”",
	4: "Jesus calls us! By thy mercies,\nSavior, may we hear thy call,\ngive our hearts to thine obedience,\nserve and love thee best of all."
}
song["sda-hymnal/286"] = {
	title: "Wonderful Words of Life",
	1: "Sing them over again to me,\nwonderful words of life,\nLet me more of their beauty see,\nwonderful words of life;\nWords of life and beauty\nteach me faith and duty.\nRefrain\nBeautiful words, wonderful words,\nwonderful words of life,\nBeautiful words, wonderful words,\nwonderful words of life.",
	2: "Christ, the blessèd One, gives to all\nwonderful words of life;\nSinner, list to the loving call,\nwonderful words of life;\nAll so freely given,\nwooing us to heaven.",
	3: "Sweetly echo the Gospel call,\nwonderful words of life;\nOffer pardon and peace to all,\nwonderful words of life;\nJesus, only Savior,\nsanctify forever."
}
song["sda-hymnal/287"] = {
	title: "Softly and Tenderly",
	1: "Softly and tenderly Jesus is calling,\ncalling for you and for me;\nsee, on the portals he’s waiting and watching,\nwatching for you and for me.\nRefrain\nCome home, come home;\nye who are weary come home;\nearnestly, tenderly, Jesus is calling,\ncalling, O sinner, come home!",
	2: "Why should we tary when Jesus is pleading,\npleading for you and for me?\nWhy should we linger and heed not his mercies,\nmercies for you and for me?",
	3: "Think of the wonderful love he has promised,\npromised for you and for me!\nThough we have sinned, he has mercy and pardon,\npardon for you and for me."
}
song["sda-hymnal/288"] = {
	title: "I Am Going to Calvary",
	tune: "See Saw Saccara Down",
	copyright: "“I Am Going to Calvary” Words copyright © 1983 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music arrangement copyright © 1983 by Charles Strange.",
	1: "I am going to Calvary,\nWould you like to come with Me\nAll the way and back again?\nYou must follow the Leader then.\nYou must follow the Leader,\nYou must follow the Leader,\nAll the way and back again,\nYou must follow the Leader.",
	2: "If I want a thorny crown,\nIf the soldiers knock me down\nCan I really be a king?\nLove will answer everything\nIf you follow the Leader,\nIf you follow the Leader,\nLove will answer everything,\nIf you follow the Leader.",
	3: "When I go along the road,\nI shall lift a heavy load.\nI will carry a cross for you.\nYou will learn to carry it too\nWhen you follow the Leader,\nWhen you follow the Leader,\nYou will learn to carry it too\nWhen you follow the Leader.",
	4: "I am going to stretch My hands,\nReaching out to all the lands.\nCan I really be a king?\nLove’s the lord of everything,\nWhen you follow the Leader,\nWhen you follow the Leader\nLove’s the lord of everything,\nWhen you follow the Leader."
}
song["sda-hymnal/289"] = {
	title: "The Savior Is Waiting",
	tune: "Carmichael",
	copyright: "“The Savior Is Waiting” Copyright © 1958 by Sacred Songs (A Div. of Word, Inc.). All  rights reserved. International copyright secured. Used by permission.",
	1: "The Savior is waiting to enter your heart\nWhy don’t you let Him come in?\nThere’s nothing in this world to keep you apart\nWhat is your answer to Him?\nRefrain\nTime after time\nHe has waited before\nAnd now He is waiting again\nTo see if you are willing\nTo open the door\nOh, how He wants to come in.",
	2: "If you’ll take one step\nToward the Savior, my friend,\nYou’ll find His arms open wide.\nReceive Him, and all of your darkness will end\nWithin your heart He’ll abide."
}
song["sda-hymnal/290"] = {
	title: "Turn Your Eyes Upon Jesus",
	copyright: "“Turn Your Eyes Upon Jesus” Copyright © 1952. Renewal 1980 by John M. Moore. Assigned to Singspiration (ASCAP), Division of the Zondervan Corp. All rights reserved. Used by permission.",
	1: "O soul, are you weary and troubled?\nNo light in the darkness you see?\nThere’s light for a look at the Savior,\nAnd life more abundant and free!\nRefrain\nTurn your eyes upon Jesus,\nLook full in His wonderful face,\nAnd the things of earth will grow strangely dim,\nIn the light of His glory and grace.",
	2: "Through death into life everlasting\nHe passed, and we follow Him there;\nOver us sin no more hath dominion—\nFor more than conquerors we are!",
	3: "His Word shall not fail you—He promised;\nBelieve Him, and all will be well:\nThen go to a world that is dying,\nHis perfect salvation to tell!"
}
song["sda-hymnal/291"] = {
	title: "We Have Not Known Thee",
	tune: "St. Chrysostom",
	1: "We have not known Thee as we ought,\nNor learned Thy wisdom, grace, and power;\nThe things of earth have filed our thoughts,\nAnd trifles of the passing hour.\nLord, give us light Thy truth to see,\nAnd make us wise in knowing Thee.",
	2: "We have not feared Thee as we ought,\nNor bowed beneath Thine awful eye,\nNor guarded deed, and word, and thought,\nRemembering that God was nigh.\nLord, give us faith to know Thee near,\nAnd grant the grace of holy fear.",
	3: "We have not loved Thee as we ought,\nNor cared that we are loved by Thee;\nThy presence we have coldly sought,\nAnd feebly longed Thy face to see.\nLord, give a pure and loving heart\nTo feel and own the love Thou art.",
	4: "We have not served Thee as we ought;\nAlas! the duties left undone,\nThe work with little fervor wrought,\nThe battles lost, or scarcely won!\nLord, give the zeal, and give the might,\nFor Thee to toil, for Thee to fight.",
	5: "When shall we know Thee as we ought,\nAnd fear, and love, and serve aright!\nWhen shall we, out of trial brought,\nBe perfect in the land of light!\nLord, may we day by day prepare\nTo see Thy face, and serve Thee there."
}
song["sda-hymnal/292"] = {
	title: "Jesus, I Come",
	1: "Out of my bondage, sorrow and night,\nJesus, I come, Jesus, I come;\nInto Thy freedom, gladness and light,\nJesus, I come to Thee;\nOut of my sickness into Thy health,\nOut of my want and into Thy wealth,\nOut of my sin and into Thyself,\nJesus, I come to Thee.",
	2: "Out of my shameful failure and loss,\nJesus, I come, Jesus, I come;\nInto the glorious gain of Thy cross,\nJesus, I come to Thee;\nOut of earth’s sorrows into Thy balm,\nOut of life’s storms and into Thy calm,\nOut of distress to jubilant psalm,\nJesus, I come to Thee.",
	3: "Out of unrest and arrogant pride,\nJesus, I come, Jesus, I come;\nInto Thy blessed will to abide,\nJesus, I come to Thee;\nOut of myself to dwell in Thy love,\nOut of despair into raptures above,\nUpward for aye on wings like a dove,\nJesus, I come to Thee.",
	4: "Out of the fear and dread of the tomb,\nJesus, I come, Jesus, I come;\nInto the joy and light of Thy home,\nJesus, I come to Thee;\nOut of the depths of ruin untold,\nInto the peace of Thy sheltering fold,\nEver Thy glorious face to behold,\nJesus, I come to Thee."
}
song["sda-hymnal/293"] = {
	title: "Heavenly Father, Bless Us Now",
	tune: "Aus der Tiefe",
	info: "Alternate tune, TRYGGARE KAN INGEN VARA, No. 101",
	1: "Heavenly Father, bless us now;\nAt the cross of Christ we bow;\nTake our guilt and grief away;\nHear and heal us now, we pray.",
	2: "Now, O Lord, this very hour,\nSend Thy grace and show Thy power;\nWhile we rest upon Thy word,\nCome, and bless us now, O Lord!",
	3: "Mercy now, O Lord, we plead\nIn this hour of utter need;\nTurn us not away unblest;\nCalm our anguish into rest.",
	4: "O Thou loving, blessed One,\nRising oér us like the sun,\nLight and life art Thou within:\nSavior, Thou, from every sin!"
}
song["sda-hymnal/294"] = {
	title: "Power in the Blood",
	composer: "Lewis E. Jones, 1899 (1865 - 1936)",
	poet: "Lewis E. Jones, 1899 (1865 - 1936)",
	1: "Would you be free from the burden of sin?\nThere’s pow’r in the blood, pow’r in the blood;\nWould you o’er evil a victory win?\nThere’s wonderful power in the blood.\nRefrain\nThere is pow’r, pow’r, wonder working pow’r\nIn the blood of the Lamb;\nThere is pow’r, pow’r, wonder working pow’r\nIn the precious blood of the Lamb.",
	2: "Would be free from your passion and pride?\nThere’s pow’r in the blood, pow’r in the blood;\nCome for a cleansing to Calvary’s tide?\nThere’s wonderful power in the blood.",
	3: "Would you do service for Jesus your King?\nThere’s pow’r in the blood, pow’r in the blood;\nWould you live daily His praises to sing?\nThere’s wonderful power in the blood."
}
song["sda-hymnal/295"] = {
	title: "Chief of Sinners",
	poet: "William McComb (1793 - 1870)",
	arranger: "Benjamin Carr, 1826 (1769 - 1831)",
	tune: "Spanish Hymn",
	1: "Chief of sinners though I be,\nJesus shed His blood for me;\nDied that I might live on high,\nDied that I might never die;\nAs the brance is to the vine,\nI am His, and He is mine.",
	2: "O the height of Jesus’ love!\nHigher than the heaven above,\nDeeper than the deepest sea,\nLasting as eternity;\nLove that found me-wondrous thought!\nFound me when I sought Him not!",
	3: "Chief of sinners though I be,\nChrist is all in all to me;\nAll my wants to Him are known,\nAll my sorrows are His own;\nSafe with Him from earthly strife,\nHe sustains the hidden life."
}
song["sda-hymnal/296"] = {
	title: "Lord, I'm Coming Home",
	scripture: "Luke 15:18",
	1: "I’ve wandered far away from God,\nNow I’m coming home;\nThe paths of sin too long I’ve trod;\nLord, I’m coming home.\nRefrain\nComing home, coming home\nNever more to roam;\nOpen wide Thine arms of love;\nLord I’m coming home.",
	2: "I’ve wasted many precious years,\nNow I’m coming home;\nI now repent with bitter tears;\nLord, I’m coming home.",
	3: "I’m tired of sin and straying Lord,\nNow I’m coming home;\nI’ll trust thy love belive thy word;\nLord I’m coming home.",
	4: "My only hope, my only plea,\nNow I’m coming home;\nThat Jesus died, and died for me;\nLord I’m coming home.",
	5: "I need His cleansing blood I know,\nNow I’m coming home;\nO wash me whiter than the snow;\nLord I’m coming home."
}
song["sda-hymnal/297"] = {
	title: "God Be Merciful to Me",
	tune: "Refuge",
	scripture: "Psalm 51",
	1: "God, be merciful to me,\nOn Thy grace I rest my plea;\nPlenteous in compassion Thou,\nBlot out my transgressions now;\nWash me, make me pure within,\nCleanse, O cleanse me from my sin,\nWash me, make me pure within,\nCleanse, O cleanse me from my sin.",
	2: "I am evil, born in sin;\nThou desirest truth within.\nThou alone my Savior art,\nTeach Thy wisdom to my heart;\nMake me pure, Thy grace bestow,\nWash me whiter than the snow,\nMake me pure, Thy grace bestow,\nWash me whiter than the snow.",
	3: "Gracious God, my heart renew,\nMake my spirit right and true;\nCast me not away from Thee,\nLet Thy Spirit dwell in me;\nThy salvation’s joy impart,\nSteadfast make my willing heart,\nThy salvation’s joy impart,\nSteadfast make my willing heart.",
	4: "Sinners then shall learn from me\nAnd return, O God, to Thee;\nSavior, all my guilt remove,\nAnd my tongue shall sing Thy love;\nTouch my silent lips, O Lord,\nAnd my mouth shall praise accord,\nTouch my silent lips, O Lord,\nAnd my mouth shall praise accord."
}
song["sda-hymnal/298"] = {
	title: "I Lay My Sins on Jesus",
	tune: "Bradley",
	info: "Alternate tune, St. Hilda, No. 283",
	copyright: "“I Lay My Sins on Jesus” Music copyright © 1965 by Allen W. Foster",
	1: "I lay my sins on Jesus,\nThe spotless Lamb of God;\nHe bears them all, and frees us\nFrom the accursed load,\nFrom the accursed load.",
	2: "I bring my guilt to Jesus,\nTo wash my crimson stains\nWhite in His blood most precious\nTill not a stain remains,\nTill not a stain remains.",
	3: "I lay my wants on Jesus,\nAll fullness dwells in Him;\nHe healeth my diseases\nHe doth my soul redeem,\nHe doth my soul redeem",
	4: "I long to be with Jesus,\nMeek, loving, lowly, mild;\nI long to be like Jesus\nThe Father’s holy child,\nThe Father’s holy child.",
	5: "I long to be with Jesus\nAmid the heav’nly throng,\nTo sing with them His praises,\nAnd learn the angels’ song.\nAnd learn the angels’ song."
}
song["sda-hymnal/299"] = {
	title: "Forgive Our Sins as We Forgive",
	tune: "Detroit",
	scripture: "Matt. 6:12",
	copyright: "“Forgive Our Sins as We Forgive” Words by permission of Oxford University Press. Arrangement copyright © 1978 Lutheran Book of Worship. Used by permission of Augsburg Publishing House.",
	1: "“Forgive our sins as we forgive,”\nYou taught us, Lord, to pray;\nBut You alone can grant us grace\nTo live the words we say.",
	2: "How can Your pardon reach and bless\nThe unforgiving heart\nThat broods on wrongs and will not let\nOld bitterness depart?",
	3: "In blazing light Your cross reveals\nThe truth we dimly knew:\nHow trifling others’ debts to us;\nHow great our debt to You!",
	4: "Lord, cleanse the depths within our souls\nAnd bid resentment cease;\nThen, by Your mercy reconciled,\nOur lives will spread Your peace."
}
song["sda-hymnal/300"] = {
	title: "Rock of Ages",
	tune: "Toplady",
	info: "Alternate tune, GETHSEMANE, No. 157",
	1: "Rock of Ages, cleft for me,\nLet me hide myself in Thee;\nLet the water and the blood,\nFrom Thy riven side which flowed,\nBe of sin the double cure,\nCleanse me from its guilt and power.",
	2: "Not the labor of my hands\nCan fulfill Thy law’s demands;\nCould my zeal no respite know,\nCould my tears forever flow,\nAll for sin could not atone;\nThou must save, and Thou alone.",
	3: "When my pilgrimage I close;\nVictor o’er the last of foes,\nWhen I soar to worlds unknown,\nAnd behold Thee on Thy throne,\nRock of Ages, cleft for me,\nLet me hide myself in Thee."
}
song["sda-hymnal/301"] = {
	title: "Near, Still Nearer",
	1: "Nearer, still nearer, close to Thy heart,\nDraw me, my Savior, so precious Thou art;\nFold me, O fold me close to Thy breast,\nShelter me safe in that haven of rest,\nShelter me safe in that haven of rest.",
	2: "Nearer, still nearer, nothing I bring,\nNought as an offering to Jesus my King-\nOnly my sinful now contrite heart,\nGrant me the cleansing Thy blood doth impart,\nGrant me the cleansing Thy blood doth impart.",
	3: "Nearer, still nearer, Lord, to be Thine;\nSin, with its follies, I gladly resign,\nAll of its pleasures, pomp and its pride;\nGive me but Jesus, my Lord crucified,\nGive me but Jesus, my Lord crucified.",
	4: "Nearer, still nearer, while life shall last;\nTill safe in glory my anchor is cast;\nThrough endless ages, ever to be,\nNearer, my Savior, still nearer to Thee,\nNearer, my Savior, still nearer to Thee."
}
song["sda-hymnal/302"] = {
	title: "Deeper Yet",
	tune: "Deeper Yet",
	1: "In the blood from the cross,\nI have been washed from sin;\nBut to be free from dross,\nStill I would enter in.\nRefrain\nDeeper yet, deeper yet,\nInto the crimson flood;\nDeeper yet, deeper yet,\nUnder the precious blood.",
	2: "Day by day, hour by hour,\nBlessings are sent to me;\nBut for more of His power,\nEver my prayer shall be.",
	3: "Near to Christ I would live,\nFollowing Him each day;\nWhat I ask He will give;\nSo then with faith I pray.",
	4: "Now I have peace, sweet peace,\nWhile in this world of sin;\nBut to pray I’ll not cease\nTill I am pure within."
}
song["sda-hymnal/303"] = {
	title: "Beneath the Cross of Jesus",
	tune: "St. Christopher",
	scripture: "Prov. 14:26",
	1: "Beneath the cross of Jesus\nI fain would take my stand,\nthe shadow of a mighty rock\nwithin a weary land;\na home within the wilderness,\na rest upon the way,\nfrom the burning of the noontide heat,\nand the burden of the day.",
	2: "Upon that cross of Jesus\nmine eye at times can see\nthe very dying form of One\nwho suffered there for me;\nand from my stricken heart with tears\ntwo wonders I confess:\nthe wonders of redeeming love\nand my unworthiness.",
	3: "I take, O cross, thy shadow\nfor my abiding place;\nI ask no other sunshine than\nthe sunshine of his face;\ncontent to let the world go by,\nto know no gain nor loss,\nmy sinful self my only shame,\nmy glory all the cross."
}
song["sda-hymnal/304"] = {
	title: "Faith of Our Fathers",
	1: "Faith of our fathers, living still,\nIn spite of dungeon, fire and sword;\nO how our hearts beat high with joy\nWhenever we hear that glorious Word!\nFaith of our fathers, holy faith!\nWe will be true to thee till death.",
	2: "Our fathers, chained in prisons dark,\nWere still in heart and conscience free:\nHow sweet would be their children’s fate.\nIf they, like them, could die for thee!\nFaith of our fathers, holy faith!\nWe will be true to thee till death.",
	3: "Faith of our fathers, we will love\nBoth friend and foe in all our strife;\nAnd preach Thee, too, as love knows how\nBy kindly words and virtuous life.\nFaith of our fathers, holy faith!\nWe will be true to thee till death."
}
song["sda-hymnal/305"] = {
	title: "Give Me Jesus",
	copyright: "“Give Me Jesus” Arrangement copyright © 1984 by Alma Blackmon.",
	1: "In the morning, when I rise\nIn the morning, when I rise\nIn the morning, when I rise\nRefrain\nGive me Jesus.\nGive me Jesus,\nGive me Jesus.\nYou can have all this world,\nJust give me Jesus.",
	2: "Dark mid-night was my cry,\nDark mid-night was my cry,\nDark midnight was my cry,\nGive me Jesus.",
	3: "Just about the break of day,\nJust about the break of day,\nJust about the break of day,\nGive me Jesus.",
	4: "Oh, when I come to die,\nOh, when I come to die,\nOh, when I come to die,\nGive me Jesus."
}
song["sda-hymnal/306"] = {
	title: "Draw Me Nearer",
	poet: "Fanny J. Crosby, 1875 (1820 - 1915)",
	1: "I am thine, O Lord, I have heard thy voice,\nand it told thy love to me;\nbut I long to rise in the arms of faith\nand be closer drawn to thee.\nRefrain\nDraw me nearer, nearer, blessed Lord,\nto the cross where thou hast died.\nDraw me nearer, nearer, nearer, blessed Lord,\nto thy precious, bleeding side.",
	2: "Consecrate me now to thy service, Lord,\nby the power of grace divine;\nlet my soul look up with a steadfast hope,\nand my will be lost in thine.",
	3: "O the pure delight of a single hour\nthat before thy throne I spend,\nwhen I kneel in prayer, and with thee, my God,\nI commune as friend with friend!"
}
song["sda-hymnal/307"] = {
	title: "I Am Coming to the Cross",
	1: "I am coming to the cross;\nI am poor and weak and blind;\nI am counting all but dross;\nI shall full salvation find.\nRefrain\nI am trusting, Lord, in Thee.\nO thou Lamb of Calvary;\nHumbly at Thy cross I bow.\nSave me, Jesus, save me now.",
	2: "Long my heart has sighed for Thee;\nLong has evil reigned within;\nJesus sweetly speaks to me:\n“I will cleanse you from all sin.”",
	3: "Here I give my all to Thee:\nFriends and time and earthly store;\nSoul and body Thine to be,\nWholly Thine forevermore.",
	4: "Jesus comes! He fills my soul!\nPerfected in Him I am;\nI am every whit made whole:\nGlory, glory to the Lamb!"
}
song["sda-hymnal/308"] = {
	title: "Wholly Thine",
	composer: "F. E. Belden, 1886 (1858 - 1945)",
	poet: "F. E. Belden, 1886 (1858 - 1945)",
	1: "I would be, dear Savior, wholly Thine;\nTeach me how, teach me how;\nI would do Thy will, O Lord, not mine;\nHelp me, help me now.\nRefrain\nWholly Thine, wholly Thine,\nWholly Thine, this is my vow;\nWholly Thine, wholly Thine,\nWholly Thine, O Lord, just now.",
	2: "What is worldly pleasure, wealth or fame,\nWithout Thee, without Thee?\nI will leave them all for Thy dear Name,\nThis my wealth shall be.",
	3: "As I cast earth’s transient joys behind,\nCome Thou near, come Thou near;\nIn Thy presence all in all I find,\n’Tis my comfort here."
}
song["sda-hymnal/309"] = {
	title: "I Surrender All",
	1: "All to Jesus I surrender;\nall to him I freely give;\nI will ever love and trust him,\nin his presence daily live.\nRefrain\nI surrender all, I surrender all,\nall to thee, my blessed Savior,\nI surrender all.",
	2: "All to Jesus I surrender;\nhumbly at his feet I bow,\nworldly pleasures all forsaken;\ntake me, Jesus, take me now.",
	3: "All to Jesus I surrender;\nmake me, Savior, wholly thine;\nfill me with thy love and power;\ntruly know that thou art mine.",
	4: "All to Jesus I surrender;\nnow I feel the sacred flame.\nO the joy of full salvation!\nGlory, glory, to his name!"
}
song["sda-hymnal/310"] = {
	title: "I Would Draw Nearer to Jesus",
	1: "I would draw nearer to Jesus, In His sweet presence abide,\nConstantly trying to serve Him, Safe and secure at His side.\nI would draw nearer to Jesus, I would draw nearer to Him;\nFully surrendered each moment, I would draw nearer to Him.",
	2: "I would draw nearer to Jesus, Nothing withholding from Him,\nKnowing He loves to be gracious, I would draw nearer to Him.\nI would draw nearer to Jesus, I would draw nearer to Him;\nFully surrendered each moment, I would draw nearer to Him.",
	3: "I would draw nearer to Jesus, Seeking Hi strength to be true,\nWilling to tell of His goodness, Gladly His blest will to do.\nI would draw nearer to Jesus, I would draw nearer to Him;\nFully surrendered each moment, I would draw nearer to Him."
}
song["sda-hymnal/311"] = {
	title: "I Would Be Like Jesus",
	copyright: "“I Would Be Like Jesus” Copyright 1912 by Hope Publishing Co. © renewed 1940, The Rodeheaver Co. (A Div. of Word, Inc.). All rights reserved. International copyright secured. Used by permission.",
	1: "Earthly pleasures vainly call me;\nI would be like Jesus;\nNothing worldly shall enthrall me;\nI would be like Jesus.\nRefrain\nBe like Jesus, this my song,\nIn the home and in the throng;\nBe like Jesus, all day long!\nI would be like Jesus.",
	2: "He has broken every fetter,\nI would be like Jesus;\nThat my soul may serve Him better,\nI would be like Jesus.",
	3: "All the way from earth to glory,\nI would be like Jesus;\nTelling o’er and o’er the story,\nI would be like Jesus.",
	4: "That in Heaven He may meet me,\nI would be like Jesus;\nThat His words “Well done” may greet me,\nI would be like Jesus."
}
song["sda-hymnal/312"] = {
	title: "Near the Cross",
	poet: "Fanny J. Crosby, 1869 (1820 - 1915)",
	1: "Jesus, keep me near the cross;\nthere a precious fountain,\nfree to all, a healing stream,\nflows from Calvary’s mountain.\nRefrain\nIn the cross, in the cross,\nbe my glory ever,\ntill my raptured soul shall find\nrest beyond the river.",
	2: "Near the cross, a trembling soul,\nlove and mercy found me;\nthere the bright and morning star\nsheds its beams around me.",
	3: "Near the cross! O Lamb of God,\nbring its scenes before me;\nhelp me walk from day to day\nwith its shadow o’er me.",
	4: "Near the cross I’ll watch and wait,\nhoping, trusting ever,\ntill I reach the golden strand"
}
song["sda-hymnal/313"] = {
	title: "Just as I Am",
	tune: "Saffron Walden",
	info: "Alternate tune, WOODWORTH, No. 314",
	copyright: "“Just as I Am” Music by permission of Oxford University Press.",
	1: "Just as I am, without one plea,\nBut that Thy blood was shed for me,\nAnd that Thou bidst me come to Thee,\nO Lamb of God, I come, I come.",
	2: "Just as I am, and waiting not\nTo rid my soul of one dark blot,\nTo Thee whose blood can cleanse each spot,\nO Lamb of God, I come, I come.",
	3: "Just as I am, though tossed about\nWith many a conflict, many a doubt,\n“Fightings within, and fears without,”\nO Lamb of God, I come, I come.",
	4: "Just as I am, poor, wretched, blind;\nSight, riches, healing of the mind,\nYea, all I need in Thee to find,\nO Lamb of God, I come, I come.",
	5: "Just as I am, Thou wilt receive,\nWilt welcome, pardon, cleanse, relieve;\nBecause Thy promise I believe,\nO Lamb of God, I come, I come.",
	6: "Just as I am, Thy love I own\nHas broken every barrier down;\nNow, to be Thine, and Thine alone,\nO Lamb of God, I come, I come."
}
song["sda-hymnal/314"] = {
	title: "Just as I Am",
	tune: "Woodworth",
	info: "Alternate tune, SAFFRON WALDEN, and other stanzas, No. 313",
	1: "Just as I am, without one plea,\nbut that thy blood was shed for me,\nand that thou bidst me come to thee,\nO Lamb of God, I come, I come.",
	2: "Just as I am, and waiting not\nto rid my soul of one dark blot,\nto thee whose blood can cleanse each spot,\nO Lamb of God, I come, I come.",
	3: "Just as I am, though tossed about\nwith many a conflict, many a doubt,\nfightings and fears within, without,\nO Lamb of God, I come, I come.",
	4: "Just as I am, poor, wretched, blind;\nsight, riches, healing of the mind,\nyea, all I need in thee to find,\nO Lamb of God, I come, I come.",
	5: "Just as I am, thou wilt receive,\nwilt welcome, pardon, cleanse, relieve;\nbecause thy promise I believe,\nO Lamb of God, I come, I come.",
	6: "Just as I am, thy love I own\nhath broken every barrier down;\nnow, to be thine, and thine alone,\nO Lamb of God, I come, I come."
}
song["sda-hymnal/315"] = {
	title: "O for a Closer Walk!",
	tune: "Manoah",
	1: "O for a closer walk with God,\nA calm and heavenly frame,\nA light to shine upon the road\nThat leads me to the Lamb!",
	2: "Return, O holy Dove, return,\nSweet messenger of rest!\nI hate the sins that made Thee mourn\nAnd drove Thee from my breast.",
	3: "What peaceful hours I once enjoyed!\nHow sweet their memory still!\nBut they have left an aching void\nThe world can never fill.",
	4: "The dearest idol I have known,\nWhat-e’er that idol be,\nHelp me to tear it from Thy throne,\nAnd worship only Thee."
}
song["sda-hymnal/316"] = {
	title: "Live Out Thy Life Within Me",
	tune: "Aurelia",
	info: "Alternate tuning, EWING, No. 429",
	1: "Live out Thy life within me, O Jesus, King of kings!\nBe Thou Thyself the answer To all my questionings;\nLive out Thy life within me, In all things have Thy way!\nI, the transparent medium Thy glory to display.",
	2: "The temple has been yielded, And purified of sin;\nLet Thy Shekinah glory Now shine forth from within,\nAnd all the earth keep silence, The body henceforth be\nThy silent, gentle servant, Moved only as by Thee.",
	3: "Its members every moment Held subject to Thy call,\nReady to have Thee use them, Or not be used at all;\nHeld without restless longing, Or strain, or stress, or fret\nOr chafings at Thy dealings, Or thoughts of vain regret.",
	4: "But restful, calm, and pliant, From bend and bias free,\nAwaiting Thy decision, When Thou hast need of me.\nLive out Thy life within me, O Jesus, King of kings!\nBe Thou the glorious answer To all my questionings."
}
song["sda-hymnal/317"] = {
	title: "Lead Me to Calvary",
	copyright: "“Lead Me to Calvary” Copyright 1921. Renewal 1949 extended by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "King of my life, I crown Thee now,\nThine shall the glory be;\nLest I forget Thy thorn crowned brow,\nLead me to Calvary.\nRefrain\nLest I forget Gethsemane,\nLest I forget Thine agony;\nLest I forget Thy love for me,\nLead me to Calvary.",
	2: "Show me the tomb where Thou wast laid,\nTenderly mourned and wept;\nAngels in robes of light arrayed\nGuarded Thee whilst Thou slept.",
	3: "Let me like Mary, through the gloom,\nCome with a gift to Thee;\nShow to me now the empty tomb,\nLead me to Calvary.",
	4: "May I be willing, Lord, to bear\nDaily my cross for Thee;\nEven Thy cup of grief to share,\nThou hast borne all for me."
}
song["sda-hymnal/318"] = {
	title: "Whiter Than Snow",
	1: "Lord Jesus, I long to be perfectly whole;\nI want Thee forever to live in my soul;\nBreak down every idol, cast out every foe;\nNow wash me, and I shall be whiter than snow.\nRefrain\nWhiter than snow, yes, whiter than snow;\nNow wash me, and I shall be whiter than snow.",
	2: "Lord Jesus, look down from Thy throne in the skies,\nAnd help me to make a complete sacrifice;\nI give up myself, and whatever I know;\nNow wash me, and I shall be whiter than snow.",
	3: "Lord Jesus, for this I most humbly entreat;\nI wait, blessed Lord, at Thy crucified feet,\nBy faith, for my cleansing; I see Thy blood flow;\nNow wash me, and I shall be whiter than snow.",
	4: "Lord Jesus, Thou seest I patiently wait;\nCome now, and within me a new heart create;\nTo those who have sought Thee,\nThou never said’st No;\nNow wash me, and I shall be whiter than snow."
}
song["sda-hymnal/319"] = {
	title: "Lord, I Want to Be a Christian",
	1: "Lord, I want to be a Christian in my heart, in my heart,\nLord, I want to be a Christian in my heart, in my heart.\nIn my heart, in my heart,\nLord, I want to be a Christian in my heart, in my heart.",
	2: "Lord, I want to be more loving in my heart, in my heart,\nLord, I want to be more loving in my heart, in my heart.\nIn my heart, in my heart,\nLord, I want to be more loving in my heart, in my heart.",
	3: "Lord, I want to be more holy in my heart, in my heart,\nLord, I want to be more holy in my heart, in my heart.\nIn my heart, in my heart,\nLord, I want to be more holy in my heart, in my heart.",
	4: "Lord, I want to be more like Jesus in my heart, in my heart,\nLord, I want to be more like Jesus in my heart, in my heart.\nIn my heart, in my heart,\nLord, I want to be more like Jesus in my heart, in my heart."
}
song["sda-hymnal/320"] = {
	title: "Lord of Creation",
	tune: "Slane",
	info: "Unison setting, No. 547",
	copyright: "“Lord of Creation” Words by permission of Mrs. J. Tyrrell. Arrangement copyright © 1984 by Melvin West.",
	1: "Lord of creation, to you be all praise!\nMost mighty your working, most wondrous your ways!\nYour glory and might are beyond us to tell,\nAnd yet in the heart of the humble you dwell.",
	2: "Lord of all power, I give you my will,\nIn joyful obedience your tasks to fulfill.\nYour bondage is freedom; your service is song;\nAnd held in your keeping, my weakness is strong.",
	3: "Lord of all wisdom, I give you my mind,\nRich truth that surpasses man’s knowledge to find;\nWhat eye has not seen and what ear has not heard\nIs taught by your spirit and shines from your word.",
	4: "Lord of all being, I give you my all,\nIf I ever disown you, I stumble and fall;\nBut led in your service your word to obey,\nI’ll walk in your freedom to the end of the way."
}
song["sda-hymnal/321"] = {
	title: "My Jesus, I Love Thee",
	tune: "Gordon",
	1: "My Jesus, I love thee, I know thou art mine;\nfor thee all the follies of sin I resign.\nMy gracious Redeemer, my Savior art thou;\nif ever I loved thee, my Jesus, ’tis now.",
	2: "I love thee because thou hast first loved me,\nand purchased my pardon on Calvary’s tree;\nI love thee for wearing the thorns on thy brow;\nif ever I loved thee, my Jesus, ’tis now.",
	3: "I’ll love Thee in life, I will love Thee ’til death,\nAnd praise Thee as long as Thou lendest me breath;\nAnd say when the death dew lies cold on my brow,\nif ever I loved thee, my Jesus, ’tis now.",
	4: "In mansions of glory and endless delight;\nI’ll ever adore thee in heaven so bright;\nI’ll sing with the glittering crown on my brow;\nif ever I loved thee, my Jesus, ’tis now."
}
song["sda-hymnal/322"] = {
	title: "Nothing Between",
	copyright: "“Nothing Between” Arrangement copyright © 1984 by Wayne Hooper",
	1: "Nothing between my soul and my Savior,\nnaught of this world’s delusive dream;\nI have renounced all sinful pleasure;\nJesus is mine, there’s nothing between.\nRefrain\nNothing between my soul and my Savior,\nso that his blessed face may be seen;\nnothing preventing the least of his favor;\nkeep the way clear! let nothing between.",
	2: "Nothing between, like worldly pleasure;\nhabits of life, though harmless they seem,\nmust not my heart from him ever sever;\nhe is my all, there’s nothing between.",
	3: "Nothing between, e’en many hard trials,\nthough the whole world against me convene;\nwatching with prayer and much self denial,\nI’ll triumph at last, there’s nothing between."
}
song["sda-hymnal/323"] = {
	title: "O for a Heart to Praise My God!",
	tune: "Beatitudo",
	1: "O for a heart to praise my God,\nA heart from sin set free,\nA heart that always feels Thy blood\nSo freely shed for me.",
	2: "A heart resigned, submissive, meek,\nMy dear Redeemer’s throne,\nWhere only Christ is heard to speak,\nWhere Jesus reigns alone.",
	3: "A heart in every thought renewed\nAnd full of love divine,\nPerfect and right and pure and good,\nA copy, Lord, of Thine.",
	4: "Thy nature, gracious Lord, impart;\nCome quickly from above;\nWrite Thy new name upon my heart,\nThy new, best name of Love."
}
song["sda-hymnal/324"] = {
	title: "Just as I Am, Thine Own to Be",
	tune: "Just as I Am",
	1: "Just as I am, Thine own to be,\nFriend of the young, who lovest me,\nTo consecrate myself to Thee,\nO Jesus Christ, I come.",
	2: "In the glad morning of my day,\nMy life to give, my vows to pay,\nWith no reserve and no delay,\nWith all my heart I come.",
	3: "I would live ever in the light,\nI would work ever for the right;\nI would serve Thee with all my might;\nTherefore, to Thee I come.",
	4: "Just as I am, young, strong, and free,\nTo be the best that I can be\nFor truth, and righteousness, and Thee,\nLord of my life, I come."
}
song["sda-hymnal/325"] = {
	title: "Jesus, I My Cross Have Taken",
	tune: "Ellesdie",
	1: "Jesus, I my cross have taken, all to leave and follow Thee.\nAll things else I have forsaken, Thou from hence my all shall be.\nPerish every fond ambition, all I’ve sought or hoped or known.\nYet how rich is my condition! God and Heaven are still mine own.",
	2: "Let the world despise and leave me, they have left my Savior, too.\nHuman hearts and looks deceive me; Thou art faithful, Thou art true.\nO, ‘tis not in grief to harm me, while thy love is left to me;\nO, ‘twere not in joy to charm me, If that love be hid from me.",
	3: "Soul, then know thy full salvation; rise o’er sin, and fear, and care;\nJoy to find in every station something still to do or bear:\nThink what Spirit dwells within thee; Think what Father’s smiles are thine;\nThink that Jesus died to win thee, child of heaven, canst thou repine?",
	4: "Haste then on from grace to glory, armed by faith, and winged by prayer,\nHeaven’s eternal day’s before thee, God’s own hand shall guide thee there.\nSoon shall close thy earthly mission, soon shall pass thy pilgrim days;\nHope soon change to glad fruition, faith to sight, and prayer to praise."
}
song["sda-hymnal/326"] = {
	title: "Open My Eyes That I May See",
	1: "Open my eyes, that I may see\nGlimpses of truth Thou hast for me;\nPlace in my hands the wonderful key\nThat shall unclasp and set me free.\nSilently now I wait for Thee,\nReady, my God, Thy will to see;\nOpen my eyes,\nillumine me, Spirit Divine!",
	2: "Open my ears that I may hear\nVoices of truth Thou sendest clear;\nand while the wavenotes fall on my ear,\nEverything false will disappear.\nSilently now I wait for Thee,\nReady, my God, Thy will to see;\nOpen my ears, illumine me, Spirit Divine!",
	3: "Open my mouth, and let me bear\nGladly the warm truth everywhere;\nOpen my heart, and let me prepare\nLove with Thy children thus to share.\nSilently now I wait for Thee,\nReady, my God, Thy will to see;\nOpen my heart, illumine me, Spirit Divine!"
}
song["sda-hymnal/327"] = {
	title: "I'd Rather Have Jesus",
	copyright: "“I'd Rather Have Jesus” Copyright © 1922, 1950. © renewed by Chancel Music, Inc. Assigned to the Rodeheaver Co. (A Div. of Word, Inc.) All rights reserved. International copyright secured. Used by permission.",
	1: "I’d rather have Jesus than silver or gold;\nI’d rather be His than have riches untold;\nI’d rather have Jesus than houses or land;\nI’d rather be led by His nail-pierced hand:\nRefrain\nThan to be the king of a vast domain or be held in sin’s dread sway!\nI’d rather have Jesus than anything this world affords today.",
	2: "I’d rather have Jesus than men’s applause;\nI’d rather be faithful to His dear cause;\nI’d rather have Jesus than world-wide fame;\nI’d rather be true to His holy name:",
	3: "He’s fairer than lilies of rarest bloom;\nHe’s sweeter than honey from out the comb;\nHe’s all that my hungering spirit needs –\nI’d rather have Jesus and let Him lead:"
}
song["sda-hymnal/328"] = {
	title: "Must Jesus Bear the Cross Alone",
	tune: "Maitland",
	1: "Must Jesus bear the cross alone,\nand all the world go free?\nNo, there’s a cross for everyone,\nand there’s a cross for me.",
	2: "The consecrated cross I’ll bear\ntill He shall set me free;\nand then go home my crown to wear,\nfor there’s a crown for me.",
	3: "Upon the crystal pavement, down\nat Jesus’ pierced feet,\nwith joy I’ll cast my golden crown,\nand His dear name repeat."
}
song["sda-hymnal/329"] = {
	title: "Take the World, but Give Me Jesus",
	poet: "Fanny J. Crosby (1820 - 1915)",
	1: "Take the world, but give me Jesus;\nAll its Joys are but a name,\nBut His love abideth ever,\nThrough eternal years the same.\nRefrain\nOh, the height and depth of mercy!\nOh, the length and breadth of love!\nOh, the fullness of redemption,\nPledge of endless life above.",
	2: "Take the world, but give me Jesus,\nSweetest comfort of my soul;\nWith my Savior watching o’er me,\nI can sing, though billows roll.",
	3: "Take the world, but give me Jesus;\nLet me view his constant smile;\nThen throughout my pilgrim journey\nLight will cheer me all the while.",
	4: "Take the world, but give me Jesus;\nIn His cross my trust shall be,\nTill, with clearer, brighter vision\nFace to face my Lord I see."
}
song["sda-hymnal/330"] = {
	title: "Take My Life and Let It Be",
	1: "Take my life, and let it be\nConsecrated, Lord, to Thee;\nTake my hands, and let them move\nAt the impulse of Thy love;\nAt the impulse of Thy love.",
	2: "Take my feet, and let them be\nSwift and beautiful for Thee.\nTake my voice, and let me sing\nAlways, only, for my King;\nAlways, only, for my King.",
	3: "Take my lips, and let them be\nFilled with messages from Thee.\nTake my silver and my gold:\nNot a mite would I withhold;\nNot a mite would I withhold.",
	4: "Take my will, and make it Thine,\nIt shall be no longer mine;\nTake my heart, it is Thine own,\nIt shall be Thy royal throne.\nIt shall be Thy royal throne.",
	5: "Take my love, my Lord, I pour\nAt Thy feet its treasure store;\nTake myself, and I will be,\nEver, only, all for Thee.\nEver, only, all for Thee."
}
song["sda-hymnal/331"] = {
	title: "O Jesus, I Have Promised",
	tune: "Angel's Story",
	1: "O Jesus, I have promised\nto serve thee to the end;\nbe thou forever near me,\nmy Master and my friend.\nI shall not fear the battle\nif thou art by my side,\nnor wander from the pathway\nif thou wilt be my guide.",
	2: "O let me feel thee near me!\nThe world is ever near;\nI see the sights that dazzle,\nthe tempting sounds I hear;\nmy foes are ever near me,\naround me and within;\nbut Jesus, draw thou nearer,\nand shield my soul from sin.",
	3: "O Jesus, thou hast promised\nto all who follow thee\nthat where thou art in glory\nthere shall thy servant be.\nAnd Jesus, I have promised\nto serve thee to the end;\nO give me grace to follow,\nmy Master and my Friend."
}
song["sda-hymnal/332"] = {
	title: "The Cleansing Wave",
	1: "Oh now I see the crimson wave,\nThe fountain deep and wide;\nJesus, my Lord, mighty to save,\nPoints to His wounded side.\nRefrain\nThe cleansing stream I see, I see,\nI plunge, and O, it cleanseth me!\nO praise the Lord! it cleanseth me,\nIt cleanseth me, yes, cleanseth me.",
	2: "I see the new creation rise,\nI hear the speaking blood;\nIt speaks polluted nature dies,\nSinks ‘neath the cleansing flood.",
	3: "I rise to walk in heaven’s own light,\nAbove the world and sin;\nWith heart made pure and garments white,\nAnd Christ enthroned within.",
	4: "Amazing grace! ’tis heaven below\nTo feel the blood applied,\nAnd Jesus, only Jesus, know,\nMy Jesus crucified."
}
song["sda-hymnal/333"] = {
	title: "On Jordan's Banks the Baptist's Cry",
	tune: "Puer nobis",
	scripture: "Luke 3:2-4",
	1: "On Jordan’s banks the Baptist’s cry\nAnnounces that the Lord is nigh;\nAwake and hearken, for he brings\nGlad tidings of the King of kings!",
	2: "Then cleansed be every life from sin;\nMake straight the way for God within,\nAnd let us all our hearts prepare\nFor Christ to come and enter there.",
	3: "We hail You as our Savior, Lord,\nOur refuge and our great reward;\nWithout Your grace we waste away\nLike flow’rs that wither and decay.",
	4: "Stretch forth Your hand, our health restore,\nAnd make us rise to fall no more;\nOh, let Your face upon us shine\nAnd fill the world with love divine."
}
song["sda-hymnal/334"] = {
	title: "Come, Thou Fount of Every Blessing",
	tune: "Nettleton",
	1: "Come, thou Fount of every blessing,\nTune my heart to sing thy grace;\nStreams of mercy, never ceasing,\nCall for songs of loudest praise.\nTeach me ever to adore Thee,\nMay I still Thy goodness prove,\nWhile the hope of endless glory\nFills my heart with joy and love.",
	2: "Here I raise mine Ebenezer;\nHither by thy help I’ve come;\nAnd I hope, by thy good pleasure,\nSafely to arrive at home.\nJesus sought me when a stranger,\nWandering from the fold of God;\nHe, to rescue me from danger,\nInterposed his precious blood.",
	3: "O, to grace how great a debtor\nDaily I’m constrained to be!\nLet thy goodness, like a fetter,\nBind me closer still to thee.\nProne to wander, Lord, I feel it,\nProne to leave the God I love;\nHere’s my heart–O, take and seal it,\nSeal it for thy courts above."
}
song["sda-hymnal/335"] = {
	title: "What a Wonderful Savior",
	1: "Christ has for sin atonement made,\nWhat a wonderful Saviour!\nWe are redeemed! the price is paid!\nWhat a wonderful Savior!\nRefrain\nWhat a wonderful Savior\nis Jesus, my Jesus!\nWhat a wonderful Savior\nis Jesus, my Lord!",
	2: "I praise Him for the cleansing blood,\nWhat a wonderful Saviour!\nThat reconciled my soul to God;\nWhat a wonderful Saviour!",
	3: "He walks beside me all the way,\nWhat a wonderful Saviour!\nAnd keeps me faithful day by day;\nWhat a wonderful Saviour!",
	4: "He gives me overcoming power,\nWhat a wonderful Saviour!\nAnd triumph in each trying hour;\nWhat a wonderful Saviour!"
}
song["sda-hymnal/336"] = {
	title: "There Is a Fountain",
	tune: "Cleansing Fountain",
	1: "There is a fountain filled with blood drawn from Emmanuel’s veins;\nAnd sinners plunged beneath that flood lose all their guilty stains.\nLose all their guilty stains, lose all their guilty stains;\nAnd sinners plunged beneath that flood lose all their guilty stains.",
	2: "The dying thief rejoiced to see that fountain in his day;\nAnd there may I, though vile as he, wash all my sins away.\nWash all my sins away, wash all my sins away;\nAnd there may I, though vile as he, wash all my sins away.",
	3: "Thou dying Lamb, Thy precious blood shall never lose its power\nTill all the ransomed church of God are saved, to sin no more.\nAre saved, to sin no more, are saved, to sin no more;\nTill all the ransomed church of God are saved, to sin no more.",
	4: "E’er since, by faith, I saw the stream Thy flowing wounds supply,\nRedeeming love has been my theme, and shall be till I die.\nAnd shall be till I die, and shall be till I die;\nRedeeming love has been my theme, and shall be till I die.",
	5: "Lord, I believe Thou hast prepared, unworthy though I be,\nFor me a blood-bought free reward, a golden harp for me!\nA golden harp for me! A golden harp for me!\nFor me a blood-bought free reward, a golden harp for me!",
	6: "There in a nobler, sweeter song, I’ll sing Thy power to save,\nWhen this poor lisping, stammering tongue is ransomed from the the grave.\nIs ransomed from the grave, is ransomed from the grace;\nWhen this poor lisping, stammering tongue is ransomed from the the grave."
}
song["sda-hymnal/337"] = {
	title: "Redeemed!",
	poet: "Fanny J. Crosby, 1882 (1820 - 1915)",
	scripture: "Lamentations 3:58",
	1: "Redeemed, how I love to proclaim it!\nRedeemed by the blood of the Lamb;\nRedeemed through His infinite mercy,\nHis child and forever I am.\nRefrain\nRedeemed, redeemed,\nRedeemed by the blood of the Lamb;\nRedeemed, redeemed,\nHis child and forever I am.",
	2: "Redeemed, and so happy in Jesus,\nNo language my rapture can tell;\nI know that the light of His presence\nWith me doth continually dwell.",
	3: "I know there’s a crown that is waiting\nIn yonder bright mansion for me;\nAnd soon, with the spirit made perfect,\nAt home with the Lord I shall be."
}
song["sda-hymnal/338"] = {
	title: "Redeemed!",
	poet: "Fanny J. Crosby, 1882 (1820 - 1915)",
	scripture: "Lamentations 3:58",
	1: "Redeemed, how I love to proclaim it!\nRedeemed by the blood of the Lamb;\nRedeemed thro’ His infinite mercy,\nHis child, and forever, I am.\nRefrain\nRedeemed, redeemed,\nRedeemed by the blood of the Lamb;\nRedeemed, how I love to proclaim it!\nHis child and forever I am.",
	2: "I think of my blessed Redeemer,\nI think of Him all the day long;\nI sing, for I cannot be silent;\nHis love is the theme of my song.",
	3: "I know I shall see in His beauty\nThe King in whose law I delight,\nWho lovingly guardeth my footsteps,\nAnd giveth me songs in the night."
}
song["sda-hymnal/339"] = {
	title: "God Is My Strong Salvation",
	tune: "Wie lieblich ist der Maien",
	scripture: "Psalm 27:1-3, 14",
	1: "God is my strong salvation,\nWhat foe have I to fear?\nIn darkness and temptation,\nMy Light, my Help, is near:\nThough hosts encamp around me,\nFirm in the fight I stand;\nWhat terror can confound me,\nWith God at my right hand?",
	2: "Place on the reliance;\nMy soul with courage wait;\nHis truth be thine affiance,\nWhen faint and desolate.\nHis might thy heart shall strengthen,\nHis love thy joy increase;\nMercy thy days shall lengthen;\nThe Lord will give thee peace."
}
song["sda-hymnal/340"] = {
	title: "Jesus Saves",
	1: "We have heard a joyful sound,\nJesus saves, Jesus saves;\nSpread the gladness all around,\nJesus saves, Jesus saves;\nBear the news to every land,\nClimb the steeps and cross the waves,\nOnward, ’tis our Lord’s command,\nJesus saves, Jesus saves.",
	2: "Waft it on the rolling tide,\nJesus saves, Jesus saves;\nTell to sinners, far and wide,\nJesus saves, Jesus saves;\nSing, ye islands of the sea.\nEcho back, ye ocean caves,\nEarth shall keep her jubilee,\nJesus saves, Jesus saves.",
	3: "Sing above the battle’s strife,\nJesus saves, Jesus saves;\nBy His death and endless life,\nJesus saves, Jesus saves;\nSing it softly through the gloom,\nWhen the heart for mercy craves,\nSing in triumph o’er the tomb,\nJesus saves, Jesus saves.",
	4: "Give the winds a mighty voice,\nJesus saves, Jesus saves;\nLet the nations now rejoice,\nJesus saves, Jesus saves;\nShout salvation full and free,\nHighest hills and deepest caves,\nThis our song of victory,\nJesus saves, Jesus saves."
}
song["sda-hymnal/341"] = {
	title: "To God Be the Glory",
	poet: "Fanny J. Crosby, 1875 (1820 - 1915)",
	1: "To God be the glory, great things he hath done!\nSo loved he the world that he gave us his Son,\nwho yielded his life an atonement for sin,\nand opened the lifegate that all may go in.\nRefrain\nPraise the Lord, praise the Lord,\nlet the earth hear his voice!\nPraise the Lord, praise the Lord,\nlet the people rejoice!\nO come to the Father thru Jesus the Son,\nand give him the glory, great things he hath done!",
	2: "O perfect redemption, the purchase of blood,\nto every believer the promise of God;\nthe vilest offender who truly believes,\nthat moment from Jesus a pardon receives.",
	3: "Great things he hath taught us, great things he hath done,\nand great our rejoicing thru Jesus the Son;\nbut purer, and higher, and greater will be\nour wonder, our transport, when Jesus we see."
}
song["sda-hymnal/342"] = {
	title: "Is This a Day of New Beginnings",
	tune: "Dronfield",
	copyright: "“Is This a Day of New Beginning?” Copyright © 1983 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Is this a day of new beginnings,\nTime to remember and move on,\nTime too believe what love is bringing,\nLaying to rest the pain that’s gone?",
	2: "How can the seasons of a planet\nMindlessly spinning round its sun\nWith just a human name and number\nSay that some new thing has begun?",
	3: "Yet through the life and death of Jesus\nLove’s mighty Spirit, now as then,\nCan make for us a world of difference\nAs faith and hope are born again.",
	4: "Then let us, with the Spirit’s daring,\nStep from the past and leave behind\nIts disappointment, guilt, and grieving,\nSeeking new paths, and sure to find.",
	5: "Christ is alive, and goes before us\nTo show and share what love can do.\nThis is a day of new beginnings;\nOur God is making all things new."
}
song["sda-hymnal/343"] = {
	title: "I Will Sing of My Redeemer",
	info: "Alternate tune, HYFRYDOL, Nos. 167, 204",
	1: "I will sing of my Redeemer,\nAnd His wondrous love to me;\nOn the cruel cross He suffered,\nFrom the curse to set me free.\nRefrain\nSing, oh sing, of my Redeemer,\nWith His blood, He purchased me.\nOn the cross, He sealed my pardon,\nPaid the debt, and made me free.",
	2: "I will tell the wondrous story,\nHow my lost estate to save,\nIn His boundless love and mercy,\nHe the ransom freely gave.",
	3: "I will sing of my Redeemer,\nAnd His heavenly love to me;\nHe from death to life hath brought me,\nSon of God with Him to be."
}
song["sda-hymnal/344"] = {
	title: "I Love Your Kingdom, Lord",
	tune: "St. Thomas (Williams' Psalter)",
	1: "I love thy kingdom, Lord,\nthe house of thine abode,\nthe church our blest Redeemer saved\nwith his own precious blood.",
	2: "I love thy church, O God!\nHer walls before thee stand\ndear as the apple of thine eye,\nand graven on thy hand.",
	3: "Beyond my highest joy\nI prize her heavenly ways,\nher sweet communion, solemn vows,\nher hymns of love and praise.",
	4: "Sure as thy truth shall last,\nto Zion shall be given\nthe brightest glories earth can yield,\nand brighter bliss of heaven."
}
song["sda-hymnal/345"] = {
	title: "Christ Is the World's True Light",
	tune: "Darmstadt",
	copyright: "“Christ Is the World's True Light” Words from Enlarged Songs of Praise by permission of Oxford University Press.",
	1: "Christ is the world’s true light,\nIts captain of salvation,\nThe daystar clear and bright\nOf every man and nation;\nNew life, new hope awakes\nWhere’er men own His sway:\nFreedom her bondage breaks,\nAnd night is turned to day.",
	2: "In Christ all races meet,\nTheir ancient feuds forgeting,\nThe whole round world complete,\nFrom sunrise to its setting:\nWhen Christ is throned as Lord,\nMen shall forsake their fear,\nTo plowshare beat the sword,\nTo pruning hook the spear.",
	3: "One Lord, in one great name\nUnite us all who own Thee,\nCast out our pride and shame\nThat hinder to enthrone Thee;\nThe world has waited long,\nHas travailed long in pain,\nTo heal its ancient wrong,\nCome, Prince of Peace, and reign."
}
song["sda-hymnal/346"] = {
	title: "Lord, Who Dost Give to Thy Church",
	tune: "Seventh and James",
	copyright: "“Lord, Who Dost Thou Give to Thy Church” Words copyright © 1966 by The Hymn Society of America, Texas Christian University, Fort Worth, TX 76129. Used by permission. Music © 1975 by Broadman Press. All rights reserved. International copyright secured. Use",
	1: "Lord, who dost give to Thy church for its healing\nGifts, and the grace to sustain and renew,\nHear as we pray that today and each morrow\nWe to Thy purpose may show ourselves true.",
	2: "Clear be the voices of preachers and prophets\nFearlessly speaking the word of the Lord,\nWord of redemption thro‘ God’s Son incarnate,\nBlessing for cursing, and peace for the sword.",
	3: "Tender and wise be the hearts of the pastors,\nGuiding and guarding the souls in their care,\nFirm with the wayward, a strength to the doubting,\nHelping the needy their burdens to bear.",
	4: "May those who teach grow in knowledge and patience,\nGuiding to wisdom the young and the old,\nTraining for worship and witness and service,\nFoes to all falsehood, in truthfulness bold.",
	5: "Lord, ever give to us gifts in due measure,\nEach needing other, and all having worth;\nSo to the Father, the Son, and the Spirit,\nGlory be shown by the church here on earth."
}
song["sda-hymnal/347"] = {
	title: "Built on the Rock",
	tune: "Kirken den er et",
	copyright: "“Built on the Rock” Words copyright 1958 Service Book and Hymnal. Used by permission of Augsburg Publishing House.",
	1: "Built on the Rock the Church shall stand,\nEven when steeples are falling;\nCrumbled have spires in every land,\nBells still are chiming and calling\nCalling the young and old to rest,\nCalling the souls of those distressed,\nLonging for life everlasting.",
	2: "Not in our temples made with hands\nGod, the Almighty, is dwelling;\nHigh in the heav’ns His temple stands,\nAll earthly temples excelling.\nYet He who dwells in heav’n above\nDeigns to abide with us in love,\nMaking our bodies His temple.",
	3: "We are God’s house of living stones,\nBuilt for His own habitation;\nHe fills our hearts, His humble thrones,\nGranting us life and salvation.\nWere two or three to seek His face,\nHe in their midst would show His grace,\nBlessings upon them bestowing.",
	4: "Yet in this house, an earthly frame,\nJesus His children is blessing;\nHither we come to praise His name,\nFaith in our Savior confessing.\nJesus to us His Spirit sent,\nMaking with us His covenant,\nGranting His children the kingdom.",
	5: "Thro‘ all the passing years, O Lord,\nGrant that, when church bells are ringing,\nMany may come to hear God’s Word\nWhere He this promise is bringing:\nI know My own, My own know Me:\nYou, not the world, My face shall see;\nMy peace I leave with you. Amen."
}
song["sda-hymnal/348"] = {
	title: "The Church Has One Foundation",
	tune: "Aurelia",
	1: "The church’s one foundation\nis Jesus Christ her Lord;\nshe is his new creation\nby water and the Word.\nFrom heaven he came and sought her\nto be his holy bride;\nwith his own blood he bought her,\nand for her life he died.",
	2: "Elect from every nation,\nyet one o’er all the earth;\nher charter of salvation,\none Lord, one faith, one birth;\none holy name she blesses,\npartakes one holy food,\nand to one hope she presses,\nwith every grace endued.",
	3: "Though with a scornful wonder\nwe see her sore oppressed,\nby schisms rent asunder,\nby heresies distressed,\nyet saints their watch are keeping;\ntheir cry goes up, “How long?”\nAnd soon the night of weeping\nshall be the morn of song.",
	4: "‘Mid toil and tribulation,\nand tumult of her war,\nshe waits the consummation\nof peace forevermore;\ntill, with the vision glorious,\nher longing eyes are blest,\nand the great church victorious\nshall be the church at rest."
}
song["sda-hymnal/349"] = {
	title: "God is Love",
	tune: "Ubi caritas",
	scripture: "1 John 4:7,8",
	copyright: "“God Is Love” Words copyright © 1969, James Quinn SJ, printed by permission of Geoffrey Chapman, a division of Cassell Ltd. Music copyright by A. Gregory Murray. Used by permission.",
	1: "Here in Christ we gather, love of Christ our calling.\nChrist, our love, is with us, gladness be His greeting.\nLet us fear Him, yes, and love Him, God eternal.\nLoving Him, let each love Christ in all his brethren.\nRefrain:\nGod is love, and where true love is\nGod Himself is there.",
	2: "When we Christians gather, members of one body,\nLet there be in us no discord but one spirit.\nBanished now be anger, strife, and every quarrel.\nChrist, our God, be always present here among us.",
	3: "Grant us love’s fulfillment, joy with all the blessed,\nWhen we see Your face, O Savior, in its glory.\nShine on us, O purest Light of all creation,\nBe our bliss while endless ages sing Your praises."
}
song["sda-hymnal/350"] = {
	title: "Blest Be the Tie That Binds",
	tune: "Dennis",
	1: "Blest be the tie that binds\nour hearts in Christian love;\nthe fellowship of kindred minds\nis like to that above.",
	2: "Before our Father’s throne\nwe pour our ardent prayers;\nour fears, our hopes, our aims are one,\nour comforts and our cares.",
	3: "We share each other’s woes,\nour mutual burdens bear;\nand often for each other flows\nthe sympathizing tear.",
	4: "When we asunder part,\nit gives us inward pain;\nbut we shall still be joined in heart,\nand hope to meet again."
}
song["sda-hymnal/351"] = {
	title: "Thy Hand, O God Has Guided",
	tune: "Thornbury",
	scripture: "Eph. 4:5",
	copyright: "“Thy Hand, O God Has Guided” Used by permission if the Executors of the late Basil Harwood,",
	1: "Thy hand, O God, has guided\nThy flock from age to age;\nThe wondrous tale is written\nFull clear on every page;\nOur fathers owned Thy goodness,\nAnd we their deeds record;\nAnd both of this bear witness,\nOne church, one faith, one Lord.",
	2: "Thy heralds brought glad tidings\nTo greatest as to least;\nThey bade men rise and hasten\nTo share the great King’s feast:\nAnd this was all their teaching,\nIn every deed and word,\nTo all alike proclaiming\nOne church, one faith, one Lord.",
	3: "When shadows thick were falling,\nAnd all seemed sunk in night,\nThou, Lord, did send Thy servants,\nThy chosen sons of light.\nOn them and on Thy people\nThy plenteous grace was poured,\nAnd this was still their message:\nOne church, one faith, one Lord.",
	4: "Thy mercy will not fail us,\nNor leave Thy work undone;\nWith Thy right hand to help us,\nThe vict’ry shall be won;\nAnd then by men and angels\nThy name shall be adored,\nAnd this shall be their anthem:\nOne church, one faith, one Lord."
}
song["sda-hymnal/352"] = {
	title: "This Is My Will",
	tune: "Suantrai",
	scripture: "John 15:12 - 17",
	copyright: "“This Is My Will” Word copyright © 1969 by James Quinn, SJ, printed by permission of Geoffrey Chapman, a division of Cassell Ltd.",
	1: "This is My will, My one command,\nThat love should dwell among you all.\nThis is My will that you should love\nAs I have shown that I love you.",
	2: "No greater love a man can have\nThan that he die to save his friends.\nYou are My friends if you obey\nWhat I command that you should do.",
	3: "You chose not Me, but I chose you\nThat you should go and bear much fruit.\nI chose you out that you in Me\nShould bear much fruit that will abide.",
	4: "All that I ask My Father, dear,\nFor My name’s sake you shall receive.\nThis is My will, My one command,\nThat love should dwell in each, in all."
}
song["sda-hymnal/353"] = {
	title: "Father, Help Your People",
	tune: "Whitworth",
	copyright: "“Father, Help Your People” Words copyright © by Hope Publishing Co., IL 60188. All rights reserved. Used by permission. Music copyright © 1973 by Waterloo Company Limited, Waterloo, Ontario, Canada. All rights reserved. Used by permission.",
	1: "Father, help Your people in this world to build\nSomething of Your kingdom and to do Your will,\nLead us to discover partnership in love;\nBless our ways of sharing and our pride remove.",
	2: "Lord of desk and altar, bind our lives in one,\nThat in work and worship love may set the tone.\nGive us grace to listen, clarity of speech;\nMake us truly thankful for the gifts of each.",
	3: "Holy is the setting of each room and yard,\nLecture hall and kitchen, office, shop, and ward.\nHoly is the rhythm of our working hours;\nHallow then our purpose, energy, and pow’rs.",
	4: "Strengthen, Lord, for service hand and heart and brain;\nHelp us good relations daily to maintain.\nLet the living presence of the servant Christ\nHeighten our devotion, make our life a feast."
}
song["sda-hymnal/354"] = {
	title: "Thy Love, O God",
	tune: "Northbrook",
	copyright: "“Thy Love, O God” Words by permission of Oxford University Press. Music from the Clarendon Hymn Book by permission of Oxford University Press.",
	1: "Thy love, O God, has all mankind created,\nAnd led Thy people to this present hour;\nIn Christ we see life’s glory consummated;\nThy Spirit manifests His living power.",
	2: "From out the darkness of our hope’s frustration,\nFrom all the broken idols of our pride,\nWe turn to seek Thy truth’s illumination,\nAnd find Thy mercy waiting at our side.",
	3: "In pity look upon Thy children’s striving\nFor life and freedom, peace and brotherhood,\nTill at the fullness of Thy truth arriving,\nWe find in Christ the crown of every good.",
	4: "Inspire Thy church, mid earth’s discordant voices,\nTo preach the gospel of her Lord above,\nUntil the day this warring world rejoices\nTo hear the mighty harmonies of love."
}
song["sda-hymnal/355"] = {
	title: "Where Cross the Crowded Ways of Life",
	tune: "Gardiner",
	info: "Lower Key, No. 177",
	1: "Where cross the crowded ways of life,\nwhere sound the cries of race and clan,\nabove the noise of selfish strife,\nwe hear your voice, O Son of Man.",
	2: "From tender childhood’s helplessness,\nfrom woman’s grief, man’s burdened toil,\nfrom famished souls, from sorrow’s stress,\nyour heart has never known recoil.",
	3: "The cup of water given for you still\nholds the freshness of your grace;\nyet long these multitudes to view\nthe sweet compassion of your face.",
	4: "O Master, from the mountainside\nmake haste to heal these hearts of pain;\namong these restless throngs abide;\nO tread the city’s streets again.",
	5: "Till all the world shall learn your love\nand follow where your feet have trod,\ntill, glorious from your heaven above,\nshall come the city of our God."
}
song["sda-hymnal/356"] = {
	title: "All Who Love and Serve Your City",
	tune: "Birabus",
	copyright: "“All Who Love and Serve Your City” Words copyright © 1969 Galliard Ltd. Used by permission of Galaxy Music Corporation, New York, sole U.S. Agent. Music copyright © 1969 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permissi",
	1: "All who love and serve your city,\nAll who bear its daily stress,\nAll who cry for peace and justice,\nAll who curse and all who bless.",
	2: "In your day of loss and sorrow,\nIn your day of helpless strife,\nHonor, peace, and love retreating,\nSeek the Lord, who is your life.",
	3: "For all days are days of judgment,\nAnd the Lord is waiting still,\nDrawing near His friends who spurn Him,\nOff’ring peace from Calv’ry’s hill.",
	4: "Risen Lord, shall yet the city\nBe the city of despair?\nCome today, our judge, our glory;\nBe its name “The Lord is there!”"
}
song["sda-hymnal/357"] = {
	title: "Come, Labor On",
	tune: "Ora labora",
	copyright: "“Come, Labor On” copyright © 1918 by The H. W. Gray Company, a division of Belwin - Mills Publishing Corp. Used by permission All rights reserved.",
	1: "Come, labor on.\nWho dares stand idle on the harvest plain\nWhile all around him waves the golden grain?\nAnd to each servant does the Master say,\n“Go work today.”",
	2: "Come, labor on.\nClaim the high calling angels cannot share;\nTo young and old the gospel gladness bear;\nRedeem the time; its hours too swiftly fly.\nThe night draws nigh.",
	3: "Come, labor on.\nNo time for rest; till glows the western sky,\nTill the long shadows oér our pathway lie,\nAnd a glad sound comes with the setting sun,\n“Well done, well done!”"
}
song["sda-hymnal/358"] = {
	title: "Far and Near the Fields Are Teeming",
	composer: "J. B. O. Clemm, 1895",
	poet: "J. O. Thompson, 1885",
	tune: "Harvest",
	1: "Far and near the fields are teeming\nwith the sheaves of ripened grain;\nFar and near their gold is gleaming\nO’er the summy slope and plain.\nRefrain\nLord of harvest, send forth reapers!\nHear us Lord,to Thee we cry;\nSend them now the sheaves to gather,\nEre the harvest-time pass by.",
	2: "Send them forth with morn’s first beaming,\nSend them in the noon-tides’s glare;\nWhen the sun’s last rays are streaming,\nbid them gather everywhere.",
	3: "O thou, whom thy Lord is sending,\ngather now the sheaves of gold;\nHeavenward then at evening wending\nThou shalt come with joy untold."
}
song["sda-hymnal/359"] = {
	title: "Hark! the Voice of Jesus Calling",
	tune: "Fillmore",
	scripture: "Isa. 6:8",
	1: "Hark! the voice of Jesus calling,\n“Who will go and work today?\nFields are white, the harvest waiting,\nWho will bear the sheaves away?”\nLoud and long the Master calleth,\nRich reward He offers free;\nWho will answer, gladly saying,\n“Here am I, O Lord, send me”?",
	2: "If you cannot cross the ocean\nAnd the heathen lands explore,\nYou can find the heathen nearer,\nYou can help them at your door;\nIf you cannot speak like angels,\nIf you cannot preach like Paul,\nYou can tell the love of Jesus,\nYou can say He died for all.",
	3: "If you cannot be the watchman,\nStanding high on Zion’s wall,\nPointing out the path to heaven,\nOffering life and peace to all;\nWith your prayers and with your bounties\nYou can do what Heaven demands,\nYou can be like faithful Aaron,\nHolding up the prophet’s hands.",
	4: "While the souls of men are dying,\nAnd the Master calls for you,\nLet none hear you idly saying,\n“There is nothing I can do!”\nGladly take the task He gives you,\nLet His work your pleasure be;\nAnswer quickly when He calleth,\n“Here am I, O Lord, send me.”"
}
song["sda-hymnal/360"] = {
	title: "From the Eastern Mountains",
	tune: "Cuddesdon",
	scripture: "Matt. 2:1, 2",
	info: "Alternate tune, Ruth No. 110",
	copyright: "“From the Eastern Mountains” Music by permission of Oxford University Press.",
	1: "From the eastern mountains Pressing on they come,\nWise men in their wisdom, To His humble home;\nStirred by deep devotion, Hasting from afar,\nEver journeying onward, Guided by a star.",
	2: "There their Lord and Savior Meek and lowly,\nWondrous light that led them Onward on their way,\nEver now to lighten Nations from afar,\nAs they journey homeward By that guiding star.",
	3: "Gather in the outcasts All who’ve gone astray,\nThrow Thy radiance o’er them, Guide them on their way;\nThose who never knew Thee, Those who’ve wandered far,\nGuide them by the brightness Of Thy guiding star.",
	4: "Until every nation, Whether bond or free,\n‘Neath Thy starlit banner, Jesus follows thee.\nO’er the distant mountains To that heavenly home,\nWhere no sin nor sorrow Evermore shall come."
}
song["sda-hymnal/361"] = {
	title: "Hark! 'Tis the Shepherd's Voice I Hear",
	1: "Hark! ’tis the Shepherd’s voice I hear,\nOut in the desert dark and drear,\nCalling the sheep who’ve gone astray,\nFar from the Shepherd’s fold away.\nRefrain\nBring them in, Bring them in,\nBring them in from the fields of sin;\nBring them in, Bring them in,\nBring the wanderers to Jesus.",
	2: "Who’ll go and help the Shepherd kind,\nHelp Him the wandering ones to find?\nWho’ll bring them back into the fold,\nWhere they’ll be sheltered from the cold?",
	3: "Out in the desert hear their cry,\nOut on the mountain wild and high,\nHark! ’tis the Master speaks to thee,\n“Go, find My sheep where ‘er they be.”"
}
song["sda-hymnal/362"] = {
	title: "Lift High the Cross",
	tune: "Crucifer",
	scripture: "John 12:32",
	copyright: "“Lift High the Cross” Words and music used by permission of Hymns Ancient and Modern.",
	1: "Come, Christians, follow where our Captain trod,\nOur King victorious, Christ, the Son of God.",
	2: "Led on their way by this triumphant sign,\nThe hosts of God in conquering ranks combine.",
	3: "All newborn soldiers of the Crucified\nBear on their brows the seal of Him Who died.",
	4: "O Lord, once lifted on the glorious tree,\nAs Thou hast promised, draw us all to Thee.",
	5: "So shall our song of triumph ever be:\nPraise to the Crucified for victory!",
	refrain: "Refrain:\nLift high the cross, the love of Christ proclaim,\nTill all the world adore His sacred Name."
}
song["sda-hymnal/363"] = {
	title: "Lord, Whose Love in Humble Service",
	tune: "Beach Spring",
	info: "Harmony setting, No. 634 Alternate tune, Abbot's Leigh, No. 61",
	copyright: "“Lord, Whose Love in Humble Service” Words by permission of Oxford University Press. Arrangement copyright 1978 Lutheran Book of Worship. Used by permission of Augsburg Publishing House.",
	1: "Lord, whose love in humble service\nBore the weight of human need,\nWho upon the cross, forsaken,\nWorked Your mercy’s perfect deed:\nWe, Your servants, bring the worship\nNot of voice alone, but heart;\nConsecrating to Your purpose\nEvery gift which You impart.",
	2: "Still Your children wander homeless;\nStill the hungry cry for bread;\nStill the captives long for freedom;\nStill in grief we mourn our dead.\nAs you, Lord, in deep compassion\nHealed the sick and freed the soul,\nBy Your Spirit send Your power\nTo our world to make it whole.",
	3: "As we worship, grant us vision,\nTill your love’s revealing light\nIn its height and depth and greatness\nDawns upon our quickened sight,\nMaking known the needs and burdens\nYour compassion bids us bear,\nStirring us to ardent service,\nYour abundant life to share."
}
song["sda-hymnal/364"] = {
	title: "O Jesus Christ, to You",
	tune: "Highwood",
	scripture: "Luke 19:41",
	copyright: "“O Jesus Christ, to You” Words copyright © 1954/ Renewal 1982 by The Hymn Society of America, Texas Christian University, Fort Worth, TX 76129. Used by permission. Music by permission of Oxford University Press.",
	1: "O Jesus Christ, to You may hymns be rising\nIn every city for Your love and care;\nInspire our worship, grant the glad surprising\nThat Your blest Spirit brings men everywhere.",
	2: "Grant us new courage sacrificial humble\nStrong in Your strength to venture and to dare,\nTo lift the fallen, guide the feet that stumble,\nSeek out the lonely &God’s mercy share",
	3: "Show us Your Spirit, brooding oér each city,\nAs You did weep above Jerusalem,\nSeeking to gather all in love and pity,\nAnd healing those who touch Your garment’s hem."
}
song["sda-hymnal/365"] = {
	title: "O Zion, Haste",
	tune: "Tidings",
	1: "O Zion, haste, thy mission high fulfilling,\nto tell to all the world that God is light,\nthat he who made all nations is not willing\none soul should perish, lost in shades of night.\nRefrain\nPublish glad tidings, tidings of peace;\ntidings of Jesus, redemption and release.",
	2: "Proclaim to every people, tongue, and nation\nthat God, in whom they live and move, is love;\ntell how he stooped to save his lost creation,\nand died on earth that we might live above.",
	3: "Give of thine own to bear the message glorious;\ngive of thy wealth to speed them on their way;\npour out thy soul for them in prayer victorious;\nO Zion, haste to bring the brighter day.",
	4: "He comes again; O Zion, ere thou meet Him,\nMake known to every heart His saving grace;\nLet none whom He hath ransomed fail to greet Him,\nThrough thy neglect, unfit to see His face."
}
song["sda-hymnal/366"] = {
	title: "O Where Are the Reapers",
	1: "O where are the reapers that garner in\nThe sheaves of the good from the fields of sin?\nWith sickles of truth must the work be done,\nAnd no one may rest till the “harvest home.”\nRefrain\nWhere are the reapers? O who will come\nAnd share in the glory of the “harvest home”?\nO who will help us to garner in\nThe sheaves of good from the fields of sin?",
	2: "The fields all are ripening, and far and wide\nThe world now is waiting the harvest tide:\nBut reapers are few, and the work is great,\nAnd much will be lost should the harvest wait.",
	3: "So come with your sickles, ye sons of men,\nAnd gather together the golden grain;\nToil on till the Lord of the harvest come,\nThen share ye His joy in the “harvest home.”"
}
song["sda-hymnal/367"] = {
	title: "Rescue the Perishing",
	poet: "Fanny J. Crosby, 1869 (1820 - 1915)",
	1: "Rescue the perishing, care for the dying,\nsnatch them in pity from sin and the grave;\nweep o’er the erring one, lift up the fallen,\ntell them of Jesus, the mighty to save.\nRefrain\nRescue the perishing, care for the dying;\nJesus is merciful, Jesus will save.",
	2: "Though they are slighting him, still he is waiting,\nwaiting the penitent child to receive;\nplead with them earnestly, plead with them gently;\nhe will forgive if they only believe.",
	3: "Rescue the perishing, duty demands it;\nstrength for thy labor the Lord will provide;\nback to the narrow way patiently win them;\ntell the poor wanderer a Savior has died."
}
song["sda-hymnal/368"] = {
	title: "Watchman, Blow the Gospel Trumpet",
	1: "Watchman, blow the gospel trumpet,\nEvery soul a warning give;\nWhosoever hears the message\nMay repent, and, turn and live.\nRefrain\nBlow the trumpet, trusty watchman,\nBlow it loud o’er land and sea;\nGod commissions, sound the message!\nEvery captive may be free.",
	2: "Sound it loud oér every hilltop,\nGloomy shade and sunny plain;\nOcean depths repeat the message,\nFull salvation’s glad refrain.",
	3: "Sound it in the hedge and highway,\nEarth’s dark spots where exiles roam;\nLet it tell all things are ready,\nFather waits to welcome home.",
	4: "Sound it for the heavy laden,\nWeary, longing to be free;\nSound a Savior’s invitation,\nSweetly saying, “Come to me.”"
}
song["sda-hymnal/369"] = {
	title: "Bringing in the Sheaves",
	1: "Sowing in the morning, sowing seeds of kindness,\nSowing in the noontide and the dewy eve,\nWaiting for the harvest and the time of reaping –\nWe shall come rejoicing, bringing in the sheaves.\nRefrain\nBringing in the sheaves, bringing in the sheaves,\nWe shall come rejoicing, bringing in the sheaves.\nBringing in the sheaves, bringing in the sheaves,\nWe shall come rejoicing, bringing in the sheaves.",
	2: "Sowing in the sunshine, sowing in the shadows,\nFearing neither clouds nor winter’s chilling breeze;\nBy and by the harvest and the labor ended –\nWe shall come rejoicing, bringin in the sheaves.",
	3: "Going forth with weeping, sowing for the Master,\nTho the loss sustained our spirit often grieves;\nWhen our weeping’s over He will bid us welcome –\nWe shall come rejoicing, bringing in the sheaves"
}
song["sda-hymnal/370"] = {
	title: "Christ for the World",
	tune: "Italian Hymn",
	info: "Alternate harmony, No. 71",
	1: "Christ for the world we sing,\nthe world to Christ we bring,\nwith loving zeal;\nthe poor, and them that mourn,\nthe faint and overborne,\nsinsick and sorrow-worn,\nwhom Christ doth heal.",
	2: "Christ for the world we sing,\nthe world to Christ we bring,\nwith fervent prayer;\nthe wayward and the lost,\nby restless passions tossed,\nredeemed at countless cost,\nfrom dark despair.",
	3: "Christ for the world we sing,\nthe world to Christ we bring,\nwith joyful song;\nthe newborn souls, whose days,\nreclaimed from error’s ways,\ninspired with hope and praise,\nto Christ belong."
}
song["sda-hymnal/371"] = {
	title: "Lift Him Up",
	1: "Lift Him up, ’tis He that bids you,\nLet the dying look and live;\nTo all weary, thirsting sinners,\nLiving waters will He give;\nAnd though once so meek and lowly,\nYet the Prince of heaven was He;\nAnd the blind, who grope in darkness,\nThrough the blood of Christ shall see.\nRefrain\nLift Him up, the risen Savior,\nHigh amid the waiting throng;\nLift Him up, ’tis He that speaketh,\nNow He bids you flee from wrong.",
	2: "Lift Him up, this precious Savior,\nLet the multitude behold;\nThey with willing hearts shall seek Him,\nHe will draw them to His fold;\nThey shall gather from the wayside,\nHastening on with joyous feet,\nThey shall bear the cross of Jesus,\nAnd shall find salvation sweet.",
	3: "Lift Him up in all His glory,\n‘Tis the Son of God on high;\nLift Him up, His love shall draw them,\nEén the careless shall draw nigh;\nLet them hear again the story\nOf the cross, the death of shame;\nAnd from tongue to tongue repeat it;\nMighty throngs shall bless His name.",
	4: "O then lift Him up in singing,\nLift the Savior up in prayer;\nHe, the glorious Redeemer,\nAll the sins of men did bear;\nYes, the young shall bow before Him,\nAnd the old their voices raise;\nAll the deaf shall hear hosannah;\nAnd the dumb shall shout His praise."
}
song["sda-hymnal/372"] = {
	title: "How Beauteous Are Their Feet",
	tune: "St. Thomas (Williams' Psalter)",
	scripture: "Isa. 52:7 - 9",
	1: "How beauteous are their feet\nWho stand on Zion’s hill;\nWho bring salvation on their tongues,\nAnd words of peace reveal!",
	2: "How charming is their voice,\nSo sweet the tidings are:\n“Zion, behold thy Savior King;\nHe reigns and triumphs here!”",
	3: "How happy are our ears,\nThat hear the joyful sound\nWhich kings and prophets waited for,\nAnd sought, but never found!",
	4: "How blessed are our eyes,\nThat see this heavenly light;\nProphets and kings desired it long,\nBut died without the sight!",
	5: "The watchmen join their voice,\nAnd tuneful notes employ;\nJerusalem breaks forth in songs,\nAnd deserts learn the joy."
}
song["sda-hymnal/373"] = {
	title: "Seeking the Lost",
	1: "Seeking the lost, yes, kindly entreating\nWanderers on the mountain astray;\n“Come unto Me,” His message repeating,\nWords of the Master speaking today.\nRefrain\nGoing afar (going afar)\nUpon the mountain (upon the mountain)\nBringing the wanderer back again, back again,\nInto the fold (into the fold)\nOf my Redeemer (of my Redeemer)\nJesus the Lamb for sinners slain, for sinners slain.",
	2: "Seeking the lost and pointing to Jesus\nSouls that are weak and hearts that are sore,\nLeading them forth in ways of salvation,\nShowing the path to life evermore.",
	3: "Thus would I go on missions of mercy,\nFollowing Christ from day unto day,\nCheering the faint and raising the fallen,\nPointing the lost to Jesus, the Way."
}
song["sda-hymnal/374"] = {
	title: "Jesus, With Thy Church Abide",
	tune: "Song 13",
	1: "Jesus, with Thy church abide;\nBe her Savior, Lord, and Guide,\nWhile on earth her faith is tried:\nWe beseech Thee, hear us.",
	2: "May her voice be ever clear,\nWarning of a judgment near,\nTelling of a Savior dear:\nWe beseech Thee, hear us.",
	3: "May she guide the poor and blind,\nSeek the lost until she find,\nAnd the broken hearted bind:\nWe beseech Thee, hear us.",
	4: "May she holy triumps win,\nOverthrow the hosts of sin,\nGather all the nations in:\nWe beseech Thee, hear us."
}
song["sda-hymnal/375"] = {
	title: "Work, for the Night Is Coming",
	tune: "Work Song",
	scripture: "John 9:4",
	copyright: "“Work, for the Night Is Coming” Arrangement copyright © 1984 by Melvin West.",
	1: "Work for the night is coming,\nWork through the morning hours;\nWork while the dew is sparkling;\nWork ‘mid springing flow’rs.\nWork when the day grows brighter,\nWork in the glowing sun;\nWork for the night is coming,\nWhen man’s work is done.",
	2: "Work for the night is coming,\nWork thro’ the sunny noon;\nFill brightest hours with labor,\nRest comes sure and soon.\nGive every flying minute\nSomething to keep in store;\nWork for the night is coming,\nWhen man works no more.",
	3: "Work for the night is coming,\nUnder the sunset skies;\nWhile their bright tints are glowing,\nWork for daylight flies.\nWork till the last beam fadeth,\nFadeth to shine no more;\nWork while the night is dark’ning,\nWhen man’s work is o’er."
}
song["sda-hymnal/376"] = {
	title: "All Things Are Thine",
	tune: "Gardiner",
	info: "Lower key, No. 177",
	1: "All things are Thine; no gift have we,\nLord of all gifts, to offer Thee;\nAnd hence with grateful hearts today,\nThine own before Thy feet we lay.",
	2: "Thy will was in the builder’s thought;\nThy hand unseen amidst us wrought;\nThro’ mortal motive, scheme and plan,\nThy wise, eternal purpose ran.",
	3: "No lack Thy perfect fullness knew;\nFor human needs and longings grew\nThis house of prayer- this home of rest.\nHere may Thy saints be often blessed.",
	4: "In weakness and in want we call\nOn Thee, for whom the heav’ns are small;\nThy glory is Thy children’s good,\nThy joy Thy tender Fatherhood.",
	5: "O Father! deign these walls to bless;\nMake this the abode of righteousness,\nAnd let these doors a gateway be\nTo lead us from ourselves to Thee!"
}
song["sda-hymnal/377"] = {
	title: "Go Forth, Go Forth With Christ",
	tune: "Darwall's 148th",
	copyright: "“Go Forth, Go Forth With Christ” From English Praise by permission of Oxford University Press.",
	1: "Go forth, go forth with Christ,\nWho called you to this day,\nHe who has led, will lead\nAnd keep you in His way:\nHis word is fast, His promise sure\nTo all who serve Him and endure.",
	2: "Go forth, go forth with Christ,\nWith purpose not your own,\nEach vict’ry you shall gain\nThrough Him your Lord alone:\nTo guard you in fidelity\nHis Spirit shall your strength’ner be.",
	3: "Go forth, go forth with Christ,\nHis Priesthood you shall share,\nWho bought us by His blood\nTo be His servants here:\nWalk in the way your Savior trod,\nGo forth with Him, go forth with God."
}
song["sda-hymnal/378"] = {
	title: "Go, Preach My Gospel",
	tune: "Truro",
	scripture: "Mark 16:15 - 17",
	1: "“Go, preach My gospel,” saith the Lord;\n“Bid the whole world My grace receive;\nHe shall be saved who trusts My word,\nAnd they condemned who disbelieve.",
	2: "“I’ll make your great commission known,\nAnd ye shall prove My gospel true\nBy all the works that I have done,\nBy all the wonders ye shall do.",
	3: "“Teach all the nations My commands;\nI’m with you till the world shall end;\nAll power is vested in My hands;\nI can destroy, and I defend.”",
	4: "He spake, and light shone round His head;\nOn a bright cloud to heaven He rode;\nThey to the farthest nations spread\nThe grace of their ascended Lord."
}
song["sda-hymnal/379"] = {
	title: "We Give This Child to You",
	tune: "Cascadel",
	copyright: "“We Give This Child to You” Words copyright © 1985 by Carol Mayes",
	1: "We give this child to You,\nOur precious gift of love.\nHelp us to lead each step aright\nWith guidance from above.",
	2: "O bless each child of Yours,\nAnd grant when they are grown,\nThey will have learned to love Your way,\nAnd choose it for their own.",
	3: "We give ourselves to You,\nAnd may Your Spirit fill\nOur hearts and home, that all we do\nBe subject to Your will."
}
song["sda-hymnal/380"] = {
	title: "Welcome, Day of Sweet Repose",
	tune: "Evans",
	info: "Alternate tune, Mercy, No. 268",
	1: "Welcome, day of sweet repose!\nBlessed be thy sacred hours!\nWe would trust the One who knows\nAll our weak and failing powers.",
	2: "Welcome, day in Eden born!\nHoly rest for sinless man!\nLike the dawning of fair morn\nCome thy hours to us again.",
	3: "Welcome, day blessed by our Lord!\nToil shall cease and anxious care.\nDay commanded by His word,\nDay for song and praise and prayer.",
	4: "Welcome, day our Savior kept!\nKeeping, wrought our righteousness,\nDay God bids us ne’er forget,\nDay of days His name to bless."
}
song["sda-hymnal/381"] = {
	title: "Holy Sabbath Day of Rest",
	1: "Holy Sabbath day of rest,\nBy our Master richly blest,\nGod created and divine,\nSet aside for holy time.\nRefrain\nYes, the holy Sabbath rest,\nBy our God divinely blest,\nIt to us a sign shall be\nThroughout all eternity.",
	2: "Seek not pleasures of this earth,\nWith its folly, noise, and mirth,\nThere are better things in store,\nOver on the other shore.",
	3: "As the Sabbath draweth on\nFriday eve at set of sun,\nChristian household then should meet,\nSing and pray at Jesus’feet.",
	4: "Asking Him for saving grace,\nAlso vict’ry in the race,\nAnd to help us by His pow’r,\nTo keep holy every hour."
}
song["sda-hymnal/382"] = {
	title: "O Day of Rest and Gladness",
	tune: "Adelaide",
	1: "O day of rest and gladness,\nO day of joy and light,\nO balm of care and sadness,\nmost beautiful, most bright;\non thee, the high and lowly,\nwho bend before throne,\nsing, “Holy, holy, holy,”\nto the Eternal One.",
	2: "Thou art a port protected\nfrom storms that round us rise;\na garden intersected\nwith streams of paradise;\nthou art a cooling fountain\nin life’s dry, dreary sand;\nfrom thee, like Pisgah’s mountain,\nwe view our promised land.",
	3: "A day of sweet reflection,\nthou art a day of love,\nA day to raise affection\nfrom earth to things above.\nNew graces ever gaining\nfrom this our day of rest,\nWe reach the rest remaining\nin mansions of the blessed."
}
song["sda-hymnal/383"] = {
	title: "O Day of Rest and Gladness",
	tune: "Mendebras",
	1: "O day of rest and gladness,\nO day of joy and light,\nO balm of care and sadness,\nMost beautiful, most bright,\nOn thee the high and lowly\nBefore th’ eternal throne\nSing, “Holy, holy, holy,”\nTo the great Three in One.",
	2: "Thou art a port protected\nFrom storms that round us rise;\nA garden intersected\nWith streams of paradise;\nThou art a cooling fountain\nIn life’s dry dreary sand;\nFrom Thee, like Pisgah’s mountain,\nWe view our promised land.",
	3: "A day of sweet reflection\nThou art, a day of love,\nA day to raise affection\nFrom earth to things above.\nNew graces ever gaining\nFrom this our day of rest,\nWe seek the rest remaining\nTo mansions of the blest."
}
song["sda-hymnal/384"] = {
	title: "Safely Through Another Week",
	tune: "Sabbath (Mason)",
	1: "Safely through another week\nGod has brought us on our way;\nLet us now a blessing seek,\nWaiting in His courts today;\nDay of all the week the best,\nEmblem of eternal rest:\nDay of all the week the best,\nEmblem of eternal rest.",
	2: "While we seek supplies of grace,\nThro’ the dear Redeemer’s name,\nShow Thy reconciling face;\nTake away the sin and shame:\nFrom our worldly cares set free,\nMay we rest this day in Thee:\nFrom our worldly cares set free,\nMay we rest this day in Thee.",
	3: "When the more shall bid us rise,\nMay we feel Thy presence near;\nMay Thy glory meet our eyes,\nWhile we in Thy house appear:\nHere afford us, Lord, a taste\nOf our everlasting feast:\nHere afford us, Lord, a taste\nOf our everlasting feast.",
	4: "May the gospel’s joyful sound\nConquer sinners, comfort saints;\nMake the fruits of grace abound,\nBring relief to all complaints:\nThus may all our Sabbaths be,\nTill we we rise to reign with Thee.\nThus may all our Sabbaths be,\nTill we we rise to reign with Thee."
}
song["sda-hymnal/385"] = {
	title: "Crowning Jewel of Creation",
	tune: "Jewel",
	info: "Alternate tune, Irby, No. 149",
	copyright: "“Crowning Jewel of Creation” Words copyright © by Review and Herald Publishing Association. Music copyright © 1984 by Wayne Hooper.",
	1: "Crowning jewel of creation,\nBlest and hallowed, sanctified;\nTime and changes all transcending,\nShared forever, glorified.\nRefrain\nBlessed Sabbath made for man,\nGift from the Creator’s hand.",
	2: "Sin and sickness, prayer and weeping\nCease at close of earthly days;\nBut Thy Sabbath is eternal,\nJoyful thanks to Thee we raise!",
	3: "Teach us Lord, in storm or sunshine\nHow to truly rest in Thee,\nMay Thy Sabbath peace enfold us\nAnd our shelter ever be."
}
song["sda-hymnal/386"] = {
	title: "The Sacred Anthem",
	tune: "Wallowa",
	copyright: "“The Sacred Anthem” Words copyright © 1984 by Ottilie Stafford. Music copyright © 1984 by Melvin West.",
	1: "The sacred anthem slowly rang\nAcross the fields of praise,\nWhen earth’s first Sabbath made complete\nAll creatures and all days.",
	2: "Walking with God, there,\nWoman and man together share\nThe blessed Sabbath mood;\nAnd in that green and golden world\nKnow all God’s works are good.",
	3: "But now in our diminished lives\nWe sing a blemished song;\nThe earth is worn and disarrayed\nAnd all our work goes wrong.",
	4: "Still in our worship,\nJoining in praise and fellowship,\nBy Sabbath radiance blessed,\nWe put our doubt and fear away\nAnd rest within God’s rest.",
	5: "And arching over time and space\nThe Lord of Sabbaths wills\nRenewal for the weary earth\nAnd healing for our ills.",
	6: "Hearts will rejoice then;\nThere will be no more weeping, when\nWe know and shall be known.\nWith hosts of the redeemed we’ll sing\nAround God’s shining throne."
}
song["sda-hymnal/387"] = {
	title: "Come, O Sabbath Day",
	tune: "Sabbath (Binder)",
	copyright: "“Come, O Sabbath Day” From Union Hymnal © 1932, Central Conference of American Rabbis.",
	1: "Peace and healing on thy wing;\nAnd to every troubled breast\nSpeak of the divine behest:\nThou shalt rest, Thou shalt rest!",
	2: "Earthly longings bid retire,\nQuench the passions’ hurtful fire;\nTo the wayward, sin oppressed,\nBring thou thy divine behest:\nThou shalt rest, Thou shalt rest!",
	3: "Wipe from every cheek the tear,\nBanish care and silence fear;\nAll things working for the best,\nTeach us the divine behest:\nThou shalt rest, Thou shalt rest!"
}
song["sda-hymnal/388"] = {
	title: "Don't Forget the Sabbath",
	poet: "Fanny J. Crosby (1820 - 1915)",
	1: "Don’t forget the Sabbath,\nThe Lord our God hath blest,\nOf all the week the brightest,\nOf all the week the best;\nIt brings repose from labor,\nIt tells of joy divine,\nIts beams of light descending,\nWith heavenly beauty shine.\nRefrain\nWelcome, welcome, ever welcome,\nBlessed Sabbath day,\nWelcome, welcome, ever welcome,\nBlessed Sabbath day.",
	2: "Keep the Sabbath holy,\nAnd worship Him today,\nWho said to His disciples,\n“I am the living way;”\nAnd if we meekly follow\nOur Savior here below,\nHe’ll give us of the fountain\nWhose streams eternal flow.",
	3: "Day of sacred pleasure!\nIts golden hours we’ll spend\nIn thankful hymns to Jesus,\nThe children’s dearest Friend;\nO gentle loving, Savior,\nHow good and kind Thou art,\nHow precious is Thy promise\nTo dwell in every heart!"
}
song["sda-hymnal/389"] = {
	title: "Light of Light, Enlighten Me",
	tune: "Hinchman",
	1: "Light of light, enlighten me,\nNow anew the day is dawning;\nSun of grace, the shadows flee;\nBrighten Thou my Sabbath morning;\nWith Thy joyous sunshine blest,\nHappy is my day of rest.",
	2: "Let me with my heart today,\nHoly, holy, holy, singing,\nRapt awhile from earth away,\nAll my soul to Thee up springing,\nHave a foretaste inward given\nHow they worship Thee in heaven.",
	3: "Hence all care, all vanity!\nFor the day to God is holy;\nCome, Thou glorious Majesty,\nDeign to fill this temple lowly;\nNought today my soul shall move,\nSimply resting in Thy love."
}
song["sda-hymnal/390"] = {
	title: "We Love Thy Sabbath, Lord",
	tune: "Trentham",
	info: "Alternate tune, Paraclete, No. 265",
	1: "We love Thy Sabbath, Lord,\nAnd worship at Thy will;\nOh may these hours sweet peace afford\nAnd deeper faith instill.",
	2: "Thine angels sang for joy\nCration’s work to see;\nWe too, this day, would raise our hearts\nIn grateful praise to Thee.",
	3: "Praise for Thy wondrous love,\nThat sealed this sacred day,\nA sign that all may understand\nWe own Thy sovereign sway.",
	4: "O great Creator King,\nThrough Thy redeeming grace,\nRenew and sanctify our hearts\nThat we may see Thy face.",
	5: "And with the white-robed throng,\nUpon Mount Sion be,\nAnd joyful sing our Sabbath song\nThrough all eternity."
}
song["sda-hymnal/391"] = {
	title: "Welcome, Welcome, Day of Rest",
	tune: "Pleyel's Hymn",
	1: "To the world in kindness given;\nWelcome to this humble breast,\nAs the beaming light from heaven.",
	2: "Day of calm and sweet repose,\nGently now thy moments run;\nBalm to soothe our cares and woes,\nTill our labor here is done.",
	3: "Holy day that most we prize,\nDay of solemn praise and prayer,\nDay to make the simple wise,\nO, how great thy blessings are!",
	4: "Welcome, welcome, day of rest,\nWith thy influence all divine;\nMay thy hallowed hours be blessed\nto this waiting heart of mine."
}
song["sda-hymnal/392"] = {
	title: "Dear Lord, We Come at Set of Sun",
	tune: "Stracathro",
	info: "Alternate tune, Serenity, No. 455",
	copyright: "“Dear Lord, We Come at Set of Sun” Words copyright © 1985 by Mary Speidel. Music from the Revised Church Hymnary 1927 by permission of Oxford University Press.",
	1: "Dear Lord, we come at set of sun,\nAnd at Your feet we kneel\nTo worship You, Creator, King,\nThis day, Your sign and seal.",
	2: "Our earthly tasks we lay aside,\nAccording to Your Word,\nTo enter now Your holy rest,\nThe Sabbath of the Lord.",
	3: "Sweet Sabbath rest, your sacred hours\nAre as a golden chain\nThat reaches back to Eden’s gate\nAnd points us home again.",
	4: "And when this earth shall be renewed,\nAnd sin and death destroyed,\nShall all redeemed each Sabbath day\nStill meet to praise their God."
}
song["sda-hymnal/393"] = {
	title: "Lord of the Sabbath",
	tune: "Park Street",
	1: "Lord of the Sabbath and its light,\nI hail Thy hallowed day of rest;\nIt is my weary soul’s delight,\nThe solace of my careworn breast,\nThe solace of my careworn breast.",
	2: "O sacred day of peace and joy,\nThy hours are ever dear to me;\nNe’er may a sinful thought destroy\nThe holy calm I find in thee,\nThe holy calm I find in thee.",
	3: "How sweetly now they glide along!\nHow hallowed is the calm they yield!\nTransporting is their rapturous song,\nAnd heavenly visions seem revealed,\nAnd heavenly visions seem revealed.",
	4: "O Jesus, let me ever hail\nThy presence with the day of rest;\nThen will Thy servant never fail\nTo deem Thy Sabbath doubly blest,\nTo deem Thy Sabbath doubly blest."
}
song["sda-hymnal/394"] = {
	title: "Far From All Care",
	tune: "Pevensey",
	1: "Far from all care we hail the Sabbath morning;\nO’er waving fields and from the distant sea\nSwell notes of praise in harmony resounding\nAs all creation turns her heart to Thee.",
	2: "Though man alone, Lord, of Thy great creation\nFails now to laud Thee for Thy love and power,\nYet still a remnant love Thee and remember\nThy holy law and each sweet Sabbath hour.",
	3: "Lord of the Sabbath, Savior and Creator,\nCalm now the throbbings of each troubled breast.\nSpeak to our hearts the peace of Thy commandments,\nBreathe on each soul fair Eden’s hallowed ret.",
	4: "Strong in Thy might and quiet in Thy meekness,\nMay we Thine image bear from day to day.\nThen may we enter pearly gates eternal\nAnd sing redemption’s song each Sabbath day."
}
song["sda-hymnal/395"] = {
	title: "As Birds Unto the Genial Homeland",
	copyright: "“As Birds Unto the Genial Homeland” From Union Hymnal © 1932, Central Conference of American Rabbis.",
	1: "As birds unto the genial homeland fly,\nThe winter’s cold and low’ring skies to flee,\nSo seeks my soul Thy gracious presence here\nAnd finds, O God, its rest and peace in Thee.",
	2: "Here at Thy shrine we leave all vexing care,\nFor get the disappointment, grief and tear,\nAnd on the wings of hopeful song and prayer\nWe rise, and rising feel Thy Spirit here.",
	3: "Bless all who spend this night in pain and woe,\nThe burdened heart, the fainting, and distressed,\nThy comfort send to darkened homes bereaved,\nThy saving help to those by want oppressed.",
	4: "Come, Sabbath joy, each trusting heart now fill,\nAnd blissful peace within our homes abide,\nMay thankful praise each grateful heart now thrill,\nAnd to God’s loving care their lives confide."
}
song["sda-hymnal/396"] = {
	title: "Lord God, Your Love Has Called Us Here",
	tune: "Ryburn",
	copyright: "“Lord God, Your Love Has Called Us Here” Words copyright © 1977 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music by permission of Oxford University Press.",
	1: "Lord God, Your love has called us here,\nAs we, by love, for love were made.\nYour living likeness still we bear\nTho’ marred, dishonored, disobeyed.\nWe come, with all our heart and mind\nYour call to hear, Your love to find.",
	2: "We come with self inflicted pains\nOf broken trust and chosen wrong,\nHalf free, half bound by inner chains,\nBy social forces swept along,\nBy powers and systems close confined,\nYet seeking hope for human kind.",
	3: "Lord God, in Christ You call our name,\nAnd then receive us as Your own,\nNot thro’ some merit, right, or claim,\nBut by Your gracious love alone.\nWe strain to glimpse Your mercy seat,\nAnd find You kneeling at our feet.",
	4: "Then take the towel, and break the bread,\nAnd humble us, and call us friends.\nSuffer and serve till all are fed\nAnd show how grandly love intends\nTo work till all creation sings,\nTo fill all worlds, to crown all things.",
	5: "Lord God, in Christ You set us free\nYour life to live, Your joy to share.\nGive us Your Spirit’s liberty\nTo turn from guilt and dull despair\nAnd offer all that faith can do,\nWhile love is making all things new."
}
song["sda-hymnal/397"] = {
	title: "An Upper Room",
	tune: "O Waly Waly",
	copyright: "“An Upper Room” Words copyright © 1974 by Hope Publishing Co., IL 60188. All rights reserved. Used by permission. Music by permission of Oxford University Press.",
	1: "An upper room did our Lord prepare\nFor those loved until the end:\nAnd His disciples still gather there,\nTo celebrate their risen Friend.",
	2: "And after supper He washed their feet,\nFor service, too, is sacrament.\nIn Him our joy shall be made complete\nSent out to serve, as He was sent.",
	3: "A lasting gift Jesus gave His own:\nTo share His bread, His loving cup.\nWhatever burdens may bow us down,\nHe by His cross shall lift us up.",
	4: "No end there is! we depart in peace.\nHe loves beyond our uttermost:\nIn every room in our Father’s house,\nHe will be there, as Lord and host."
}
song["sda-hymnal/398"] = {
	title: "Bread of the World",
	tune: "Communion",
	info: "Alternate tune, Rendez A Dieu, No. 13",
	1: "Bread of the world in mercy broken,\nWine of the soul in mercy shed,\nBy whom the words of life are spoken,\nAnd in whose death our sins are dead;",
	2: "Look on the heart by sorrow broken,\nLook on the tears by sinners shed;\nAnd be Thy feast to us the token\nThat by Thy grace our souls are fed. Amen."
}
song["sda-hymnal/399"] = {
	title: "Beneath the Forms of Outward Rite",
	tune: "Perry",
	scripture: "Mark 14:15, 22, 23",
	copyright: "“Beneath the Forms of Outward Rite” Words used by permission of Christine S. Blaisdell. Music copyright © 1964 by Abingdon Press. Used by permission.",
	1: "Beneath the forms of outward rite\nThy supper, Lord, is spread\nIn every quiet upper room\nWhere fainting souls are fed.",
	2: "The bread is always consecrate\nWhich men divide with men;\nAnd every act of brotherhood\nRepeats Thy feast again.",
	3: "The blessed cup is only passed\nTrue memory of Thee,\nWhen life anew pours out its wine\nWith rich sufficiency.",
	4: "O Master, through these symbols shared,\nThine own dear self impart,\nThat in our daily life may flame\nThe passion of Thy heart."
}
song["sda-hymnal/400"] = {
	title: "I Come With Joy",
	tune: "Dove of Peace",
	copyright: "“I Come With Joy” Words copyright © by Hope Publishing Co., Carol Stream, IL60188. All rights reserved. Used by permission. Arrangement © 1976 by J. S. Paluch Company, Inc. Reprinted with permission.",
	1: "I come with joy to meet my Lord,\nForgiven, loved and free,\nIn awe and wonder to recall\nHis life laid down for me,\nHis life laid down for me.",
	2: "I come with Christians far and near\nTo find, as all are fed,\nOur true community of love\nIn Christ’s communion bread,\nIn Christ’s communion bread.",
	3: "As Christ breaks bread for us to share\nEach proud division ends.\nThat love that made us makes us one,\nAnd strangers now are friends,\nAnd strangers now are friends.",
	4: "And thus with joy we meet our Lord.\nHis presence always near,\nIs in such friendship better known:\nWe see and praise Him here,\nWe see and praise Him here.",
	5: "Together met, together bound,\nWe’ll go our diff’rent ways,\nAnd as His people in the world,\nWe’ll live and speak His praise,\nWe’ll live and speak His praise."
}
song["sda-hymnal/401"] = {
	title: "In Imitation, Lord of Thee",
	tune: "McCabe",
	scripture: "John 13:14",
	1: "In imitation, Lord, of Thee,\nThis solemn service we repeat;\nFor Thine example full of grace,\nHas made this humble duty sweet.",
	2: "Renew each sacred spark of love,\nAnd vitalize the holy flame;\nMay union strong our hearts unite\nWhile this we do in Jesus’ name.",
	3: "Our great example Thou shalt be,\nIn washing Thy disciples feet;\nAnd as we follow Thy command,\nMake Thou our fellowship complete."
}
song["sda-hymnal/402"] = {
	title: "By Christ Redeemed",
	tune: "Anticipation",
	scripture: "1 Corinthians 11:23-26",
	copyright: "“By Christ Redeemed” Music copyright © 1984 by J. Bruce Ashton.",
	1: "By Christ redeemed, In Christ restored,\nWe keep the memory adored,\nAnd show the death of our dear Lord,\nUntil He come.",
	2: "His broken body in our stead is here,\nIn his memorial bread;\nAnd so our feeble love is fed,\nUntil He come.",
	3: "His fearful drops of agony,\nHis lifeblood shed for us we see;\nThe wine shall tell the mystery,\nUntil He come.",
	4: "And thus that dark betrayal night,\nWith the last advent we unite\nThe shame, the glory, by this rite,\nUntil He come."
}
song["sda-hymnal/403"] = {
	title: "Let Us Break Bread Together",
	scripture: "Malachi 4:2",
	copyright: "“Let Us Break Bread Together” Arrangement copyright 1972 Contemporary Worship 4: Hymns for Baptism and Holy Communion. Used by permission of Augsburg Publishing House.",
	1: "Let us break bread together on our knees,\nlet us break bread together on our knees.\nWhen I fall on my knees with my face to the rising sun,\nO Lord, have mercy on me.",
	2: "Let us drink wine together on our knees,\nlet us drink wine together on our knees.\nWhen I fall on my knees with my face to the rising sun,\nO Lord, have mercy on me.",
	3: "Let us praise God together on our knees,\nlet us praise God together on our knees.\nWhen I fall on my knees with my face to the rising sun,\nO Lord, have mercy on me."
}
song["sda-hymnal/404"] = {
	title: "Now Let Us From This Table Rise",
	tune: "Deus tuorum militum",
	copyright: "“Now Let Us From This Table Rise” Words copyright © 1968 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Arrangement copyright © 1984 by Melvin West.",
	1: "Now let us from this table rise\nRenewed in body, mind, and soul;\nWith Christ we die and live again,\nHis selfless love has made us whole.",
	2: "With minds alert, upheld by grace,\nTo spread the word in speech and deed,\nWe follow in the steps of Christ,\nAt one with all in hope and need.",
	3: "To fill each human house with love,\nIt is the sacrament of care;\nThe work that Christ began to do\nWe humbly pledge ourselves to share.",
	4: "Then grant us courage, Father God,\nTo choose again the pilgrim way\nAnd help us to accept with joy\nThe challenge of tomorrow’s day."
}
song["sda-hymnal/405"] = {
	title: "O God, Unseen, Yet Ever Near",
	tune: "Meditation",
	1: "O God, unseen, yet ever near,\nReveal Thy presence now\nWhile we in love that hath no fear,\nBefore Thy glory bow.",
	2: "Here may Thy faithful people know\nThe blessings of Thy love,\nThe streams that thro’ the dessert flow,\nThe manna from above.",
	3: "We come, obedient to Thy Word\nTo feast on heav’nly food,\nOur meat, the body of our Lord,\nOur drink, His precious blood.",
	4: "Thus may we all Thy words obey,\nFor we, O God, are Thine,"
}
song["sda-hymnal/406"] = {
	title: "Love Consecrates the Humblest Act",
	tune: "Twenty-Fourth",
	scripture: "John 13:14",
	info: "Harmony setting, No. 681",
	copyright: "“Love Consecrates the Humblest Act” Setting copyright 1978 Luteran Book of Worship. Used by permission of Augusburg Publishing House.",
	1: "Love consecrates the humblest act\nAnd haloes mercy’s deeds;\nIt sheds a benediction sweet\nAnd hallows human needs.",
	2: "When in the shadow of the cross\nChrist knelt and washed the feet\nOf His disciples, He gave us\nA sign of love complete.",
	3: "Love serves and willing, stoops to serve;\nWhat Christ in love so true\nHas freely done for one and all,\nLet us now gladly do!"
}
song["sda-hymnal/407"] = {
	title: "Sent Forth by God's Blessing",
	tune: "The Ash Grove",
	info: "Harmony setting, No. 560",
	copyright: "“Sent Forth by God's Blessing” Words copyright © 1964 by World Library Publications. Reprinted by permission. Setting copyright 1972 Contemporary Worship 4: Hymns for Baptism and Holy Communion. Used by permission o fAugsburg Publishing House.",
	1: "Sent forth by God’s blessing,\nOur true faith confessing,\nThe people of God from His dwelling take leave.\nThe supper is ended.\nOh, now be extended\nThe fruits of this service in all who believe.",
	2: "The seed of His teaching,\nReceptive souls reaching,\nShall blossom in action for God and for all.\nHis grace did invite us,\nHis love shall unite us\nTo work for God’s kingdom and answer His call.",
	3: "With praise and thanksgiving\nTo God ever living,\nThe tasks of our everyday life we will face.\nOur faith ever sharing,\nIn love ever caring,\nEmbracing His children of each tribe and race.",
	4: "With Your feast You feed us,\nWith Your light now lead us;\nUnite us as one in this life that we share.\nThen may all the living\nWith praise and thanks giving\nGive honor to Christ and His name that we bear."
}
song["sda-hymnal/408"] = {
	title: "Lord, Enthroned in Heavenly Splendor",
	tune: "Kingley Vale",
	1: "Lord, enthroned in heav’nly splendor,\nFirst begotten from the dead,\nYou alone, our strong defender,\nLifting up Your people’s head.\nAlleluia, Jesus, true and living Bread!",
	2: "Tho’ the lowliest form now veil You\nAs of old in Bethlehem,\nHere as there Your angels hail You\nBranch and flow’r of Jesse’s stem.\nAlleluia, We in worship join with them.",
	3: "Paschal Lamb, Your off’ring finished\nOnce for all when You were slain,\nIn its fullness undiminished\nShall forever more remain.\nAlleluia, Cleansing souls from every stain.",
	4: "Life imparting heav’nly manna,\nStricken rock with streaming side,\nHeav’n and earth with loud hosanna\nWorship You the Lamb who died.\nAlleluia, Ris’n, ascended, glorified!"
}
song["sda-hymnal/409"] = {
	title: "Jesus Invites His Saints",
	tune: "Schumann",
	copyright: "“Jesus Invites His Saints” Music copyright © 1984 by Wayne Hooper",
	1: "Jesus invites His saints\nTo meet around His board,\nAnd sup in memory of the death\nAnd sufferings of their Lord.",
	2: "We take the bread and wine\nAs emblems of Thy death;\nLord, raise our souls above the sign,\nTo feast on Thee by faith.",
	3: "Faith eats the bread of life,\nAnd drinks the living wine;\nIt looks beyond this scene of strife\nUnites us to the Vine.",
	4: "Soon shall the night be gone,\nOur Lord will come again;\nThe marriage supper of the Lamb\nWill usher in His reign."
}
song["sda-hymnal/410"] = {
	title: "Thy Broken Body, Gracious Lord",
	tune: "Janice",
	copyright: "“Thy Broken Body, Gracious Lord” Music copyright © 1984 by Wayne Hooper.",
	1: "Thy broken body, gracious Lord,\nIs shadowed by this broken bread;\nThe wine which in this cup is poured,\nPoints to the blood which Thou hast shed.",
	2: "And while we meet together thus,\nWe show that we are one in Thee;\nThy precious blood was shed for us,\nThy death, O Lord, hast set us free!",
	3: "We have one hope that Thou wilt come,\nThee in the air we wait to see;\nWhen Thou wilt give Thy saints a home,\nAnd we shall ever reign with Thee."
}
song["sda-hymnal/411"] = {
	title: "The Son of God Proclaim",
	tune: "Day of Praise",
	copyright: "“The Son of God Proclaim” Words used by permission of Basil E. Bridge.",
	1: "The Son of God proclaim,\nThe Lord of time and space;\nThe God who bade the light break forth\nNow shines in Jesus’ face.",
	2: "Behold His out stretched hands,\nThough all was in His power\nHe took the towel and basin then,\nAnd serves us in this hour.",
	3: "He, God’s creative word,\nThe church’s Lord and head,\nHere bids us gather as His friends,\nAnd share His wine and bread.",
	4: "The Lord of life and death\nWith wondering praise we sing;\nWe break the bread at His command,\nAnd name Him God and King.",
	5: "We take this cup in hope:\nFor He, who gladly bore\nThe shameful cross, is ris’n again,\nAnd reigns forevermore."
}
song["sda-hymnal/412"] = {
	title: "Cover With His Life",
	composer: "F. E. Belden, 1899 (1858 - 1945)",
	poet: "F. E. Belden, 1899 (1858 - 1945)",
	1: "Look upon Jesus, sinless is He;\nFather, impute His life unto me.\nMy life of scarlet, my sin and woe,\nCover with His life, whiter than snow.\nRefrain\nCover with His life, whiter than snow;\nFullness of His life then shall I know;\nMy life of scarlet, my sin and woe,\nCover with His life, whither than snow.",
	2: "Deep are the wounds transgression has made;\nRed are the stains; my soul is afraid.\nO to be covered, Jesus, with Thee,\nSafe from the law that now judgeth me!",
	3: "Longing the joy of pardon to know;\nJesus holds out a robe white as snow;\n“Lord, I accept it! Leaving my own,\nGladly I wear Thy pure life alone.”",
	4: "Reconciled by His death for my sin,\nJustified by His life pure and clean,\nSanctified by obeying His word,\nGlorified when returneth my Lord."
}
song["sda-hymnal/413"] = {
	title: "God Has Spoken by His Prophets",
	tune: "Ton-y-Botel",
	scripture: "Hebrews 1:1, 2",
	info: "Music used by permission of Eluned Jones and Dilys Evans, representatives of the late Gwenlwn Evans.",
	copyright: "“God Has Spoken by His Prophets” Words copyright © 1953. Renewal 1981 by The Hymn Society of America, Texas Christian University, Fort Worth, TX 76129. Used by permission.",
	1: "God has spoken by His prophets,\nSpoken His unchanging Word,\nEach from age to age proclaiming\nGod, the one, the righteous Lord.\nMid the world’s despair and turmoil,\nOne firm anchor holding fast;\nGod is King, His throne eternal,\nGod the first, and God the last.",
	2: "God has spoken by Christ Jesus,\nChrist, the everlasting Son,\nBrightness of the Father’s glory,\nWith the Father ever one;\nSpoken by the Word incarnate,\nGod of God, ere time began,\nLight of light, to earth descending,\nMan, revealing God to man.",
	3: "God yet speaks by His own Spirit\nSpeaking to the hearts of men,\nIn the age-long Word expounding\nGod’s own message, now as then;\nThrough the rise and fall of nations\nOne sure faith yet standing fast,\nGod is King, His Word unchanging,\nGod the first, and God the last."
}
song["sda-hymnal/414"] = {
	title: "Fruitful Trees, the Spirit's Sowing",
	tune: "All for Jesus",
	scripture: "Galatians 5:22, 23",
	copyright: "“Fruitful Trees, the Spirit’s Sowing” Words copyright © 1984 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Fruitful trees, the Spirit’s sowing,\nMay we ripen and increase,\nFruit to life eternal growing,\nRich in love and joy and peace.",
	2: "Laden branches freely bearing\nGifts the Giver loves to bless;\nHere is fruit that grows by sharing,\nPatience, kindness, gentleness.",
	3: "Rooted deep in Christ, our Master,\nChrist, our pattern and our goal,\nTeach us, as the years fly faster,\nGoodness, faith, and self control.",
	4: "Fruitful trees, the Spirit’s tending,\nMay we grow till harvests cease;\nTill we taste, in life unending,\nHeaven’s love and joy and peace."
}
song["sda-hymnal/415"] = {
	title: "Christ the Lord, All Power Possessing",
	tune: "Cwm Rhondda",
	scripture: "Matthew 28:18-20; Daniel 7:9-14; Revelation 1:7",
	info: "Alternate tune, UNSER HERRSCHER, No. 45 Higher key, No. 201",
	copyright: "“Christ the Lord, All Power Possessing” Words copyright © 1984 by C. Mervyn Maxwell",
	1: "Christ, the Lord, all power possessing,\nParting, mounted heaven’s height,\nGracious hands outstretched in blessing,\nClouds received His from their sight.\nChrist ascended, Christ ascended,\nChrist ascended on the clouds.\n(on the clouds)\nChrist ascended on the clouds.",
	2: "Daniel views earth’s judgment hour,\nAngels gathering open books.\nGod enthroned inflaming power\nFor His Son’s arrival looks.\nChrist approaches, Christ approaches,\nChrist approaches on the clouds.\n(on the clouds)\nChrist approaches on the clouds.",
	3: "Revelation’s word fulfilling,\nTrumpet, voices pierce the air.\nSaint and sinner fainting, thrilling,\nEvery eye behold Him there.\nChrist is coming, Christ is coming,\nChrist is coming on the clouds.\n(on the clouds)\nChrist is coming on the clouds."
}
song["sda-hymnal/416"] = {
	title: "The Judgment Has Set",
	1: "The judgment has set, the books have been opened;\nHow shall we stand in that great day,\nWhen every thought, and word, and action,\nGod, the righteous Judge, shall weigh?\nRefrain\nHow shall we stand in that great day?\nHow shall we stand in that great day?\nShall we be found before Him wanting?\nOr with our sins all washed away?",
	2: "The work is begun with those who are sleeping,\nSoon will the living here be tried,\nOut of the books of God’s remembrance,\nHis decision to abide.",
	3: "O, how shall we stand that moment of searching,\nWhen all our sins those books reveal?\nWhen from that court, each case decided,\nShall be granted no appeal?"
}
song["sda-hymnal/417"] = {
	title: "O Solemn Thought",
	tune: "Finally",
	info: "Alternate tune, Duane Street, No. 441",
	copyright: "“O Solemn Thought” Music copyright © 1984 by Allen Foster.",
	1: "O solemn thought and can it be\nThe hour o judgment now is come\nWhich soon must fix our destiny,\nAnd seal the sinner’s fearful down?\nYes, it is so; the judgment hour\nIs swiftly hastening to its close;\nThen will the Judge, in mighty power,\nDescend in vengeance on His foes.",
	2: "He who came down to earth to die\nAn offering for the sins of men,\nAnd then ascended upon on high,\nAnd will e’er long return again,\nIs standing now before the ark,\nAnd mercy seat and cherubim,\nTo plead His blood for saints, and make\nThe last remembrance of their sin.",
	3: "The solemn moment is at hand\nWhen we who have His name confessed,\nEach in his lot must singly stand\nAnd pass the final, searching test.\nJesus! we hope in Thee alone;\nIn mercy now upon us look,\nConfess our names before the throne,\nAnd blot our sins from out Thy book.",
	4: "O blessed Savior! may we feel\nThe full importance of this hour.\nInspire our hearts with holy zeal,\nAnd aid us by Thy Spirit’s power,\nThat we may in Thy strength be strong,\nAnd brave the conflict valiantly;\nThen, on Mount Zion, join the song,\nAnd swell the notes of victory."
}
song["sda-hymnal/418"] = {
	title: "Day of Judgment, Day of Wonders!",
	tune: "Unser Herrscher",
	info: "Alternate tune, CWM RHONDA, No. 415 Alternate harmony, No. 45",
	1: "Day of judgement, day of wonders!\nHark the trumpet’s awful sound,\nLouder than a thousand thunders,\nShakes the vast creation round!\nHow the summons\nWill the sinner’s heart confound!",
	2: "See the Lord in the glory nearing,\nClothed in majesty divine,\nYou who long for His appearing,\nThen shall say, “This God is mine!”\nGracious Savior, Gracious Savior,\nOwn me in that day as Thine.",
	3: "At His call the dead awaken,\nRise to life form earth and sea!\nAll the powers of nature shaken\nBy His looks prepare to flee.\nCareless sinner, Careless sinner,\nWhat will then become of thee?",
	4: "But to those who have confessed\nLoved and served the Lord below,\nHe will say, “Come near, ye blessed,\nSee the kingdom I bestow;\nYou forever, You forever\nShall my love and glory know.”"
}
song["sda-hymnal/419"] = {
	title: "Soon Shall the Trump of God",
	tune: "United Man",
	info: "Alternate tune, FESTAL SONG, No. 615",
	copyright: "“Soon Shall the Trump of God” Music copyright Normal L. Warren",
	1: "Soon shall the trump of God\nGive out the welcome sound,\nThat shakes death’s silent chamber walls,\nAnd breaks the turf sealed ground.",
	2: "You dwellers in the dust,\nAwake, come forth, and sing;\nSharp has your frost of winter been,\nBut bright shall be your spring.",
	3: "‘Twas sown in weakness here;\n‘Twill then be raised in power;\nThat which was sown in earthly seed\nShall rise a heav’nly flower."
}
song["sda-hymnal/420"] = {
	title: "Jerusalem, My Happy Home",
	tune: "Land of Rest",
	copyright: "“Jerusalem, My Happy Home” Music copyright © 1938 by J. Fischer & Bro., a division of Belwin - Mills Publishing Corp. Used with permission. All rights reserved.",
	1: "Jerusalem, my happy home,\nO how I long for thee!\nWhen will my sorrows have an end,\nThe joys when shall I see?",
	2: "The walls are all of precious stone,\nMost glorious to behold;\nThy gates are richly set with pearl,\nThy streets are paved with gold.",
	3: "Thy garden and thy pleasant walks\nMy study long have been;\nSuch dazzling views by human sight\nHave never yet been seen.",
	4: "Lord, help us by Thy mighty grace,\nTo keep in view the prize,\nTill Thou dost come to take us home\nTo that blessed paradise."
}
song["sda-hymnal/421"] = {
	title: "For All the Saints",
	tune: "Sine nomine",
	info: "Alternate tune, ENGELBERG, NO. 32",
	copyright: "“For All the Saints” Music from The English Hymnal by permission of Oxford of University Press.",
	1: "For all the saints\nwho from their labors rest,\nWho thee by faith\nbefore the world confessed.\nThy name, O Jesus,\nbe forever blest.\nAlleluia!\nAlleluia!",
	2: "Thou wast their Rock,\ntheir Fortress and their Might;\nThou, Lord, their captain\nin the well-fought fight;\nThou, in the darkness drear,\ntheir one true light.\nAlleluia!\nAlleluia!",
	3: "O may Thy soldiers,\nfaithful, true, and bold,\nFight as the saints\nwho nobly fought of old,\nAnd win with them\nthe victor’s crown of gold.\nAlleluia!\nAlleluia!",
	4: "And when the strife is fierce,\nthe warfare long,\nSteals on the ear\nthe distant triumph song,\nAnd hearts are brave again,\nand arms are strong.\nAlleluia!\nAlleluia!",
	5: "From earth’s wide bounds,\nfrom ocean’s farthest coast,\nThro’ gates of pearl\nstreams in the countless host,\nSinging to Father,\nSon, and Holy Ghost,\nAlleluia!\nAlleluia!"
}
song["sda-hymnal/422"] = {
	title: "Marching to Zion",
	1: "Come, we that love the Lord,\nAnd let our joys be known;\nJoin in a song with sweet accord,\nJoin in a song with sweet accord,\nAnd thus surround the throne,\nAnd thus surround the throne.\nRefrain\nWe’re marching to Zion,\nBeautiful, beautiful Zion;\nWe’re marching upward to Zion,\nThe beautiful city of God.",
	2: "Let those refuse to sing\nWho never knew our God;\nBut children of the heav’nly King,\nBut children of the heav’nly King\nMay speak their joys abroad,\nMay speak their joys abroad.",
	3: "The hill of Zion yields\nA thousand sacred sweets\nBefore we reach the heav’nly fields,\nBefore we reach the heav’nly fields\nOr walk the golden streets,\nOr walk the golden streets.",
	4: "Then let our songs abound\nAnd ev’ry tear be dry;\nWe’re marching thru Immanuel’s ground,\nWe’re marching thru Immanuel’s ground\nTo fairer worlds on high,\nTo fairer worlds on high."
}
song["sda-hymnal/423"] = {
	title: "Glorious Things of Thee Are Spoken",
	tune: "Austria",
	info: "Alternate tune, ABBOT'S LEIGH, No. 61",
	1: "Glorious things of thee are spoken,\nZion, city of our God;\nHe whose word cannot be broken\nFormed thee for His own abode;\nOn the Rock of Ages founded,\nWhat can shake thy sure repose?\nWith salvation’s wall surrounded,\nThou mayest smile at all thy foes.",
	2: "See the streams of living waters\nSpringing from eternal love,\nWell supply thy sons and daughters,\nAnd all fear and want remove;\nWho can faint when such a river\nEver flows their thirst to assuage?\nGrace, which, like the Lord, the Giver,\nNever fails from age to age.",
	3: "Round each habitation hovering,\nSee the cloud and fire appear\nFor a glory and a covering,\nShowing that the Lord is near;\nBlest inhabitants of Zion,\nWashed in the Redeemer’s blood;\nJesus, whom their souls rely on,\nMakes them kings and priests to God.",
	4: "Savior, if of Zion’s city\nI, through grace, a member am,\nLet the world deride or pity,\nI will glory in Thy name;\nFading is the worldling’s pleasure,\nAll his boasted pomp and show;\nSolid joys and lasting treasure\nNone but Zion’s children know."
}
song["sda-hymnal/424"] = {
	title: "For Thee, O Dear, Dear Country",
	tune: "Ely Cathedral",
	scripture: "Rev. 21",
	copyright: "“For Thee, O Dear, Dear Country” Coptright © 1910 by G. Schirmer, Inc. Used my permission of the publisher.",
	1: "For thee, O dear country,\nMine eyes their vigils keep;\nFor very love beholding\nThy holy name, they weep.",
	2: "O one, O only mansion!\nO Paradise of joy!\nWhere tears are ever banish’d\nAnd smiles have no alloy;",
	3: "With jasper glow thy burwarks,\nThy streets with emeralds blaze;\nThe sardius and the topaz\nUnite in thee their rays;",
	4: "The cross is all thy splendor,\nThe Crucified thy praise;\nHis laud and benediction\nThy ransom’d saints shall raise;",
	5: "O sweet and blessed country,\nThe home of God’s elect!\nO sweet and blessed country\nThat eager hearts expect!"
}
song["sda-hymnal/425"] = {
	title: "Holy, Holy, Is What the Angels Sing",
	1: "There is singing up in heaven\nsuch as we have never known,\nWhere the angels sing the praises\nof the Lamb upon the throne;\nTheir sweet harps are ever tuneful\nand their voices are always clear,\nO that we might be more like them\nwhile we serve the Master here!\nRefrain\nHoly, holy, is what the angels sing,\nAnd I expect to help them make\nthe courts of heaven ring;\nBut when I sing redemption’s story,\nthey will fold their wings,\nFor angels never felt the joys\nthat our salvation brings.",
	2: "But I hear another anthem,\nblending voices clear and strong,\n“Unto Him who hath redeemed us\nand hath bought us,” is the song;\nWe have come thro’ tribulations\nto this land so fair and bright,\nIn the fountain freely flowing\nHe hath made our garments white.",
	3: "Then the angels stand and listen,\nfor they cannot join that song,\nLike the sound of many waters,\nby that happy, blood-washed throng;\nFor they sing about great trials,\nbattles fought and vict’ries won,\nAnd they praised the great Redeemer,\nwho hath said to them, “Well done.”",
	4: "So, although I’m not an angel,\nyet I know that over there\nI will join a blessed chorus\nthat the angels cannot share;\nI will sing about my Savior,\nwho upon dark Calvary\nFreely pardoned my transgressions,\ndied to set the sinner free."
}
song["sda-hymnal/426"] = {
	title: "I Shall See the King",
	copyright: "“I Shall See the King” Copyright 1915 by B. D. Ackley. © renewed 1943, The Rodeheaver Co. (A Div. of Word, Inc.). All rights reserved. International copyright secured. Used by permission.",
	1: "I shall see the King where the angels sing,\nI shall see the King some day, in the better land,\nOn that golden strand,\nAnd with Him shall ever stay.\nRefrain:\nIn His glory, I shall see the King,\nAnd forever endless praises sing;\n‘Twas on Calvary Jesus died for me;\nI shall see the King someday.",
	2: "In the land of song, in the glory throng,\nWhere there never comes a night,\nWith my Lord once slain I shall ever reign\nIn the glory land of light.",
	3: "I shall see the King, all my tributes bring,\nAnd shall look upon His face;\nThen my song shall be how He ransomed me\nAnd has kept me by His grace."
}
song["sda-hymnal/427"] = {
	title: "No Night There",
	1: "In the land of fadeless day\nLies “the city four-square;”\nIt shall never pass away,\nAnd there is “no night there.”\nRefrain\nGod shall “wipe away all tears;”\nThere’s no death, no pain, nor fears;\nAnd they count not time by years,\nFor there is “no night there.”",
	2: "All the gates of pearl are made,\nIn “the city four-square;”\nAll the streets with gold are laid,\nAnd there is “no night there.”",
	3: "And the gates shall never close\nTo “the city four-square;”\nThere life’s crystal river flows,\nAnd there is “no night there.”",
	4: "There they need no sunshine bright,\nIn “that city four-square;”\nFor the Lamb is all the light,\nAnd there is “no night there.”"
}
song["sda-hymnal/428"] = {
	title: "Sweet By and By",
	1: "There’s a land that is fairer than day,\nAnd by faith we can see it afar;\nFor the Father waits over the way\nTo prepare us a dwelling place there.\nRefrain\nIn the sweet in the sweet\nBy and by by and by,\nWe shall meet on that beautiful shore;\nIn the sweet in the sweet\nBy and by by and by\nWe shall meet on that beautiful shore.",
	2: "We shall sing on that beautiful shore\nThe melodious songs of the blest,\nAnd our spirits shall sorrow no more\nNot a sigh for the blessing of rest.",
	3: "To our bountiful father above\nWe will offer our tribute of praise;\nFor the glorious gift of His love\nAnd the blessings that hallow our days."
}
song["sda-hymnal/429"] = {
	title: "Jerusalem the Golden",
	tune: "Ewing",
	1: "Jerusalem the golden,\nWith milk and honey blest,\nBeneath thy contemplation\nSink heart and voice oppressed.\nI know not, O I know not\nWhat holy joys are there;\nWhat radiancy of glory,\nWhat bliss beyond compare.",
	2: "They stand, those hall of Zion,\nAll jubilant with song,\nAnd bright with many an angel,\nAnd all the martyr throng.\nThe Prince is ever in them,\nThe daylight is serene;\nThe pastures of the blessed\nAre deck in glorious sheen.",
	3: "There is the throne of David,\nAnd there, from care released,\nThe shout of them that triumph,\nThe song of them that feast;\nAnd they who, with their Leader,\nHave conquered in the fight,\nForever and forever\nAre clad in robes of white.",
	4: "O sweet and blessed country,\nThe home of God’s elect!\nO sweet and blessed country,\nThat eager hearts expect!\nJesus, in mercy bring us\nTo that dear land of rest;\nWho art, with God the Father,\nAnd Spirit, ever blest."
}
song["sda-hymnal/430"] = {
	title: "Joy By and By",
	1: "O there’ll be joy when the work is done,\nJoy when the reapers gather home,\nBringing the sheaves at set of sun\nTo the New Jerusalem.\nRefrain\nJoy, joy, joy, there’ll be joy by and by,\nJoy, joy, joy, where the joys never die;\nJoy, joy, joy, for the day draweth nigh\nWhen the workers gather home.",
	2: "Sweet are the songs that we hope to sing,\nGrateful the thanks our hearts shall bring,\nPraising forever Christ our King\nIn the New Jerusalem.",
	3: "Pure are the joys that await us there,\nMany the golden mansions fair;\nJesus Himself doth them prepare,\nIn the New Jerusalem."
}
song["sda-hymnal/431"] = {
	title: "Over Yonder",
	1: "Come let us sing of homeland,\nDown by the crystal sea;\nWonderful land where Jesus\nBuildeth a mansion for me.\nRefrain\nOver yonder, down by the crystal sea,\ndown              by the crystal sea,\nOver yonder, there’s where I long to be,\nThere’s            where I long to be,\nNo more sorrow, toil, grief, nor care,\nIn the homeland bright and fair,\nOver, over there.\nOver there.",
	2: "Water of life there floweth,\nFruit in abundant store;\nCitizens of that country\nHunger and thirst never more.",
	3: "Come go with me to homeland,\nJesus invites you there;\nHelp spread the invitation,\nTell it to men everywhere."
}
song["sda-hymnal/432"] = {
	title: "Shall We Gather at the River",
	1: "Shall we gather at the river,\nwhere bright angel feet have trod,\nwith its crystal tide forever\nflowing by the throne of God?\nRefrain\nYes, we’ll gather at the river,\nthe beautiful, the beautiful river;\ngather with the saints at the river\nthat flows by the throne of God.",
	2: "On the margin of the river,\nwashing up its silver spray,\nwe will walk and worship ever,\nall the happy golden day.",
	3: "Ere we reach the shining river,\nlay we every burden down;\ngrace our spirits will deliver,\nand provide a robe and crown.",
	4: "Soon we’ll reach the shining river,\nsoon our pilgrimage will cease;\nsoon our happy hearts will quiver\nwith the melody of peace."
}
song["sda-hymnal/433"] = {
	title: "Ten Thousand Times Ten Thousand",
	tune: "Alford",
	1: "Ten thousand times ten thousand,\nIn sparkling raiment bright,\nThe armies of the ransomed saints\nThrong up the steeps of light.\n‘Tis finished, all is finished,\nTheir fight with death and sin.\nFling open wide the golden gates,\nAnd let the victors in.",
	2: "What rush of hallelujahs\nFills all the earth and sky!\nThe ringing of a thousand harps\nProclaims the triumph high.\nO day for which creation\nAnd all its tribes were made!\nO joy, for all its former woes\nA thousandfold repaid!",
	3: "O then what raptured greetings\nOn Canaan’s happy shore!\nWhat knitting severed friendship where\nDeath partings are no more!\nThen eyes with joy shall sparkle,\nThat brimmed with tears of late;\nOrphans no longer fatherless,\nNor widows desolate.",
	4: "Bring near Thy great salvation,\nThou Lamb for sinners slain,\nFill up the roll of Thine elect,\nThen take Thy power and reign!\nAppear, Desire of nations,\nThine exiles long for home;\nShow in the heavens Thy promised sign;\nThou Prince and Savior come!"
}
song["sda-hymnal/434"] = {
	title: "We Speak of the Realms",
	tune: "Contrast",
	1: "We speak of the realms of the blest,\nThat country so bright and so fair,\nAnd oft are its glories confessed-\nBut what must it be to be there!\nWe speak of its pathway of gold-\nIts walls decked with jewels so rare,\nIts wonders and pleasures untold-\nBut what must it be to be there!",
	2: "We speak of its freedom of sin,\nFrom sorrow, temptation and care,\nFrom trials without and within—\nBut what must it be to be there!\nWe speak of its service of love,\nOf the robes which glorified wear,\nOf the church of the First Born above-\nBut what must it be to be there!",
	3: "Our mourning is all at an end,\nWhen, raised by the life-giving word,\nWe see the new city descend,\nAdorned as a bride for her Lord;\nThe city so holy and clean,\nNo sorrow can breathe in the air;\nNo gloom of affliction or sin,\nNo shadow of evil, is there.",
	4: "Do Thou, midst temptation and woe,\nFor heaven my spirit prepare;\nAnd shortly I also so shall know\nAnd feel what it is to be there.\nThen o’er the bright fields we shall roam,\nIn glory celestial and fair,\nWith saints and with angels at home,\nAnd Jesus Himself will be there."
}
song["sda-hymnal/435"] = {
	title: "The Glory Song",
	1: "When all my labors and trails are o’er,\nAnd I am safe on that beautiful shore,\nJust to be near the Lord I adore,\nWill through the ages be glory for me.\nRefrain\nO that will be\nO      that will\nGlory for me, Glory for me,\nBe    glory for me,    Glory for me,\nGlory for me; When by His grace\nGlory for me;\nI shall look on His face,\nThat will be glory, be glory for me.",
	2: "When, by the gift of His infinite grace,\nI am accorded in heaven a place,\nJust to be there and to look on His face,\nWill through the ages be glory for me.",
	3: "Friends will be there I have loved long ago;\nJoy like a river around me will flow,\nYet, just a smile form my Savior, I know,\nWill through the ages be glory for me."
}
song["sda-hymnal/436"] = {
	title: "The Homeland",
	scripture: "Rev. 21:1,4",
	1: "The homeland! O the homeland!\nThe land of the free born!\nThere’s no night in the homeland,\nBut aye the fadeless morn;\nI’m sighing for homeland,\nMy heart is aching here;\nThere is no pain in the homeland\nTo which I’m drawing near;\nThere is no pain in the homeland\nTo which I’m drawing near.",
	2: "My Lord is in the homeland,\nWith angels bright and fair;\nThere’s no sin in the homeland,\nAnd no temptation there;\nThe music of the homeland\nIs ringing in my ears;\nAnd when I think of the homeland\nMy eyes are filled with tears;\nAnd when I think of the homeland\nMy ears are filled with tears.",
	3: "The dwellers in the homeland\nAre beckoning me to come,\nWhere neither death nor sorrow\nInvades their holy name;\nO dear, dear native country!\nO rest and peace above!\nChrist bring us all to the homeland\nOf Thy redeeming love;\nChrist bring us all to the homeland\nOf Thy redeeming love"
}
song["sda-hymnal/437"] = {
	title: "I'm Going Home",
	scripture: "Rev. 21:4",
	1: "My heav’nly home is bright and fair,\nNor pain nor death can enter there;\nIt’s glitt’ring tow’rs the sun outshine,\nThat heav’nly mansion shall me mine.\nRefrain\nI’m going home, I’m going home,\nI’m going home to die no more;\nTo die no more; to die no more-\nI’m going home to die no more.",
	2: "My Father’s house is built on high,\nFar, far above the starry sky;\nWhen from this earthly prison free,\nThat heav’nly mansion mine shall be.",
	3: "Let others seek a home below,\nWhich flames devour or waves oe’r flow;\nBe mine a happier lot to own\nA heav’nly mansion near the throne."
}
song["sda-hymnal/438"] = {
	title: "You Will See Your Lord A-Coming",
	tune: "Old Church Yard",
	copyright: "“You Will See Your Lord A-Coming” Arrangement copyright © 1984 by Wayne Hooper.",
	1: "You will see your Lord a-coming,\nYou will see your Lord a-coming,\nYou will see your Lord a-coming\nIn a few more days.\nRefrain:\nHear the band of music,\nHear the band of music,\nHear the band of music\nwhich is sounding thro’ the air.",
	2: "Gabriel sounds his mighty trumpet,\nGabriel sounds his mighty trumpet,\nGabriel sounds his mighty trumpet\nIn a few more days.",
	3: "You will see the saints arising,\nYou will see the saints arising,\nYou will see the saints arising\nIn a few more days.",
	4: "Angels bear them to the Savior,\nAngels bear them to the Savior,\nAngels bear them to the Savior\nIn a few more days.",
	5: "Then we’ll shout, our suff’ring over,\nThen we’ll shout, our suff’ring over,\nThen we’ll shout, our suff’ring over\nIn a few more days."
}
song["sda-hymnal/439"] = {
	title: "How Far From Home",
	tune: "'Tis Midnight Hour",
	1: "How far from? I asked, as on\nI bent my steps-the watchman spake:\n“the long, dark night is almost gone,\nThe morning soon will break.\nThen weep no more, but speed thy flight,\nWith Hope’s bright star guiding ray,\nTill thou shalt reach the realms of light,\nIn everlasting days.”",
	2: "I asked the warrior on the field;\nThis was his soul-inspiring song:\n“With courage, bold, the sword I’ll wield,\nThe battle is not long.\nThen weep no more, but well endure\nThe conflict, till thy work is done;\nFor this we know, the prize is sure,\nWhen victory is won.”",
	3: "I asked again; earth, sea, and sun\nSeemed, with one voice, to make reply:\n“Time’s wasting sands are nearly run,\nEternity is nigh.\nThen weep no more-with warning tones,\nPortentous signs are thickening round,\nThe whole creation, waiting, groans,\nTo hear the trumpet sound.”",
	4: "Not far from home! O blessed thought!\nThe traveler’s lonely heart to cheer;\nWhich oft a healing balm has brought,\nAnd dried the mourner’s tear.\nThen weep no more, since we shall meet\nWhere weary footsteps never roam-\nOur trails past, our joys complete,\nSafe in our Father’s home."
}
song["sda-hymnal/440"] = {
	title: "How Cheering Is the Christian's Hope",
	tune: "Exhortation",
	1: "How cheering is the Christian’s hope,\nWhile toiling here below!\nIt bouys us up while this passing through\nThis wilderness of woe,",
	2: "It bouys us up while this passing through\nThis wilderness of woe,\nIt bouys us up while passing through\nThis wilderness of woe.",
	3: "It points us to a land of rest,\nWhere saints with Christ will reign;\nWhere we shall meet the loved of earth,\nAnd never part again.",
	4: "Fly, lingering moments, fly, O, fly,\nDear Savior, quickly come!\nWe long to see Thee as Thou art,\nAnd reach that blissful home."
}
song["sda-hymnal/441"] = {
	title: "I Saw One Weary",
	tune: "Duane Street",
	scripture: "Titus 2:13",
	1: "I saw one weary, sad, and torn,\nWith eager steps press on the way,\nWho long the hallowed cross had born,\nStill looking for the promised day;\nWhile many a line of grief and care,\nUpon his brow was furrowed there;\nI asked what buoyed his spirits up,\n“O this” said he-“the blessed hope.”",
	2: "And one I saw, with sword and shield,\nWho boldly braved the world’s cold frown,\nAnd fought, unyielding, on the field,\nTo win an everlasting crown.\nThough worn with toil, oppressed by foes,\nNo murmur from his heart arose;\nI asked what buoyed his spirits up,\n“O this!” said he-“the blessed hope.”",
	3: "And there was one who left behind\nThe cherished friends of early years,\nAnd honor, pleasure, wealth re-signed,\nTo tread the path bedewed with tears.\nThrough trials deep and conflict sore,\nYet still a smile of joy he wore;\nO! what can bouy the spirit up?\n‘Tis this alone-the blessed hope.",
	4: "While pilgrims here we journey on\nIn this dark vale of sin and gloom,\nThrough tribulation, hate, and scorn,\nOr through the portals of the tomb,\nTill our returning King shall come\nTo tkae His exile captives home,\nO! what can bouy the spirits up?\n‘Tis this alone– the blessed hope."
}
song["sda-hymnal/442"] = {
	title: "How Sweet Are the Tidings",
	tune: "Bonnie Eloise",
	1: "How sweet are the tidings that greet the pilgrim’s ear,\nAs he wanders in exile from home!\nSoon, soon will the Savior in glory appear,\nAnd soon will the kingdom come.\nRefrain\nHe’s coming, coming, coming soon I know,\nComing back to this earth again;\nAnd the weary pilgrims will to glory go,\nWhen the Savior comes to reign.",
	2: "The mossy old graves where the pilgrims sleep\nShall be open as wide as before,\nAnd the millions that sleep in the mighty deep\nShall live on this earth once more.",
	3: "There we’ll meet ne’er to part in our happy\nEden home, Sweet songs of redemption we’ll sing;\nFrom the north, from the south, all the ransomed shall come,\nAnd worship our heavenly King.",
	4: "Hallelujah, Amen! Hallelujah again!\nSoon, if faithful, we all shall be there;\nO, be watchful, be hopeful, be joyful till then,\nAnd a crown of bright glory we’ll wear."
}
song["sda-hymnal/443"] = {
	title: "There'll Be No Sorrow There",
	tune: "No Sorrow There",
	scripture: "Revelation 21:4",
	1: "There’ll be no night in heaven,\nIn that blest world above;\nNo anxious toil, no weary hours;\nFor labor there is love.\nRefrain\nThere’ll be no sorrow there,\nThere’ll be no sorrow there;\nIn heaven above, where all is love,\nThere’ll be no sorrow there.",
	2: "There’ll be no grief in heaven;\nFor life is one glad day,\nAnd tears are those of former things\nWhich all have passed away.",
	3: "There’ll be no sin in heaven;\nBehold that blessed throng,\nAll holy in there spotless robes,\nAll holy in their song."
}
song["sda-hymnal/444"] = {
	title: "I'm a Pilgrim",
	1: "I’m a pilgrim, and I’m a stranger;\nI can tarry, I can tarry but a night;\nDo not detain me, for I am going\nTo where the fountains are ever flowing.\nRefrain\nI’m a pilgrim, and I’m a stranger;\nI can tarry, I can tarry but a night.",
	2: "There the glory is ever shining!\nO, my longing heart, my longing heart is there;\nHere in this country so dark and dreary,\nI long have wandered forlorn and weary.",
	3: "There’s the city to which I journey;\nMy Redeemer, my Redeemer is its light!\nThere is no sorrow, nor any sighing,\nNor any tears there, or any dying."
}
song["sda-hymnal/445"] = {
	title: "I'm But a Stranger Here",
	tune: "Oak",
	1: "I’m but a stranger here,\nHeav’n is my home;\nEarth is a desert drear,\nHeav’n is my home.\nDanger and sorrow stand\nRound me on every hand;\nHeav’n is my fatherland,\nHeav’n is my home.",
	2: "What though the tempest rage,\nHeav’n is my home;\nShort is my pilgrimage,\nHeav’n is my home;\nAnd time’s wild wintry blast\nSoon shall be over past;\nI shall reach home at last,\nHeav’n is my home.",
	3: "There at my Savior’s side\nHeav’n is my home;\nI shall be glorified,\nHeav’n is my home;\nThere are the good and blest,\nThose I love most and best;\nAnd there I, too, shall rest,\nHeav’n is my home."
}
song["sda-hymnal/446"] = {
	title: "Lo, What a Glorious Sight Appears",
	tune: "New Jerusalem",
	scripture: "Rev. 21:1-4",
	1: "Lo, what a glorious sight appears\nTo our believing eyes!\nThe earth and seas are passed away\nAnd the old rolling skies.\nAnd the old rolling skies,\nAnd the old rolling skies;\nThe earth and seas are passed away,\nAnd the old rolling skies.\nRefrain\nO that will be joyful, joyful, joyful!\nO that will be joyful\nWhen we meet to part no more!\nWhen we meet to part no more\nOn Canaan’s happy shore;\n‘Tis there we’ll meet at Jesus’ feet,\nWhen we meet to part no more!",
	2: "Attending angels shout for joy\nAnd the bright armies sing-\nMortals! Behold the sacred seat\nOf your descending King.\nOf your descending King,\nOf your descending King;\nMortals! Behold the sacred seat\nOf your descending King.",
	3: "His own soft hand shall wipe the tears\nFrom every weeping eye;\nAnd pains, and groans, and griefs, and fears,\nAnd death itself shall die!\nAnd death itself shall die,\nAnd death itself shall die;\nAnd pains, and groans, and griefs, and fears,\nAnd death itself shall die.",
	4: "How long, dear Savior! Oh, how long\nShall this bright hour delay?\nFly swifter round, ye wheels of time!\nAnd bring the welcome day.\nAnd bring the welcome day,\nAnd bring the welcome day;\nFly swifter round, ye wheels of time!\nAnd bring the welcome day."
}
song["sda-hymnal/447"] = {
	title: "Long Upon the Mountains",
	tune: "Greenville",
	scripture: "Eze. 34:12",
	1: "Long upon the mountains weary,\nHave the scattered flocks been torn;\nDark the dessert paths, and dreary;\nGrievous trials have they borne.\nNow the gathering call is sounding,\nSolemn in its warning voice;\nUnion, faith, and love, abounding,\nBid the little flock rejoice.",
	2: "Now the light of truth they’re seeking,\nIn its onward track pursue;\nAll the ten commandments keeping,\nThey are holy, just, and true.\nOn the words of lire they’re feeding,\nPrecious to their taste so sweet;\nAll their Master’s percepts heeding,\nBowing humbly to His feet.",
	3: "In that light of light and beauty,\nIn that golden city fair,\nSoon its pearly gates they’ll enter,\nAnd of all its glories share.\nThere, divine the soul’s expansions;\nFree from sin, and death, and pain;\nTear will never dim those mansions\nWhere the souls immortal reign.",
	4: "Soon He comes! With clouds descending;\nAll His saints, entombed arise;\nThe redeemed, in anthems blending,\nShout their vict’ry thro’ the skies.\nO, we long for Thine appearing;\nCome, O Savior, quickly come!\nBlessed hope! Our spirits cheering,\nTake thy ransomed children home."
}
song["sda-hymnal/448"] = {
	title: "O, When Shall I See Jesus",
	tune: "Morning Trumpet",
	copyright: "“O, When Shall I See Jesus” Arrangement copyright © 1984 by Melvin West",
	1: "O, when shall I see Jesus and reign with Him above,\nAnd shall hear the trumpet sound in that morning?\nAnd from the flowing fountain drink everlasting love,\nAnd shall hear the trumpet sound in that morning.\nRefrain:\nO, shout, glory!\nFor I shall mount above the skies,\nWhen I hear the trumpet sound in that morning.",
	2: "Gird on the gospel armor of faith and hope and love,\nAnd you’ll hear the trumpet sound in that morning.\nAnd when the combat’s ended He’ll carry you above,\nAnd you’ll hear the trumpet sound in that morning.",
	3: "Our ears shall hear with transport the host of heaven sing,\nAnd shall hear the trumpet sound in that morning.\nOur tongues shall chant the glories of our immortal King.\nAnd shall hear the trumpet sound in that morning,"
}
song["sda-hymnal/449"] = {
	title: "Never Part Again",
	copyright: "“Never Part Again” Arrangement used by permission of Dona Haynes Schultz.",
	1: "There is a land of pure delight,\nWhere bliss eternal reigns,\nInfinite day excludes the night\nAnd pleasures banish pain.\nRefrain\nWe’re trav’ling to Immanuel’s land,\nWe soon shall hear the trumpet sound,\nAnd soon we shall with Jesus reign,\nAnd never, never part again.\nWhat! Never part again? No, never part again,\nWhat! Never part again? No, never part again,\nAnd soon we shall with Jesus reign,\nAnd never, never part again.",
	2: "There everlasting spring abides,\nAnd never with’ring flowers,\nAnd but a little space divides\nThis heav’nly land from ours.",
	3: "Could we but stand where Moses stood,\nAnd view the landscape o’er,\nNot all this world’s pretended good\nCould ever charm us more."
}
song["sda-hymnal/450"] = {
	title: "Beautiful Zion",
	copyright: "“Beautiful Zion” Arrangement copyright © 1984 by Wayne Hooper.",
	1: "Beautiful Zion, built above,\nBeautiful city that I love,\nBeautiful gates of pearly white,\nBeautiful temple, God its light.",
	2: "Beautiful trees forever there,\nBeautiful fruit they always bear,\nBeautiful rivers gliding by,\nBeautiful fountains never dry.",
	3: "Beautiful crowns on every brow,\nBeautiful palms the conquerors show,\nBeautiful robes the ransomed wear,\nBeautiful all who enter there."
}
song["sda-hymnal/451"] = {
	title: "Together Let Us Sweetly Live",
	tune: "Canaan",
	copyright: "“Together Let Us Sweetly Live” Arrangement copyright © 1984 by Melvin West.",
	1: "Together let us sweetly live,\nI am bound for the land of Canaan.\nTogether love to Jesus give;\nI am bound for the land of Canaan.\nRefrain:\nO Canaan, bright Canaan,\nI am bound for the land of Canaan.\nO Canaan, it is my happy home,\nI am bound for the land of Canaan.",
	2: "Together let us watch and pray;\nI am bound for the land of Canaan.\nAnd wait redemption’s joyous day;\nI am bound for the land of Canaan.",
	3: "Our songs of praise shall fill the skies;\nI am bound for the land of Canaan.\nWhile higher still our joys shall rise;\nI am bound for the land of Canaan.",
	4: "Then come with me, beloved friend;\nI am bound for the land of Canaan.\nThe joys to come shall never end;\nI am bound for the land of Canaan."
}
song["sda-hymnal/452"] = {
	title: "What Heavenly Music",
	tune: "Heavenly Music",
	1: "What heavenly music steals over the sea!\nEntrancing the senses like sweet melody!\n‘Tis the voice of the angels borne soft on the air;\nFor me they are singing; their welcome I hear.",
	2: "On the banks of old Jordan, here gazing I stand,\nAnd earnestly longing, I stretch forth my hand;\nSend a convoy of angels, dear Jesus, I pray!\nLet me join that sweet music; come, take me away.",
	3: "Though dark are the waters and rough is the wave,\nIf Jesus permit, the wild surges I’ll brave;\nFor that heavenly music hath ravished me so,\nI must join in that chorus! I’ll go! let me go!"
}
song["sda-hymnal/453"] = {
	title: "We Have Heard",
	1: "We have heard from the bright, the holy, land;\nWe have heard, and our hearts are glad;\nFor we were a lonely pilgrim band,\nAnd weary, and worn, and sad.\nThey tell us the saints have a dwelling there-\nNo longer are homeless ones;\nAnd we know that the goodly land is fair,\nWhere life’s pure river runs.",
	2: "They say green fields are waving there,\nThat never a blight shall know;\nAnd the deserts wild are blooming fair,\nAnd the roses of Sharon grow.\nThere are lovely birds in the bowers green,\nTheir songs are blithe and sweet;\nAnd their warblings, gushing ever new,\nThe angels’ harpings greet.",
	3: "We have heard of the palms, the robes, the crowns,\nAnd the silvery band in white;\nOf the city fair, with pearly gates,\nAll radiant with light.\nWe have heard of the angels there, and saints,\nWith their harps of gold, how they sing;\nOf the mount with the fruitful tree of life,\nOf the leaves that healing bring.",
	4: "The King of that country, He is fair,\nHe’s the joy and light of the place;\nIn His beauty we shall behold Him there,\nAnd bask in His smiling face,\nWe’ll be there, we’ll be there in a little while,\nWe’ll join the pure and the blest;\nWe’ll have the palm, the robe, the crown,\nAnd forever be at rest."
}
song["sda-hymnal/454"] = {
	title: "Don't You See My Jesus Coming",
	tune: "Warrenton",
	copyright: "“Don't You See My Jesus Coming” Arrangement copyright © 1984 by Wayne Hooper.",
	1: "Don’t you see my Jesus coming,\nSee Him come in yonder cloud?\nWith ten thousand angels round Him,\nHow they do my Jesus crowd!\nRefrain\nI am bound for the kingdom,\nWill you go to glory with me?\nHallelujah! O praise ye the Lord!",
	2: "Don’t you see the saints ascending,\nHear them shouting thro’ the air.\nJesus smiling, trumpets sounding,\nNow His glory they shall share."
}
song["sda-hymnal/455"] = {
	title: "Immortal Love, Forever Full",
	tune: "Serenity",
	1: "Immortal Love, forever full,\nForever flowing free,\nForever shared, forever whole,\nA never ebbing sea!",
	2: "We nay not climb the heavenly steeps\nTo bring the Lord Christ down;\nIn vain we search the lowest deeps,\nFor him no depths can drown.",
	3: "But warm, so tender, even yet\nA present help is He;\nAnd faith has still its Olivet,\nAnd love its Galilee.",
	4: "The healing of His seamless dress\nIs by our beds of pain;\nWe touch Him in life’s throng and press,\nAnd we are whole again.",
	5: "Through Him the first fond prayers are said,\nOur lips of childhood frame;\nThe last low whispers of our dead\nAre burdened with His name.",
	6: "O Lord and Master of us all:\nWhate’er our names or sign,\nWe own Thy sway, we hear Thy call,\nWe test our lives by Thine!"
}
song["sda-hymnal/456"] = {
	title: "My Lord and I",
	1: "I have a Friend so precious,\nSo very dear to me,\nHe loves me with such tender love,\nHe loves so faithfully;\nI could not live apart form Him,\nI love to feel Him nigh,\nAnd so we dwell together,\nMy Lord and I.",
	2: "Sometimes I’m faint and weary,\nHe knows that I am weak,\nAnd as He bids me lean on Him,\nHis help I gladly seek;\nHe leads me in the paths of light,\nBeneath a sunny sky,\nAnd so we walk together,\nMy Lord and I.",
	3: "I tell Him all my sorrows,\nI tell Him all my joys,\nI tell Him all that pleases me,\nI tell Him what annoys;\nHe tells me what I ought to do,\nHe tells me how to try,\nAnd so we talk together,\nMy Lord and I.",
	4: "He knows that I am longing\nSome weary soul to win,\nAnd so He bids me go and speak\nThe loving word for Him;\nHe bids me tell His wondrous love,\nAnd why He came to die,\nAnd so we work together,\nMy Lord and I."
}
song["sda-hymnal/457"] = {
	title: "I Love to Tell the Story",
	1: "I love to tell the story\nof unseen things above,\nof Jesus and his glory,\nof Jesus and his love.\nI love to tell the story,\nbecause I know ’tis true;\nit satisfies my longings\nas nothing else can do.\nRefrain\nI love to tell the story,\n’twill be my theme in glory,\nto tell the old, old story\nof Jesus and his love.",
	2: "I love to tell the story;\nmore wonderful it seems\nthan all the golden fancies\nof all our golden dreams.\nI love to tell the story,\nit did so much for me;\nand that is just the reason\nI tell it now to thee.",
	3: "I love to tell the story;\n’tis pleasant to repeat\nwhat seems, each time I tell it,\nmore wonderfully sweet.\nI love to tell the story,\nfor some have never heard\nthe message of salvation\nfrom God’s own holy Word.",
	4: "I love to tell the story,\nfor those who know it best\nseem hungering and thirsting\nto hear it like the rest.\nAnd when, in scenes of glory,\nI sing the new, new song,\n’twill be the old, old story\nthat I have loved so long."
}
song["sda-hymnal/458"] = {
	title: "More Love to Thee",
	1: "More love to thee, O Christ, more love to thee!\nHear thou the prayer I make on bended knee.\nThis is my earnest plea: More love, O Christ, to thee;\nmore love to thee, more love to thee!",
	2: "Once earthly joy I craved, sought peace and rest;\nnow thee alone I seek, give what is best.\nThis all my prayer shall be: More love, O Christ, to thee;\nmore love to thee, more love to thee!",
	3: "Let sorrow do its work, come grief and pain;\nsweet are thy messengers, sweet their refrain,\nwhen they can sing with me: More love, O Christ, to thee;\nmore love to thee, more love to thee!",
	4: "Then shall my latest breath whisper thy praise;\nthis be the parting cry my heart shall raise;\nthis still its prayer shall be: More love, O Christ, to thee;\nmore love to thee, more love to thee!"
}
song["sda-hymnal/459"] = {
	title: "As the Bridegroom to His Chosen",
	tune: "Bridegroom",
	copyright: "“As the Bridegroom to His Chosen” Music copyright © 1969 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "As the bridegroom to His chosen,\nAs the king to His realm,\nAs the keep unto the castle,\nAs the pilot to the helm,\nSo, Lord, art Thou to me.",
	2: "As the fountain in the garden,\nAs the candle in the dark,\nAs the treasure in the coffer,\nAs the manna in the ark,\nSo, Lord, art Thou to me.",
	3: "As the ruby in the setting,\nAs the honey in the comb,\nAs the light within the lantern,\nAs the father in the home,\nSo, Lord, art Thou to me.",
	4: "As the sunshine in the heavens,\nAs the image in the glass,\nAs the fruits up in the fig tree,\nAs the dew upon the grass,\nSo, Lord, art Thou to me."
}
song["sda-hymnal/460"] = {
	title: "As Water to the Thirsty",
	copyright: "“As Water to the Thirsty” Words copyright © 1979 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © 1979 by Stainer & Bell, Ltd. Used by permission of Galaxy Music Corporation, New York, sole U.S. ag",
	1: "As water to the thirsty,\nAs beauty to the eyes,\nAs strength that follows weakness,\nAs truth instead of lies;\nAs song-time and springtime and summertime to be,\nSo is my Lord, my living Lord,\nSo is my Lord to me.",
	2: "Like calm in place of clamor\nLike peace that follows pain,\nLike meeting after parting,\nLike sunshine after rain;\nLike moonlight and starlight and sunlight on the sea,\nSo is my Lord, my living Lord,\nSo is my Lord to me.",
	3: "As sleep that follows fever,\nAs gold instead of grey,\nAs freedom after bondage,\nAs sunrise to the day;\nAs home to the trav’ler and all he longs to see,\nSo is my Lord, my living Lord,\nSo is my Lord to me."
}
song["sda-hymnal/461"] = {
	title: "Be Still, My Soul",
	tune: "Finlandia",
	scripture: "Psalm 46:10; 1 Thess. 4:17",
	copyright: "“Be Still, My Soul”  Melody used by permission of Breitkopf & Härtel, Wiesbaden. Arrangement copyright © 1933 by the Presbyterian Board of Christian Education; renewed 1961; from The Hymnal. Used by permission of Westminister Press, Philadelphia, PA.",
	1: "Be still, my soul:\nthe Lord is on thy side;\nBear patiently the cross of grief or pain;\nLeave to thy God to order and provide;\nIn every change He faithful will remain.\nRefrain\nBe still, my soul:\nthy best, thy heavenly friend\nThrough thorny ways leads to a joyful end.",
	2: "Be still, my soul:\nthy God doth undertake\nTo guide the future as He has the past.\nThy hope, thy confidence let nothing shake;\nAll now mysterious shall be bright at last.",
	3: "Be still, my soul:\nthe hour is hastening on\nWhen we shall be forever with the Lord,\nWhen disappointment, grief, and fear are gone,\nSorrow forgot, love’s purest joys restored."
}
song["sda-hymnal/462"] = {
	title: "Blessed Assurance Jesus is Mine",
	poet: "Fanny J. Crosby, 1873 (1820 - 1915)",
	1: "Blessed assurance, Jesus is mine!\nO what a foretaste of glory divine!\nHeir of salvation, purchase of God,\nborn of his Spirit, washed in his blood.\nRefrain\nThis is my story, this is my song,\npraising my Savior all the day long;\nthis is my story, this is my song,\npraising my Savior all the day long.",
	2: "Perfect submission, perfect delight,\nvisions of rapture now burst on my sight;\nangels descending bring from above\nechoes of mercy, whispers of love.",
	3: "Perfect submission, all is at rest;\nI in my Savior am happy and blest,\nwatching and waiting, looking above,\nfilled with his goodness, lost in his love."
}
song["sda-hymnal/463"] = {
	title: "Peace, Perfect Peace",
	tune: "Pax tecum",
	scripture: "Isa. 26:3",
	1: "Peace, perfect peace, in this dark world of sin?\nThe blood of Jesus whispers peace within.",
	2: "Peace, perfect peace, by thronging duties pressed?\nTo do the will of Jesus: this is rest.",
	3: "Peace, perfect peace, with loved ones far away?\nIn Jesus’ keeping we are safe, and they.",
	4: "Peace, perfect peace, our future all unknown?\nJesus we know, and He is on the throne."
}
song["sda-hymnal/464"] = {
	title: "When I Can Read My Title Clear",
	copyright: "“When I Can Read My Title Clear” Arrangement copyright © 1984 by Wayne Hooper",
	1: "When I can read my title clear\nTo mansions in the skies\nI’ll bid farewell to every fear,\nAnd wipe my weeping eyes;\nAnd wipe my weeping eyes,\nAnd wipe my weeping eyes,\nI’ll bid farewell to every fear,\nAnd wipe my weeping eyes.",
	2: "Should earth against my soul engage,\nAnd fiery darts be hurled,\nThen I can smile at Satan’s rage,\nAnd face a frowning world;\nAnd face a frowning world,\nAnd face a frowning world,\nThen I can smile at Satan’s rage,\nAnd face a frowning world."
}
song["sda-hymnal/465"] = {
	title: "I Heard the Voice of Jesus",
	tune: "Kingsfold",
	copyright: "“I Heard the Voice of Jesus” Music from The English Hymnal by permission Oxford University Press.",
	1: "I heard the voice of Jesus say,\n“Come unto Me and rest;\nLay down, thou weary one, lay down\nThy head upon My breast.”\nI came to Jesus as I was,\nWeary and worn and sad;\nI found in Him a resting place,\nAnd He has made me glad.",
	2: "I heard the voice of Jesus say,\n“Behold, I freely give\nThe living water; thirsty one,\nStoop down and drink and live.”\nI came to Jesus, and I drank\nOf that life giving stream;\nMy thirst was quench’d, my soul revived,\nAnd now I live in Him.",
	3: "I heard the voice of Jesus say,\n“I am this dark world’s light;\nLook unto Me, thy morn shall rise,\nAnd all thy day be bright.”\nI looked to Jesus, and I found\nIn Him my star, my sun;\nAnd in that light of life I’ll walk,\nTill trav’ling days are done."
}
song["sda-hymnal/466"] = {
	title: "Wonderful Peace",
	1: "Far away in the depths of my spirit tonight,\nRolls a melody sweeter than psalm;\nIn celestial like strains it unceasingly falls\nO’er my soul like an infinite calm.\nRefrain\nPeace! Peace! wonderful peace,\nComing down from the Father above;\nSweep over my spirit forever, I pray,\nIn fathomless billows of love.",
	2: "What a treasure I have in this wonderful peace,\nBuried deep in my inner most soul,\nSo secure that no power can mine it away,\nWhile the years of eternity roll!",
	3: "I believe when I rise to that city of peace,\nWhere the Author of peace I shall see,\nThat one strain of the song which the ransomed will sing,\nIn that heavenly kingdom will be-",
	4: "Weary soul, without gladness or comfort or rest,\nPassing down the rough pathway of time!\nMake the Savior your friend ere the shadows grow dark;\nO accept of this peace so sublime."
}
song["sda-hymnal/467"] = {
	title: "Life Is Great! So Sing About It",
	tune: "Litherop",
	info: "Alternate tune, LAUDA ANIMA, No. 4",
	copyright: "“Life Is Great! So Sing About It” Copyright © 1983 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Life is great! So sing about it,\nAs we can and as we should-\nShop and buses, towns and people,\nVillage, farmland, field and wood,\nLife is great and life is given;\nLife is lovely, free and good.",
	2: "Life is great! What ever happens,\nSnow or sunshine, joy or pain,\nHardship, grief or disillusion,\nSuffering that I can’t explain\nLife is great if someone loves me,\nHolds my hand and calls my name.",
	3: "Love is great! The love of lovers,\nWhispered words and longing eyes;\nLove that gazes at the cradle\nWhere a child of loving lies;\nLove that lasts when youth has faded,\nBends with age, but never dies.",
	4: "Love is giving and receiving\nBoy and girl, or friend with friend;\nLove is bearing and forgiving\nAll the hurts that hate can send;\nLove’s the greatest way of living,\nHoping, trusting to the end.",
	5: "God is great! in Christ He loved us,\nAs we should, but never can-\nLove that suffered, hoped and trusted\nWhen disciples turned and ran,\nLove that broke through death forever.\nPraise that loving, living Man!"
}
song["sda-hymnal/468"] = {
	title: "A Child of the King",
	1: "My Father is rich in houses and lands,\nHe holdeth the wealth of the world in His hands!\nOf rubies and diamonds, of silver and gold,\nHis coffers are full, He has riches untold.\nRefrain\nI’m a child of the King,\nA child of the King:\nWith Jesus my Savior,\nI’m a child of the King.",
	2: "My Father’s own Son, the Savior of men,\nOnce wandered on earth as the poorest of them;\nBut now He is pleading our pardon on high,\nThat we may be His when He comes by and by.",
	3: "I once was an outcast stranger on earth,\nA sinner by choice, an alien by birth,\nBut I’ve been adopted, my name’s written down,\nAn heir to a mansion, a robe and a crown.",
	4: "A tent or a cottage, why should I care?\nThey’re building a palace for me over there;\nThough exiled from home, yet still may I sing:\nAll glory to God, I’m a child of the King."
}
song["sda-hymnal/469"] = {
	title: "Leaning on the Everlasting Arms",
	scripture: "Deuteronomy 33:27",
	1: "What a fellowship, what a joy divine,\nleaning on the everlasting arms;\nwhat a blessedness, what a peace is mine,\nleaning on the everlasting arms.\nRefrain\nLeaning, leaning,\nsafe and secure from all alarms;\nleaning, leaning,\nleaning on the everlasting arms.",
	2: "O how sweet to walk in this pilgrim way,\nleaning on the everlasting arms;\nO how bright the path grows from day to day,\nleaning on the everlasting arms.",
	3: "What have I to dread, what have I to fear,\nleaning on the everlasting arms?\nI have blessed peace with my Lord so near,\nleaning on the everlasting arms."
}
song["sda-hymnal/470"] = {
	title: "There's Sunshine in My Soul Today",
	1: "There’s sunshine in my soul today,\nMore glorious and bright\nThan glows in any earthly sky,\nFor Jesus is my light.\nRefrain\nO there’s sunshine, blessed sunshine,\nWhen the peaceful, happy moments roll\nWhen Jesus shows His smiling face\nThere is sunshine in the soul.",
	2: "There’s music in my soul today,\nA carol to my King,\nAnd Jesus, listening, can hear\nThe songs I cannot sing.",
	3: "There’s springtime in my soul today,\nFor when the Lord is near,\nThe dove of peace sings in my heart,\nThe flowers of grace appear.",
	4: "Theres gladness in my soul today,\nAnd hope, and praise, and love,\nFor blessings which He gives me now,\nFor joys “laid up” above."
}
song["sda-hymnal/471"] = {
	title: "Grant Us Your Peace",
	copyright: "“Grant Us Your Peace” Arrangement copyright © 1984 by Melvin West.",
	1: "Dona nobis pacem, pacem;\nDona nobis pacem.\nDona nobis pacem;\nDona nobis pacem.\nDona nobis pacem;\nDona nobis pacem.",
	2: "Father grant us, grant us Your peace;\nOh, loving Father, grant us Your peace.\nGrant us, grant us peace;\nGrant us, grant us, grant Your peace;\nGrant us, grant us peace;\nLoving Father, grant us Your peace.",
	3: "Accordenous ta paix, ta paix, Accordenous ta paix.\nAccordenous ta paix;\nAccordenous ta paix.\nAccordenous ta paix,\nAccordenous ta paix.",
	4: "Padre, danos tupaz, tupaz;\nPadre, danos, danos tupaz\nPadre, danos, tupaz.\nPadre, danos, danos tupaz\nPadre, danos tupaz.\nPadre, danos, danos tupaz."
}
song["sda-hymnal/472"] = {
	title: "A Song of Heaven and Homeland",
	1: "Sometimes I hear strange music,\nLike none e’er heard before,\nCome floating softly earthward\nAs thro’ heav’n’s open door:\nIt seems like angel voices,\nIn strains of and love,\nThat swell the mighty chorus\nAround the throne above.\nRefrain\nO sweet, celestial music,\nHeard from a land afar-\nThe song of Heav’n and Homeland,\nThro’ doors God leaves ajar!",
	2: "Now soft, and low, and restful,\nIt floods my soul with peace,\nAs if God’s benediction\nBade all earth’s troubles cease.\nThen grander than the voices,\nOf wind, and wave, and sea-\nIt fills the dome of heaven\nWith glorious harmony.",
	3: "This music haunts me ever,\nLike something heard in dreams-\nIt seems to catch the cadence\nOf heav’nly winds and streams,\nMy heart is filled with rapture,\nTo think, some day to come,\nI’ll sing it with the angels-\nThe song of heav’n and home."
}
song["sda-hymnal/473"] = {
	title: "Nearer My God, to Thee",
	tune: "Bethany",
	scripture: "Gen. 28:10-19",
	1: "Nearer, my God, to thee, nearer to thee!\nE’en though it be a cross that raiseth me,\nstill all my song shall be,\nnearer, my God, to thee;\nnearer, my God, to thee, nearer to thee!",
	2: "Though like the wanderer, the sun gone down,\ndarkness be over me, my rest a stone;\nyet in my dreams I’d be\nnearer, my God, to thee;\nnearer, my God, to thee, nearer to thee!",
	3: "There let the way appear, steps unto heaven;\nall that thou sendest me, in mercy given;\nangels to beckon me\nnearer, my God, to thee;\nnearer, my God, to thee, nearer to thee!",
	4: "Then, with my waking thoughts bright with thy praise,\nout of my stony griefs Bethel I’ll raise;\nso by my woes to be\nnearer, my God, to thee;\nnearer, my God, to thee, nearer to thee!",
	5: "Or if, on joyful wing cleaving the sky,\nsun, moon, and stars forgot, upward I fly,\nstill all my song shall be,\nnearer, my God, to thee;\nnearer, my God, to thee, nearer to thee!"
}
song["sda-hymnal/474"] = {
	title: "Take the Name of Jesus With You",
	1: "Take the Name of Jesus with you,\nChild of sorrow and of woe,\nIt will joy and comfort give you;\nTake it then, where’er you go.\nRefrain\nPrecious Name, O how sweet!\nHope of earth and joy of Heav’n.\nPrecious Name, O how sweet!\nHope of earth and joy of Heav’n.",
	2: "Take the Name of Jesus ever,\nAs a shield from every snare;\nIf temptations round you gather,\nBreathe that holy Name in prayer.",
	3: "O the precious Name of Jesus!\nHow it thrills our souls with joy,\nWhen His loving arms receive us,\nAnd His songs our tongues employ!",
	4: "At the Name of Jesus bowing,\nFalling prostrate at His feet,\nKing of kings in heav’n we’ll crown Him,\nWhen our journey is complete."
}
song["sda-hymnal/475"] = {
	title: "Balm in Gilead",
	scripture: "Jeremiah 8:22",
	1: "There is a balm in Gilead\nTo make the wounded whole;\nThere is a balm in Gilead\nTo heal the sin-sick soul.",
	2: "Sometimes I feel discouraged,\nAnd think my work’s in vain,\nBut then the Holy Spirit\nRevives my soul again.",
	3: "If you can not preach like Peter,\nIf you can not pray like Paul,\nYou can tell the love of Jesus,\nAnd say He died for all."
}
song["sda-hymnal/476"] = {
	title: "Burdens Are Lifted at Calvary",
	copyright: "“Burdens Are Lifted at Calvary” Copyright © 1952. Renewal 1980 by John M. Moore. Assigned to Singspiration (ASCAP), Division of the Zondervan Corporation, All rights reserved. Used by permission.",
	1: "Days are filled with sorrow and care,\nHearts are lonely and drear;\nBurdens are lifted at Calvary,\nJesus is very near.\nRefrain\nBurdens are lifted at Calvary,\nCalvary, Calvary,\nBurdens are lifted at Calvary,\nJesus is very near.",
	2: "Cast your care on Jesus today,\nLeave your worry and fear;\nBurdens are lifted at Calvary,\nJesus is very near.",
	3: "Troubled soul, the Saviour can see,\nEv’ry heartache and tear;\nBurdens are lifted at Calvary\nJesus is very near."
}
song["sda-hymnal/477"] = {
	title: "Come, Ye Disconsolate",
	tune: "Consalator",
	1: "Come, ye disconsolate, where’er ye languish.\ncome to the mercy seat, fervently kneel.\nHere bring your wounded hearts, here tell your anguish;\nearth has no sorrow that heaven cannot heal.",
	2: "Joy of the desolate, light of the straying,\nhope of the penitent, fadeless and pure!\nHere speaks the Comforter, tenderly saying,\n“Earth has no sorrow that heaven cannot cure.”",
	3: "Here see the bread of life; waters flowing\nforth from the throne of God, pure from above.\nCome to the feast of love; come, ever knowing\nearth has no sorrow but heaven can remove."
}
song["sda-hymnal/478"] = {
	title: "Sweet Hour of Prayer",
	1: "Sweet hour of prayer! sweet hour of prayer!\nthat calls me from a world of care,\nand bids me at my Father’s throne\nmake all my wants and wishes known.\nIn seasons of distress and grief,\nmy soul has often found relief,\nand oft escaped the tempter’s snare\nby thy return, sweet hour of prayer!",
	2: "Sweet hour of prayer! sweet hour of prayer!\nthy wings shall my petition bear\nto him whose truth and faithfulness\nengage the waiting soul to bless.\nAnd since he bids me seek his face,\nbelieve his word, and trust his grace,\nI’ll cast on him my every care,\nand wait for thee, sweet hour of prayer!",
	3: "Sweet hour of prayer! sweet hour of prayer!\nMay I thy consolation share\nTill from Mount Pisgah’s lofty height\nI view my home and take my flight.\nIn my immortal flesh I’ll rise\nTo size the everlasting prize.\nAnd shout while passing through the air,\n“Farewell, farewell, sweet hour of prayer!”"
}
song["sda-hymnal/479"] = {
	title: "Tread Softly",
	poet: "Fanny J. Crosby (1820 - 1915)",
	1: "Be silent, be silent, A whisper is heard;\nBe silent, and listen, Oh treasure each word.\nRefrain\nTread softly, tread softly, The Master is here;\nTread softly here, tread softly here,\nTread softly, tread softly, He bids us draw near.\nTread softly here, tread softly here,",
	2: "Be silent, be silent, For holy this place,\nThis altar that echoes The message of grace.",
	3: "Be silent, be silent, Breathe humbly our prayer;\nA foretaste of Eden This moment we share.",
	4: "Be silent, be silent, His mercy record;\nBe silent, be silent, And wait on the Lord."
}
song["sda-hymnal/480"] = {
	title: "Dear Lord and Father",
	tune: "Repton",
	1: "Dear Lord and Father of mankind,\nforgive our foolish ways;\nreclothe us in our rightful mind,\nin purer lives thy service find,\nin deeper reverence, praise.",
	2: "In simple trust like theirs who heard,\nbeside the Syrian sea,\nthe gracious calling of the Lord,\nlet us, like them, without a word,\nrise up and follow thee.",
	3: "O sabbath rest by Galilee,\nO calm of hills above,\nwhere Jesus knelt to share with thee\nthe silence of eternity,\ninterpreted by love!",
	4: "Drop thy still dews of quietness,\ntill all our strivings cease;\ntake from our souls the strain and stress,\nand let our ordered lives confess\nthe beauty of thy peace.",
	5: "Breathe through the heats of our desire\nthy coolness and thy balm;\nlet sense be dumb, let flesh retire;\nspeak through the earthquake, wind, and fire,\nO still, small voice of calm."
}
song["sda-hymnal/481"] = {
	title: "Dear Lord and Father",
	tune: "Rest",
	info: "Alternate tune and additional stanzas, Repton, No. 480",
	1: "Dear Lord and Father of mankind,\nforgive our foolish ways;\nreclothe us in our rightful mind,\nin purer lives thy service find,\nin deeper reverence, praise.",
	2: "In simple trust like theirs who heard,\nbeside the Syrian sea,\nthe gracious calling of the Lord,\nlet us, like them, without a word,\nrise up and follow thee.",
	3: "O sabbath rest by Galilee,\nO calm of hills above,\nwhere Jesus knelt to share with thee\nthe silence of eternity,\ninterpreted by love!",
	4: "Drop thy still dews of quietness,\ntill all our strivings cease;\ntake from our souls the strain and stress,\nand let our ordered lives confess\nthe beauty of thy peace.",
	5: "Breathe through the heats of our desire\nthy coolness and thy balm;\nlet sense be dumb, let flesh retire;\nspeak through the earthquake, wind, and fire,\nO still, small voice of calm."
}
song["sda-hymnal/482"] = {
	title: "Father, Lead Me Day by Day",
	tune: "Posen",
	1: "Father, lead me day by day,\nEver in Thine own sweet way;\nTeach me to be pure and true;\nShow me what I ought to do.",
	2: "When in danger, make me brave;\nMake me know that Thou canst save;\nKeep me safe by Thy dear side;\nLet me in Thy love abide.",
	3: "When tempted to do wrong,\nMake me steadfast, wise, and strong;\nAnd when all alone I stand,\nShield me with Thy mighty hand.",
	4: "May I do the good I know,\nBe Thy loving child below,\nThen at last go home to Thee,\nEvermore Thy child to be."
}
song["sda-hymnal/483"] = {
	title: "I Need Thee Every Hour",
	1: "I need thee every hour, most gracious Lord;\nno tender voice like thine can peace afford.\nRefrain\nI need thee, O I need thee;\nevery hour I need thee;\nO bless me now, my Savior, I come to thee.",
	2: "I need thee every hour; stay thou nearby;\ntemptations lose their power when thou art nigh.",
	3: "I need thee every hour, in joy or pain;\ncome quickly and abide, or life is vain.",
	4: "I need thee every hour; teach me thy will;\nand thy rich promises in me fulfill."
}
song["sda-hymnal/484"] = {
	title: "I Need Thee, Precious Jesus",
	tune: "Rutherford",
	1: "I need Thee precious Jesus, for I am very poor;\nA stranger and a pilgrim, I have no earthly store.\nI need the love of Jesus to cheer me on my way,\nTo guide my doubting foot steps, to be my strength and stay.",
	2: "I need the heart of Jesus to feel each anxious care,\nTo tell my every trial, and all my sorrows share.\nI need the Holy Spirit to teach me what I am,\nTo show me more of Jesus, to point me to the Lamb.",
	3: "I need Thee precious Jesus, I hope to see Thee soon,\nEncircled with the rainbow, and seated on Thy throne.\nThere with Thy blood-bought children, my joy shall ever be\nTo sing Thy ceaseless praises, to gaze, my Lord, on Thee."
}
song["sda-hymnal/485"] = {
	title: "I Must Tell Jesus",
	1: "I must tell Jesus all of my trials,\nI cannot bear these burdens alone;\nIn my distress He kindly will help me,\nHe ever loves and cares for His own.\nRefrain\nI must tell Jesus! I must tell Jesus!\nI cannot bear my burdens alone;\nI must tell Jesus! I must tell Jesus!\nJesus can help me, Jesus alone.",
	2: "I must tell Jesus all of my troubles,\nHe is a kind, compassionate Friend;\nIf I but ask Him He will deliver,\nMake of my troubles quickly an end.",
	3: "Oh, how the world to evil allures me!\nOh, how my heart is tempted to sin!\nI must tell Jesus, and He will help me\nOver the world the vict’ry to win."
}
song["sda-hymnal/486"] = {
	title: "I Do Believe",
	tune: "I Do Believe",
	1: "Father, I stretch my hands to Thee;\nNo other help I know;\nIf Thou withdraw Thyself from me,\nAh, wither shall I go?",
	2: "On Thy dear Son I now believe,\nO let me feel Thy power;\nAnd all my varied wants relieve,\nIn this accepted hour.",
	3: "Author of faith! To Thee I lift\nMy weary, longing eyes;\nO let me now receive that gift;\nMy soul without it dies.",
	4: "Surely Thou canst not let me die;\nO speak, and I shall live;\nAnd here I will unwearied lie,\nTill Thou Thy Spirit give.",
	5: "How would my fainting soul rejoice\nCould I but see Thy face!\nNow let me hear Thy quickening voice,\nAnd taste Thy pardoning grace.",
	6: "I do believe, I now believe\nThat Jesus died for me,\nAnd that He shed His precious blood\nFrom sin to set me free."
}
song["sda-hymnal/487"] = {
	title: "In the Garden",
	scripture: "John 20:15-17",
	copyright: "“In the Garden” Copyright 1912 by Hall-Mack Co. © renewed The Rodeheaver Co. (A Div. of Word, Inc.) All rights reserved. International copyright secured. Used by permission.",
	1: "I come to the garden alone,\nWhile the dew is still on the roses;\nAnd the voice I hear, falling on my ear,\nThe Son of God discloses.\nRefrain:\nAnd He walks with me,\nAnd He talks with me,\nAnd He tells me I am His own;\nAnd the joy we share as we tarry there,\nNone other has ever known.",
	2: "He speaks, and the sound of His voice\nIs so sweet the birds hush their singing,\nAnd the melody that He gave to me\nWithin my heart is ringing.",
	3: "I’d stay in the garden with Him\nThough the night around me be falling,\nBut He bids me go; through the voice of woe,\nHis voice to me is calling."
}
song["sda-hymnal/488"] = {
	title: "At First I Prayed for Light",
	tune: "Woolwich",
	1: "At first I prayed for light:\nCould I but see the way,\nHow gladly, swiftly would I walk\nTo everlasting day!",
	2: "And next I prayed for strength:\nThat I might tread the road\nWith firm, unfaltering feet, and win\nThe heaven’s serene abode.",
	3: "And then I asked for faith:\nCould I but trust my God,\nI’d live infolded in His peace,\nThough foes were all abroad.",
	4: "But now I pray for love:\nDeep love to God and man;\nA living love that will not fail,\nHowever dark His plan.",
	5: "And light and strength and faith\nAre opening everywhere!\nGod waited patiently until\nI prayed the larger prayer."
}
song["sda-hymnal/489"] = {
	title: "Jesus, Lover of My Soul",
	tune: "Martyn",
	info: "Alternate tune, REFUGE, No. 297",
	1: "Jesus, lover of my soul,\nLet me to thy bosom fly,\nWhile the billows near me roll,\nWhile the tempest still is high;\nHide me, O my Savior, hide!\nTill the storm of life is past;\nSafe into the haven guide,\nO receive my soul at last!",
	2: "Other refuge has I none,\nHangs my helpless soul on thee;\nLeave O leave me not alone!\nStill support and comfort me;\nAll my trust on Thee is stayed,\nAll my help from Thee I bring;\nCover my defenseless head\nWith the shadow of Thy wing.",
	3: "Thou, O Christ, all I want,\nMore than all in Thee I find;\nRaise the fallen, cheer the faint,\nHeal the sick, and lead the blind.\nJust and holy is Thy name,\nI am all unrighteousness;\nVile and full of sin I am;\nThou art full of truth and grace.",
	4: "Plenteous grace with Thee is found-\nGrace to pardon all my sin;\nLet the healing streams abound,\nMake and keep me pure within;\nThou of life the Fountain art,\nFreely let me take of Thee;\nSpring Thou up within my heart,\nRise to all eternity."
}
song["sda-hymnal/490"] = {
	title: "Jesus, Lover of My Soul",
	tune: "Hollingside",
	info: "Alternate tune, ABERYSTWYTH, No. 592",
	1: "Jesus, lover of my soul, let me to Thy bosom fly,\nWhile the nearer waters roll, while the tempest still is high.\nHide me, O my Savior, hide, till the storm of life is past;\nSafe into the haven guide; O receive my soul at last.",
	2: "Other refuge have I none, hangs my helpless soul on Thee;\nLeave, ah! leave me not alone, still support and comfort me.\nAll my trust on Thee is stayed, all my help from Thee I bring;\nCover my defenseless head with the shadow of Thy wing.",
	3: "Thou, O Christ, art all I want, more than all in Thee I find;\nRaise the fallen, cheer the faint, heal the sick, and lead the blind.\nJust and holy is Thy Name, I am all unrighteousness;\nFalse and full of sin I am; Thou art full of truth and grace.",
	4: "Plenteous grace with Thee is found, grace to cover all my sin;\nLet the healing streams abound; make and keep me pure within.\nThou of life the fountain art, freely let me take of Thee;\nSpring Thou up within my heart; rise to all eternity."
}
song["sda-hymnal/491"] = {
	title: "In the Hour of Trial",
	tune: "Penitence",
	1: "In the hour of trial, Jesus, plead for me,\nLest by base denial I depart from Thee;\nWhen Thou see’st me waver, with a look recall,\nNor, for fear or favor, suffer me to fall.",
	2: "With forbidden pleasures would this vain world charm;\nOr its sordid treasures spread to work me harm;\nBring to my remembrance sad Gethsemane,\nOr, in darker semblance, cross-crowned Calvary.",
	3: "Should Thy mercy send me sorrow, toil, and woe,\nOr should pain attend me on my path below,\nGrant that I may never fail Thy hand to see;\nGrant that I may ever cast my care on Thee."
}
song["sda-hymnal/492"] = {
	title: "Like Jesus",
	copyright: "“Like Jesus” Copyright 1909. © renewed 1937 The Rodeheaver Co. (A Div. of Word, Inc.). All rights reserved. International copyright secured. Used by permission.",
	1: "Teach, me, Father, what to say;\nTeach me, Father, how to pray;\nTeach me all along the way\nHow to be like Jesus.\nRefrain:\nI would be like Jesus,\nI would be like Jesus!\nHelp me, Lord, to daily grow\nMore and more like Jesus!",
	2: "Teach me as the days go by,\nTeach me not to reason why,\nTeach me that to do and die,\nIs to be like Jesus.",
	3: "Teach me that the time is short,\nTeach me how to live and work,\nTeach me that to never shirk\nIs to be like Jesus.",
	4: "Teach me how we may be one,\nLike the Father and the Son;\nAnd when all is overcome,\nI will be like Jesus."
}
song["sda-hymnal/493"] = {
	title: "Fill My Cup, Lord",
	copyright: "“Fill My Cup, Lord” Copyright © 1959 by Richard Blanchard. Assigned to Sacred Songs (A Div. of Word, Inc.). All rights reserved. International copyright secured. Used by permission.",
	1: "Like the woman at the well I was seeking\nFor things that could not satisfy:\nAnd then I heard my Savior speaking:\n” Draw from my well that never shall run dry”.\nRefrain\nFill my cup Lord, I lift it up, Lord!\nCome and quench this thirsting of my soul;\nBread of heaven, Feed me till I want no more–\nFill my cup, fill it up and make me whole!",
	2: "There are millions in this world who are craving\nThe pleasures earthly things afford;\nBut none can match the wondrous treasure\nThat I find in Jesus Christ my Lord.",
	3: "So, my brother, if the things this world gave you\nLeave hungers that won’t pass away,\nMy blessed Lord will come and save you,\nIf you kneel to Him and humbly pray:"
}
song["sda-hymnal/494"] = {
	title: "We Would See Jesus",
	scripture: "John 12:21",
	1: "“We would see Jesus;” for the shadows lengthen\nAcross the little landscape of our life;\nWe would see Jesus, our weak faith to strengthen\nFor the last conflict, in this mortal strife.",
	2: "“We would see Jesus,” Rock of our salvation,\nWhere on our feet were set with sovereign grace;\nNot life, nor death, with all their agitation,\nCan thence remove us, gazing on His face.",
	3: "“We would see Jesus;” other lights are paling,\nWhich for long years we did rejoice to see;\nThe blessings of this sinful world are failing;\nWe would not mourn them, in exchange for Thee.",
	4: "“We would see Jesus;” this is all we’re needing-\nStrength, joy and willingness come with the sight;\nWe would see Jesus, dying, risen, pleading,\nSoon to return and end this mortal night!"
}
song["sda-hymnal/495"] = {
	title: "Near to the Heart of God",
	1: "There is a place of quiet rest,\nnear to the heart of God;\na place where sin cannot molest,\nnear to the heart of God.\nRefrain\nO Jesus, blest Redeemer,\nsent from the heart of God,\nhold us who wait before thee\nnear to the heart of God.",
	2: "There is a place of comfort sweet,\nnear to the heart of God;\na place where we our Savior meet,\nnear to the heart of God.",
	3: "There is place a place of full release,\nnear to the heart of God;\na place where all is joy and peace,\nnear to the heart of God."
}
song["sda-hymnal/496"] = {
	title: "Eternal Love, We Have No Good",
	tune: "Eternal Love",
	scripture: "Rev. 8:3",
	copyright: "“Eternal Love, We Have No Good” From Wings by Amy Carmichael. Copyright by Dohnavur Fellowship.",
	1: "Eternal Love, we have no good to bring Thee,\nNo single good of all our hands have wrought,\nNo worthy music have we found to sing Thee,\nNo jewelled word, no quick up soaring thought.",
	2: "And yet we come; and when our faith would falter\nShow us, O Love, the quiet place of prayer,\nThe golden censer and the golden altar,\nAnd the great angel waiting for us there."
}
song["sda-hymnal/497"] = {
	title: "O Gracious Father of Mankind",
	tune: "Halifax",
	copyright: "“O Gracious Father of Mankind” Music from The Hymnal 1940, copyright by The Church Pension Fund. Used by permission.",
	1: "O gracious Father of mankind,\nOur spirits’ unseen friend;\nHigh heaven’s Lord, our hearts’ dear guest,\nTo Thee our prayers ascend.",
	2: "Thou dost not wait till human speech\nThy gifts divine implore;\nOur dreams, our aims, our work, our lives\nAre prayers Thou lovest more.",
	3: "Thou hearest these, the good and ill,\nDeep buried in each breast;\nThe secret thought, the hidden plan,\nWrought out our unexpressed.",
	4: "O cleanse our prayers from human dross,\nAttune our lives to Thee,\nUntil we labor for those gifts\nWe ask on bended knee.",
	5: "Our best is but Thyself in us,\nOur highest thought Thy will;\nTo hear Thy voice we need but love,\nAnd listen, and be still.",
	6: "We would not bend Thy will to ours,\nBut blend our wills to Thine;\nNot dreams, our aims, our work, our lives\nAre prayers Thou lovest more.",
	7: "Thou seekest us in love and truth\nMore than our minds seek Thee;\nThrough open gates Thy power flows in\nLike flood tides from the sea.",
	8: "No more we seek Thee from afar,\nNor ask Thee for a sign,\nContent to pray in life and love\nAnd toil, till all are Thine."
}
song["sda-hymnal/498"] = {
	title: "Still, Still With Thee",
	tune: "Consolation",
	scripture: "Psalm 139:18",
	1: "Still, still with Thee, when purple morning breaketh,\nWhen the bird waketh, and the shadows flee;\nFairer than morning, lovelier than daylight,\nDawns the sweet consciousness, I am with Thee.",
	2: "Alone with Thee, amid the mystic shadows,\nThe solemn hush of nature newly born;\nAlone with Thee in breathless adoration,\nIn the calm dew and freshness of the morn.",
	3: "Still, still with Thee! As to each newborn morning\nA fresh and solemn splendor still is given,\nSo does this blessed consciousness, awaking,\nBreathe each day nearness unto Thee and heaven.",
	4: "So shall it be at last, in that bright morning,\nWhen the soul waketh and life’s shadows flee;\nO in that hour, fairer than daylight dawning,\nShall rise the glorious thought, I am with Thee."
}
song["sda-hymnal/499"] = {
	title: "What a Friend We Have in Jesus",
	tune: "Converse",
	1: "What a friend we have in Jesus,\nAll our sins and griefs to bear!\nWhat a privilege to carry\nEverything to God in prayer!\nOh, what peace we often forfeit;\nOh, what needless pain we bear\nAll because we do not carry\nEverything to God in prayer!",
	2: "Have we trials and temptations?\nIs there trouble anywhere?\nWe should never be discouraged\nTake it to the Lord in prayer.\nCan we find a friend so faithful\nWho will all our sorrows share?\nJesus knows our every weakness\nTake it to the Lord in prayer.",
	3: "Are we weak and heavy laden,\nCumbered with a load of care?\nPrecious Savior, still our refuge\nTake it to the Lord in prayer.\nDo your friends despise, forsake you?\nTake it to the Lord in prayer.\nIn his arms he’ll take and shield you;\nYou will find a solace there."
}
song["sda-hymnal/500"] = {
	title: "Take Time to Be Holy",
	scripture: "1 Peter 1:16",
	1: "Take time to be holy,\nspeak oft with thy Lord;\nabide in him always,\nand feed on his word.\nMake friends of God’s children,\nhelp those who are weak,\nforgetting in nothing\nhis blessing to seek.",
	2: "Take time to be holy,\nthe world rushes on;\nspend much time in secret\nwith Jesus alone.\nBy looking to Jesus,\nlike him thou shalt be;\nthy friends in thy conduct\nhis likeness shall see.",
	3: "Take time to be holy,\nlet him be thy guide,\nand run not before him,\nwhatever betide.\nIn joy or in sorrow,\nstill follow the Lord,\nand, looking to Jesus,\nstill trust in his word.",
	4: "Take time to be holy,\nbe calm in thy soul,\neach thought and each motive\nbeneath his control.\nThus led by his spirit\nto fountains of love,\nthou soon shalt be fitted\nfor service above."
}
song["sda-hymnal/501"] = {
	title: "Tis the Blessed Hour of Prayer",
	1: "‘Tis the blessed hour of prayer, when our hearts lowly bend,\nAnd we gather to Jesus, our Savior and Friend;\nIf we come to Him in faith, His protection to share,\nRefrain\nWhat a balm for the weary! O how sweet to be there!\nBlessed hour of prayer, Blessed hour of prayer,\nWhat a balm for the weary! O how sweet to be there!",
	2: "‘Tis the blessed hour of prayer, when the Savior draws near,\nWith a tender compassion His children to hear;\nWhen He tells us we may cast at His feet every care,",
	3: "‘Tis the blessed hour of prayer, when the tempted and tried\nTo the Savior who loves them their sorrows confide;\nWith a sympathizing heart He removes every care;",
	4: "‘Tis the blessed hour of prayer, trusting Him we believe,\nThat the blessings we’re needing we’ll surely receive;\nIn the fullness of this trust we shall lose every care;"
}
song["sda-hymnal/502"] = {
	title: "Sun of My Soul",
	tune: "Hursley",
	1: "Sun of my soul, O Savior dear!\nIt is not night if Thou be near;\nO may no earth-born cloud arise\nTo hide Thee from Thy servant’s eyes.",
	2: "When soft the dews of kindly sleep\nMy weary eyelids gently steep,\nBe my last thought how sweet to rest\nForever on my Savior’s breast!",
	3: "Abide with me from morn till eve,\nFor without Thee I cannot live;\nAbide with me when night is nigh,\nFor without Thee I dare not die.",
	4: "Be near and bless me when I wake,\nEre through the world my way I take;\nTill in the ocean of Thy love\nI lose myself in heaven above."
}
song["sda-hymnal/503"] = {
	title: "A Quiet Place",
	copyright: "“A Quiet Place” Copyright © 1967 by Lexicon Music, Inc. ASCAP. All Rights Reserved. International copyright secured. Used by permission.",
	1: "There is a quiet place,\nFar from the rapid pace where God can soothe my troubled mind.\nSheltered by tree and flow’r,\nThere in my quiet hour with Him my cares are left behind.\nWhether a garden small,\nOr on a mountain tall,\nNew strength and courage there I find;\nThen from this quiet place\nI go prepared to face a new day\nWith love for all mankind."
}
song["sda-hymnal/504"] = {
	title: "Lord Jesus, Think on Me",
	tune: "Southwell",
	1: "Lord Jesus, think on me, And purge away my sin;\nFrom earth-born passions set me free, And make me pure within.",
	2: "Lord Jesus, think on me, With care and woe oppressed;\nLet me Thy loving servant be, And taste Thy promised rest.",
	3: "Lord Jesus, think on me, Nor let me go astray;\nThrough darkness and perplexity Point Thou the heavenly way.",
	4: "Lord Jesus, think on me, That I may sing above\nTo Father, Spirit, and to Thee, The strains of praise and love."
}
song["sda-hymnal/505"] = {
	title: "I Need the Prayers",
	1: "I need the prayers of those I love,\nWhile trav’ling o’er life’s rugged way,\nThat I may true and faithful be,\nAnd live for Jesus every day.\nRefrain\nI want my friends to pray for me,\nTo bear my tempted soul above,\nAnd intercede with God for me;\nI need the prayers of those I love.",
	2: "I need the prayers of those I love,\nTo help me in each trying hour,\nTo bear my tempted sould to Him,\nThat He may keep me by His pow’r.\nRefrain\nI want my friends to pray for me,\nTo hold me up on wing of faith,\nThat I may walk the narrow way,\nKept by our Father’s glorious grace."
}
song["sda-hymnal/506"] = {
	title: "A Mighty Fortress",
	tune: "Ein' feste Burg",
	scripture: "Psalm 46:1",
	1: "A mighty fortress is our God,\na bulwark never failing;\nour helper he amid the flood\nof mortal ills prevaling.\nFor still our ancient foe\ndoth seek to work us woe;\nhis craft and power are great,\nand armed with cruel hate,\non earth is not his equal.",
	2: "Did we in our own strength confide,\nour striving would be losing,\nwere not the right man on our side,\nthe man of God’s own choosing.\nDost ask who that may be?\nChrist Jesus, it is he;\nLord Sabbaoth, his name,\nfrom age to age the same,\nand he must win the battle.",
	3: "And though this world, with devils filled,\nshould threaten to undo us,\nwe will not fear, for God hath willed\nhis truth to triumph through us.\nThe Prince of Darkness grim,\nwe tremble not for him;\nhis rage we can endure,\nfor lo, his doom is sure;\none little word shall fell him.",
	4: "That word above all earthly powers,\nno thanks to them, abideth;\nthe Spirit and the gifts are ours,\nthru him who with us sideth.\nLet goods and kindred go,\nthis mortal life also;\nthe body they may kill;\nGod’s truth abideth still;\nhis kingdom is forever."
}
song["sda-hymnal/507"] = {
	title: "Moment by Moment",
	1: "Dying with Jesus, by death reckoned mine,\nLiving with Jesus, a new life divine,\nLooking to Jesus till glory doth shine,\nMoment by moment, O Lord, I am Thine.\nRefrain\nMoment by moment I’m kept in His love;\nMoment by moment I’ve life from above;\nLooking to Jesus till glory doth shine;\nMoment by moment, O Lord, I am Thine.",
	2: "Never a trial that He is not there,\nNever a burden that He doth not bear,\nNever a sorrow that He doth not share,\nMoment by moment I’m under His care.",
	3: "Never a heart-ache, and never a groan,\nNever a teardrop and never a moan;\nNever a danger but there on teh throne,\nMoment by moment He thinks of His own.",
	4: "Never a weakness that He doth not feel,\nNever a sickness that He cannot heal;\nMoment by moment, in woe or in weal,\nJesus, my Savior, abides with me still."
}
song["sda-hymnal/508"] = {
	title: "Anywhere With Jesus",
	1: "Anywhere with Jesus I can safely go,\nAnywhere He leads me in this world below;\nAnywhere without Him dearest joys would fade;\nAnywhere with Jesus I am not afraid.\nRefrain\nAnywhere, anywhere! Fear I cannot know;\nAnywhere with Jesus I can safely go.",
	2: "Anywhere with Jesus I am not alone;\nOther friends may fail me, He is still my own;\nThough His hand may lead me over drearest ways,\nAnywhere with Jesus is a house of praise.",
	3: "Anywhere with Jesus I can go to sleep,\nWhen the darkening shadows round about me creep,\nKnowing I shall waken nevermore to roam;\nAnywhere with Jesus will be home, sweet home."
}
song["sda-hymnal/509"] = {
	title: "How Firm a Foundation",
	tune: "Foundation",
	info: "Alternate tune, ADESTE FIDELIS, No. 132",
	copyright: "“How Firm a Foundation” Arrangement copyright © 1984 by Melvin West",
	1: "How firm a foundation, ye saints of the Lord,\nis laid for your faith in his excellent word!\nWhat more can he say than to you he hath said,\nto you who for refuge to Jesus have fled?",
	2: "“Fear not, I am with thee, O be not dismayed,\nfor I am thy God and will still give thee aid;\nI’ll strengthen and help thee, and cause thee to stand\nupheld by my righteous, omnipotent hand.”",
	3: "“When through deep waters I call thee to go,\nthe rivers of woe shall not thee overflow;\nfor I will be with thee, thy troubles to bless,\nand sanctify to thee thy deepest distress.”",
	4: "“When through fiery trials thy pathways shall lie,\nmy grace, all-sufficient, shall be thy supply;\nthe flame shall not hurt thee; I only design\nthy dross to consume, and thy gold to refine.”",
	5: "“The soul that on Jesus hath leaned for respose,\nI will not, I will not depart to His foes;\nThat soul, though all hell should endeavor to shake,\nI’ll never, no never, no never forsake!”"
}
song["sda-hymnal/510"] = {
	title: "If You But Trust in God to Guide You",
	tune: "Wer nur den lieben Gott",
	scripture: "Psalm 55:22",
	copyright: "“If You But Trust in God to Guide You” Stanza 2 copyright 1978 by Lutheran Book of Worship. Used by permission of Augsburg Publishing House.",
	1: "If you but trust in God to guide you\nAnd place your confidence in Him,\nYou’ll find Him always there beside you,\nTo give you hope and strength within.\nFor those who trust God’s changeless love\nBuild on the rock that will not move.",
	2: "What gain is there in futile weeping,\nIn helpless anger and distress?\nIf you are in His care and keeping,\nIn sorrow will He love you less?\nFor He who took for you a cross\nWill bring you safe through every loss.",
	3: "In patient trust await His leisure\nIn cheerful hope, with heart content\nTo take whate’er your Father’s pleasure\nAnd all discerning love have sent;\nDoubt not your inmost wants are known\nTo Him who chose you for His own.",
	4: "Sing, pray, and keep His ways unswerving,\nOffer your service faithfully,\nAnd trust His word; though undeserving,\nYou’ll find His promise true to be.\nGod never will forsake in need\nThe soul that trusts in Him indeed."
}
song["sda-hymnal/511"] = {
	title: "I Know Whom I Have Believed",
	scripture: "2 Timothy 1:12",
	1: "I know not why God’s wondrous grace To me He hath made known,\nNor why, unworthy, Christ in love Redeemed me for His own.\nRefrain\nBut “I know whom I have believed, and am persuaded that His is able\nTo keep that which I’ve committed Unto Him against that day.”",
	2: "I know not how this saving faith To me He did impart,\nNor how believing in His word Wrought peace within my heart.",
	3: "I know not how the Spirit moves, Convincing men of sin,\nRevealing Jesus through the word, Creating faith in Him.",
	4: "I know not when my Lord may come, At night or noonday fair,\nNor if I walk the vale with Him, Or meet Him in the air."
}
song["sda-hymnal/512"] = {
	title: "Just When I Need Him Most",
	copyright: "“Just When I Need Him Most”  Copyright 1908 by Charles H. Gabriel. © renewed 1936 The Rodeheaver Co. (A Div. of Word, Inc.). All rights reserved. Used by permission.",
	1: "Just when I need Him, Jesus is near,\nJust when I falter, just when I fear;\nReady to help me, ready to cheer,\nJust when I need Him most.\nJust when I need Him most,\nJust when I need Him most,\nJesus is near to comfort and cheer,\nJust when I need Him most.",
	2: "Just when I need Him, Jesus is true,\nNever forsaking, all the way through,\nGiving for burdens pleasures anew,\nJust when I need Him most.\nJust when I need Him most,\nJust when I need Him most,\nJesus is near to comfort and cheer,\nJust when I need Him most.",
	3: "Just when I need Him, Jesus is strong,\nBearing my burdens all the day long;\nFor all my sorrow giving a song,\nJust when I need Him most.\nJust when I need Him most,\nJust when I need Him most,\nJesus is near to comfort and cheer,\nJust when I need Him most.",
	4: "Just when I need Him, He is my all,\nAnswering when upon Him I call;\nTenderly watching lest I should fall,\nJust when I need Him most.\nJust when I need Him most,\nJust when I need Him most,\nJesus is near to comfort and cheer,\nJust when I need Him most."
}
song["sda-hymnal/513"] = {
	title: "In Heavenly Love Abiding",
	tune: "Nyland",
	scripture: "Psalm 23",
	copyright: "“In Heavenly Love Abiding” Music from the Revised Church Hymnary 1927 by permission of Oxford University Press",
	1: "In heavenly love abiding, No change my heart shall fear;\nAnd safe is such confiding, For nothing changes here.\nThe storm may roar without me, My heart may low be laid;\nBut God is round about me, And can I be dismayed?",
	2: "Wherever He may guide me, No want shall turn me back;\nMy Shepherd is besided me, And nothing can I lack.\nHis wisdom ever waketh, His sight is never dim;\nHe knows the way He taketh, And I will walk with Him.",
	3: "Green pastures are before me, Which yet I have not seen;\nBright skies will soon be o’er me, Where darkest clouds have been.\nMy hope I cannot measure, My path to life is free;\nMy Savior has my teasure, And He will walk with me."
}
song["sda-hymnal/514"] = {
	title: "Lord of Our Life",
	tune: "Cloisters",
	1: "Lord of our life, and God of our salvation,\nStar of our night, and hope of every nation,\nHear and receive Thy church’s supplication,\nLord God Almighty.",
	2: "Lord, Thou canst help when earthly armor faileth;\nLord, Thou canst save when deadly sin assaileth;\nLord, o’er Thy rock nor death nor hell prevaileth;\nGrant us Thy peace, Lord",
	3: "Peace in our hearts our evil thoughts assuaging;\nPeace in Thy church, where brothers are engaging;\nPeace, when the world its busy war is waging;\nSend us, O Savior.",
	4: "Grant us Thy help till foes are backward driven;\nGrant them Thy truth that they may be forgiven;\nGrant peace on earth, and, after we have striven,\nPeace in Thy heaven."
}
song["sda-hymnal/515"] = {
	title: "The Lord Is My Light",
	1: "The Lord is my light; then why should I fear?\nBy day and by night His presence is near;\nHe is my salvation from sorrow and sin;\nThis blessed persuasion the Spirit brings in.\nRefrain\nThe Lord is my light, my joy, and my song;\nBy day and by night He leads me along;\nThe Lord is my light, my joy, and my song;\nBy day and by night He leads me along.",
	2: "The Lord is my light; though clods may arise,\nFaith, stronger than sight, looks up to the skies\nWhere Jesus forever in glory doth reign:\nThen how can I ever in darkness remain?",
	3: "The Lord is my light, the Lord is my strength;\nI know in His might I’ll conquer at length;\nMy weakness in mercy He covers with power,\nAnd, walking by faith, He upholds me each hour.",
	4: "The lord is my light, my all and in all;\nThere is in His sight no darkness at all;\nHe is my Redeemer, my Savior and King;\nWith saints and with angels His praises I sing."
}
song["sda-hymnal/516"] = {
	title: "All the Way",
	1: "All the way my Savior leads me;\nWhat have I to ask beside?\nCan I doubt His tender mercy,\nWho through life has been my guide?\nHeavenly peace, divinest comfort,\nHere by faith in Him to dwell;\nFor I know whate’er befall me,\nJesus doeth all things well;\nFor I know whate’er befall me,\nJesus doeth all things well.",
	2: "All the way my Savior leads me;\nCheers each winding path I tread;\nGives me grace for every trial,\nFeeds me with the living bread;\nThough my weary steps may falter,\nAnd my soul athirst may be,\nGushing from the Rock before me,\nLo, a spring of joy I see;\nGushing from the Rock before me,\nLo, a spring of joy I see.",
	3: "All the way my Savior leads me;\nO the fullness of His love!\nPerfect rest to me is promised\nIn my Father’s house above;\nWhen I wake to life immortal,\nWing my flight to realms of day,\nThis my song through endless ages,\nJesus led me all the way;\nThis my song through endless ages,\nJesus led me all the way."
}
song["sda-hymnal/517"] = {
	title: "My Faith Looks Up to Thee",
	tune: "Olivet",
	1: "My faith looks up to thee,\nthou Lamb of Calvary,\nSavior divine;\nNow hear me while I pray,\ntake all my guilt away,\nO let me from this day\nbe wholly Thine.",
	2: "May thy rich grace impart\nstrength to my fainting heart,\nmy zeal inspire!\nAs thou hast died for me,\nO may my love to thee\npure, warm, and changeless be,\na living fire!",
	3: "While life’s dark maze I tread,\nand griefs around me spread,\nbe thou my guide;\nbid darkness turn to day,\nwipe sorrow’s tears away,\nnor let me ever stray\nfrom Thee aside."
}
song["sda-hymnal/518"] = {
	title: "Standing on the Promises",
	scripture: "2 Peter 1:4",
	1: "Standing on the promises of Christ my King,\nThru eternal ages let His praises ring;\nGlory in the highest I will shout and sing,\nStanding on the promises of God.\nRefrain\nStanding, standing,\nStanding on the promises\nof God my Savior;\nStanding, standing,\nI’m standing on the promises of God.",
	2: "Standing on the promises that cannot fail,\nWhen the howling storms of doubt and fear assail,\nBy the living word of God I shall prevail,\nStanding on the promises of God.",
	3: "Standing on the promises of Christ the Lord,\nBound to Him eternally by love’s strong cord,\nOvercoming daily with the Spirit’s sword,\nStanding on the promises of God."
}
song["sda-hymnal/519"] = {
	title: "Give to the Winds Your Fears",
	composer: "Samuel Howard, 1762 (1710 - 1781)",
	poet: "Paul Gerhardt, 1653 (1607 - 1676)",
	tune: "St. Bride",
	scripture: "Psalm 37:5",
	1: "Give to the winds your fears;\nIn hope be undismayed:\nGod hears your sighs and coundts your tears,\nGod shall lift up your head.",
	2: "To Him commit your griefs;\nYour ways put in His hands\nTo His sure truth adn tender care\nWho earth adn heave commands.",
	3: "O put your trust in God;\nIn duty’s path go on.\nWalk in His strength with faith and hope,\nSo shall your work be done.",
	4: "Leave to His sovereign sway\nTo choose and to command;\nSo you shall, faithful, seek His way\nhow wise, how strong His hand!"
}
song["sda-hymnal/520"] = {
	title: "He Hideth My Soul",
	1: "A wonderful Savior is Jesus my Lord,\nA wonderful Savior to me,\nHe hideth my soul in the cleft of the rock,\nWhere rivers of pleasure I see.\nRefrain\nHe hideth my soul in the cleft of the rock\nThat shadows a dry, thirsty land;\nhe hideth my life in the depths of His love,\nAnd covers me there with His hand,\nAnd covers me there with His hand.",
	2: "A wonderful Savior is Jesus my Lord,\nHe taketh my burden away,\nHe holdeth me up, and I shall not be moved,\nHe giveth me strength as my day.",
	3: "With numberless blessings each moment He crowns,\nAnd filled with His fullness divine,\nI sing in my rapture, Oh, Glory to God\nFor such a Redeemer as mine.",
	4: "When clothed in His brightness, transported I rise\nTo meet Him in clouds of the sky,\nHis perfect salvation, His wonderful love,\nI’ll shout with the millions on high."
}
song["sda-hymnal/521"] = {
	title: "Depth of Mercy",
	tune: "Aletta",
	scripture: "1 Tim. 1:15",
	1: "Depth of mercy!–can there be\nMercy still reserved for me?\nCan my God His wrath forbear?\nMe, the chief of sinners, spare?",
	2: "I have long withstood His grace,\nLong provoked Him to His face,\nWould not hearken to His calls,\nGrieved Him by a thousand falls.",
	3: "Now incline me to repent;\nLet me now my sins lament;\nNow my foul revolt deplore,\nWeep, believe, and sin no more.",
	4: "There for me the Savior stands,\nShows His wounds and spreads His hands;\nGod is love! I know, I feel;\nJesus weeps, and loves me still."
}
song["sda-hymnal/522"] = {
	title: "My Hope Is Built on Nothing Less",
	1: "My hope is built on nothing less\nthan Jesus’ blood and righteousness.\nI dare not trust the sweetest frame,\nbut wholly lean on Jesus’ name.\nRefrain\nOn Christ the solid rock I stand;\nall other ground is sinking sand,\nall other ground is sinking sand.",
	2: "When Darkness veils his lovely face,\nI rest on his unchanging grace.\nin every high and stormy gale,\nmy anchor holds within the veil.",
	3: "His oath, his covenant, his blood\nsupports me in the whelming flood.\nWhen all around my soul gives way,\nhe then is all my hope and stay.",
	4: "When He shall come with trumpet sound,\nO may I then in him be found!\nDressed in his righteousness alone,\nfaultless to stand before the throne."
}
song["sda-hymnal/523"] = {
	title: "My Faith Has Found a Resting Place",
	tune: "No Other Plea",
	1: "My faith has found a resting place,\nNot in a manmade creed;\nI trust the ever living One,\nThat He for me shall plead.\nRefrain\nI need no other evidence,\nI need no other plea;\nIt is enough that Jesus died,\nAnd rose again for me.",
	2: "Enough for me that Jesus saves,\nThis ends my fear and doubt;\nA sinful soul, I come to Him,\nHe will not cast me out.",
	3: "My soul is resting on the Word,\nThe living Word of God,\nSalvation in my Savior’s name,\nSalvation through His blood.",
	4: "My great Physician heals the sick,\nThe lost He came to save;\nFor me His precious blood He shed,\nFor me His life He gave."
}
song["sda-hymnal/524"] = {
	title: "Tis So Sweet to Trust in Jesus",
	1: "‘Tis so sweet to trust in Jesus,\nand to take him at his word;\njust to rest upon his promise,\nand to know, “Thus saith the Lord.”\nRefrain\nJesus, Jesus, how I trust him!\nHow I’ve proved him o’er and o’er!\nJesus, Jesus, precious Jesus!\nO for grace to trust him more!",
	2: "O how sweet to trust in Jesus,\njust to trust his cleansing blood;\nand in simple faith to plunge me\nneath the healing, cleansing flood!",
	3: "Yes, ’tis sweet to trust in Jesus,\njust from sin and self to cease;\njust from Jesus simply taking\nlife and rest, and joy and peace.",
	4: "I’m so glad I learned to trust thee,\nprecious Jesus, Savior, friend;\nand I know that thou art with me,\nwilt be with me to the end."
}
song["sda-hymnal/525"] = {
	title: "Hiding in Thee",
	scripture: "Psalm 61:1,2",
	1: "O safe to the Rock that is higher than I,\nMy soul in its conflicts and sorrows would fly;\nSo sinful, so weary, Thine, Thine, would I be;\nThou blest “Rock of Ages,” I’m hiding in Thee.\nRefrain\nHiding in Thee, hiding in Thee,\nThou blest “Rock of Ages,”\nI’m hiding in Thee.",
	2: "In the calm of the noontide, in sorrow’s lone hour,\nIn times when temptation casts over me its power;\nIn the tempests of life, on its wide, heaving sea,\nThou blest “Rock of Ages,“ I’m hiding in Thee.",
	3: "How oft in the conflict, when pressed by the foe,\nI have fled to my refuge and breathed out my woe;\nHow often, when trials like sea billows roll,\nHave I hidden in Thee, O Thou Rock of my soul."
}
song["sda-hymnal/526"] = {
	title: "Because He Lives",
	tune: "Resurrection",
	copyright: "“Because He Lives” Copyright © 1971, 1976 by William J. Gaither. All rights reserved. International copyright secured. Used by permission of Gaither Music Company.",
	1: "God sent His Son, they called Him Jesus,\nHe came to love, heal, and forgive;\nHe lived and died to buy my pardon,\nAn empty grave is there to prove my Savior lives.\nRefrain\nBecause He lives I can face tomorrow,\nBecause He lives all fear is gone;\nBecause I know He holds the future.\nAnd life is worth the living just because He lives.",
	2: "How sweet to hold a newborn baby,\nAnd feel the pride, and joy He gives;\nBut greater still the calm assurance,\nThis child can face uncertain days because He lives."
}
song["sda-hymnal/527"] = {
	title: "From Every Stormy Wind",
	tune: "Retreat",
	scripture: "Exe. 25:22",
	1: "From every stormy wind that blows, From every swelling tide of woes,\nThere is a calm, a sure retreat: ‘Tis found beneath the mercy seat.",
	2: "There is a place where Jesus sheds The oil of gladness on our heads,\nA place than all besides more sweet; It is the blood-bought mercy seat.",
	3: "There is a scene where spirits blend, Where friend holds fellowship with friend;\nThough sundered far, by faith they meet Around one common mercy seat.",
	4: "There, there, on angel’s wings we soar, And earthly cares molest no more,\nAnd heaven comes down our souls to greet, And glory crowns the mercy seat.",
	5: "Ah! whither should we flee for aid, When tempted, desolate dismayed?\nOr how the hosts of sin defeat, Had suffering saints no mercy seat?"
}
song["sda-hymnal/528"] = {
	title: "A Shelter in the Time of Storm",
	1: "The Lord’s our Rock, in Him we hide,\nA shelter in the time of storm;\nSecure whatever ill betide,\nA shelter in the time of storm.\nRefrain\nMighty Rock in a weary land,\nCooling shade on the burning sand,\nFaithful guide for the pilgrim band—\nA shelter in the time of storm.",
	2: "A shade by day, defense by night,\nA shelter in the time of storm;\nNo fears alarm, no foes afright,\nA shelter in the time of storm.",
	3: "The raging storms may round us beat,\nA shelter in the time of storm\nWe’ll never leave our safe retreat,\nA shelter in the time of storm.",
	4: "O Rock divine, O Refuge dear,\nA Shelter in the time of storm;\nBe Thou our helper ever near,\nA Shelter in the time of storm."
}
song["sda-hymnal/529"] = {
	title: "Under His Wings",
	1: "Under His wings I am safely abiding;\nThough the night deepens and tempests are wild,\nStill I can trust Him; I know He will keep me;\nhe has redeemed me, and I am His child.\nRefrain\nUnder His wings, under His wings,\nWho from His love can sever?\nUnder His wings, my soul shall abide,\nSafely abide forever.",
	2: "Under His wings, what a refuge in sorrow!\nHow the heart yearningly turns to its rest!\nOften when earth has no balm for my healing,\nThere I find comfort, and there I am blest.",
	3: "Under His wings, O what precious enjoyment!\nThere will I hide till life’s trials are over;\nSheltered, protected, no evil can harm me;\nResting in Jesus I’m safe evermore."
}
song["sda-hymnal/530"] = {
	title: "It Is Well With My Soul",
	1: "When peace, like a river, attendeth my way,\nwhen sorrows like sea billows roll;\nwhatever my lot, thou hast taught me to say,\nIt is well, it is well with my soul.\nRefrain\nIt is well with my soul,\nit is well, it is well with my soul.",
	2: "My sin–oh, the joy of this glorious thought–\nMy sin, not in part but the whole,\nis nailed to the cross, and I bear it no more,\npraise the Lord, praise the Lord, O my soul!",
	3: "And, Lord, haste the day when my faith shall be sight,\nthe clouds be rolled back as a scroll;\nthe trump shall resound, and the Lord shall descend,\neven so, it is well with my soul."
}
song["sda-hymnal/531"] = {
	title: "We'll Build on the Rock",
	scripture: "Matt. 7:24-26",
	1: "We’ll build on the Rock, the living Rock,\nOn Jesus, the Rock of Ages;\nSo shall we abide the fearful shock,\nWhen loud the tempest rages.\nRefrain\nWe’ll build on the Rock,\nWe’ll build on the Rock;\nWe’ll build on the Rock, on the solid Rock,\nOn Christ, the mighty Rock.",
	2: "Some build on the sinking sands of life,\nOn visions of earthly treasure;\nSome build on the waves of sin and strife,\nOf fame, and worldly pleasure.",
	3: "O build on the Rock, for ever sure,\nThe firm and the true foundation;\nIts hope is the hope which shall endure,\nThe hope of our salvation."
}
song["sda-hymnal/532"] = {
	title: "Day by Day",
	arranger: "Melvin West, 1984 (1930 - )",
	tune: "Blott en Dag",
	scripture: "Deuteronomy 33:25",
	copyright: "“Day by Day”  Arrangement copyright © 1984 by Melvin West",
	1: "Day by day, and with each passing moment,\nStrength I find, to meet my trials here;\nTrusting in my Father’s wise bestowment,\nI’ve no cause for worry or for fear.\nHe Whose heart is kind beyond all measure\nGives unto each day what He deems best—\nLovingly, its part of pain and pleasure,\nMingling toil with peace and rest.",
	2: "Every day, the Lord Himself is near me\nWith a special mercy for each hour;\nAll my cares He fain would bear, and cheer me,\nHe Whose Name is Counselor and Power;\nThe protection of His child and treasure\nIs a charge that on Himself He laid;\n“As thy days, thy strength shall be in measure,”\nThis the pledge to me He made.",
	3: "Help me then in every tribulation\nSo to trust Thy promises, O Lord,\nThat I lose not faith’s sweet consolation\nOffered me within Thy holy Word.\nHelp me, Lord, when toil and trouble meeting,\nEver to take, as from a father’s hand,\nOne by one, the days, the moments fleeting,\nTill I reach the promised land."
}
song["sda-hymnal/533"] = {
	title: "O for a Faith",
	tune: "Northfield",
	1: "O, for a faith that will not shrink,\nThough pressed by many a foe,\nThat will not tremble on the brink of poverty,\nOf poverty or woe; Of poverty or woe;",
	2: "That will not murmur or complain\nBeneath the chastening rod,\nBut in the hour of grief or pain, of grief or pain\nCan lean upon its God; Can lean upon its God.",
	3: "A faith that shines more bright and clear\nWhen tempests rage without;\nThat when in danger knows no fear, knows of no fear,\nIn darkness feels no doubt; In darkness feels no doubt.",
	4: "That bears unmoved the world’s dread frown,\nNor heeds its scornful smile;\nThat sin’s wild ocean cannot drown, no, cannot drown,\nNor its soft arts beguile; Nor its soft arts beguile.",
	5: "Lord, give me such a faith as this,\nAnd then, whate’er may come\nI’ll taste, e’en here the hallowed bliss, the hallowed bliss\nOf an eternal home; Of an eternal home."
}
song["sda-hymnal/534"] = {
	title: "Will Your Anchor Hold",
	1: "Will your anchor hold in the storms of life,\nWhen the clouds unfold their wings of strife?\nWhen the strong tides lift, and the cables strain,\nWill your anchor drift or firm remain?\nRefrain\nWe have an anchor that keeps the soul\nSteadfast and sure while the billows roll,\nFastened to the Rock which cannot move,\nGrounded firm and deep in the Savior’s love.",
	2: "It is safely moored, ’twill the storm withstand,\nFor ’tis well secured by the Savior’s hand;\nAnd the cables passed from His heart to mine,\nCan defy the blast, through strength divine.",
	3: "It will firmly hold in the straits of fear,\nWhen the breakers have told the reef is near;\nThough the tempest rave and the wild winds blow,\nNot an angry wave shall our bark o’erflow.",
	4: "It will surely hold in the floods of death,\nWhen the waters cold chill our latest breath;\nOn the rising tide it can never fail,\nWhile our hopes abide within the veil.",
	5: "When our eyes behold, in the dawning light,\nShining gates of pearl, our harbor bright,\nWe shall anchor fast to the heavenly shore,\nWith the storms all past forevermore."
}
song["sda-hymnal/535"] = {
	title: "I Am Trusting Thee, Lord Jesus",
	tune: "Bullinger",
	scripture: "Psalm 71:1",
	1: "I am trusting Thee, Lord Jesus,\nTrusting only Thee;\nTrusting Thee for full salvation,\nGreat and free.",
	2: "I am trusting Thee for pardon;\nAt Thy feet I bow;\nFor Thy grace and tender mercy,\nTrusting now.",
	3: "I am trusting Thee to guide me;\nThou alone shalt lead;\nEvery day and hour supplying\nAll my need.",
	4: "I am trusting Thee, Lord Jesus;\nNever let me fall;\nI am trusting Thee forever,\nAnd for all."
}
song["sda-hymnal/536"] = {
	title: "God, Who Stretched",
	tune: "Henise",
	info: "Alternate tune, HYMN TO JOY, No. 12",
	copyright: "“God, Who Stretched” Words copyright © 1967 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright © 1984 by Eurydice Osterman. Used by permission.",
	1: "God, who stretched the spangled heavens\nInfinite in time and place,\nFlung the suns in burning radiance\nThrough the silent fields of space;\nWe, Your children in Your likeness,\nShare inventive powers with You;\nGreat Creator, still creating,\nShow us what we yet may do.",
	2: "We have ventured worlds undreamed of\nSince the childhood of our race;\nKnowing the ecstasy of winging\nThrough untraveled realms of space,\nProbed the secrets of the atom,\nYielding unimagined power,\nFacing us with life’s destruction\nOr our most triumphant hour.",
	3: "As each far horizon beckons,\nMay it challenge us anew:\nChildren of creative purpose,\nServing others, honoring You.\nMay our dreams prove rich with promise;\nEach endeavor well begun;\nGreat Creator, give us guidance\nTill our goals and Yours are one."
}
song["sda-hymnal/537"] = {
	title: "He Leadeth Me",
	scripture: "Psalm 23:2",
	1: "He leadeth me: O blessed thought!\nO words with heavenly comfort fraught!\nWhate’er I do, where’er I be,\nstill ’tis God’s hand that leadeth me.\nRefrain\nHe leadeth me, he leadeth me,\nby his own hand he leadeth me;\nhis faithful follower I would be,\nfor by his hand he leadeth me.",
	2: "Sometimes mid scenes of deepest gloom,\nsometimes where Eden’s bowers bloom,\nby waters still, o’er troubled sea,\nstill ’tis his hand that leadeth me.",
	3: "Lord, I would place my hand in thine,\nnor ever murmur nor repine;\ncontent, whatever lot I see,\nsince ’tis my God that leadeth me.",
	4: "And when my task on earth is done,\nwhen by thy grace the victory’s won,\ne’en death’s cold wave I will not flee,\nsince God through Jordan leadeth me."
}
song["sda-hymnal/538"] = {
	title: "Guide Me, O Thou Great Jehovah",
	tune: "Cwm Rhondda",
	info: "Alternate tune, BRYN CALFARIA, No. 165. Higher key, No. 201.",
	1: "Guide me, O thou great Jehovah,\npilgrim through this barren land.\nI am weak, but thou art mighty;\nhold me with thy powerful hand.\nBread of heaven, bread of heaven,\nfeed me till I want no more;\nfeed me till I want no more.",
	2: "Open now the crystal fountain,\nwhence the healing stream doth flow;\nlet the fire and cloudy pillar\nlead me all my journey through.\nStrong deliverer, strong deliverer,\nbe thou still my strength and shield;\nbe thou still my strength and shield.",
	3: "When I tread the verge of Jordan,\nbid my anxious fears subside;\ndeath of death and hell’s destruction,\nland me safe on Canaan’s side.\nSongs of praises, songs of praises,\nI will ever give to thee;\nI will ever give to thee."
}
song["sda-hymnal/539"] = {
	title: "I Will Early Seek the Savior",
	1: "I will early seek the Savior,\nI will learn of Him each day;\nI will follow in His footsteps,\nI will walk the narrow way.\nRefrain\nFor He loves me, yes, He loves me,\nJesus loves me, this I know.\nJesus loves me, died to save me,\nThis is why I love Him so.",
	2: "I will hasten where He bids me,\nI am not too young to go\nIn the pathway where He leadeth,\nNot too young His will to know.",
	3: "He is standing at the doorway\nOf escape from every sin;\nI will knock, for He has promised,\nHe will hear and let me in."
}
song["sda-hymnal/540"] = {
	title: "Gentle Jesus, Meek and Mild",
	tune: "Gentle Jesus",
	info: "Alternate tune, ORIENTUS PARTIBUS, No. 549",
	copyright: "“Gentle Jesus, Meek and Mild” Music copyright © J. Curwen & Sons. Used by permission of G. Schirmer, Inc. U.S.A. agents.",
	1: "Gentle Jesus, meek and mild,\nLook upon a little child;\nPity my simplicity,\nSuffer me to come to Thee.",
	2: "Lamb of God, I look to Thee;\nThou shalt my example be;\nThou art gentle, meek, and mild,\nThou wast once a little child.",
	3: "Fain I would be as Thou art;\nGive me Thy obedient heart;\nThou art pitiful and kind,\nLet me have Thy loving mind.",
	4: "I shall then show forth Thy praise,\nServe Thee all my happy days;\nThen the world shall always see\nChrist, the Holy Child, in me."
}
song["sda-hymnal/541"] = {
	title: "Lord, Speak to Me",
	tune: "Canonbury",
	scripture: "Rom. 14:7",
	1: "Lord, speak to me, that I may speak\nin living echoes of thy tone;\nas thou has sought, so let me seek\nthine erring children lost and lone.",
	2: "O lead me, Lord, that I may lead\nthe wandering and the wavering feet;\nO feed me, Lord, that I may feed\nthy hungering ones with manna sweet.",
	3: "O strengthen me, that while I stand\nfirm on the rock, and strong in thee,\nI may stretch out a loving land\nto wrestlers with the troubled sea."
}
song["sda-hymnal/542"] = {
	title: "Jesus, Friend So Kind",
	tune: "Tender Song",
	copyright: "“Jesus, Friend So Kind” Words by permission of Denalde M. Gregory. Music copyright ©’ 1984 by Wayne Hooper.",
	1: "Jesus, Friend, so kind and gentle,\nLittle ones we bring to Thee:\nGrant to them Thy dearest blessing,\nLet Thine arms around them be;\nNow enfold them in Thy goodness,\nFrom all danger keep them free.",
	2: "Thou who did receive the children\nTo Thyself so tenderly,\nGive to all who teach and guide them\nWisdom and humility.\nVision true to keep them noble,\nLove to serve them faithfully."
}
song["sda-hymnal/543"] = {
	title: "Jesus, Friend of Little Children",
	tune: "Simonside",
	1: "Jesus, Friend of little children,\nBe a friend to me;\nTake my hand and ever keep me\nClose to Thee.",
	2: "Teach me how to grow in goodness,\nDaily as I grow;\nThou hast been a child, and surely\nThou dost know.",
	3: "Step by step, O, lead me onward,\nUpward into youth;\nWiser, stronger, still becoming\nIn Thy truth.",
	4: "Never leave me, nor forsake me;\nEver be my friend;\nFor I need Thee from life’s dawning\nTo its end."
}
song["sda-hymnal/544"] = {
	title: "Jesus, Son of Blessed Mary",
	tune: "Shipston",
	info: "Higher key, No. 55",
	copyright: "“Jesus, Son of Blessed Mary” Music from The English Hymnal by permission of Oxford University Press.",
	1: "Jesus, Son of blessed Mary,\nOnce on earth a little child,\nPattern fair of holy living,\nGracious, loving, undefiled;",
	2: "Though Thy eager heart was yearning\nHeavy laden souls to free,\nYet Thou calledst little children\nIn their happiness to Thee.",
	3: "Grant that we, like little children,\nFree from pride and guile my be;\nCheerful, trusting, safe, protected\nBy the blessed Trinity."
}
song["sda-hymnal/545"] = {
	title: "Savior, Like a Shepherd",
	tune: "Shepherd",
	1: "Savior, like a shepherd lead us,\nmuch we need thy tenderest care;\nin thy pleasant pastures feed us,\nfor our use thy folds prepare.\nBlessed Jesus, blessed Jesus!\nThou hast bought us, thine we are.\nBlessed Jesus, blessed Jesus!\nThou hast bought us, thine we are.",
	2: "We are thine, thou dost befriend us,\nbe the guardian of our way;\nkeep thy flock, from sin defend us,\nseek us when we go astray.\nBlessed Jesus, blessed Jesus!\nHear, O hear us when we pray.\nBlessed Jesus, blessed Jesus!\nHear, O hear us when we pray.",
	3: "Thou hast promised to receive us,\npoor and sinful though we be;\nthou hast mercy to relieve us,\ngrace to cleanse and power to free.\nBlessed Jesus, blessed Jesus!\nWe will early turn to thee.\nBlessed Jesus, blessed Jesus!\nWe will early turn to thee."
}
song["sda-hymnal/546"] = {
	title: "The Lord's My Shepherd",
	tune: "Brother James' Air",
	scripture: "Psalm 23",
	info: "Alternate tune, CRIMOND, No. 552",
	copyright: "“The Lord's My Shepherd” Arrangement by permission of Oxford University Press.",
	1: "The Lord’s my shepherd, I’ll not want\nHe makes me down to lie\nIn pastures green; he leadeth me\nThe quiet waters by.\nHe leadeth me, He leadeth me\nThe quiet waters by.",
	2: "My soul he doth restore again\nAnd me to walk doth make\nWithin the paths of righteousness\nEven for his own name’s sake.\nWithin the paths of righteousness\nEven for his own name’s sake.",
	3: "Yea, though I walk through death’s dark vale\nYet will I fear no ill\nFor thou art with me, and thy rod\nAnd staff me comfort still.\nFor thou art with me, and thy rod\nAnd staff me comfort still.",
	4: "My table thou has furnished\nIn presence of my foes;\nMy head thou dost with oil anoint\nAnd my cup overflows.\nMy head thou dost with oil anoint\nAnd my cup overflows.",
	5: "Goodness and mercy all my life\nShall surely follow me,\nAnd in God’s house for evermore\nMy dwelling-place shall be.\nAnd in God’s house for evermore\nMy dwelling-place shall be."
}
song["sda-hymnal/547"] = {
	title: "Be Thou My Vision",
	tune: "Slane",
	info: "Harmony setting, No, 320",
	copyright: "“Be Thou My Vision” Words from The Poem Book of the Gael, edited by Eleanor Hull, by permission of the Editor’s Estate and Chatto & Windus. Arrangement from the Revised Church Hymnary 1927 by permission of the Oxford University Press.",
	1: "Be Thou my Vision, O Lord of my heart;\nNaught be all else to me, save that Thou art\nThou my best Thought, by day or by night,\nWaking or sleeping, Thy presence my light.",
	2: "Be Thou my Wisdom, and Thou my true Word;\nI ever with Thee and Thou with me, Lord;\nThou my great Father, I Thy true son;\nThou in me dwelling, and I with Thee one.",
	3: "Riches I heed not, nor man’s empty praise,\nThou mine Inheritance, now and always:\nThou and Thou only, first in my heart,\nHigh King of Heaven, my Treasure Thou art.",
	4: "High King of Heaven, my victory won,\nMay I reach Heaven’s joys, O bright Heaven’s Sun!\nHeart of my own heart, whatever befall,\nStill be my Vision, O Ruler of all."
}
song["sda-hymnal/548"] = {
	title: "Now Praise the Hidden God of Love",
	tune: "Canonbury",
	copyright: "“Now Praise the Hidden God of Love ” Words copyright © 1982 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "Now praise the hidden God of love\nIn whom we all must live and move,\nWho shepherds us, at every stage\nThro’ youth, maturity, and age.",
	2: "Who challenged us, when were young\nTo storm the citadels of wrong;\nIn care for others taught us how\nGod’s true community must grow.",
	3: "Who bids us never lose our zest,\nTho’ age is urging us to rest,\nBut proves to us that we have still\nA work to do, a place to fill.",
	4: "Then talk no more of wasted time,\nBut Godward look, and upward climb,\nContent to sleep, when day is done,\nAnd rise refreshed, and travel on."
}
song["sda-hymnal/549"] = {
	title: "Loving Shepherd of Thy Sheep",
	tune: "Orientis partibus",
	scripture: "John 10:27,28",
	1: "Loving Shepherd of Thy sheep,\nKeep Thy lamb, in safety keep;\nNothing can Thy power withstand;\nNone can pluck me from Thy hand.",
	2: "Loving Shepherd, ever near,\nTeach Thy lamb Thy voice to hear;\nSuffer not my steps to stray\nFrom the straight and narrow way.",
	3: "Where Thou leadest I would go,\nWalking in Thy steps below,\nTill within the heavenly fold\nI my Shepherd shall behold."
}
song["sda-hymnal/550"] = {
	title: "Every Flower That Grows",
	tune: "Laurel",
	copyright: "“Every Flower That Grows” Music copyright 1966 by Lutheran Church Press, Philadelphia, and Augsberg Publishing House, Minneapolis. Used by permission.",
	1: "Every flow’r that grows, Every brook that flows,\nTell of beauty God has giv’n for me:\nThroughout my life may beauty be\nDeep within a heart from sin set free.",
	2: "Gracious Lord above, Looking down in love,\nGuide my thoughts, my life, in my walk with Thee,\nThat day by day the world may see\nChrist, the Lord and Savior, lives in me.",
	3: "All my earthly days, I shall sing and praise\nGod the Father, Spirit, and Christ with the Son.\nGrant faith when life on earth is done,\nI shall sing with those whose rest is won."
}
song["sda-hymnal/551"] = {
	title: "Jesus, Savior, Pilot Me",
	tune: "Pilot",
	1: "Jesus, Savior, pilot me\nover life’s tempestuous sea;\nunknown waves before me roll,\nhiding rock and treacherous shoal.\nChart and compass come from thee;\nJesus, Savior, pilot me.",
	2: "As a mother stills her child,\nthou canst hush the ocean wild;\nboisterous waves obey thy will,\nwhen thou sayest to them, “Be still!”\nWondrous sovreign of the sea,\nJesus, Savior, pilot me.",
	3: "When at last I near the shore,\nand the fearful breakers roar\n‘twixt me and the peaceful rest,\nthen, while leaning on thy breast,\nmay I hear thee say to me,\n“Fear not, I will pilot thee."
}
song["sda-hymnal/552"] = {
	title: "The Lord's My Shepherd",
	tune: "Crimond",
	scripture: "Psalm 23",
	info: "Alternate tune, BROTHER JAMES, No. 546",
	1: "The Lord’s my Shepherd, I’ll not want.\nHe makes me down to lie\nIn pastures green; He leadeth me\nThe quiet waters by.",
	2: "My soul He doth restore again.\nAnd me to walk doth make\nWithin the paths of righteousness,\nE’en for His own name’s sake.",
	3: "Yea, though I walk in death’s dark vale,\nYet will I fear no ill,\nFor Thou art with me; and Thy rod\nAnd staff me comfort still.",
	4: "My table Thou hast furnished\nIn presence of my foes;\nMy head Thou dost with oil anoint,\nAnd my cup overflows.",
	5: "Goodness and mercy all my life\nShall surely follow me;\nAnd in God’s house forevermore\nMy dwelling place shall be."
}
song["sda-hymnal/553"] = {
	title: "Jesus, Guide Our Way",
	tune: "Seelenbräutigam",
	1: "Jesus, guide our way\nTo eternal day:\nSo shall we, no more delaying,\nFollow Thee, Thy voice obeying:\nLead us by the hand\nTo our Father’s land.",
	2: "When we danger meet\nSteadfast make our feet;\nLord, preserve us uncomplaining\nMid the darkness round us reigning:\nThrough adversity lies our way to Thee.",
	3: "Order all our way\nThrough the mortal day:\nIn our toil, with aid be near us:\nIn our need, with succour cheer us:\nTill we safely stand\nIn our Father’s land."
}
song["sda-hymnal/554"] = {
	title: "O Let Me Walk With Thee",
	tune: "Morton",
	1: "O let me walk with Thee, my God,\nAs Enoch walked in days of old;\nPlace Thou my trembling hand in Thine,\nAnd sweet communion with me hold;\nE’en though the path I may not see,\nYet, Jesus, let me walk with Thee.",
	2: "I cannot, dare not, walk alone;\nThe tempest rages in the sky,\nA thousand snares beset my feet,\nA thousand foes are lurking nigh.\nStill Thou the raging of the sea,\nO Master! let me walk with Thee.",
	3: "If I may rest my hand in Thine,\nI’ll count the joys of earth but loss,\nAnd firmly, bravely journey on;\nI’ll bear the banner of the cross\nTill Zion’s glorious gates I see;\nYet, Savior, let me walk with Thee."
}
song["sda-hymnal/555"] = {
	title: "Shepherd of Tender Youth",
	tune: "Kirby Bedon",
	scripture: "Deut. 32:7",
	copyright: "“Shepherd of Tender Youth” The earlier Christian hymn extant",
	1: "Shepherd of tender you,\nGuiding in love and truth,\nThrough devious way;\nChrist our triumphant King,\nWe come Thy name to sin,\nHither our children bring\nTo shout Thy praise.",
	2: "Thou are our holy Lord,\nThee all subduing Word,\nHealer of strife;\nThou didst Thyself abase,\nThat from sin’s deep disgrace\nThou mightest save our race,\nAnd give us life.",
	3: "Thou are the great High Priest;\nThou has prepared the feast\nOf heavenly love;\nWhile in our mortal pain\nNone call on Thee in vain;\nHelp Thou does not disdain,\nHelp from above.",
	4: "Ever be Thou our Guide,\nOur Shepherd and our pride,\nOur staff and song;\nJesus, Thou Christ of God,\nBy Thy perennial word,\nLead us where Thou hast trod,\nMake our faith strong."
}
song["sda-hymnal/556"] = {
	title: "As Saints of Old",
	tune: "Regwal",
	copyright: "“As Saints of Old” Words copyright © 1961 by The Hymn Society of America. Texas Christian University, Fort Worth, TX 76129. Used by permission. Music copyright Augsburg Publishing House. Used by permission.",
	1: "As saints of old their first fruits brought\nOf orchard, flock, and field\nTo God the giver all good,\nThe source of bounteous yield;\nSo we today first fruits would bring:\nThe wealth of this good land,\nOf farm and market, shop and home,\nOf mind, and heart, and hand.",
	2: "A world in need now summons us\nTo labor, love, and give;\nTo make our life an offering\nTo God, that all may live.\nThe Church of Christ is calling us\nTo make the dream come true:\nA world redeemed by Christ-like love;\nAll life in Christ made new.",
	3: "In gratitude and humble trust\nWe bring our best today,\nTo serve Your cause and share Your love\nWith all along life’s way.\nO God, who gave yourself to us\nIn Jesus Christ Your Son,\nTeach us to give ourselves each day\nUntil life’s work is done."
}
song["sda-hymnal/557"] = {
	title: "Come, Ye Thankful People",
	tune: "St. George's, Windsor",
	1: "Come, ye thankful people, come,\nRaise the song of harvest home;\nAll is safely gathered in,\nEre the winter storms begin.\nGod our Maker doth provide\nFor our wants to be supplied;\nCome to God’s own temple, come;\nRaise the song of harvest home!",
	2: "We ourselves are God’s own field,\nFruit unto His praise to yield;\nWheat and tares together sown,\nUnto joy or sorrow grown;\nFirst the blade and then the ear,\nThen the full corn shall appear;\nGrant, O harvest Lord, that we\nWholesome grain and pure may be.",
	3: "For the Lord our God shall come,\nAnd shall take His harvest home;\nFrom His field shall purge away\nAll that doth offend, that day;\nGive His angels charge at last\nIn the fire the tares to cast;\nBut the fruitful ears to store\nIn His garner evermore.",
	4: "Then, thou church triumphant, come,\nRaise the song of harvest home;\nAll are safely gathered in,\nFree from sorrow, free from sin,\nThere, forever purified,\nIn God’s garner to abide;\nCome, ten thousand angels, come,\nRaise the glorious harvest home!"
}
song["sda-hymnal/558"] = {
	title: "For the Fruits of His Creation",
	tune: "East Acklam",
	copyright: "“For the Fruits of His Creation” Words copyright © 1970 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music used by permission.",
	1: "For the fruits of His creation, thanks be to God;\nFor the gifts to every nation, thanks be to God;\nFor the plowing, sowing, reaping,\nSilent growth while men are sleeping,\nFuture needs in earth’s safe keeping, thanks be to God!",
	2: "In the just reward of labor, God’s will is done;\nIn the hop we give our neighbor, God’s will is done;\nIn our worldwide take of caring\nFor the hungry and despairing,\nIn the harvests men are sharing, God’s will is done.",
	3: "For the harvests of His Spirit, thanks be to God;\nFor the good all men inherit, thanks be to God;\nFor the wonders that astound us,\nFor the truths that still confound us,\nMost of all, that love has found us, thanks be to God!"
}
song["sda-hymnal/559"] = {
	title: "Now Thank We All Our God",
	tune: "Nun Danket",
	1: "Now thank we all our God\nwith heart and hands and voices,\nwho wondrous things hath done,\nin whom His world rejoices;\nWho, from our mothers’ arms\nhath blessed us on our way\nwith countless gifts of love,\nand still is ours today.",
	2: "O may this bounteous God\nthrough all our life be near us,\nwith ever joyful hearts\nand blessed peace to cheer us;\nand keep us still in grace,\nand guide us when perplexed;\nand free us from all ills,\nin this world and the next.",
	3: "All praise and thanks to God\nthe Father now be given;\nthe Son, and Him who reigns\nwith them in highest heaven;\nthe one eternal God,\nwhom earth and heaven adore;\nfor thus it was, is now,\nand shall be evermore."
}
song["sda-hymnal/560"] = {
	title: "Let All Things Now Living",
	scripture: "1 Chronicles 16:23",
	copyright: "“Let All Things Now Living” Words used with permission of E.C. Schirmer Music Co., copyright owner. Arrangement copyright © 1984 by Wayne Hooper.",
	1: "Let all things now living a song of thanksgiving\nTo God the Creator triumphantly raise,\nWho fashioned and made us, protected and stayed us,\nWho guideth us on to the end of our days.\nHis banners are o’er us,\nHis light goes before us,\nA pillar of fire shining forth in the night,\n‘Til shadows have vanished and darkness is banished,\nas forward we travel from light into light.",
	2: "His law He enforces: the stars in their courses,\nThe sun in His orbit, obediently shine;\nThe hills and the mountains, the rivers and fountains,\nThe deeps of the ocean proclaim Him divine,\nWe too should be voicing our love and rejoicing,\nWith glad adoration a song let us raise,\n‘Til all things now living unite in thanksgiving\nTo God in the highest, hosanna and praise."
}
song["sda-hymnal/561"] = {
	title: "We Plow the Fields",
	tune: "Wir pflügen",
	1: "We plow the fields, and scatter the good seed on the land,\nBut it is fed and watered by God’s almighty hand.\nHe sends the snow in winter, the warmth to swell the grain,\nThe breezes and the sunshine, and soft refreshing rain.\nRefrain\nAll good gifts around us\nAre sent from heaven above;\nThen thank the Lord, O thank the Lord\nFor all His love.",
	2: "He only is the Maker of all things near and far;\nHe paints the wayside flower, He lights the evening star.\nThe winds and waves obey Him, by Him the birds are fed;\nMuch more, to us His children, He gives our daily bread.",
	3: "We thank Thee then, O Father, for all things bright and good,\nThe seed-time and the harvest, our life, our health, and food.\nAccept the gifts we to offer for all Thy love imparts,\nAnd, what Thou most desirest, our humble, thankful hearts."
}
song["sda-hymnal/562"] = {
	title: "Come, Sing a Song of Harvest",
	tune: "Lebendige Gemeinde",
	copyright: "“Come, Sing a Song of Harvest” Words copyright © by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music copyright by Horst Gehann. Used by permission",
	1: "Come, sing a song of harvest, Of thanks for daily food!\nTo offer God the first-fruits is old as gratitude.",
	2: "Long, long ago, the reapers, before they kept the feast,\nPut first-fruits in a basket, and took it to the priest.",
	3: "Shall we, sometimes forgetful oh where creation starts,\nWith science in our pockets lose wonder from our hearts?",
	4: "May God, the great Creator, to whom all life belongs,\nAccept these gifts we offer, our service and our songs.",
	5: "And lest the world go hungry while we ourselves are fed,\nMake each of us more ready to share our daily bread."
}
song["sda-hymnal/563"] = {
	title: "Praise and Thanksgiving",
	tune: "Lobet und Preiset",
	copyright: "“Praise and Thanksgiving” Used by permission of Friendship Press.",
	1: "Praise and thanksgiving let everyone bring\nUnto our Father for every good thing.\nAll together joyfully sing!"
}
song["sda-hymnal/564"] = {
	title: "For Sunrise Hope and Sunset Calm",
	tune: "Hymn of Praise",
	copyright: "“For Sunrise Hope and Sunset Calm” From Wings by Amy Carmichael. Copyright 1960 by Dohnavur Fellowship.",
	1: "For sunrise hope and sunset calm, and all that lies between,\nFor all the sweetness and the balm that is and that has been,\nFor comradeship for peace in strife, and light on darkened days;\nFor work to do and strength for life we sing our hymn of praise.",
	2: "But O, we press far, far above these gifts of pure delight,\nAnd find in Thee, and in Thy love contentment infinite.\nO Lord, beloved, in whom are found all joys of time and place,\nWhat will it be when joy is crowned by vision of Thy face?"
}
song["sda-hymnal/565"] = {
	title: "For the Beauty of the Earth",
	tune: "Dix",
	info: "Unison setting, No. 123",
	1: "For the beauty of the earth,\nfor the glory of the skies,\nfor the love which from our birth\nover and around us lies;\nLord of all, to Thee we raise\nthis our grateful song of praise.",
	2: "For the joy of human love,\nbrother, sister, parent, child,\nfriends on earth and friends above,\nPleasures pure and undefiled,\nLord of all, to Thee we raise\nthis our grateful song of praise.",
	3: "For the gift of Thy dear Son,\nfor the hope of heaven at last,\nfor the Spirit’s victory won,\nFor the crown when life is past,\nLord of all, to Thee we raise\nSongs of gratitude and praise."
}
song["sda-hymnal/566"] = {
	title: "Father, We Thank You",
	tune: "All Kinds of Light",
	copyright: "“Father, We Thank You” Copyright © by Caryl Micklem.",
	1: "Father, we thank You for the light that shines all the day;\nFor the bright sky You have given most like Your heaven;\nFather, we thank You.",
	2: "Father, we thank You for the lamps that lighten the way;\nFor human skill’s exploration of Your creation;\nFather, we thank You.",
	3: "Father, we thank You for the friends who brighten our play;\nFor Your command to call others sisters and brothers;\nFather, we thank You.",
	4: "Father, we thank You for Your love in Jesus today,\nGiving us hope for tomorrow through joy and sorrow;\nFather, we thank You."
}
song["sda-hymnal/567"] = {
	title: "Have Thine Own Way, Lord",
	1: "Have thine own way, Lord! Have thine own way!\nThou art the potter, I am the clay.\nMold me and make me after thy will,\nwhile I am waiting, yielded and still.",
	2: "Have thine own way, Lord! Have thine own way!\nSearch me and try me, Savior today!\nWash me just now, Lord, wash me just now,\nas in thy presence humbly I bow.",
	3: "Have thine own way, Lord! Have thine own way!\nWounded and weary, help me I pray!\nPower, all power, surely is thine!\nTouch me and heal me, Savior divine!",
	4: "Have thine own way, Lord! Have thine own way!\nHold o’er my being absolute sway.\nFill with thy Spirit till all shall see\nChrist only, always, living in me!"
}
song["sda-hymnal/568"] = {
	title: "Make Me a Captive, Lord",
	tune: "Leominster",
	1: "Make me a captive, Lord, and then I shall be free;\nForce me to render up my sword, and I shall conqueror be.\nI sink in life’s alarms when by myself I stand;\nImprison me within Thine arms, and strong shall be my hand.",
	2: "My heart is weak and poor until it amaster find;\nIt has no spring of action sure, it varies with the wind.\nIt cannot freely move till Thou has wrought its chain;\nEnslave it with Thy matchless love, and deathless it shall reign.",
	3: "My will is not my own till Thou hast made it Thine;\nIf it would reach a monarch’s throne, it must its crown resign;\nIt only stands unbent, amid the clashing strife,\nWhen on Thy bosom it has leant, and found in Thee its life."
}
song["sda-hymnal/569"] = {
	title: "Pass Me Not, O Gentle Savior",
	poet: "Fanny J. Crosby, 1868 (1820 - 1915)",
	1: "Pass me not, O gentle Savior,\nhear my humble cry;\nwhile on others thou art calling,\ndo not pass me by.\nRefrain\nSavior, Savior, hear my humble cry;\nwhile on others thou art calling,\ndo not pass me by.",
	2: "Let me at thy throne of mercy\nfind a sweet relief,\nkneeling there in deep contrition;\nhelp my unbelief.",
	3: "Trusting only in thy merit,\nwould I seek thy face;\nheal my wounded, broken spirit,\nsave me by thy grace.",
	4: "Thou the spring of all my comfort,\nmore than life to me,\nwhom have I on earth beside thee?\nWhom in heaven but thee?"
}
song["sda-hymnal/570"] = {
	title: "Not I, But Christ",
	tune: "Bolton",
	1: "Not I, but Christ, be honored, loved, exalted;\nNot I, but Christ, be seen, be known, be heard;\nNot I, but Christ, in every look and action,\nNot I, but Christ, in every thought and word.",
	2: "Not I, but Christ, to gently soothe in sorrow,\nNot I, but Christ, to wipe the falling tear;\nNot I, but Christ, to lift the weary burden,\nNot I, but Christ, to hush away all fear.",
	3: "Christ, only Christ! No idle words e’er falling,\nChrist, only Christ; no needless bustling sound;\nChrist, only Christ, no self important bearing;\nChrist, only Christ, no trace of “I” be found.",
	4: "Not I, but Christ, my every need supplying,\nNot I, but Christ, my strength and health to be;\nChrist, only Christ, for body, soul, and spirit,\nChrist, only Christ, here and eternally."
}
song["sda-hymnal/571"] = {
	title: "What Does the Lord Require",
	tune: "Sharpthorne",
	scripture: "Micah 6:8",
	copyright: "“What Does the Lord Require?” Words by permission o fOxford University Press. Music copyright © 1969 by Hope Publishing Co., Carol Stream, Il 60188. All rights reserved. Used by permission.",
	1: "What does the Lord require for praise and offering?\nWhat sacrifice, desire or tribute bid you bring?\nDo justly; Love mercy; Walk humbly with your God.",
	2: "Rulers of men, give ear! Should you not justice show?\nWill God your pleading hear, while crime and cruelty grow?\nDo justly; Love mercy; Walk humbly with your God.",
	3: "How shall our life fulfill God’s law so hard and high?\nLet Christ endue our will with grace to fortify.\nThen justly, in mercy we’ll humbly walk with God."
}
song["sda-hymnal/572"] = {
	title: "Give of Your Best to the Master",
	tune: "Barnard",
	1: "Give of your best to the master,\nGive of the strength of your youth;\nThrow your soul’s fresh, glowing ardor\nInto the battle for truth.\nJesus has set the example –\nDauntless was He, young and brave;\nGive Him your loyal devotion,\nGive Him the best that you have.\nRefrain\nGive of your best to the master,\nGive of the strength of your youth;\nClad in salvation’s full armor,\nJoin in the battle for truth.",
	2: "Give of your best to the Master,\nGive Him first place in your heart;\nGive Him first place in your service,\nConsecrate every part.\nGive, and to you shall be given –\nGod His beloved Son gave;\nGratefully seeking to serve Him,\nGive Him the best that you have.",
	3: "Give of your best to the Master,\nNaught else is worthy His love;\nHe gave Himself for your ransom,\nHave up His glory above;\nLaid down His life without murmur,\nYou from sin’s ruin to save;\nGive Him your heart’s adoration,\nGive Him the best that you have."
}
song["sda-hymnal/573"] = {
	title: "I'll Go Where You Want Me to Go",
	1: "It may not be on the mountain height\nOr over the stormy sea,\nIt may not be at the battle’s front\nMy Lord will have need of me.\nBut if, by a still, small voice he calls\nTo paths that I do not know,\nI’ll answer, dear Lord, with my hand in thine;\nI’ll go where you want me to go.\nRefrain\nI’ll go where you want me to go, dear Lord,\nOver mountain or plain or sea;\nI’ll say what you want me to say, dear Lord;\nI’ll be what you want me to be.",
	2: "Perhaps today there are loving words\nWhich Jesus would have me speak\nThere may be now in the paths of sin\nSome wand’rer whom I should seek.\nO Savior, if thou wilt be my guide,\nTho dark and rugged the way,\nMy voice shall echo the message sweet;\nI’ll say what you want me to say.",
	3: "There’s surely somewhere a lowly place\nIn earth’s harvest fields so wide\nWhere I may labor through life’s short day\nFor Jesus, the Crucified.\nSo trusting my all to thy tender care,\nAnd knowing thou lovest me,\nI’ll do thy will with a heart sincere:\nI’ll be what you want me to be."
}
song["sda-hymnal/574"] = {
	title: "O Master, Let Me Walk With Thee",
	tune: "Martyn",
	1: "O Master, let me walk with thee\nin lowly paths of service free;\ntell me thy secret; help me bear\nthe strain of toil, the fret of care.",
	2: "Help me the slow of heart to move\nby some clear, winning word of love;\nteach me the wayward feet to stay,\nand guide them in the homeward way.",
	3: "Teach me thy patience; still with thee\nin closer, dearer company,\nin work that keeps faith sweet and strong,\nin trust that triumphs over wrong;",
	4: "In hope that sends a shining ray\nfar down the future’s broadening way,\nin peace that only thou canst give,\nwith thee, O Master, let me live."
}
song["sda-hymnal/575"] = {
	title: "Let Your Heart Be Broken",
	tune: "Bjorklund",
	copyright: "“Let Your Heart Be Broken” Copyright © 1975 and commissioned by the Evangelical Covenant Church of America. Used by permission.",
	1: "Let your heart be broken for a world in need:\nFeed the mouths that hunger, soothe the wounds that bleed,\nGive the cup of water and the loaf of bread\nBe the hands of Jesus, serving in His stead.",
	2: "Here on earth applying principles of love,\nVisible expression – God still rules above\nLiving illustration of the Living Word\nTo the minds of all who’ve Never seen or heard.",
	3: "Blest to be a blessing privileged to care,\nChallenged by the need apparent everywhere.\nWhere mankind is wanting, fill the vacant place.\nBe the means through which the Lord reveals His grace.",
	4: "Add to your believing deeds that prove it true,\nKnowing Christ as Savior, make Him Master, too.\nFollow in His footsteps, go where He has trod;\nIn the world’s great trouble risk yourself for God.",
	5: "Let your heart be tender and your vision clear;\nSee mankind as God sees, serve Him far and near.\nLet your heart be broken by a brother’s pain;\nShare your rich resources, give and give again."
}
song["sda-hymnal/576"] = {
	title: "Awake, Awake to Love and Work",
	tune: "Morning Song",
	info: "Unison setting, No. 215",
	1: "Awake, awake to love and work!\nThe lark is in the sky;\nThe fields are wet with diamond dew;\nThe worlds awake to cry\nTheir blessings on the Lord of life,\nAs He goes meekly by.",
	2: "Come, let thy voice be one with theirs,\nShout with their shout of praise;\nSee how the giant sun soars up,\nGreat lord of years and days!\nSo let the love of Jesus come\nAnd set thy soul ablaze.",
	3: "To give and give, and give again,\nWhat God hath given thee;\nTo spend thyself nor count the cost;\nTo serve right gloriously\nThe God who gave all worlds that are,\nAnd all that are to be."
}
song["sda-hymnal/577"] = {
	title: "In the Heart of Jesus",
	tune: "Heart of Jesus",
	1: "In the heart of Jesus there is love for you,\nLove most pure and tender, love most deep and true;\nWhy should you be lonely, why for friendship sigh,\nWhen the heart of Jesus has a full supply?",
	2: "In the mind of Jesus there is thought for you,\nWarm as summer sunshine, sweet as morning dew;\nWhy should you be fearful, why take anxious thought,\nSince the mind of Jesus cares for those He bought?",
	3: "In the field of Jesus there is work for you;\nSuch as even angels might rejoice to do;\nWhy stand idly sighing for some lifework grand,\nWhile the field of Jesus seeks your reaping hand?",
	4: "In the home of Jesus there’s a place for you;\nGlorious, bright, and joyous, calm and peaceful too;\nWhy then, like a wanderer, roam with weary pace,\nIf the home of Jesus holds for you a place?"
}
song["sda-hymnal/578"] = {
	title: "So Send I You",
	tune: "Toronto",
	scripture: "John 20:21",
	copyright: "“So Send I You” Copyright © 1954. Renewal 1982 by John W. Peterson. Assigned to Singspiration, Division of the Zondervan Corporation. All rights reserved. Used by permission.",
	1: "So sent I you- by grace made strong to triumph\nO’er hosts of hell, o’er darkness, death, and sin,\nMy name to bear, and in that name to conquer-\nSo send I you, My victory to win.",
	2: "So send I you- to take to souls in bondage\nThe word of truth that sets the captive fee,\nTo break the bonds of sin, to loose dearth’s fetters-\nSo send I you, to bring the lost to Me.",
	3: "So send I you- My strength to know in weakness,\nMy joy in grief, my perfect peace in pain,\nTo prove My power, My grace, My promised presence-\nSo send I you, eternal fruit to gain.",
	4: "So Send I you – to bear My cross with patience,\nAnd then one day with joy to lay it down\nTo hear My voice, “Well done, My faithful servant –\nCome, share My throne, My Kingdom, and My crown!”\nAs the Father hath sent Me, so send I You.”"
}
song["sda-hymnal/579"] = {
	title: "'Tis Love That Makes Us Happy",
	composer: "F. E. Belden, 1892 (1858 - 1945)",
	poet: "F. E. Belden, 1892 (1858 - 1945)",
	1: "‘Tis love that makes us happy,\n‘Tis love that smooths the way;\nIt helps us mind, it makes us kind\nTo others every day.\nRefrain\nGod is love; we’re his little children.\nGod is love; we would be like Him.\n‘Tis love that makes us happy,\n‘Tis love that smooths the way;\nIt helps us “mind,” it makes us kind\nTo others every day.",
	2: "This world is full of sorrow,\nOf sickness, death, and sin;\nWith loving heart we’ll do our part,\nAnd try some soul to win.",
	3: "And when this life is over,\nAnd we are called above\nOur song shall be, eternally,\nOf Jesus and His love."
}
song["sda-hymnal/580"] = {
	title: "This Little Light of Mine",
	copyright: "“This Little Light of Mine” Copyright © 1943 by Galaxy Music Corporation, New York. Used by permission.",
	1: "This little light of mine, I’m going to let it shine, (shine)\nThis little light of mine, I’m going to let it shine, (shine)\nThis little light of mine, I’m going to let it shine,\nLet it shine, let it shine, let it shine.",
	2: "Every where I go, I’m going to let it shine, (shine)\nEvery where I go, I’m going to let it shine, (shine)\nEvery where I go, I’m going to let it shine,\nLet it shine, let it shine, let it shine.",
	3: "All through the night, I’m going to let it shine, (shine)\nAll through the night, I’m going to let it shine, (shine)\nAll through the night, I’m going to let it shine,\nLet it shine, let it shine, let it shine."
}
song["sda-hymnal/581"] = {
	title: "When the Church of Jesus",
	tune: "King's Weston",
	info: "Unison setting, No. 232",
	copyright: "“When the Church of Jesus” Words copyright © 1969 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music from Enlarged Songs of Praise by permission of Oxford University Press.",
	1: "When the church of Jesus shuts its outer door,\nLest the roar of traffic drown the voice of prayer:\nMay our prayers, Lord make up ten times more aware\nThat the world we banish is our Christian care.",
	2: "If our hearts are lifted where devotion soars\nHigh above this hungry suffering world of ours:\nLest our hymns should drug us to forget its needs,\nForge our Christian worship into Christian deeds.",
	3: "Lest the gifts we offer, money, talents, time,\nServe to salve our conscience to our secret shame:\nLord, reprove, inspire us by the way you give;\nTeach us, dying Savior, how true Christians live."
}
song["sda-hymnal/582"] = {
	title: "Working, O Christ, With Thee",
	tune: "St. Edmund",
	1: "Working, O Christ, with Thee, working with Thee,\nUnworthy, sinful, weak, though we may be;\nOur all to Thee we give, for Thee alone we live,\nAnd by Thy grace achieve, working with Thee.",
	2: "Along the city’s waste, working with Thee,\nOur eager footsteps haste, like Thee to be;\nThe poor we gather in, the outcasts raise from sin,\nAnd labor souls to win, working with Thee.",
	3: "Savior, we weary not, working with Thee,\nAs hard as Thine our lot can never be;\nOur joy and comfort this, “Thy grace sufficent is;”\nThis changes toil to bliss, working with Thee.",
	4: "So let us labor on, working with Thee,\nTill earth to Thee is won, from sin set free;\nTill men, from shore to shore, receive Thee, and adore,\nAnd join us evermore, working with Thee."
}
song["sda-hymnal/583"] = {
	title: "You That Know the Lord",
	tune: "Abbot's Leigh",
	scripture: "1 Peter 2:4-10",
	copyright: "“You That Know the Lord” Word by permission of Hymns Ancient and Modern. Music copyright © 1942. Renewal 1970 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "You that know the Lord is gracious,\nYou for whom a cornerstone\nStands, of God elect and precious,\nLaid that you may build there-on,\nSee that on that sure foundation\nYou a living temple raise.\nTowers that may tell forth salvation,\nWalls that may reech-o praise.",
	2: "Living stones by God appointed\nEach to his allotted place,\nKings and priests, by God anointed,\nShall you not declare His grace?\nYou, a royal generation,\nTell the tidings of your birth,\nTidings of a new creation\nTo an old and weary earth.",
	3: "Tell the praise of Him who called you\nOut of darkness into light,\nBroke the fetters that enthralled you,\nGave you freedom, peace, and sight:\nTell the tale of sins forgiven,\nStrength renewed and hope restored,\nTill the earth, in tune with heaven,\nPraise and magnify the Lord."
}
song["sda-hymnal/584"] = {
	title: "There's a Spirit in the Air",
	tune: "Lauds",
	copyright: "“There's a Spirit in the Air” Words and music copyright © 1979 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "There’s a Spirit in the air, telling Christians everywhere\n“Praise the love that Christ revealed, living, working in our world.”",
	2: "Lose your shyness, find your tongue; tell the world what God has done:\nGod in Christ has come to stay, we can see His pow’r today.",
	3: "When believers break the bread, when a hungry child is fed:\nPraise the love that Christ revealed, living, working in our world.",
	4: "Still His Spirit leads the fight, seeing wrong and setting right:\nGod in Christ has come to stay, we can see His pow’r today.",
	5: "When a stranger’s not alone, where the homeless find a home,\nPraise the love that Christ revealed, living, working in our world.",
	6: "May His Spirit fill our praise, guide our thoughts and change our ways.\nGod in Christ has come to stay, we can see His power today.",
	7: "There’s a Spirit in the air, calling people everywhere;\nPraise the love that Christ revealed: living, working in our world."
}
song["sda-hymnal/585"] = {
	title: "When Christ Was Lifted From the Earth",
	tune: "St. Botolph",
	copyright: "“When Christ Was Lifted From the Earth” Words copyright © 1980 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music used by permission of Oxford University Press.",
	1: "When Christ was lifted from the earth His arms stretched out above\nThrough every culture, every birth, to draw an answering love.",
	2: "Still east and west His love extends and always, near or far,\nHe calls and claims us as His friends and loves us as we are.",
	3: "Where generation, class, or race divides us to our shame,\nHe sees not labels but a face, a person and a name.",
	4: "Thus freely loved, tho’ fully known, may I in Christ be free\nTo welcome and accept His own as Christ accepted me."
}
song["sda-hymnal/586"] = {
	title: "What Joy It Is to Worship Here",
	tune: "Jeg er saa glad",
	copyright: "“What Joy It Is to Worship Here” Words copyright © 1982 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission",
	1: "What joy it is to worship here,\nAnd find ourselves at home,\nWhere God, who uses every gift,\nHas room for all who come!",
	2: "Yet are no two of us alike\nOf all the human race,\nAnd we must seek a common ground\nIf we would share His grace."
}
song["sda-hymnal/587"] = {
	title: "In Christ There Is No East nor West",
	tune: "St. Peter",
	info: "Alternate tune, McKee, No. 62",
	1: "In Christ there is no east nor west,\nIn Him no south or north;\nBut one great fellowship of love\nThroughout the whole wide earth.",
	2: "In Him shall true heart everywhere\nTheir high communion find;\nHis service is the golden cord\nClose binding all mankind.",
	3: "Join hands, then, brothers of the faith,\nWhate’er your race may be.\nWho serves my Father as a son\nIs surely kin to me.",
	4: "In Christ now meet both east and west,\nIn Him meet south and north;\nAll Christly souls are one in Him\nThroughout the whole wide earth."
}
song["sda-hymnal/588"] = {
	title: "Lord of All Nations",
	tune: "Melrose",
	scripture: "Acts 10:34, 35",
	copyright: "“Lord of All Nations” Words from The Worship Supplement copyright © 1969 by Concordia Publishing House. Used by permission.",
	1: "Lord of all nations, grant me grace\nTo love all people, every race,\nAnd in each person may I see\nMy kindred loved, redeemed by Thee.",
	2: "Break down the wall that would divide\nThy children, Lord, on every side.\nMy neighbor’s good let me pursue;\nLet Christian love bind warm and true.",
	3: "Forgive me, Lord, where I have erred\nBy loveless act and thoughtless word.\nMake me to see the wrong I do\nWill crucify my Lord anew.",
	4: "Give me Thy courage, Lord, to speak\nWhenever strong oppress the weak.\nShould I myself the victim be,\nHelp me forgive, remembering Thee.",
	5: "With Thine own love may I be filled\nAnd by Thy Holy Spirit willed,\nThat all I touch, where’er I be,\nMay be divinely touched by Thee."
}
song["sda-hymnal/589"] = {
	title: "Holy Spirit, Gracious Guest",
	tune: "Guilford Cathedral",
	scripture: "1 Corinthians 13",
	copyright: "“Holy Spirit, Gracious Guest” Music printed by permission of Basil Ramsey Publisher of Music Ltd.",
	1: "Holy Spirit, gracious guest,\nHear and grant our heart’s request\nFor that gift supreme and best:\nHoly heav’nly love.",
	2: "Faith that mountains could remove,\nTongues of earth or heaven above,\nKnowledge, all things, empty prove\nIf I have no love.",
	3: "Though I as a martyr bleed,\nGive my goods the poor to feed,\nAll is vain if love I need:\nTherefore give me love.",
	4: "Love is kind and suffers long,\nLove is pure and thinks no wrong,\nLove than death it self more strong:\nTherefore give us love.",
	5: "Prophecy will fade away,\nMelting in the light of day;\nLove will ever with us stay:\nTherefore give us love.",
	6: "Faith and hope and love we see\nJoining hand in hand agree-\nBut the greatest of the three,\nAnd the best is love."
}
song["sda-hymnal/590"] = {
	title: "Trust and Obey",
	1: "When we walk with the Lord\nin the light of his word,\nwhat a glory he sheds on our way!\nWhile we do his good will,\nhe abides with us still,\nand with all who will trust and obey.\nRefrain\nTrust and obey, for there’s no other way\nto be happy in Jesus, but to trust and obey.",
	2: "Not a shadow can rise,\nnot a cloud in the skies,\nbut his smile quickly drives it away;\nnot a doubt nor a fear,\nnot a sigh nor a tear,\ncan abide while we trust and obey.",
	3: "Not a burden we bear,\nnot a sorrow we share,\nbut our toil he doth richly repay;\nnot a grief or a loss,\nnot a frown or a cross,\nbut is blest if we trust and obey.",
	4: "But we never can prove\nthe delights of his love\nuntil all on the altar we lay;\nfor the favor he shows,\nand the joy he bestows,\nare for them who will trust and obey.",
	5: "Then in fellowship sweet\nwe will sit at his feet,\nor we’ll walk by his side in the way;\nwhat he says we will do,\nwhere he sends we will go;\nnever fear, only trust and obey."
}
song["sda-hymnal/591"] = {
	title: "In Our Work and in Our Play",
	1: "In our work and in our play,\nJesus, ever with us stay;\nMay be always strive to be\nTrue and faithful unto Thee.\nThen we truthfully can sing,\nWe are children of the King.",
	2: "May we in Thy strength subdue\nEvil tempers, words untrue,\nThoughts impure, and deeds unkind,\nAll things hateful to Thy mind.\nThen we truthfully can sing,\nWe are children of the King.",
	3: "Children of the King are we!\nMay we loyal to Him be;\nTry to please Him every day,\nIn our work and in our play.\nThen we truthfully can sing,\nWe are children of the King."
}
song["sda-hymnal/592"] = {
	title: "Watchman, Tell Us of the Night",
	tune: "Aberystwyth",
	scripture: "Isaiah 21:11, 12",
	1: "Watchman, tell us of the night what its signs of promise are.\nTraveler, o’er yon mountain’s height, see that glory beaming star.\nWatchman, does its beauteous ray aught of joy or hope foretell?\nTraveler, yes; it brings the day, promised day of Israel.",
	2: "Watchman, tell us of the night, higher yet that star ascends.\nTraveler, blessedness and light, peace and truth its course portends.\nWatchman, will its beams alone gild the spot that gave them birth?\nTraveler, ages are its own; See it bursts o’er all the earth.",
	3: "Watchman, tell us of the night, for the morning seems to dawn.\nTraveler, darkness takes its flight, doubt and terror are withdrawn.\nWatchman, let your wanderings cease; hasten to your quiet home.\nTraveler, lo, the Prince of Peace, lo, the Son of God is come!"
}
song["sda-hymnal/593"] = {
	title: "In Times Like These",
	copyright: "“In Times Like These” Copyright © 1944. Renewal 1972 by Ruth Caye Jones. Assigned to Singspiration , Division of the Zondervan Corporation. All rights reserved. Use by permission.",
	1: "In times like these you need a Savior,\nIn times like these you need and anchor;\nBe very sure, be very sure Your anchor holds and grips the Solid rock!\nThis Rock is Jesus, Yes, He’s the One;\nThis Rock is Jesus, The only One!\nBe very sure, be very sure Your anchor holds and grips the Solid rock!",
	2: "In times like these you need the Bible,\nIn times like these O be not idle;\nBe very sure, be very sure Your anchor holds and grips the Solid rock!\nThis Rock is Jesus, Yes, He’s the One;\nThis Rock is Jesus, The only One!\nBe very sure, be very sure Your anchor holds and grips the Solid rock!",
	3: "In times like these I have a Savior,\nIn times like these I have an anchor\nI’m very sure, be very sure Your anchor holds and grips the Solid rock!\nThis Rock is Jesus, Yes, He’s the One;\nThis Rock is Jesus, The only One!\nI’m very sure, be very sure Your anchor holds and grips the Solid rock!"
}
song["sda-hymnal/594"] = {
	title: "Heir of the Kingdom",
	tune: "Rodman",
	1: "Heir of the kingdom, O why dost thou slumber?\nWhy art thou sleeping so near thy blest home?\nWake thee, arouse thee, and gird on thine armor,\nSpeed, for the moments are hurrying on.",
	2: "Heir of the kingdom, say, why dost thou linger?\nHow canst thou tarry in sight of the prize?\nUp, and adorn thee, the Savior is coming;\nHaste to receive Him descending the skies.",
	3: "Earth’s mighty nations, in strife and commotion,\nTremble with terror, and sink in dismay;\nListen, ’tis nought but the chariot’s loud rumbling;\nHeir of the kingdom, no longer delay.",
	4: "Stay not, O stay not for earth’s vain allurements!\nSee how its glory is passing away;\nBreak the strong fetters the foe hath bound o’er thee;\nHeir of the kingdom, turn, turn thee away.",
	5: "Keep the eye single, the head upward lifted;\nWatch for the glory of earth’s coming King;\nLo! o’er the mountaintops light is now breaking;\nHeirs of the kingdom, rejoice ye and sing."
}
song["sda-hymnal/595"] = {
	title: "Let Every Lamp Be Burning",
	composer: "F. E. Belden, 1886 (1858 - 1945)",
	poet: "F. E. Belden, 1886 (1858 - 1945)",
	tune: "Diligence",
	1: "Let every lamp be burning bright,\nThe darkest hour is nearing;\nThe darkest hour of earth’s long night,\nBefore the Lord’s appearing.\nRefrain\nThen trim your lamps, my brethren dear,\nThen trim your lamps with godly fear;\nThe Master’s coming draweth near,\nLet every lamp be burning.",
	2: "Though thousands calmly slumber on,\nThe last great message spuring,\nWel’ll rest our living faith upon\nHis promise of returning.",
	3: "His word our lamp, His truth our guide,\nWe cannot be mistaken;\nThough dangers rise on every side,\nWe shall not be forsaken.",
	4: "Then let good works with faith appear,\nTo help the world atound us;\nObedience bring the blessing near\nWhen faith has firmly bound us."
}
song["sda-hymnal/596"] = {
	title: "Look for the Waymarks",
	scripture: "Daniel 2:31-44",
	1: "Look for the way-marks as you journey on,\nLook for the way-marks passing one by one;\nDown through the ages, past the kingdoms four—\nWhere are we standing? Look the way-marks o’er.\nRefrain\nLook for the way-marks, the great prophetic way-marks,\nDown through the ages, past the kingdoms four.\nLook for the waymarks, the great prophetic way-marks;\nThe journey’s almost o’er.",
	2: "First, the Assyrian kingdom ruled the world,\nThen Medo-Persia’s banners were unfurled;\nAnd after Greece held universal sway,\nRome seized the scepter—where are we today?",
	3: "Down in the feet of iron and of clay,\nWeak and divided, soon to pass away;\nWhat will the next great, glorious drama be?\nChrist and His coming, and eternity."
}
song["sda-hymnal/597"] = {
	title: "Ye Servants of the Lord",
	tune: "Garelochside",
	scripture: "Luke 12:35-37",
	info: "Alternate tune, El Kader, No. 15",
	copyright: "“Ye Servants of the Lord” Music by permission of Bromhill Church, copyright trustees.",
	1: "Ye servants of the Lord,\nEach in his office wait,\nObservant of His heavenly Word,\nAnd watchful at His gate.",
	2: "Let all your lamps be bright,\nAnd trim the golden flame,\nGird up your loins as in His sight,\nHis coming thus proclaim.",
	3: "Watch, ’tis your Lord’s command,\nAnd while we speak He’s near;\nMark the first signal of His hand,\nAnd ready all appear.",
	4: "O happy servant, he,\nIn such a posture found!\nHe shall His Lord with rapture see,\nAnd be with honor crowned."
}
song["sda-hymnal/598"] = {
	title: "Watch, Ye Saints",
	1: "Watch, ye saints, with eyelids waking;\nLo! The powers of heaven are shaking;\nKeep your lamps all trimmed an burning,\nReady for your Lord’s returning.\nRefrain\nLo! He comes, lo! Jesus comes;\nLo! He come, He comes all glorious!\nJesus comes to reign victorious,\nLo! He comes, yes, Jesus comes.",
	2: "Lo! the promise of your Savior,\nPardoned sin and purchased favor,\nBlood-washed robes and crowns of glory;\nHaste to tell redemption’s story.",
	3: "Kingdoms at their base are crumbling,\nHark! His chariot wheels are rumbling;\nTell, O tell of grace abounding,\nWhile the seventh trump is sounding.",
	4: "Nations wane, though proud and stately;\nChrist His kingdom hasteneth greatly;\nEarth her latest pangs is summing;\nShout, ye saints, your Lord is coming.",
	5: "Sinners, come, while Christ is pleading;\nNow for you He’s interceding;\nHaste, ere grace and time diminished\nShall proclaim the mystery finished."
}
song["sda-hymnal/599"] = {
	title: "Rejoice, Rejoice, Believers",
	tune: "Greenland",
	scripture: "Matthew 25:1-7",
	1: "Rejoice, rejoice, believers, and let your lights appear:\nThe evening is advancing, and darker night is near.\nThe Bridegroom is arising, and soon He draweth nigh.\nUp, pray, and watch, and wrestle, at midnight comes the cry.",
	2: "The watchers on the mountain proclaim the Bride-groom near.\nGo, meet Him as He cometh, with hallelujahs clear.\nThe marriage feast is waiting, the gates wide open stand;\nUp, up, you heirs of glory, the Bride-groom is at hand.",
	3: "You saints who here in patience your cross and suff’rings bore,\nShall live and reign forever, when sorrow is no more.\nUpon the throne of glory the Lamb you shall behold,\nIn triumph cast before Him your diadems of gold!",
	4: "Our hope and expectation, O Jesus, now appear;\nArise, O sun so longed for, o’er this benighted sphere!\nWith hearts and hands uplifted, we plead, O Lord, to see\nThe day of earth’s redemption that brings us unto Thee."
}
song["sda-hymnal/600"] = {
	title: "Hold Fast Till I Come",
	1: "Sweet promise is given to all who believe–\n“Behold I come quickly, Mine own to receive;\nHold fast till I come; the danger is great;\nSleep not as do others; be watchful, and wait.”\nRefrain\n“Hold fast till I come,” sweet promise of heaven–\n“The kingdom restored, to you shall be given.”\n“Come, enter My joy, sit down on the throne;\nBright crowns are in waiting; hold fast till I come.”",
	2: "We’ll “watch unto prayer” with lamps burning bright;\nHe comes to all others a “thief in the night.”\nWe know He is near, but know not the day–\nAs spring shows that summer is not far away.",
	3: "Yes! this is our hope, ’tis built on His word–\nThe glorious appearing of Jesus, our Lord;\nOf promises all, it stands as the sum:\n“Behold I come quickly; hold fast till I come.”"
}
song["sda-hymnal/601"] = {
	title: "Watchmen, on the Walls of Zion",
	tune: "Zion",
	1: "Watchmen, on the walls of Zion,\nWhat O tell us, of the night?\nIs the daystar now arising?\nWill the morn soon greet our sight?\nO’er your vision Shine there now\nsome rays of light?\nO’er your vision Shine there now\nsome rays of light?",
	2: "Tell, O tell us, are the landmarks\nOn our voyage all passed by?\nAre we nearing now the haven?\nCan we e’en the land descry?\nDo we truly See the heavenly kingdom nigh?\nDo we truly See the heavenly kingdom nigh?",
	3: "Light is beaming, day is coming!\nLet us sound aloud the cry;\nWe behold the daystar rising\nPure and bright in yonder sky!\nSaints, be joyful; Your redemption draweth nigh;\nSaints, be joyful; Your redemption draweth nigh.",
	4: "We have found the chart and compass,\nAnd are sure the land is near;\nOnward, onward we are hasting,\nSoon the haven will appear;\nLet your voices Sound aloud your holy cheer;\nLet your voices Sound aloud your holy cheer."
}
song["sda-hymnal/602"] = {
	title: "O Brother, Be Faithful",
	1: "O brother be faithful! soon Jesus will come,\nFor whom we have waited so long;\nO, soon we shall enter our glorious home,\nAnd join in the conqueror’s song.\nO brother be faithful! for why should we prove\nUnfaithful to Him who had shown\nSuch deep, such unbounded and infinite love\nWho died to redeem us His own.",
	2: "O brother be faithful! the city of gold,\nPrepared for the good and the blest,\nIs waiting its portals of pearl to unfold,\nAnd welcome thee into thy rest.\nThen, brother be faithful! not long shall we stay\nIn weariness here, and forlorn,\nTime’s dark night of sorrow is wearing away,\nWe haste to the glorious morn.",
	3: "O brother be faithful! He soon will descend,\nCreation’s omnipotent King,\nWhile legions of angels His chariot attend,\nAnd palm wreaths, of victory bring.\nO brother be faithful! and soon shalt thou hear\nThey Savior pronounce the glad word,\nWell done, faithful servant, they title is clear,\nTo enter the joy of thy Lord.",
	4: "O brother be faithful! eternity’s years\nShall tell for thy faithfulness now,\nWhen bright smiles of gladness shall scatter thy tears,\nA coronet gleam on thy brow.\nO brother be faithful! the promise is sure,\nThat waits for the faithful and tried;\nTo reign with the ransomed, immortal and pure,\nAnd ever with Jesus abide."
}
song["sda-hymnal/603"] = {
	title: "Christian, Seek Not Repose",
	tune: "Vigilate",
	scripture: "Matthew 26:41",
	1: "Christian, seek not yet repose, Cast thy dreams of ease away;\nThou aret in teh midst of foes; Watch and pray!",
	2: "Gird thy heavenly armor on, Wear it ever, night and day;\nAmbushed lies the evil one; Watch and pray!",
	3: "Hear the victors who o’ercame; Still they mark each warrior’s way;\nAll with one sweet voice exclaim: “Watch and pray!”",
	4: "Hear, above all, hear thy Lord, Him thou lovest to obey;\nHide within thy heart His word; “Watch and pray!”",
	5: "Watch, as if on that alone Hung the issue of teh day;\nPray that help may be send down; Watch and pray!"
}
song["sda-hymnal/604"] = {
	title: "We Know Not the Hour",
	scripture: "Matthew 24:36-42",
	1: "We know not the hour of the Master’s appearing;\nYet signs all foretell that the moment is nearing\nWhen He shall return ’tis the promise most cheering\nBut we know not the hour.\nRefrain\nHe will come, let us watch and be ready;\nHe will come, hallelujah! hallelujah!\nHe will come in the clouds of His Father’s\nbright glory but we know not the hour.",
	2: "There’s light for the wise who are seeking salvation;\nThere’s truth in the book of the Lord’s revelation;\nEach prophecy points to the great comsummation\nBut we know not the hour.",
	3: "We’ll watch and we’ll pray, with our lamps trimmed and burning;\nWe’ll work and we’ll wait till the Master’s returning;\nWe’ll sing and rejoice, every omen discerning\nBut we know not the hour."
}
song["sda-hymnal/605"] = {
	title: "My Soul, Be on Thy Guard",
	tune: "Laban",
	1: "My soul, be on they guard!\nTen thousand foes arise;\nThe hosts of sin are pressing hard\nTo draw thee from the skies.",
	2: "O watch, and fight, and pray!\nThe battle ne’er give o’er;\nRenew it boldly every day,\nAnd help divine implore.",
	3: "Ne’er think the victory won,\nNor lay thine armor down;\nThy arduous task will not be done\nTill thou obtain the crown."
}
song["sda-hymnal/606"] = {
	title: "Once to Every Man and Nation",
	tune: "Ton-y-Botel",
	copyright: "“Once to Every Man and Nation” Music used by permission of Eluned Jones and Dilys Evans, representatives of the late Gwenlyn Evans.",
	1: "Once to every man and nation, comes the moment to decide,\nIn the strife of truth with falsehood, for the good or evil side;\nSome great cause, some great decision, offering each the bloom or blight,\nAnd the choice goes by forever, ’twixt that darkness and that light.",
	2: "Then to side with truth is noble, when we share her wretched crust,\nEre her cause bring fame and profit, and ’tis prosperous to be just;\nThen it is the brave man chooses while the coward stands aside,\nTill the multitude make virtue of the faith they had denied.",
	3: "By the light of burning martyrs, Christ, Thy bleeding feet we track,\nToiling up new Calv’ries ever with the cross that turns not back;\nNew occasions teach new duties, time makes ancient good uncouth,\nThey must upward still and onward, who would keep abreast of truth.",
	4: "Though the cause of evil prosper, yet the truth alone is strong;\nThough her portion be the scaffold, and upon the throne be wrong;\nYet that scaffold sways the future, and behind the dim unknown,\nStandeth God within the shadow, keeping watch above His own."
}
song["sda-hymnal/607"] = {
	title: "God of Grace and God of Glory",
	tune: "Westminister Abbey",
	info: "Alternate tune, CWM RHONDDA, No. 201",
	copyright: "“God of Grace and God of Glory” Words from The New Church Hymnal Copyright © 1937 Fleming H. Revell Co. Renewed 1965.",
	1: "God of grace and God of glory,\nOn Thy people pour Thy power;\nNow fulfill Thy church’s story,\nBring her bud to glorious flower.\nGrant us wisdom, grant us courage,\nFor the facing of this hour.",
	2: "Lo, the hosts of evil round us\nScorn Thy Christ, assail His ways;\nFrom the fears that long have bound us\nFree our hearts to faith and praise.\nGrant us wisdom, grant us courage,\nFor the facing of this hour.",
	3: "Cure Thy children’s warring madness,\nBend our pride to Thy control;\nShame our wanton selfish gladness\nRich in goods and poor in soul.\nGrant us wisdom, grant us courage,\nLest we miss Thy kingdom’s goal.",
	4: "Set our feet on lofty places,\nGird our lives that they may be\nArmored with all Christ-like graces\nIn the fight to set all free.\nGrant us wisdom, grant us courage,\nThat we fail not man nor Thee."
}
song["sda-hymnal/608"] = {
	title: "Faith Is the Victory",
	1: "Encamped along the hills of light,\nYe Christian soldiers, rise,\nAnd press the battle ere the night\nShall veil the glowing skies.\nAgainst the foe in vales below,\nLet all our strength be hurled;\nFaith is the victory, we know,\nThat overcomes the world.\nRefrain\nFaith is the victory!\nFaith is the victory!\nOh, glorious victory\nThat overcomes the world.",
	2: "His banner over us in love,\nOur sword the Word of God;\nWe tread the road the saints above\nWith shouts of triumph trod.\nBy faith they, like whirlwind’s breath,\nSwept on o’er ev’ry field;\nThe faith by which they conquered death\nIs still our shining shield.",
	3: "To him who overcomes the foe\nWhite raiment shall be giv’n;\nBefore the angels he shall know\nHis name confessed in heav’n.\nThen onward from the hills of light,\nOur hearts with love aflame;\nWe’ll vanquish all the hosts of night,\nIn Jesus’ conq’ring name."
}
song["sda-hymnal/609"] = {
	title: "Am I a Soldier of the Cross",
	tune: "Arlington",
	1: "Am I a soldier of the cross,\na follower of the Lamb,\nand shall I fear to own his cause,\nor blush to speak his name?",
	2: "Must I be carried to the skies\non flowery beds of ease,\nwhile others fought to win the prize,\nand sailed through bloody seas?",
	3: "Are there no foes for me to face?\nMust I not stem the flood?\nIs this vile world a friend to grace,\nto help me on to God?",
	4: "Sure I must fight, if I would reign;\nincrease my courage, Lord\nI’ll bear the toil, endure the pain,\nsupported by thy word",
	5: "Thy saints in all this glorious war\nshall conquer though they die;\nthey see the triumph from afar,\nby faith they bring it nigh",
	6: "When that illustrious day shall rise,\nand all thy armies shine\nin robes of victory through the skies,\nthe glory shall be thine"
}
song["sda-hymnal/610"] = {
	title: "Stand Like the Brave",
	poet: "Fanny J. Crosby (1820 - 1915)",
	1: "O Christian, awake! ’tis the Master’s command;\nWith helmet and shield, and a sword in thy hand,\nTo meet the bold tempter, go, fearlessly go,\nThen stand like the brave, with thy face to the foe.\nRefrain\nStand like the brave, stand like the brave,\nStand like the brave, with thy face to the foe.",
	2: "The cause of thy Master with vigor defend;\nBe watchful, be zealous, and fight to the end;\nWherever He leads thee, go, valiantly go,\nThen stand like the brave, with thy face to the foe.",
	3: "Press on, never doubting, thy Captain is near,\nWith grace to supply, and with comfort to cheer;\nHis love, like a stream in the desert will flow;\nThen stand like the brave, with thy face to the foe."
}
song["sda-hymnal/611"] = {
	title: "Awake, My Soul!",
	tune: "Chesterfield",
	scripture: "Philippians 3:14",
	info: "Alternate tune, ARLINGTON, No. 609",
	1: "Awake, my soul! stretch every nerve,\nAnd press with vigor on;\nA heavenly race demands thy zeal,\nAnd an immortal crown.",
	2: "’Tis God’s all animating voice\nThat calls thee from on high;\n’Tis He whose hand presents the prize\nTo thine aspiring eye.",
	3: "A cloud of witnesses around\nHold thee in full survey;\nForget the steps already trod,\nAnd onward urge thy way.",
	4: "Blest Savior, introduced by Thee,\nOur race have we begun;\nAnd, crowned with victory, at Thy feet\nWe’ll lay our trophies down."
}
song["sda-hymnal/612"] = {
	title: "Onward, Christian Soldiers!",
	tune: "St. Gertrude",
	1: "Onward, Christian soldiers, marching as to war,\nwith the cross of Jesus going on before.\nChrist, the royal Master, leads against the foe;\nforward into battle see his banners go!\nRefrain\nOnward, Christian soldiers, marching as to war,\nwith the cross of Jesus going on before.",
	2: "Like a mighty army moves the church of God;\nbrothers, we are treading where the saints have trod.\nWe are not divided, all one body we,\none in hope and doctrine, one in charity.",
	3: "Crowns and thrones my perish, kingdoms rise and wane,\nbut the church of Jesus constant will remain.\nGates of hell can never ‘gainst that church prevail;\nwe have Christ’s own promise, and that cannot fail.",
	4: "Onward then, ye people, join our happy throng,\nblend with ours your voices in the triumph song.\nGlory, laud, and honor unto Christ the King,\nthis through countless ages men and angels sing."
}
song["sda-hymnal/613"] = {
	title: "Fight the Good Fight",
	tune: "Pentecost",
	scripture: "1 Timothy 6:12",
	info: "Alternate tune, Duke Street, Nos. 82, 227",
	1: "Fight the good fight with all thy might;\nChrist is thy strength, and Christ thy right;\nLay hold on life and it shall be\nThy joy and crown eternally.",
	2: "Run the straight race through God’s good grace;\nLift up thine eyes, and seek His face.\nLife with its path before us lies;\nChrist is the way, and Christ the prize.",
	3: "Cast care aside, lean on thy guide,\nHis boundless mercy will provide;\nTrust, and the trusting soul shall prove\nChrist is its life, and Christ its love.",
	4: "Faint not, nor fear, His arms are near;\nHe changeth not, and thou art dear.\nOnly believe, and thou shalt see\nThat Christ is all in all to thee."
}
song["sda-hymnal/614"] = {
	title: "Sound the Battle Cry",
	1: "Sound the battle cry,\nSee! the foe is nigh;\nRaise the standard high\nFor the Lord;\nGird your armor on,\nStand firm, every one,\nRest your cause upon His holy word.\nRefrain\nRouse, then soldiers!\nrally round the banner!\nReady, steady, pass the word along;\nOnward, forward, shout aloud hosanna!\nChrist is Captain of the mighty throng.",
	2: "Strong to meet the foe,\nMarching on we go,\nWhile our cause we know\nMust prevail;\nShield and banner bright,\nGleaming in the light,\nBattling for the right,\nWe ne’er can fail.",
	3: "O Thou God of all,\nHear us when we call,\nHelp us, one and all,\nBy Thy grace;\nWhen the battle’s done,\nAnd the victory won,\nMay we wear the crown\nBefore Thy face."
}
song["sda-hymnal/615"] = {
	title: "Rise Up, O Church of God",
	tune: "Festal Song",
	info: "Lower key, No. 117",
	copyright: "“Rise Up, O Church of God” Words adaptation copyright © 1984 by Ottilie Stafford.",
	1: "Rise up, O men of God!\nHis kingdom tarries long.\nBring in the day of brotherhood\nand end the night of wrong.",
	2: "Let women all rise up!\nHave done with lesser things.\nGive heart and mind and soul and strength\nto serve the King of kings.",
	3: "Rise up, courageous youth!\nThe church for you doth wait,\nher strength unequal to her task;\nrise up, and make her great!",
	4: "Lift high the cross of Christ!\nTread where his feet have trod.\nDisciples of the Son of Man,\nrise up, O church of God!"
}
song["sda-hymnal/616"] = {
	title: "Soldiers of Christ, Arise",
	tune: "Diademata",
	scripture: "Ephesians 6:13",
	info: "Alternate harmony, No. 223",
	copyright: "“Soldiers of Christ, Arise” Arrangment copyright © 1984 by Melvin West.",
	1: "Soldiers of Christ,arise,\nAnd put your armor on,\nStrong in the strength which God supplies\nThrough His eternal Son\nStrong in the Lord of hosts,\nAnd in His mighty power,\nWho in the strength of Jesus trusts\nIs more than conqueror.",
	2: "Stand then in His great might,\nWith all His strength endued,\nBut take, to arm you for the fight,\nThe panoply of God;\nThat, having all things done,\nAnd all your conflicts passed,\nYe may o’ercome through Christ alone,\nAnd stand entire at last.",
	3: "From strength to strength go on,\nWrestle and fight and pray,\nTread all the powers of darkness down\nAnd win the well-fought day.\nStill let the Spirit cry\nIn all His soldiers, “Come!”\nTill Christ the Lord who reigns on high\nShall take the conquerors home."
}
song["sda-hymnal/617"] = {
	title: "We Are Living, We Are Dwelling",
	tune: "The Alarm",
	1: "We are living, we are dwelling, In a grand and awful time,\nIn an age on ages telling– To be living is sublime.\nHark! the waking up of nations, Gog and Magog to the fray;\nHark! what soundeth? Is creation Groaning for her latter day?",
	2: "Christian, rouse and arm for conflict, Nerve thee for the battlefield;\nBear the helmet of salvation, And the mighty gospel shield;\nLet the breastplate, peace, be on thee, Take the Spirit’s sword in hand;\nBoldly, fearlessly, go forth then, In Jehovah’s strength to stand.",
	3: "And the prince of evil spirits, Great deceiver of the world!\nHe who at the blessed Jesus Once his deadly weapons hurled,\nCometh with unwonted power, Knowing that his reign will cease\nWhen the kingdom shall be given To the mighty Prince of Peace.",
	4: "Christians, rouse! fight in this warfare, Cease not till the victory’s won;\nTill your Captain loud proclaimeth, “Servant of the Lord, well done!”\nHe, alone, who thus is faithful, Who abideth to the end,\nHath the promise, in the kingdom An eternity to spend."
}
song["sda-hymnal/618"] = {
	title: "Stand Up! Stand Up for Jesus!",
	tune: "Webb",
	scripture: "Ephesians 6:14",
	info: "Alternate tune, ELLACOMBE, No. 382",
	1: "Stand up, stand up for Jesus! ye soldiers of the cross;\nLift high His royal banner, it must not suffer loss:\nFrom vict’ry unto vict’ry, His army shall He lead,\nTill every foe is vanquished, and Christ is Lord indeed.",
	2: "Stand up, stand up for Jesus! The trumpet call obey:\nForth to the mighty conflict, in this His glorious day;\nYe that are men now serve Him against unnumbered foes;\nLet courage rise with danger, and strength to strength oppose.",
	3: "Stand up, stand up for Jesus! Stand in His strength alone,\nThe arm of flesh will fail you, ye dare not trust your own;\nPut on the gospel armor, and watching unto prayer,\nWhere calls the voice of duty, be never wanting there.",
	4: "Stand up, stand up for Jesus! the strife will not be long;\nThis day the noise of battle, the next the victor’s song;\nTo him that overcometh a crown of life shall be;\nHe with the King of glory shall reign eternally."
}
song["sda-hymnal/619"] = {
	title: "Lead On, O King Eternal",
	tune: "Lancashire",
	1: "Lead on, O King eternal,\nthe day of march has come;\nhenceforth in fields of conquest\nthy tents shall be our home.\nThrough days of preparation\nthy grace has made us strong;\nand now, O King eternal,\nwe lift our battle song.",
	2: "Lead on, O King eternal,\ntill sin’s fierce war shall cease,\nand holiness shall whisper\nthe sweet amen of peace.\nFor not with swords loud clashing,\nnor roll of stirring drums;\nwith deeds of love and mercy\nthe heavenly kingdom comes.",
	3: "Lead on, O King eternal,\nwe follow, not with fears,\nfor gladness breaks like morning\nwhere’er thy face appears.\nThy cross is lifted o’er us,\nwe journey in its light;\nthe crown awaits the conquest;\nlead on, O God of might."
}
song["sda-hymnal/620"] = {
	title: "On Jordan's Stormy Banks",
	copyright: "“On Jordan’s Stormy Banks” Arrangement copyright © 1984 by Melvin West",
	1: "On Jordan’s stormy banks I stand,\nAnd cast a wishful eye\nTo Canaan’s fair and happy land,\nWhere my possessions lie.\nI am bound for the promised land,\nI am bound for the promised land;\nO who will come and go with me?\nI am bound for the promised land.",
	2: "O’er all those wide extended plain\nShines one eternal day;\nThere, Christ, the Sun, for ever reigns,\nAnd scatters night away.\nI am bound for the promised land,\nI am bound for the promised land;\nO who will come and go with me?\nI am bound for the promised land.",
	3: "When shall I reach that happy place,\nAnd be forever blest?\nWhen shall I see my Father’s face,\nAnd in His kingdom rest?\nI am bound for the promised land,\nI am bound for the promised land;\nO who will come and go with me?\nI am bound for the promised land.",
	4: "Filled with delight, my raptured soul\nWould here no longer stay;\nThough Jordan’s waves around me roll,\nFearless I’d launch away.\nI am bound for the promised land,\nI am bound for the promised land;\nO who will come and go with me?\nI am bound for the promised land."
}
song["sda-hymnal/621"] = {
	title: "Gracious Father, Guard Thy Children",
	tune: "Ellesdie",
	1: "Gracious Father, guard Thy children\nFrom the foe’s destructive power;\nSave, O save them, Lord, from falling\nIn this dark and trying hour.\nThou wilt surely prove Thy people,\nAll our graces must be tried;\nBut Thy word illumes our pathway,\nAnd in God we still confide.",
	2: "We are in the time of waiting;\nSoon we shall behold our Lord,\nWafted far away from sorrow,\nTo receive our rich reward.\nKeep us, Lord, till Thine appearing,\nPure, unspotted from the world;\nLet Thy Holy Spirit cheer us\nTill Thy banner is unfurled.",
	3: "With what joyful exultation\nShall the saints Thy banner see,\nWhen the Lord for whom we’ve waited\nShall proclaim the jubilee!\nFreedom from this world’s pollutions;\nFreedom from all sin and pain;\nFreedom from the wiles of Satan,\nAnd from death’s destructive reign."
}
song["sda-hymnal/622"] = {
	title: "Come, Come, Ye Saints",
	tune: "All Is Well",
	copyright: "“Come, Come, Ye Saints” Words copyright © 1960 by Broadman Press. All rights reserved. Used by permission.",
	1: "Come, come, ye saints, no toil nor labor fear;\nBut with joy wend your way.\nThough hard to you the journey may appear,\nGrace shall be as your day.\nWe will have a living lord to guide,\nAnd we can trust Him to provide;\nDo this, and joy your hearts will swell:\nAll is well! All is well!",
	2: "We’ll find the rest which God for us prepared,\nWhen at last He will call;\nWhere none will come to hurt or make afraid,\nHe will reign over all.\nWe will make the air with music ring,\nShout praise to God our Lord and King:\nO how we’ll make the chorus swell:\nAll is well! All is well!"
}
song["sda-hymnal/623"] = {
	title: "I Will Follow Thee",
	tune: "Lawson",
	1: "I will follow Thee, my Savior,\nWheresoe’er my lot may be.\nWhere thou goest I will follow;\nYes, my Lord, I’ll follow Thee.\nRefrain\nI will follow Thee, my Saviour,\nThou didst shed Thy blood for me;\nAnd though all men should forsake Thee;\nBy Thy grace I’ll follow Thee.",
	2: "Though the road be rough and thorny,\nTrackless as the foaming sea,\nThou hast trod this way before me,\nAnd I’ll gladly follow Thee.",
	3: "Though I meet with tribulations,\nSorely tempted though I be;\nI remember Thou wast tempted,\nAnd rejoice to follow Thee.",
	4: "Though Thou leadest me through affliction,\nPoor, forsaken though I be;\nThou wast destitute, afflicted,\nAnd I only follow Thee.",
	5: "Though to Jordan’s rolling billows,\nCold and deep, Thou leadest me,\nThou hast crossed the waves before me,\nAnd I still will follow Thee."
}
song["sda-hymnal/624"] = {
	title: "I Want Jesus to Walk With Me",
	copyright: "“I Want Jesus to Walk With Me” Arrangement copyright © 1984 by Eurydice Osterman. Used by permission.",
	1: "I want Jesus to walk with me.\n(walk with me)\nI want Jesus to walk with me.\n(walk with me)\nAll along my pilgrim journey,\nI want Jesus to walk with me.\n(walk with me)",
	2: "In my trials, Lord walk with me.\n(walk with me)\nIn my trials, Lord walk with me.\n(walk with me)\nWhen the shades of life are falling,\nI want Jesus to walk with me.\n(walk with me)",
	3: "In my sorrows, Lord walk with me.\n(walk with me)\nIn my sorrows, Lord walk with me.\n(walk with me)\nWhen my heart within is aching,\nI want Jesus to walk with me.\n(walk with me)"
}
song["sda-hymnal/625"] = {
	title: "Higher Ground",
	1: "I’m pressing on the upward way,\nNew heights I’m gaining every day;\nStill praying as I’m onward bound,\n“Lord, plant my feet on higher ground.”\nRefrain\nLord, lift me up and let me stand,\nBy faith, on Heaven’s table land,\nA higher plane than I have found;\nLord, plant my feet on higher ground.",
	2: "My heart has no desire to stay\nWhere doubts arise and fears dismay;\nThough some may dwell where those abound,\nMy prayer, my aim, is higher ground.",
	3: "I want to live above the world,\nThough Satan’s darts at me are hurled;\nFor faith has caught the joyful sound,\nThe song of saints on higher ground.",
	4: "I want to scale the utmost height\nAnd catch a gleam of glory bright;\nBut still I’ll pray till Heav’n I’ve found,\n“Lord, plant my feet on higher ground.”"
}
song["sda-hymnal/626"] = {
	title: "In a Little While We're Going Home",
	1: "Let us sing a song that will cheer us by the way,\nIn a little while we’re going home.\nFor the night will end in the everlasting day,\nIn a little while we’re going home.\nRefrain\nIn a little while, In a little while,\nWe shall cross the billow’s foam;\nWe shall meet at last, When the stormy winds are past,\nIn a little while we’re going home.",
	2: "We will do the work that our hands may find to do,\nIn a little while we’re going home.\nAnd the grace of God will our daily strength renew,\nIn a little while we’re going home.",
	3: "We will smooth the path for some weary, wayworn feet,\nIn a little while we’re going home.\nAnd may loving hearts spread around an influence sweet!\nIn a little while we’re going home.",
	4: "There’s a rest beyond, there’s relief from every care,\nIn a little while we’re going home;\nAnd no tears shall fall in that city bright and fair,\nIn a little while we’re going home."
}
song["sda-hymnal/627"] = {
	title: "Jacob's Ladder",
	copyright: "“Jacob's Ladder” Arrangement copyright © 1984 by Melvin West",
	1: "We are climbing Jacob’s ladder,\nWe are climbing Jacob’s ladder,\nWe are climbing Jacob’s ladder,\nSoldiers of the cross.",
	2: "Every round goes higher, higher,\nEvery round goes higher, higher,\nEvery round goes higher, higher,\nSoldiers of the cross.",
	3: "Sinner, do you love my Jesus?\nSinner, do you love my Jesus?\nSinner, do you love my Jesus?\nSoldiers of the cross.",
	4: "If you love him, why not serve him?\nIf you love him, why not serve him?\nIf you love him, why not serve him?\nSoldiers of the cross."
}
song["sda-hymnal/628"] = {
	title: "As Jacob With Travel Was Weary",
	tune: "Jacob's Ladder",
	scripture: "Genesis 28:12",
	copyright: "“As Jacob With Travel Was Weary” Music reprinted by permission of the executors of the late Gerald H. Knight.",
	1: "As Jacob with travel was weary one day,\nAt night on a stone for a pillow he lay;\nHe saw in a vision a ladder so high\nThat its foot was on earth and its top in the sky.\nRefrain\nAlleluia to Jesus who died on the tree,\nAnd has raised up a ladder of mercy for me,\nAnd has raised up a ladder of mercy for me.",
	2: "Come let us ascend! All may climb it who will;\nFor the angels of Jacob are guarding it still:\nAnd remember each step that by faith we passo’er,\nSome prophet or martyr has trod it before.",
	3: "And when we arrive at the haven of rest\nWe shall hear the glad words, “Come up hither, ye blest,\nHere are regions of light, here are mansions of bliss.”\nO who would not climb such a ladder as this?"
}
song["sda-hymnal/629"] = {
	title: "O Happy Band of Pilgrims",
	tune: "Vulpius",
	scripture: "1 Peter 1:6, 7",
	1: "O happy band of pilgrims,\nIf onward ye will tread\nWith Jesus as your fellow,\nTo Jesus as your Head!",
	2: "O happy if ye labor\nAs Jesus did for men;\nO happy if ye hunger\nAs Jesus hungered then!",
	3: "The trials that beset you,\nThe sorrows ye endure,\nThe manifold temptations\nThat death alone can cure,",
	4: "What are they but His jewels\nOf right celestial worth?\nWhat are they but the ladder\nSet up to heaven on earth?",
	5: "O happy band of pilgrims,\nLook upward to the skies,\nWhere such a light affliction\nShall win you such a prize!"
}
song["sda-hymnal/630"] = {
	title: "Rise, My Soul, and Stretch Thy Wings",
	tune: "Amsterdam",
	1: "Rise, my soul, and stretch thy wings, thy better portion trace;\nRise from transitory things toward heaven, thy native place;\nSun, and moon, and stars decay; time shall soon this earth remove;\nRise, my soul, and haste away to seats prepared above.",
	2: "Rivers to the ocean run, nor stay in all their course;\nFire ascending seeks the sun; both speed them to their source;\nSo a soul that’s born of God, longs to view His glorious face,\nForward tends to His abode to rest in His embrace.",
	3: "Cease, ye pilgrims, cease to mourn; press onward to the prize;\nSoon our Savior will return, triumphant in the skies;\nYet a season, and you know happy entrance will be given,\nAll our sorrows left below, and earth exhcanged for heaven."
}
song["sda-hymnal/631"] = {
	title: "When on Life a Darkness Falls",
	tune: "Emerald Gates",
	copyright: "“When on Life a Darkness Falls” Word copyright © 1985 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "When on life a darkness falls,\nWhen the mist flows chilling,\nPaths and sign posts lost in doubt,\nLoveless, unfulfilling,\nReach us, Jesus, from Your cross,\nThough we feel forsaken;\nKeep us through the aching night\nTill new dawns awaken.",
	2: "When the dreams and vows of youth\nPainfully accuse us,\nStab our conscience, steal our worth,\nChrist will not refuse us:\nPeace the world cannot provide,\nDaily resurrection,\nStrong companion at our side\nFor each new direction.",
	3: "Come and meet Him, Friend and Lord,\nThro’ the gospel story:\nOpen door to life and peace,\nWindow into glory.\nAll who seek Him, soon are found,\nMade His close relation:\nChrist our pathway, Christ our home,\nChrist our sure foundation."
}
song["sda-hymnal/632"] = {
	title: "Until Then",
	copyright: "“Until Then” Copyright © 1958 by Hamblen Music Co., Inc.",
	1: "My heart can sing when I pause to remember,\nA heartache here is but a stepping stone.\nAlong a trail, thats winding always upward,\nThis troubled world, is not my final home.\nRefrain\nBut until then, my heart will go on singing,\nUntil then, with joy I’ll carry on,\nUntil the day my eyes behold the city,\nUntil the day God calls me home.",
	2: "The things of earth will dim and lose their value,\nIf we recall they’re borrowed for awhile;\nAnd things of earth that cause the heart to tremble,\nRemembered there, will only bring a smile."
}
song["sda-hymnal/633"] = {
	title: "When We All Get to Heaven",
	poet: "Eliza E. Hewitt (1851 - 1920)",
	1: "Sing the wondrous love of Jesus;\nSing his mercy and his grace.\nIn the mansions bright and blessed\nHe’ll prepare for us a place.\nRefrain\nWhen we all get to heaven,\nWhat a day of rejoicing that will be!\nWhen we all see Jesus,\nWe’ll sing and shout the victory!",
	2: "While we walk the pilgrim pathway,\nClouds will overspread the sky;\nBut when traveling days are over,\nNot a shadow, not a sigh.",
	3: "Let us then be true and faithful,\nTrusting, serving every day;\nJust one glimpse of him in glory\nWill the toils of life repay.",
	4: "Onward to the prize before us!\nSoon his beauty we’ll behold;\nSoon the pearly gates will open;\nWe shall tread the streets of gold."
}
song["sda-hymnal/634"] = {
	title: "Come, All Christians, Be Committed",
	tune: "Beach Spring",
	info: "Unison setting, No. 363",
	copyright: "“Come, All Christians, Be Committed” Words copyright © 1966 Broadman Press. All rights reserved. International copyright secured. Used by permission. Arrangement copyright © 1984 by Melvin West.",
	1: "Come, all Christians, be committed\nTo the service of the lord;\nMake your lives for him more fitted,\nTune your hearts with one accord.\nCome into His courts with gladness,\nEach his sacred vows renew,\nTurn away from sin and sadness,\nBe transformed with life anew.",
	2: "Of your time and talents give ye,\nThey are gifts from God above;\nTo be used by Christians freely\nTo proclaim His wondrous love.\nCome again to serve the Savior,\nTithes and off’rings with you bring.\nIn your work, with Him find favor,\nAnd with joy His praises sing.",
	3: "God’s command to love each other\nIs required of every one;\nShowing mercy to one another\nMirrors His redemptive plan.\nIn compassion He has given\nOf His love that is divine;\nOn the cross sins were forgiven;\nJoy and peace are fully thine.",
	4: "Come in praise and adoration,\nAll who in Christ’s name believe;\nWorship Him with consecration,\nGrace and love you will receive.\nFor His grace give Him the glory,\nFor the Spirit and the Word,\nAnd repeat the gospel story\nTill mankind His name has heard."
}
song["sda-hymnal/635"] = {
	title: "Lord of All Good",
	tune: "Morestead",
	copyright: "“Lord of All Good” Words by permission of Oxford University Press. Music copyright by Sydney Watson.",
	1: "Lord of all good, our gifts we bring You now;\nUse them Your holy purpose to fulfill.\nTokens of love and pledges they shall be\nThat our whole life is offered to Your will.",
	2: "We give our minds to understand Your ways;\nHands, voices, eyes to serve Your great design;\nHearts with the flame of your own love ablaze:\nThus for Your glory all our pow’rs combine.",
	3: "Father, whose bounty all creation shows;\nChrist, by whose willing sacrifice we live;\nSpirit, from whom all life in fullness flows:\nTo You with grateful hearts ourselves we give."
}
song["sda-hymnal/636"] = {
	title: "God, Whose Giving Knows No Ending",
	tune: "Rustington",
	info: "Alternate tune, Nettleton, No. 334",
	copyright: "“God, Whose Giving Knows No Ending” Words copyright © 1961 by the Hymn Society of America, Texas Christian University, Fort Worth, TX 76129. Used by permission.",
	1: "God, whose giving knows no ending,\nFrom Your rich and endless store:\nNature’s wonder, Jesus’ wisdom,\nCostly cross, grave’s shattered door.\nGifted by You, we turn to You,\nOff’ring up ourselves in praise:\nThankful song shall rise forever,\nGracious donor of our days.",
	2: "Skills and time are ours for pressing\nToward the goals of Christ, Your Son:\nAll at peace in health and freedom,\nRaces joined, the church made one.\nNow direct our daily labor,\nLest we strive for self alone:\nBorn with talents, make us servants\nFit to answer at Your throne.",
	3: "Treasure, too, You have entrusted,\nGain through pow’rs Your grace conferred:\nOurs to use for home and kindred,\nAnd to spread the Gospel Word.\nOpen wide our hands in sharing,\nAs we heed Christ’s ageless call.\nHealing, teaching, and reclaiming,\nServing You by loving all."
}
song["sda-hymnal/637"] = {
	title: "Son of God, Eternal Savior",
	tune: "In Babilone",
	copyright: "“Son of God, Eternal Savior” Music used by permission o fJohanna Röntgen Schwartz.",
	1: "Son of God, eternal Savior,\nSource of life and truth and grace,\nWork made flesh, whose birth among us\nHallows all our human race,\nYou our head, who throned in glory,\nFor Your own will ever plead:\nFill us with Your love and pity,\nHeal our wrongs, and help our need.",
	2: "Bind us all as one together\nIn Your church’s sacred fold,\nWeak and healthy, poor and wealthy,\nSad and joyful, young and old.\nIs there want or pain or sorrow?\nMake us all the burden share.\nAre there spirits crushed and broken?\nTeach us, Lord, to soothe their care.",
	3: "As You, Lord, have lived for others,\nSo may we for others live.\nFreely have Your gifts been granted;\nFreely may Your servants give.\nYours the gold and Yours the silver,\nYours the wealth of land and sea;\nWe but stewards of Your bounty\nHeld in solemn trust will be.",
	4: "Come, O Christ, and reign among us,\nKing of love and Prince of Peace;\nHush the storm of strife and passion,\nBid its cruel discords cease.\nBy Your patient years of toiling,\nBy Your silent hours of pain,\nQuench our fevered thirst of pleasure,\nStem our selfish greed of gain.",
	5: "Son of God, eternal Savior,\nSource of life and truth and grace,\nWord made flesh, whose birth among us\nHallows all our human race:\nBy Your praying, by Your willing\nThat Your people should be one,\nGrant, oh, grant our hope’s fruition:\nHere on earth Your will be done."
}
song["sda-hymnal/638"] = {
	title: "The Wise May Bring Their Learning",
	tune: "Tyrolese",
	copyright: "“The Wise May Bring Their Learning” Arrangement copyright © 1984 by Melvin West.",
	1: "wise may bring their learning,\nThe rich may bring their wealth,\nAnd some may bring their greatness,\nAnd some their strength and health:\nWe too would bring our treasures\nTo offer to the King,\nWe have no wealth or learning—\nWhat shall we children bring?",
	2: "We’ll bring Him hearts that love Him,\nWe’ll bring Him thankful praise,\nAnd young souls meekly striving\nTo follow in His ways:\nAnd these be the treasures\nWe offer to the King,\nAnd these are gifts that ever\nThe poorest child may bring.",
	3: "We’ll bring the little duties\nWe have to do each day;\nWe’ll try our best to please Him\nAt home, at school, at play:\nAnd better are these treasures\nTo offer to the King\nThan richest gift without them:\nYet these a child may bring."
}
song["sda-hymnal/639"] = {
	title: "A Diligent and Grateful Heart",
	tune: "St. Columba",
	copyright: "“A Diligent and Grateful Heart” Words copyright 1980 Reorganized Church of Jesus Christ of Latter Day Saints, Independence, Missouri. Used by permission.",
	1: "A diligent and grateful heart\nPrompts me to sing Thy praise.\nThy love and mercies from the start\nHave blessed me all my days.",
	2: "I thank Thee for the means to serve\nWith talents and with tithes,\nFor sharing brings the utmost joy\nWhen lifting other lives.",
	3: "My thanks I give you for stewardship\nTo minister through deeds,\nTo serve and share with patient care\nThy people in their needs.",
	4: "O Lord, I dedicate my all\nIn this response to Thee.\nHelp me to magnify this call\nIn deep humility."
}
song["sda-hymnal/640"] = {
	title: "For Beauty of Meadows",
	tune: "Anniversary Song (Marshall)",
	copyright: "“For Beauty of Meadows” Words used by permission of the author, Walter Farquharson. Music copyright © 1982 by Jane Marshall. Assigned to Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.",
	1: "For beauty of meadows, for grandeur of trees,\nFor flowers of woodlands, for creatures of seas,\nFor all You created and gave us to share,\nWe praise You,\nCreator, extolling your care.",
	2: "As stewards of beauty received at Your hand,\nAs creatures who hear Your most urgent command,\nWe turn from our wasteful destruction of life,\nConfessing our failures, confessing our strife.",
	3: "Teach us once again to be gardeners in peace;\nAll nature around us is ours but on lease;\nYour name we would hallow in all that we do,\nFulfilling our calling, creating with You."
}
song["sda-hymnal/641"] = {
	title: "God in His Love for Us",
	tune: "Wallog",
	info: "Alternate tune, Eternal Love, No. 496",
	copyright: "“God in His Love for Us” Words copyright © 1973 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission. Music by permission of Oxford University Press.",
	1: "God in His love for us lent us this planet,\nGave it a purpose in time and in space:\nSmall as a spark from the fire of creation,\nCradle of life and the home of our race.",
	2: "Thanks be to God for its bounty and beauty,\nLife that sustains us in body and mind:\nPlenty for all, if we learn how to share it,\nRiches undreamed of to fathom and find.",
	3: "Long have our human wars ruined its harvest;\nLong has earth bowed to the terror of forced;\nLong have we wasted what others have need of,\nPoisoned the fountain of life at its source.",
	4: "Earth is the Lord’s: it is ours to enjoy it,\nOurs, as His stewards, to farm and defend.\nFrom its pollution, misuse, and destruction,\nGood Lord, deliver us, world without end!"
}
song["sda-hymnal/642"] = {
	title: "We Praise Thee With Our Minds",
	copyright: "“We Praise Thee With Our Minds” Words copyright © 1964 Broadman Press. All rights reserved. Arrangement copyright © 1952 Broadman Press. All rights reserved.",
	1: "We praise Thee with our minds,\nO Lord\nKept sharp think Thy thought;\nCome,\nHoly Ghost with grace outpoured,\nTo teach what Christ hath taught.\nIn all our learning may we seek\nThat wisdom from above\nWhich comes to all: the brave,\nthe meek\nWho ask in faith and love.",
	2: "We praise Thee thro’ our bodies,\nLord,\nKept strong to do Thy will;\nThy Spirit’s temples, which afford\nA means to praise Thee still.\nWe give ourselves, a sacrifice,\nTo live as unto Thee;\nFor Thou alone hast paid the price\nTo bring salvation free.",
	3: "We praise Thee in our hearts,\nO king,\nKept pure to know Thy ways;\nAnd raise to Thee a hymn to sing\nEternally Thy praise.\nAltho adoring hearts will bow\nAs age on ages roll;\nWe praise Thee in our beings now,\nMind, body, heart, and soul."
}
song["sda-hymnal/643"] = {
	title: "Father, Who on Us Do Shower",
	tune: "Charing",
	copyright: "“Father, Who on Us Do Shower” Words from the English Hymnal by permission of Oxford University Press. Music from Enlarged Songs of Praise by permission of Oxford University Press.",
	1: "Father, who on us do shower\nGifts of plenty form Your dower,\nTo Your people give the power\nAll Your gifts to use aright.",
	2: "Give pure happiness in leisure\nTemperance in every pleasure,\nWholesome use of earthly treasure\nBodies clean and spirits bright.",
	3: "Lift from this and every nation\nAll that brings us degradation;\nQuell the forces of temptation;\nPut Your enemies to flight.",
	4: "Father, You who sought and found us,\nSon of God, whose love has bound us,\nHoly Spirit, in us, round us,\nHear us, Godhead infinite."
}
song["sda-hymnal/644"] = {
	title: "O God, Whose Will Is Life and Good",
	tune: "Bishopthorpe",
	1: "O God, whose will is life and good\nFor all of mortal breath:\nUnite in bonds of servanthood\nAll those who strive with death.",
	2: "Make strong their hands and hearts and wills\nTo drive disease afar,\nTo strive against the body’s ills\nAnd wage Your healing war.",
	3: "By healing of the sick and blind,\nChrist’s mercy they proclaim,\nMake known the great physician’s mind,\nAffirm the Savior’s name.",
	4: "Before them set Your gracious will,\nThat they, with heart and soul,\nTo You may consecrate their skill\nAnd make the sufferer whole."
}
song["sda-hymnal/645"] = {
	title: "God of our fathers",
	tune: "National Hymn",
	1: "God of our fathers, Whose almighty hand\nLeads forth in beauty all the starry band\nOf shining worlds in splendor through the skies\nOur grateful songs before Thy throne arise.",
	2: "Thy love divine hath led us in the past,\nIn this free land by Thee our lot is cast,\nBe Thou our Ruler, Guardian, Guide and Stay,\nThy Word our law, Thy paths our chosen way.",
	3: "From war’s alarms, from deadly pestilence,\nBe Thy strong arm our ever sure defense;\nThy true religion in our hearts increase,\nThy bounteous goodness nourish us in peace.",
	4: "Refresh Thy people on their toilsome way,\nLead us from night to never ending day;\nFill all our lives with love and grace divine,\nAnd glory, laud, and praise be ever Thine."
}
song["sda-hymnal/646"] = {
	title: "To the Name That Brings Salvation",
	tune: "St. Leonard",
	1: "To the name that brings salvation\nLet the nations bow the head;\nLet them kneel in adoration\nWhen this name of names is said;\nLet them pray for restoration\nOf all things in Christ the head.",
	2: "He through every generation\nRules in endless majesty;\nMay the kings of every nation\nNow foreswear their enmity,\nAnd with humble veneration\nIn the love of God agree.",
	3: "Lord, we pray for upright rulers:\nGuard them surely in their need\nFrom the vanity of power\nAnd the emptiness of greed;\nLet them see the truth of lowness,\nAnd on justice let them feed."
}
song["sda-hymnal/647"] = {
	title: "Mine Eyes Have Seen the Glory",
	tune: "Battle Hymn",
	1: "Mine eyes have seen the glory of the coming of the Lord;\nHe is trampling out the vintage where the grapes of wrath are stored;\nHe has loosed the fateful lightning of His terrible swift sword;\nHis truth is marching on.\nRefrain\nGlory! Glory! Hallelujah! Glory! Glory! Hallelujah!\nGlory! Glory! Hallelujah! His truth is marching on.",
	2: "He has sounded forth the trumpet that shall never call retreat;\nHe is sifting out the hearts of men before His judgment seat;\nO be swift, my soul, to answer Him! be jubilant, my feet!\nOur God is marching on.",
	3: "In the beauty of the lilies Christ was born across the sea,\nWith a glory in His bosom that transfigures you and me;\nAs He died to make men holy, let us live to make men free!\nWhile God is marching on."
}
song["sda-hymnal/648"] = {
	title: "I Vow to Thee, My Country",
	tune: "Thaxted",
	copyright: "“I Vow to Thee, My Country” Music copyright © J. Curwen & Sons. Used by permission o fG. Schirmer, Inc., U.S.A. agents.",
	1: "I vow to thee, my country, all earthly things above,\nEntire and whole and perfect, the service of my love:\nThe love that asks the reason, the love that stands the test,\nThat lays upon the altar the dearest and the best;\nThe love that never falters, the love that pays the price,\nThe love that makes undaunted the final sacrifice.",
	2: "And there’s another country, I’ve heard of long ago,\nMost dear to them that love her, most great to them that know;\nWe may not count her armies, we may not see her King;\nHer fortress is a faithful heart, her pride is suffering;\nAnd one by one and fervently we pray for her increase,\nAnd her ways are ways of gentleness, and all her paths are peace."
}
song["sda-hymnal/649"] = {
	title: "Lord, While for All Mankind We Pray",
	tune: "Dunfermline",
	1: "Lord, while for all mankind we pray\nOf every clime and coast,\nO hear us for our native land,\nThe land we love the most!",
	2: "O guard our shores from every foe,\nWith peace our borders bless;\nWith prosperous times our cities crown,\nOur fields with plenteousness!",
	3: "Unite us in the sacred love\nOf knowledge, truth, and Thee;\nAnd let our hills and valleys shout\nThe songs of liberty.",
	4: "Lord of the nations, thus to Thee\nOur country we commend;\nBe Thou her Refuge and her trust,\nHer everlasting friend."
}
song["sda-hymnal/650"] = {
	title: "Our Father, by Whose Name",
	tune: "Rhosymedre",
	copyright: "“Our Father, by Whose Name” Words from The Hymnal, 1940, copyright by The Church Pension Fund. Used by permission. Arrangement from Worship Supplement © 1969 by Concordia Publishing House. Used by permission.",
	1: "Our Father,\nby whose name,\nAll fatherhood is known,\nWho dost in love proclaim\nEach family Thine own,\nBless Thou all parents,\nguarding well,\nWith constant love as sentinel,\nThe homes in which Thy people dwell.",
	2: "O Christ,\nThyself a child\nWithin an earthly home,\nWith heart still undefiled,\nThou didst to manhood come;\nOur children bless, in every place,\nThat they may all behold Thy face,\nAnd knowing Thee may grow in grace.",
	3: "O Spirit,\nwho dost bind\nOur hearts in unity,\nWho teaches us to find\nThe love from self set free,\nIn all our hearts such love increase,\nThat every home, by this release,\nMay be dwelling place of peace."
}
song["sda-hymnal/651"] = {
	title: "Happy the Home That Welcomes You",
	tune: "Welwyn",
	copyright: "“Happy the Home That Welcomes You” Words copyright © by Honor Mary Thwaites.",
	1: "Happy the home that welcomes You, Lord Jesus,\nTruest of friends, most honored guest of all,\nWhere hearts and eyes are bright with joy to greet You,\nYour lightest wishes eager to fulfill.",
	2: "Happy the home where men and wife together\nAre of one mind believing in Your love:\nThrough love and pain, prosperity and hardship,\nThrough good and evil days Your care they prove.",
	3: "Happy the home, O loving Friend of children,\nWhere they are giv’n to You with hands of prayer,\nWhere at Your feet they early learn to listen\nTo Your own words, and thank You for Your care."
}
song["sda-hymnal/652"] = {
	title: "Love at Home",
	1: "There is beauty all around, When there’s love at home;\nThere is joy in every sound, When there’s love at home.\nPeace and plenty here abide, Smiling fair on every side;\nTime doth softly, sweetly glide, When there’s love at home.\nRefrain\nLove at home, love at home;\nTime doth softly, sweetly glide,\nWhen there’s love at home.",
	2: "Kindly heaven smiles above, When there’s love at home;\nAll the earth is fill’d with love, When there’s love at home.\nSweeter sings the brooklet by, Brighter beams the azure sky;\nO, there’s One who smiles on high When there’s love at home.",
	3: "Jesus, make me wholly Thine, Then there’s love at home;\nMay Thy sacrifice be mine, Then there’s love at home.\nSafely from all harm I’ll rest, With no sinful care distress’d,\nThro’ Thy tender mercy blessed, When there’s love at home."
}
song["sda-hymnal/653"] = {
	title: "Lead Them, My God, to Thee",
	tune: "Robinson",
	1: "Lead them, my God, to Thee, Lead them to Thee,\nThese children dear of mine, Thou gavest me;\nO, by Thy love divine, Lead them, my God, to Thee;\nLead them, my God, to Thee, Lead them to Thee.",
	2: "When earth looks bright and fair, Festive and gay,\nLet no delusive snare Lure them astray;\nBut from temptation’s power, Lead them, my God, to Thee,\nLead them, my God, to Thee, Lead them to Thee.",
	3: "E’en for such little ones, Christ came a child,\nAnd in this world of sin Lived undefiled.\nO, for His sake, I pray, Lead them, my God, to Thee,\nLead them, my God, to Thee, Lead them to Thee.",
	4: "Yea, though my faith be dim, I would believe\nThat Thou this precious gift Wilt now receive;\nO take their young hearts now, Lead them my God to Thee,\nLead them, my God, to Thee, Lead them to Thee."
}
song["sda-hymnal/654"] = {
	title: "Lord, Bless Our Homes",
	tune: "Charterhouse",
	copyright: "“Lord, Bless Our Homes” Words reprinted by permission of the publisher from The Hymnal of the United Church of Christ. Copyright © 1974 United Church of Christ. Music from the Revised Church Hymnary 1927 by permission of Oxford University Press.",
	1: "Lord, bless our homes with peace and love and laughter,\nWith understanding and with loyalty.\nMay we together follow Christ the Master\nAnd know the blessing of His sov’reignty.",
	2: "May every heart receive His loving spirit\nAnd know the t4ruth that makes life truly free;\nThen, in that spirit may we live united,\nAnd find in God our deep security.",
	3: "Forgive the hurts our selfishness inflicted\nOn whose we love and those who love us best.\nChrist, heal the scars and draw us all together\nIn Him whose will is peace and joy and rest.",
	4: "Father, in gratitude for homes and loved one,\nWe open now our hearts to all mankind.\nGrand us Your spirit love for one another\nSo in Your peace may we our concord find."
}
song["sda-hymnal/655"] = {
	title: "Happy the Home",
	tune: "St. Agnes",
	1: "Happy the home when God is there,\nand love fills every breast;\nwhen one their wish, and one their prayer,\nand one their heavenly rest.",
	2: "Happy the home where Jesus’ name\nis sweet to every ear;\nwhere children early speak his fame,\nand parents hold him dear.",
	3: "Happy the home where prayer is heard,\nand praise is wont to rise;\nwhere parents love the sacred Word\nand all its wisdom prize.",
	4: "Lord, let us in our homes agree\nthis blessed peace to gain;\nunite our hearts in love to thee,\nand love to all will reign."
}
song["sda-hymnal/656"] = {
	title: "O Perfect Love",
	tune: "O Perfect Love",
	1: "O perfect Love, all human thought transcending,\nlowly we kneel in prayer before thy throne,\nthat theirs may be the love which knows no ending,\nwhom thou forevermore dost join in one.",
	2: "O perfect Life, be thou their full assurance\nof tender charity and steadfast faith,\nof patient hope and quiet, brave endurance,\nwith childlike trust that fears nor pain nor death.",
	3: "Grant them the joy which brightens earthly sorrow;\ngrant them the peace which calms all earthly strife,\nand to life’s day the glorious unknown morrow\nthat dawns upon eternal love and life."
}
song["sda-hymnal/657"] = {
	title: "O God, From Whom Mankind",
	composer: "Melvin West, 1984 (1930 - )",
	tune: "Sly Park",
	info: "Alternate tune, MORECAMBE, No. 266",
	copyright: "“O God, From Whom Mankind”  Words copyright © 1968 by Hope Publishing Co., Carol Stream, IL 60188. All rights reserved. Used by permission.  Music copyright © 1984 by Melvin West.",
	1: "O God from whom mankind derives its name;\nWhose covenant of grace remains the same,\nBe with these two who now before You wait;\nEnlarge the love they come to consecrate.",
	2: "May through their union other lives be blessed;\nTheir door be wide to stranger and to guest,\nGive them the understanding that is kind,\nGrant them the blessing of an open mind.",
	3: "Preserve their days from inwardness of hear;\nTo each the gift of truthful speech impart.\nTheir bond be strong against all strain and strife\nAmid the changes of this earthly life.",
	4: "From stage to stage on life’s unfolding way\nBring to their mind the vows they make this day,\nYour Spirit be their Guide in every move,\nTheir faith in Christ the basis of their love."
}
song["sda-hymnal/658"] = {
	title: "Heavenly Father, Hear Our Prayer",
	tune: "Name of Jesus",
	copyright: "“Heavenly Father, Hear Our Prayer” Words copyright Board of Publication, Lutheran Chuch of America. Reprinted by permission. Music copyright 1958 Service Book and Hymnal. Used by permission of Augsburg Publishing House.",
	1: "Heav’nly Father, hear our prayer\nAs we bow before You:\nBless them in the life they share,\nHumble we implore You.\nBe their guide in all endeavors,\nBe their hope that nothing severs;\nConstant source of love divine,\nLet Your love within them shine!",
	2: "As they pledge their love this day\nHere before Your altar,\nMay their hearts,, upon You stayed,\nNever fail or falter.\nBe their comfort in all sorrow;\nBe their reason for tomorrow.\nGrant them strength to live each hour\nTrusting solely in Your pow’r.",
	3: "Blest Creator, Lord of life,\nHear our glad thanksgiving.\nHusband You have joined to wife\nFor their earthly living.\nJustified by Jesus’ merit,\nLife eternal they inherit.\nWhen their days on earth have passed,\nTake them to Your home at last!"
}
song["sda-hymnal/659"] = {
	title: "May the Grace of Christ Our Savior",
	tune: "Stuttgart",
	scripture: "2 Corinthians 13:14",
	1: "May the grace of Christ our Savior\nAnd the Father’s boundless love,\nWith the Holy Spirit’s favor,\nRest upon them from above.",
	2: "Thus may they abide in union\nWith each other and the Lord,\nAnd possess, in sweet communion,\nJoys which earth cannot afford."
}
song["sda-hymnal/660"] = {
	title: "Glory Be to the Father",
	tune: "Gloria Patri",
	1: "Glory be to the Father,\nand to the Son,\nand to the Holy Ghost;\nAs it was in the beginning,\nis now, and ever shall be,\nworld without end.\nAmen, Amen."
}
song["sda-hymnal/661"] = {
	title: "Holy, Holy, Holy",
	1: "Holy, holy, holy, Holy is the Lord!\nHoly, holy, holy, Holy is our God!\nHe who always liveth, Evermore the same\nHeav’n and earth He ruleth, Come and praise His name!",
	2: "Holy, holy, holy, Holy is the Lord!\nHoly, holy, holy, Holy is our God!\nGlorious adn beloved Is the One adored!\nHoly, holy, holy, Holy is the Lord."
}
song["sda-hymnal/662"] = {
	title: "Let All Mortal Flesh Keep Silence",
	tune: "Picardy",
	1: "Let all mortal flesh keep silence,\nAnd with fear and trembling stand;\nPonder nothing earthly-minded,\nFor with blessing in his hand,\nChrist our God to earth descendeth,\nOur full homage to demand. Amen."
}
song["sda-hymnal/663"] = {
	title: "Amens",
	copyright: "“Amens”  Verses 3-5 Copyright © 1984 by Melvin West.",
	1: "A-men, A—men.",
	2: "A—–men.",
	3: "A—men.",
	4: "A—men, (A-men.)",
	5: "A—–men.",
	6: "A-men, A-men, A—men."
}
song["sda-hymnal/664"] = {
	title: "Sevenfold Amen",
	1: "A-men,\nA-men,\nA—men,\nA—–men,\nA—–men,\nA—–men,\nA-men."
}
song["sda-hymnal/665"] = {
	title: "All Things Come of Thee",
	scripture: "1 Chronicles 29:14",
	1: "All things come of thee, O Lord;\nand of thine own have we given thee.\nAmen."
}
song["sda-hymnal/666"] = {
	title: "Cast Thy Burden Upon the Lord",
	tune: "Birmingham",
	scripture: "Psalms 55:22, 16:8",
	1: "Cast thy burden upon the Lord,\nAnd He shall sustain thee;\nHe never will suffer the righteous to fall;\nHe is at thy right hand.\nThy mercy, Lord, is great, and far above the heavens;\nLet none be made ashamed, that wait upon Thee."
}
song["sda-hymnal/667"] = {
	title: "Lord, Bless Thy Word to Every Heart",
	tune: "Benediction (Ledington)",
	1: "Lord, bless Thy word to every heart\nIn this Thy house today,\nAnd help us each as now we part,\nIts precepts to obey.\nAmen\nAmen"
}
song["sda-hymnal/668"] = {
	title: "O Thou Who Hearest",
	tune: "Morecambe",
	1: "O Thou who hearest every heartfelt prayer,\nWith Thy rich grace, Lord, all our hearts prepare;\nThou art our life, Thou art our love and light,\nO let this Sabbath hour with Him be bright. Amen."
}
song["sda-hymnal/669"] = {
	title: "The Lord Bless You and Keep You",
	tune: "Choral Blessing",
	scripture: "Numbers 6:24-26",
	copyright: "“The Lord Bless You and Keep You” Music used by permission of Summy - Birchard Music division of Birch Tree Group Ltd.",
	1: "The Lord bless you and keep you,\nthe Lord life His countenance upon you,\nand give you peace;\nthe Lord make His face to shine upon you,\nand be gracious unto you,\nbe gracious,\nthe Lord be gracious unto you.\nAmen."
}
song["sda-hymnal/670"] = {
	title: "We Give Thee But Thine Own",
	tune: "Schumann",
	1: "We give Thee but Thine own,\nWhat-e’er the gift may be;\nAll that we have is Thine alone,\nA trust, O Lord, from Thee."
}
song["sda-hymnal/671"] = {
	title: "Now, Dear Lord, As We Pray",
	copyright: "“As We Come to You in Prayer”  Copyright © 1970 by Lexicon Music, Inc. ASCAP. All rights reserved. International copyright secured.  Used by permission.",
	1: "Now, dear Lord, as we pray,\ntake our hearts and minds far away\nfrom the press of the world all around\nto your throne where grace does abound.\nMay our lives be transformed by Your love,\nmay our souls be refreshed from above.\nAt this moment, let people everywhere\njoin us now as we come to You in prayer."
}
song["sda-hymnal/672"] = {
	title: "Spirit of the Living God",
	copyright: "“Spirit of the Living God”  Copyright © 1935, 1963 by Moody Press, Moody Bible Institute, Chicago, IL. Used by permission.",
	1: "Spirit of the living God,\nFall afresh on me!\nSpirit of the living God,\nFall afresh on me!\nBreak me, melt me, mold me, fill me!\nSpirit of the living God,\nFall afresh on me!"
}
song["sda-hymnal/673"] = {
	title: "May God Be With You",
	composer: "Wayne Hooper, 1969 (1920 - )",
	poet: "Anonymous",
	tune: "Mizpah",
	scripture: "Genesis 31:49",
	copyright: "“May God Be With You”  Music copyright © 1969 Wayne Hooper",
	1: "May God be with you Till we meet again,\nMay God be with you, Keep you safe till then;\nAnd may His blessings Be within your heart,\nMay God be with you While we’re apart,\nMay God be with you.",
	2: "May God be with you, Watch you from above,\nMay God protect you in His tender love;\nAnd with the dawning Of each bright, new day,\nMay God be with you, To guide your way,\nMay God be with you."
}
song["sda-hymnal/674"] = {
	title: "Shalom",
	info: "The word “Shalom” has a triple meaning: hello, farewell, and peace—with special overtones of loving concern and sincere caring for each other.",
	copyright: "“Shalom”  Words copyright © 1974 by Wayne Hooper.  Arrangement copyright © 1984 by Melvin West.",
	1: "Shalom, good friends,\nshalom, good friends,\nshalom, shalom.\nTill we meet again,\ntill we meet again,\nshalom, shalom."
}
song["sda-hymnal/675"] = {
	title: "May the Lord Bless and Keep You",
	composer: "Melvin West (1930 - )",
	poet: "Melvin West (1930 - )",
	copyright: "“May the Lord Bless and Keep You”  Music copyright © by Melvin West",
	1: "May the Lord bless you and keep you\nboth now and evermore.\nAmen, Amen."
}
song["sda-hymnal/676"] = {
	title: "Thy Word Is a Lantern",
	scripture: "Psalm 119:105",
	copyright: "“Thy Word Is a Lantern”  Music from Chroal Sentences for the Worship Service. Copyright © 1966, Shawnee Press, Inc., Delaware Water Gap, PA 18327. All rights reserved. Used by permission.",
	1: "Thy Word is a lantern unto my feet,\nAnd a light unto my path."
}
song["sda-hymnal/677"] = {
	title: "Heavenly Father, to Thee We Pray",
	copyright: "“Heavenly Father, to Thee We Pray”  Words and music copyright © 1983 by Clarion Call Music, Inc. All rights reserved. Used by permission.",
	1: "Heavenly Father, to Thee we pray,\nOn the holy Sabbath day;\nThrough Thy Word Thy will make known;\nMay each heart become Thy throne,\nLet Thy living water flow\nThat we Thy boundless love may know.\nAmen."
}
song["sda-hymnal/678"] = {
	title: "God Be in My Head",
	tune: "God Be in My Head",
	copyright: "“God Be in My Head”  Music used by permission of Oxford University Press.",
	1: "God be in my head, and in my understanding;\nGod be in mine eyes, and in my looking;\nGod be in my mouth, and in my speaking;\nGod be in my heart, and in my thinking;\nGod be at mine end, and at my departing."
}
song["sda-hymnal/679"] = {
	title: "God Be in My Head",
	copyright: "“God Be in My Head” Music copyright © 1984 by Lorayne Coombs.",
	1: "God be in my head,\nAnd in my thinking.\ngod be in my eyes,\nAnd in my looking.\nGod be in my mouth\nAnd in my speaking.\nOh, God be in my hear,\nAnd in my understanding."
}
song["sda-hymnal/680"] = {
	title: "Holy Spirit, Hear Us",
	tune: "Ernstein",
	1: "Holy Spirit, hear us;\nHelp us while we sing;\nBreath into the music\nOf the praise we bring.",
	2: "Holy Spirit, prompt us\nWhen we kneel to pray;\nNearer come, and teach us\nWhat we ought to say.",
	3: "Holy Spirit, shine Thou\nOn the Book we read;\nGild it’s holy pages\nWith the light we need."
}
song["sda-hymnal/681"] = {
	title: "This Is the Day the Lord Hath Made",
	tune: "Twenty-Fourth",
	scripture: "Psalm 118:24",
	info: "Unison setting, No. 406",
	1: "This is the day the Lord hath made;\nhe calls the hours his own.\nLet heaven rejoice, let earth be glad,\nand praise surround the throne."
}
song["sda-hymnal/682"] = {
	title: "As You Have Promised, Lord",
	tune: "Nunc Dimittis",
	scripture: "Luke 2:29-32",
	copyright: "“As You Have Promised, Lord”  Reprinted by permission of the publisher from The Hymnal of the United Methodist Church of Christ. Copyright © 1974 United Church Press.",
	1: "As You have promised, Lord, today,\nYou are letting Your servant go away in peace.\nMay eyes have seen You in broad daylight\nbefore all nations, planning salvation.\nLight of revelation for the nations,\nand glory of Your people Israel."
}
song["sda-hymnal/683"] = {
	title: "Jesus, Stand Among Us",
	tune: "Bemerton",
	scripture: "John 20:19, 22",
	1: "Jesus, stand among us\nIn Thy risen power;\nLet this time of worship\nBe a hallowed hour.",
	2: "Breathe the Holy Spirit\nInto every heart;\nBid the fears and sorrows\nFrom each soul depart.",
	3: "Thus with quickened footsteps\nWe pursue our way,\nWatching for the dawning\nOf eternal day."
}
song["sda-hymnal/684"] = {
	title: "Hear Our Prayer, O Lord",
	1: "Hear Our Prayer, O Lord,\nHear our prayer, O Lord;\nIncline Thine ear to us,\nAnd grant us Thy peace.\nAmen."
}
song["sda-hymnal/685"] = {
	title: "Cause Me to Hear",
	composer: "Melvin West, 1984 (1930 - )",
	scripture: "Psalm 143:8, 9",
	copyright: "“Cause Me to Hear”  Music copyright © 1984 by Melvin West",
	1: "Cause me to hear Thy loving kindness in the morning,\nfor in Thee do I trust.\nCause me to know they way where-in I shall walk\nfor I lift up my soul to Thee.\nAmen"
}
song["sda-hymnal/686"] = {
	title: "Bless Thou the Gifts",
	tune: "Canonbury",
	1: "Bless thou the gifts our hands have brought;\nbless thou the work our hearts have planned.\nOurs is the faith, the will, the thought;\nthe rest, O God, is in thy hand.\nAmen."
}
song["sda-hymnal/687"] = {
	title: "The Lord Is in His Holy Temple",
	scripture: "Habakkuk",
	1: "The Lord is in HIs holy temple,\nThe Lord is in His holy temple,\nLet all the earth be silent\nbe silent before Him."
}
song["sda-hymnal/688"] = {
	title: "Surely, Surely",
	copyright: "“Surely, Surely”  Copyright © 1978 by Eleanor Wright",
	1: "Surely, surely the Lord has been here,\nSurely angels still linger near;\nI hear music soft on my ear,\nI feel His Spirit,\nI have no fear."
}
song["sda-hymnal/689"] = {
	title: "Day by Day, Dear Lord",
	copyright: "“Day by Day, Dear Lord”  Music copyright © 1960 by H. W. Gray Co., a division of Belwin-Mills Publishing Corp. Used by permission. All rights reserved.",
	1: "Day by day, Dear Lord, of Thee three things I pray:\nTo see Thee more clearly, Love Thee more dearly,\nFollow Thee more nearly, Day by day."
}
song["sda-hymnal/690"] = {
	title: "Dismiss Us, Lord With Blessing",
	tune: "Benediction (Froom)",
	1: "Dismiss us, Lord, with blessing, we pray;\nAs from Thy worship we go our ways;\nGuide in life’s conflicts, all through the day;\nSave in Thy kingdom, Thine be the praise.\nAmen."
}
song["sda-hymnal/691"] = {
	title: "Lead Me, Lord",
	scripture: "Psalms 5:8, 4:8",
	1: "Lead me, Lord, lead me in thy righteousness;\nmake thy way plain before my face.\nFor it is thou, Lord, thou, Lord only,\nthat makest me dwell in safety."
}
song["sda-hymnal/692"] = {
	title: "The Lord Is in His Holy Temple",
	tune: "Quam dilecta",
	scripture: "Habakkuk 2:20",
	1: "The Lord is in His holy temple,\nThe Lord is in His holy temple,\nLet all the earth keep silence,\nLet all the earth keep silence, before Him,\nKeep silence, keep silence, before Him.\nAmen."
}
song["sda-hymnal/693"] = {
	title: "Almighty Father",
	scripture: "Isaiah 30:18",
	1: "Almighty Father, hear our prayer,\nand bless all souls that wait before Thee.\nAmen."
}
song["sda-hymnal/694"] = {
	title: "Praise God, From Whom All Blessings",
	tune: "Old Hundredth",
	scripture: "Psalm 148:1, 2",
	1: "Praise God, from Whom all blessings flow;\nPraise Him, all creatures here below;\nPraise Him above, ye heavenly host;\nPraise Father, Son, and Holy Ghost."
}
song["sda-hymnal/695"] = {
	title: "Praise God, From Whom All Blessings",
	tune: "Old Hundredth",
	scripture: "Psalm 148:1, 2",
	1: "Praise God, from Whom all blessings flow;\nPraise Him, all creatures here below;\nPraise Him above, ye heavenly host;\nPraise Father, Son, and Holy Ghost."
}
